// Generate policy configuration based on device type and user role
const deviceType = $input.first().json.device_type;
const userRole = $input.first().json.user_role;
const bandwidthLimit = parseInt($input.first().json.bandwidth_limit);
const sessionTimeout = parseInt($input.first().json.session_timeout);
const vlanAssignment = parseInt($input.first().json.vlan_assignment);
const authMethod = $input.first().json.authentication_method;

// Policy templates based on device type and user role
const policyTemplates = {
  corporate: {
    network_access: 'full',
    bandwidth_down: bandwidthLimit || 100,
    bandwidth_up: (bandwidthLimit || 100) / 2,
    session_timeout: sessionTimeout || 0, // No timeout for corporate
    vlan_id: vlanAssignment || 10,
    firewall_rules: [
      { action: 'allow', destination: 'any', service: 'any' }
    ],
    application_policies: [
      { category: 'productivity', action: 'allow' },
      { category: 'business', action: 'allow' },
      { category: 'collaboration', action: 'allow' }
    ]
  },
  contractors: {
    network_access: 'limited',
    bandwidth_down: Math.min(bandwidthLimit || 50, 50),
    bandwidth_up: Math.min((bandwidthLimit || 50) / 2, 25),
    session_timeout: sessionTimeout || 480, // 8 hours
    vlan_id: vlanAssignment || 20,
    firewall_rules: [
      { action: 'allow', destination: 'internet', service: 'http' },
      { action: 'allow', destination: 'internet', service: 'https' },
      { action: 'deny', destination: 'internal', service: 'any' }
    ],
    time_restrictions: {
      allowed_hours: '08:00-18:00',
      allowed_days: 'Mon-Fri'
    }
  },
  guest: {
    network_access: 'internet_only',
    bandwidth_down: Math.min(bandwidthLimit || 10, 20),
    bandwidth_up: Math.min((bandwidthLimit || 10) / 2, 10),
    session_timeout: sessionTimeout || 240, // 4 hours
    vlan_id: vlanAssignment || 100,
    firewall_rules: [
      { action: 'allow', destination: 'internet', service: 'http' },
      { action: 'allow', destination: 'internet', service: 'https' },
      { action: 'deny', destination: 'internal', service: 'any' }
    ],
    captive_portal: true,
    terms_acceptance_required: true
  },
  byod: {
    network_access: 'segregated',
    bandwidth_down: Math.min(bandwidthLimit || 30, 50),
    bandwidth_up: Math.min((bandwidthLimit || 30) / 2, 25),
    session_timeout: sessionTimeout || 720, // 12 hours
    vlan_id: vlanAssignment || 30,
    firewall_rules: [
      { action: 'allow', destination: 'internet', service: 'any' },
      { action: 'allow', destination: 'internal', service: 'email' },
      { action: 'allow', destination: 'internal', service: 'web' },
      { action: 'deny', destination: 'internal', service: 'file_share' }
    ],
    device_compliance_required: true,
    certificate_auth: authMethod === '802.1x'
  },
  iot: {
    network_access: 'restricted',
    bandwidth_down: Math.min(bandwidthLimit || 5, 10),
    bandwidth_up: Math.min((bandwidthLimit || 5) / 2, 5),
    session_timeout: 0, // No timeout for IoT
    vlan_id: vlanAssignment || 200,
    firewall_rules: [
      { action: 'allow', destination: 'specific_servers', service: 'https' },
      { action: 'deny', destination: 'any', service: 'any' }
    ],
    device_specific_policies: true
  }
};

// Role-based enhancements
const roleEnhancements = {
  executive: {
    priority_bandwidth: true,
    qos_class: 'platinum',
    additional_bandwidth: 50
  },
  manager: {
    priority_bandwidth: true,
    qos_class: 'gold',
    additional_bandwidth: 25
  },
  employee: {
    qos_class: 'silver'
  },
  guest: {
    qos_class: 'bronze'
  }
};

// Generate final policy
let policy = policyTemplates[deviceType] || policyTemplates.guest;
const roleEnhancement = roleEnhancements[userRole] || roleEnhancements.guest;

// Apply role enhancements
if (roleEnhancement.additional_bandwidth) {
  policy.bandwidth_down += roleEnhancement.additional_bandwidth;
  policy.bandwidth_up += roleEnhancement.additional_bandwidth / 2;
}

policy.qos_class = roleEnhancement.qos_class;
policy.priority_bandwidth = roleEnhancement.priority_bandwidth || false;

// Add authentication method
policy.authentication = {
  method: authMethod,
  require_cert: authMethod === '802.1x',
  captive_portal: authMethod === 'captive_portal',
  mac_auth: authMethod === 'mac_auth',
  psk: authMethod === 'psk'
};

// Add compliance requirements
policy.compliance = {
  antivirus_required: deviceType === 'byod' || deviceType === 'corporate',
  os_updates_required: deviceType === 'corporate',
  encryption_required: deviceType === 'corporate' || deviceType === 'byod'
};

return [{
  json: {
    ...policy,
    policy_name: $input.first().json.policy_name,
    client_mac: $input.first().json.client_mac,
    timestamp: new Date().toISOString()
  }
}];

// Generate BYOD configuration
const deviceType = $input.first().json.device_type;
const authMethod = $input.first().json.authentication_method;
const certificateType = $input.first().json.certificate_type;

// BYOD configuration template
const byodConfig = {
  enabled: true,
  device_registration: {
    self_registration: true,
    admin_approval_required: deviceType === 'corporate',
    sponsor_approval_required: deviceType === 'contractor'
  },
  authentication: {
    method: authMethod,
    certificate_based: authMethod === '802.1x',
    multi_factor: deviceType === 'corporate'
  },
  device_compliance: {
    antivirus_check: true,
    os_version_check: true,
    jailbreak_detection: true,
    device_encryption_required: true
  },
  onboarding: {
    automatic_profile_install: true,
    wifi_profile_install: true,
    certificate_install: authMethod === '802.1x',
    vpn_profile_install: deviceType === 'corporate'
  },
  policies: {
    container_mode: deviceType === 'corporate',
    app_wrapping: true,
    data_loss_prevention: true,
    remote_wipe_enabled: true
  },
  certificates: {
    type: certificateType,
    auto_renewal: true,
    validity_period: 365, // days
    ca_validation: true
  },
  network_access: {
    vlan_assignment: 'dynamic',
    quarantine_vlan: 999,
    compliant_vlan: 30,
    non_compliant_action: 'quarantine'
  }
};

return [{
  json: {
    ...byodConfig,
    timestamp: new Date().toISOString(),
    configured_by: 'n8n_automation'
  }
}];

// Client troubleshooting automation
const clientMac = $input.first().json.client_mac;
const baseUrl = $input.first().json.central_base_url;
const customerId = $input.first().json.customer_id;

// Troubleshooting steps to perform
const troubleshootingSteps = [
  {
    name: 'Get Client Information',
    url: `${baseUrl}/monitoring/v1/clients`,
    method: 'GET',
    params: { macaddr: clientMac }
  },
  {
    name: 'Check Authentication Status',
    url: `${baseUrl}/monitoring/v1/clients/${clientMac}/authentication`,
    method: 'GET'
  },
  {
    name: 'Get Client Health',
    url: `${baseUrl}/monitoring/v1/clients/${clientMac}/health`,
    method: 'GET'
  },
  {
    name: 'Check Policy Assignment',
    url: `${baseUrl}/configuration/v1/clients/${clientMac}/policy`,
    method: 'GET'
  },
  {
    name: 'Get Audit Trail',
    url: `${baseUrl}/monitoring/v1/audit_trail`,
    method: 'GET',
    params: { client_mac: clientMac, limit: 50 }
  }
];

return troubleshootingSteps.map(step => {
  return {
    json: {
      ...step,
      customer_id: customerId,
      timestamp: new Date().toISOString()
    }
  };
});

// Aggregate troubleshooting results
const allResults = $input.all();
const clientMac = $('Parse Input Parameters').first().json.client_mac;

// Analyze results and generate diagnosis
const diagnosis = {
  client_mac: clientMac,
  timestamp: new Date().toISOString(),
  overall_status: 'healthy',
  issues_found: [],
  recommendations: [],
  detailed_results: {}
};

allResults.forEach((result, index) => {
  const stepName = result.json.name || `Step ${index + 1}`;
  diagnosis.detailed_results[stepName] = {
    status: result.json.statusCode || 'unknown',
    data: result.json.data || result.json,
    timestamp: result.json.timestamp
  };

  // Analyze for common issues
  if (result.json.statusCode >= 400) {
    diagnosis.overall_status = 'issues_detected';
    diagnosis.issues_found.push({
      step: stepName,
      issue: `API call failed with status ${result.json.statusCode}`,
      severity: 'high'
    });
  }

  // Check for authentication issues
  if (stepName === 'Check Authentication Status' && result.json.data) {
    const authData = result.json.data;
    if (authData.auth_status === 'failed') {
      diagnosis.issues_found.push({
        step: stepName,
        issue: 'Client authentication failed',
        severity: 'high',
        details: authData
      });
      diagnosis.recommendations.push('Check client credentials and authentication method');
    }
  }

  // Check for policy issues
  if (stepName === 'Check Policy Assignment' && result.json.data) {
    const policyData = result.json.data;
    if (!policyData.policy_assigned) {
      diagnosis.issues_found.push({
        step: stepName,
        issue: 'No policy assigned to client',
        severity: 'medium'
      });
      diagnosis.recommendations.push('Assign appropriate policy based on device type and user role');
    }
  }

  // Check client health
  if (stepName === 'Get Client Health' && result.json.data) {
    const healthData = result.json.data;
    if (healthData.signal_strength < -70) {
      diagnosis.issues_found.push({
        step: stepName,
        issue: 'Poor signal strength detected',
        severity: 'medium',
        details: { signal_strength: healthData.signal_strength }
      });
      diagnosis.recommendations.push('Consider relocating access point or checking for interference');
    }
  }
});

// Set overall status based on issues
if (diagnosis.issues_found.length === 0) {
  diagnosis.overall_status = 'healthy';
} else if (diagnosis.issues_found.some(issue => issue.severity === 'high')) {
  diagnosis.overall_status = 'critical';
} else {
  diagnosis.overall_status = 'issues_detected';
}

return [{ json: diagnosis }];

// Enhanced error handling with policy rollback
const error = $input.first().json;
const operation = $('Parse Input Parameters').first()?.json?.operation || 'unknown';
const clientMac = $('Parse Input Parameters').first()?.json?.client_mac;
const userId = $('Parse Input Parameters').first()?.json?.user_id;

// Categorize error types
let errorCategory = 'unknown';
let rollbackRequired = false;
let rollbackSteps = [];

if (error.message?.includes('authentication') || error.message?.includes('401')) {
  errorCategory = 'authentication';
} else if (error.message?.includes('authorization') || error.message?.includes('403')) {
  errorCategory = 'authorization';
} else if (error.message?.includes('timeout') || error.message?.includes('ETIMEDOUT')) {
  errorCategory = 'timeout';
} else if (error.message?.includes('network') || error.message?.includes('ECONNREFUSED')) {
  errorCategory = 'network';
} else if (error.message?.includes('rate limit') || error.message?.includes('429')) {
  errorCategory = 'rate_limit';
} else if (error.statusCode >= 500) {
  errorCategory = 'server_error';
} else if (error.statusCode >= 400) {
  errorCategory = 'client_error';
}

// Determine rollback requirements
if (['apply_policy', 'create_user', 'configure_byod'].includes(operation)) {
  rollbackRequired = true;

  if (operation === 'apply_policy' && clientMac) {
    rollbackSteps.push({
      action: 'remove_policy',
      target: clientMac,
      api_call: `/configuration/v1/clients/${clientMac}/policy`,
      method: 'DELETE'
    });
  }

  if (operation === 'create_user' && userId) {
    rollbackSteps.push({
      action: 'delete_user',
      target: userId,
      api_call: `/platform/authz/v1/users/${userId}`,
      method: 'DELETE'
    });
  }

  if (operation === 'configure_byod') {
    rollbackSteps.push({
      action: 'reset_byod_config',
      api_call: '/device/v1/byod',
      method: 'PUT',
      body: { enabled: false }
    });
  }
}

// Generate recovery recommendations
const recoveryRecommendations = {
  authentication: [
    'Verify API credentials are valid and not expired',
    'Check token permissions and scopes',
    'Regenerate access token if necessary'
  ],
  authorization: [
    'Verify user has required permissions for this operation',
    'Check customer ID is correct',
    'Review role assignments'
  ],
  timeout: [
    'Retry the operation with exponential backoff',
    'Check network connectivity to Aruba Central',
    'Consider splitting bulk operations into smaller batches'
  ],
  network: [
    'Verify network connectivity',
    'Check firewall rules and proxy settings',
    'Test with different network connection'
  ],
  rate_limit: [
    'Implement rate limiting in workflow',
    'Add delays between API calls',
    'Consider using bulk operations where available'
  ],
  server_error: [
    'Retry after short delay',
    'Check Aruba Central service status',
    'Contact Aruba support if persistent'
  ],
  client_error: [
    'Review request parameters for validity',
    'Check API documentation for required fields',
    'Validate input data format'
  ]
};

const errorResponse = {
  error_id: `error_${Date.now()}`,
  timestamp: new Date().toISOString(),
  operation_failed: operation,
  error_category: errorCategory,
  error_details: {
    message: error.message,
    status_code: error.statusCode,
    node_name: error.node?.name,
    stack_trace: error.stack
  },
  rollback_required: rollbackRequired,
  rollback_steps: rollbackSteps,
  recovery_recommendations: recoveryRecommendations[errorCategory] || recoveryRecommendations.unknown,
  next_actions: [
    'Review error details and implement recommended fixes',
    'Execute rollback steps if necessary',
    'Test with corrected parameters',
    'Monitor for recurring issues'
  ]
};

return [{ json: errorResponse }];

