// Prepare final response with comprehensive results
const inputParams = $('Parse Input Parameters').first().json;
const operation = inputParams.operation;

// Collect results from different operation paths
let operationResult = {};
let success = true;
let message = '';

// Determine which operation was executed and collect results
switch(operation) {
  case 'create_user':
    const createUserResult = $('Create User API Call').first()?.json;
    if (createUserResult) {
      operationResult = createUserResult;
      success = createUserResult.statusCode < 400;
      message = success ? 'User created successfully' : 'Failed to create user';
    }
    break;

  case 'update_user':
    const updateUserResult = $('Update User API Call').first()?.json;
    if (updateUserResult) {
      operationResult = updateUserResult;
      success = updateUserResult.statusCode < 400;
      message = success ? 'User updated successfully' : 'Failed to update user';
    }
    break;

  case 'create_guest':
    const createGuestResult = $('Create Guest User API Call').first()?.json;
    if (createGuestResult) {
      operationResult = createGuestResult;
      success = createGuestResult.statusCode < 400;
      message = success ? 'Guest user created successfully' : 'Failed to create guest user';
    }
    break;

  case 'apply_policy':
    const policyResult = $('Apply Client Policy API Call').first()?.json;
    if (policyResult) {
      operationResult = policyResult;
      success = policyResult.statusCode < 400;
      message = success ? 'Policy applied successfully' : 'Failed to apply policy';
    }
    break;

  case 'configure_byod':
    const byodResult = $('Configure BYOD API Call').first()?.json;
    if (byodResult) {
      operationResult = byodResult;
      success = byodResult.statusCode < 400;
      message = success ? 'BYOD configured successfully' : 'Failed to configure BYOD';
    }
    break;

  case 'troubleshoot_client':
    const troubleshootResult = $('Analyze Troubleshooting Results').first()?.json;
    if (troubleshootResult) {
      operationResult = troubleshootResult;
      success = troubleshootResult.overall_status !== 'critical';
      message = `Client troubleshooting completed - Status: ${troubleshootResult.overall_status}`;
    }
    break;

  default:
    message = `Unknown operation: ${operation}`;
    success = false;
}

// Check for error handling results
const errorResult = $('Process Error and Generate Rollback').first()?.json;
if (errorResult) {
  success = false;
  message = `Operation failed: ${errorResult.error_category}`;
  operationResult = errorResult;
}

// Generate comprehensive response
const response = {
  workflow_name: 'Aruba Central Client Policy Management',
  execution_id: $execution.id,
  timestamp: new Date().toISOString(),
  operation: operation,
  success: success,
  message: message,
  input_parameters: inputParams,
  operation_result: operationResult,
  execution_summary: {
    duration_ms: Date.now() - new Date($execution.startedAt).getTime(),
    nodes_executed: $workflow.nodes.length,
    api_calls_made: Object.keys(operationResult).length > 0 ? 1 : 0
  }
};

// Add policy details for policy operations
if (operation === 'apply_policy') {
  const policyConfig = $('Generate Policy Configuration').first()?.json;
  if (policyConfig) {
    response.policy_details = {
      device_type: inputParams.device_type,
      user_role: inputParams.user_role,
      bandwidth_limit: policyConfig.bandwidth_down,
      session_timeout: policyConfig.session_timeout,
      vlan_assignment: policyConfig.vlan_id,
      authentication_method: policyConfig.authentication?.method
    };
  }
}

// Add troubleshooting details for troubleshoot operations
if (operation === 'troubleshoot_client') {
  const troubleshootResult = $('Analyze Troubleshooting Results').first()?.json;
  if (troubleshootResult) {
    response.troubleshooting_summary = {
      overall_status: troubleshootResult.overall_status,
      issues_found: troubleshootResult.issues_found?.length || 0,
      recommendations: troubleshootResult.recommendations?.length || 0,
      steps_executed: Object.keys(troubleshootResult.detailed_results || {}).length
    };
  }
}

return [{ json: response }];
