// Aruba Central Location Services Input Validation and Processing
// Extract and validate all input parameters for location services operations

const requestBody = $json.body || $json;

// Required parameters
const operation = requestBody.operation;
const site_id = requestBody.site_id;
const central_base_url = requestBody.central_base_url || 'https://apigw-uswest4.central.arubanetworks.com';
const customer_id = requestBody.customer_id;

// Validate required parameters
if (!operation) {
  throw new Error('Missing required parameter: operation');
}
if (!site_id) {
  throw new Error('Missing required parameter: site_id');
}
if (!customer_id) {
  throw new Error('Missing required parameter: customer_id');
}

// Valid operations
const validOperations = [
  'setup_rtls', 'configure_beacon', 'create_geofence', 
  'set_ap_location', 'analytics_setup', 'proximity_config',
  'list_sites', 'create_campus', 'update_campus', 'create_building',
  'get_aps', 'list_beacons', 'delete_beacon', 'get_clients',
  'presence_analytics', 'update_geofence'
];

if (!validOperations.includes(operation)) {
  throw new Error(`Invalid operation. Must be one of: ${validOperations.join(', ')}`);
}

// Extract all parameters based on operation type
let processedData = {
  operation: operation,
  site_id: site_id,
  central_base_url: central_base_url,
  customer_id: customer_id,
  site_name: requestBody.site_name || `Site-${site_id}`,
  campus_name: requestBody.campus_name || 'Main Campus',
  building_name: requestBody.building_name || 'Building A',
  floor_name: requestBody.floor_name || 'Floor 1'
};

// Operation-specific parameter extraction and validation
switch (operation) {
  case 'setup_rtls':
    processedData.rtls_config = {
      accuracy_mode: requestBody.accuracy_mode || 'high', // high, medium, low
      update_interval: requestBody.update_interval || 30, // seconds
      positioning_algorithm: requestBody.positioning_algorithm || 'trilateration',
      enable_analytics: requestBody.enable_analytics !== false,
      minimum_aps: requestBody.minimum_aps || 3
    };
    break;

  case 'configure_beacon':
    // Beacon configuration parameters
    processedData.beacon_type = requestBody.beacon_type || 'ibeacon';
    if (!['ibeacon', 'eddystone', 'both'].includes(processedData.beacon_type)) {
      throw new Error('beacon_type must be: ibeacon, eddystone, or both');
    }

    // iBeacon parameters
    if (processedData.beacon_type === 'ibeacon' || processedData.beacon_type === 'both') {
      processedData.beacon_uuid = requestBody.beacon_uuid || 'E2C56DB5-DFFB-48D2-B060-D0F5A71096E0';
      processedData.beacon_major = requestBody.beacon_major || 1;
      processedData.beacon_minor = requestBody.beacon_minor || 1;

      // Validate UUID format
      const uuidRegex = /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i;
      if (!uuidRegex.test(processedData.beacon_uuid)) {
        throw new Error('Invalid beacon_uuid format. Must be standard UUID format.');
      }
    }

    // Eddystone parameters
    if (processedData.beacon_type === 'eddystone' || processedData.beacon_type === 'both') {
      processedData.eddystone_url = requestBody.eddystone_url || 'https://aruba.com';
      if (!processedData.eddystone_url.startsWith('http')) {
        throw new Error('eddystone_url must be a valid HTTP/HTTPS URL');
      }
    }

    // Common beacon parameters
    processedData.beacon_power = requestBody.beacon_power || -12; // dBm
    processedData.beacon_interval = requestBody.beacon_interval || 1000; // ms

    // Validate power range
    if (processedData.beacon_power < -12 || processedData.beacon_power > 4) {
      throw new Error('beacon_power must be between -12 and 4 dBm');
    }

    // Validate interval range
    if (processedData.beacon_interval < 100 || processedData.beacon_interval > 10000) {
      throw new Error('beacon_interval must be between 100 and 10000 ms');
    }
    break;

  case 'set_ap_location':
    processedData.ap_serial = requestBody.ap_serial;
    if (!processedData.ap_serial) {
      throw new Error('ap_serial is required for set_ap_location operation');
    }

    processedData.ap_coordinates = requestBody.ap_coordinates || { x: 0, y: 0, z: 0 };
    if (typeof processedData.ap_coordinates !== 'object') {
      throw new Error('ap_coordinates must be an object with x, y, z properties');
    }
    break;

  case 'create_geofence':
    processedData.geofence_name = requestBody.geofence_name;
    if (!processedData.geofence_name) {
      throw new Error('geofence_name is required for create_geofence operation');
    }

    processedData.geofence_coordinates = requestBody.geofence_coordinates || [];
    if (!Array.isArray(processedData.geofence_coordinates) || processedData.geofence_coordinates.length < 3) {
      throw new Error('geofence_coordinates must be an array with at least 3 coordinate points');
    }

    processedData.geofence_type = requestBody.geofence_type || 'enter';
    if (!['enter', 'exit', 'dwell'].includes(processedData.geofence_type)) {
      throw new Error('geofence_type must be: enter, exit, or dwell');
    }
    break;

  case 'analytics_setup':
    processedData.analytics_enabled = requestBody.analytics_enabled !== false;
    processedData.asset_tracking = requestBody.asset_tracking !== false;
    processedData.analytics_config = {
      retention_days: requestBody.retention_days || 30,
      export_format: requestBody.export_format || 'json',
      real_time_updates: requestBody.real_time_updates !== false,
      privacy_mode: requestBody.privacy_mode || 'anonymized'
    };
    break;

  case 'proximity_config':
    processedData.proximity_distance = requestBody.proximity_distance || 2; // meters
    processedData.proximity_action = requestBody.proximity_action || 'notify';
    processedData.proximity_message = requestBody.proximity_message || 'Welcome!';
    break;
}

// Environment-specific templates for location services
const environmentTemplates = {
  retail: {
    analytics_focus: ['customer_flow', 'heat_maps', 'dwell_time'],
    beacon_density: 'high',
    geofence_types: ['entrance', 'department', 'checkout'],
    privacy_level: 'high'
  },
  healthcare: {
    analytics_focus: ['asset_tracking', 'patient_flow', 'staff_efficiency'],
    beacon_density: 'medium',
    geofence_types: ['room', 'department', 'emergency_exit'],
    privacy_level: 'maximum'
  },
  corporate: {
    analytics_focus: ['workspace_utilization', 'desk_usage', 'meeting_room_optimization'],
    beacon_density: 'medium',
    geofence_types: ['floor', 'department', 'meeting_room'],
    privacy_level: 'medium'
  },
  education: {
    analytics_focus: ['attendance', 'space_utilization', 'safety_monitoring'],
    beacon_density: 'medium',
    geofence_types: ['classroom', 'library', 'cafeteria'],
    privacy_level: 'high'
  },
  manufacturing: {
    analytics_focus: ['equipment_tracking', 'safety_zones', 'efficiency_monitoring'],
    beacon_density: 'high',
    geofence_types: ['production_line', 'safety_zone', 'storage'],
    privacy_level: 'low'
  },
  hospitality: {
    analytics_focus: ['guest_services', 'way_finding', 'service_optimization'],
    beacon_density: 'high',
    geofence_types: ['lobby', 'restaurant', 'amenities'],
    privacy_level: 'medium'
  }
};

// Apply environment template if specified
if (requestBody.environment_type && environmentTemplates[requestBody.environment_type]) {
  processedData.environment_template = environmentTemplates[requestBody.environment_type];
  processedData.environment_type = requestBody.environment_type;
}

// Generate timestamp for tracking
processedData.timestamp = new Date().toISOString();
processedData.workflow_id = `location-${operation}-${Date.now()}`;

// Set headers for Aruba Central API
processedData.api_headers = {
  'Authorization': `Bearer ${requestBody.access_token || '{{$credentials.arubaCentralApi.accessToken}}'}`,
  'Content-Type': 'application/json',
  'X-API-Version': '2',
  'Accept': 'application/json'
};

console.log('Location Services Input Processed:', {
  operation: processedData.operation,
  site_id: processedData.site_id,
  workflow_id: processedData.workflow_id,
  environment: processedData.environment_type || 'default',
  timestamp: processedData.timestamp
});

return { json: processedData };

// Location Services Response Processing and Error Handling
// Process API responses and handle various error scenarios

const inputData = $input.all();
const originalRequest = $('Input Processor').all()[0].json;

let results = [];
let errors = [];
let successCount = 0;
let rollbackActions = [];

// Process each operation result
for (const item of inputData) {
  try {
    const response = item.json;
    const statusCode = response.statusCode || 200;

    // Successful response processing
    if (statusCode >= 200 && statusCode < 300) {
      successCount++;

      // Extract relevant data based on operation
      let processedResult = {
        operation: originalRequest.operation,
        site_id: originalRequest.site_id,
        status: 'success',
        timestamp: new Date().toISOString(),
        data: response
      };

      // Operation-specific result processing
      switch (originalRequest.operation) {
        case 'setup_rtls':
          processedResult.rtls_id = response.rtls_id || response.id;
          processedResult.campus_id = response.campus_id;
          processedResult.message = 'RTLS setup completed successfully';
          rollbackActions.push({
            action: 'delete_rtls',
            campus_id: response.campus_id,
            url: `${originalRequest.central_base_url}/location/v1/sites/${originalRequest.site_id}/campus/${response.campus_id}`
          });
          break;

        case 'configure_beacon':
          processedResult.beacon_id = response.beacon_id || response.id;
          processedResult.beacon_uuid = response.uuid || originalRequest.beacon_uuid;
          processedResult.message = `Beacon configured: ${originalRequest.beacon_type}`;
          rollbackActions.push({
            action: 'delete_beacon',
            beacon_id: response.beacon_id,
            url: `${originalRequest.central_base_url}/location/v1/beacons/${response.beacon_id}`
          });
          break;

        case 'create_geofence':
          processedResult.geofence_id = response.geofence_id || response.id;
          processedResult.geofence_name = response.name || originalRequest.geofence_name;
          processedResult.message = `Geofence created: ${originalRequest.geofence_name}`;
          rollbackActions.push({
            action: 'delete_geofence',
            geofence_id: response.geofence_id,
            url: `${originalRequest.central_base_url}/location/v1/geofences/${response.geofence_id}`
          });
          break;

        case 'set_ap_location':
          processedResult.ap_serial = originalRequest.ap_serial;
          processedResult.coordinates = originalRequest.ap_coordinates;
          processedResult.message = `AP location set: ${originalRequest.ap_serial}`;
          break;

        case 'analytics_setup':
          processedResult.analytics_id = response.analytics_id || response.id;
          processedResult.message = 'Location analytics configured successfully';
          break;

        case 'proximity_config':
          processedResult.proximity_id = response.proximity_id || response.id;
          processedResult.message = 'Proximity services configured successfully';
          break;
      }

      results.push(processedResult);

    } else {
      // Error response handling
      const errorDetails = {
        operation: originalRequest.operation,
        site_id: originalRequest.site_id,
        status: 'error',
        timestamp: new Date().toISOString(),
        error_code: statusCode,
        error_message: response.message || response.error || 'Unknown error',
        details: response
      };

      // Categorize errors for better handling
      if (statusCode === 400) {
        errorDetails.error_category = 'validation_error';
        errorDetails.suggested_action = 'Check input parameters and try again';
      } else if (statusCode === 401) {
        errorDetails.error_category = 'authentication_error';
        errorDetails.suggested_action = 'Check API credentials and refresh token';
      } else if (statusCode === 403) {
        errorDetails.error_category = 'authorization_error';
        errorDetails.suggested_action = 'Check API permissions for location services';
      } else if (statusCode === 404) {
        errorDetails.error_category = 'resource_not_found';
        errorDetails.suggested_action = 'Verify site ID and resource existence';
      } else if (statusCode === 409) {
        errorDetails.error_category = 'conflict_error';
        errorDetails.suggested_action = 'Resource already exists or conflicts with existing configuration';
      } else if (statusCode === 429) {
        errorDetails.error_category = 'rate_limit_error';
        errorDetails.suggested_action = 'Wait and retry, implement rate limiting';
      } else if (statusCode >= 500) {
        errorDetails.error_category = 'server_error';
        errorDetails.suggested_action = 'Retry operation, contact support if persistent';
      } else {
        errorDetails.error_category = 'unknown_error';
        errorDetails.suggested_action = 'Review error details and contact support';
      }

      errors.push(errorDetails);
    }

  } catch (error) {
    // Handle processing errors
    errors.push({
      operation: originalRequest.operation,
      site_id: originalRequest.site_id,
      status: 'processing_error',
      timestamp: new Date().toISOString(),
      error_message: error.message,
      error_category: 'processing_error',
      suggested_action: 'Review workflow logic and input data'
    });
  }
}

// Determine overall operation status
let overallStatus = 'success';
if (errors.length > 0) {
  overallStatus = successCount > 0 ? 'partial_success' : 'failure';
}

// Create comprehensive result
const finalResult = {
  workflow_id: originalRequest.workflow_id,
  operation: originalRequest.operation,
  site_id: originalRequest.site_id,
  overall_status: overallStatus,
  timestamp: new Date().toISOString(),
  summary: {
    total_operations: inputData.length,
    successful: successCount,
    failed: errors.length,
    success_rate: Math.round((successCount / inputData.length) * 100)
  },
  results: results,
  errors: errors,
  rollback_actions: rollbackActions,
  environment_template: originalRequest.environment_template,
  next_steps: {
    verification_required: overallStatus !== 'success',
    rollback_available: rollbackActions.length > 0,
    monitoring_enabled: originalRequest.analytics_enabled !== false
  }
};

// Add operation-specific recommendations
switch (originalRequest.operation) {
  case 'setup_rtls':
    finalResult.recommendations = [
      'Verify AP locations are set for accurate positioning',
      'Test location accuracy with known reference points',
      'Monitor RTLS performance metrics',
      'Configure client tracking policies'
    ];
    break;

  case 'configure_beacon':
    finalResult.recommendations = [
      'Test beacon signal strength and range',
      'Verify mobile app beacon detection',
      'Monitor beacon battery status if applicable',
      'Document beacon deployment map'
    ];
    break;

  case 'create_geofence':
    finalResult.recommendations = [
      'Test geofence trigger accuracy',
      'Configure notification recipients',
      'Set up analytics for geofence events',
      'Document geofence purpose and actions'
    ];
    break;

  case 'analytics_setup':
    finalResult.recommendations = [
      'Configure data export schedules',
      'Set up dashboard access for stakeholders',
      'Review privacy compliance settings',
      'Monitor analytics data quality'
    ];
    break;
}

console.log('Location Services Processing Complete:', {
  operation: finalResult.operation,
  status: finalResult.overall_status,
  success_rate: finalResult.summary.success_rate,
  errors: finalResult.errors.length
});

return { json: finalResult };

// Location Services Rollback Handler
// Implement rollback for failed location service operations

const result = $json;
const rollbackActions = result.rollback_actions || [];

if (rollbackActions.length === 0) {
  console.log('No rollback actions required');
  return { json: { ...result, rollback_status: 'no_actions_required' } };
}

let rollbackResults = [];
let rollbackErrors = [];

// Execute rollback actions
for (const action of rollbackActions) {
  try {
    console.log(`Executing rollback action: ${action.action}`);

    // Prepare rollback API call
    const rollbackData = {
      method: 'DELETE',
      url: action.url,
      headers: {
        'Authorization': `Bearer {{$credentials.arubaCentralApi.accessToken}}`,
        'Content-Type': 'application/json',
        'X-API-Version': '2'
      },
      action_type: action.action,
      resource_id: action.beacon_id || action.geofence_id || action.campus_id,
      timestamp: new Date().toISOString()
    };

    rollbackResults.push(rollbackData);

  } catch (error) {
    rollbackErrors.push({
      action: action.action,
      error: error.message,
      timestamp: new Date().toISOString()
    });
  }
}

const finalResult = {
  ...result,
  rollback_status: rollbackErrors.length === 0 ? 'completed' : 'partial',
  rollback_actions_executed: rollbackResults.length,
  rollback_errors: rollbackErrors.length,
  rollback_details: rollbackResults
};

console.log('Rollback processing completed:', {
  actions_executed: rollbackResults.length,
  errors: rollbackErrors.length,
  status: finalResult.rollback_status
});

return { json: finalResult };

