// Location Services Final Response
// Prepare the final response for the webhook caller

const result = $json;

// Create a clean response for the API caller
const apiResponse = {
  status: result.overall_status,
  operation: result.operation,
  site_id: result.site_id,
  workflow_id: result.workflow_id,
  timestamp: result.timestamp,
  summary: result.summary,

  // Include success results
  results: result.results.map(r => ({
    operation: r.operation,
    status: r.status,
    message: r.message,
    id: r.beacon_id || r.geofence_id || r.rtls_id || r.analytics_id || r.proximity_id,
    data: r.data
  })),

  // Include error information if any
  errors: result.errors.map(e => ({
    operation: e.operation,
    error_category: e.error_category,
    error_message: e.error_message,
    suggested_action: e.suggested_action
  })),

  // Include recommendations
  recommendations: result.recommendations || [],

  // Include rollback information if applicable
  rollback: result.rollback_status ? {
    status: result.rollback_status,
    actions_executed: result.rollback_actions_executed,
    errors: result.rollback_errors
  } : undefined,

  // Next steps for the user
  next_steps: result.next_steps
};

// Log final response
console.log('Location Services API Response:', {
  status: apiResponse.status,
  operation: apiResponse.operation,
  results_count: apiResponse.results.length,
  errors_count: apiResponse.errors.length
});

return { json: apiResponse };
