// Process input parameters and determine operation
const scheduledRun = $input.first().json.hasOwnProperty('timestamp');
const webhookData = !scheduledRun ? $input.first().json : {};

// Default parameters for scheduled backup
const defaultParams = {
  operation: 'backup',
  backup_type: 'running',
  switch_ip: process.env.DEFAULT_SWITCH_IP || '192.168.1.100',
  backup_name: `scheduled_backup_${new Date().toISOString().split('T')[0]}_${Date.now()}`,
  compression_enabled: true,
  retention_days: 30,
  compare_configs: false
};

// Merge webhook parameters with defaults
const params = scheduledRun ? defaultParams : { ...defaultParams, ...webhookData };

// Validate required parameters
const validOperations = ['backup', 'restore', 'compare', 'list_backups', 'create_checkpoint'];
if (!validOperations.includes(params.operation)) {
  throw new Error(`Invalid operation: ${params.operation}. Must be one of: ${validOperations.join(', ')}`);
}

if (['backup', 'restore'].includes(params.operation) && !params.switch_ip) {
  throw new Error('switch_ip is required for backup and restore operations');
}

if (params.operation === 'restore' && !params.restore_file) {
  throw new Error('restore_file is required for restore operation');
}

// Validate IP address format
const ipRegex = /^(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;
if (params.switch_ip && !ipRegex.test(params.switch_ip)) {
  throw new Error(`Invalid IP address format: ${params.switch_ip}`);
}

// Set backup directory path
const backupDir = '/Users/jeangiet/Documents/Claude/aruba-workflows/aos-cx-config-management/backups';
params.backup_directory = backupDir;

// Generate backup filename if not provided
if (params.operation === 'backup' && !params.backup_name) {
  const timestamp = new Date().toISOString().replace(/[:.]/g, '-').split('T')[0];
  params.backup_name = `${params.switch_ip}_${params.backup_type}_${timestamp}_${Date.now()}`;
}

return [{ json: params }];

// Process backup and prepare file storage
const systemInfo = $input.first().json;
const configData = $input.all()[1].json;
const params = $('Parameter Processor').first().json;

// Extract system information
const deviceInfo = {
  hostname: systemInfo.hostname || 'unknown',
  software_version: systemInfo.software_version || 'unknown',
  platform_name: systemInfo.platform_name || 'unknown',
  serial_number: systemInfo.serial_number || 'unknown',
  model: systemInfo.product_model || 'unknown',
  mgmt_ip: systemInfo.mgmt_intf_status?.ip4_address || params.switch_ip
};

// Create backup metadata
const backupMetadata = {
  backup_timestamp: new Date().toISOString(),
  backup_type: params.backup_type,
  device_info: deviceInfo,
  backup_size: JSON.stringify(configData).length,
  backup_name: params.backup_name,
  compression_enabled: params.compression_enabled,
  api_version: 'v10.08',
  backup_method: 'REST_API'
};

// Create backup content
const backupContent = {
  metadata: backupMetadata,
  configuration: configData,
  created_by: 'AOS-CX Backup & Restore Workflow',
  workflow_version: '1.0.0'
};

// Prepare filename and path
const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
const filename = `${params.backup_name}${params.compression_enabled ? '.json.gz' : '.json'}`;
const filepath = `${params.backup_directory}/${filename}`;

// Convert to JSON string
let fileContent = JSON.stringify(backupContent, null, 2);

// Simulate compression if enabled (in real implementation, use compression library)
if (params.compression_enabled) {
  // For simulation, just add compression marker
  fileContent = JSON.stringify({
    compressed: true,
    original_size: fileContent.length,
    data: Buffer.from(fileContent).toString('base64')
  }, null, 2);
}

return [{
  json: {
    ...params,
    backup_metadata: backupMetadata,
    filepath: filepath,
    filename: filename,
    file_content: fileContent,
    backup_size: fileContent.length,
    device_info: deviceInfo
  }
}];

// Process and validate backup file for restore
const params = $('Parameter Processor').first().json;
const fileContent = $input.first().json.data;

try {
  // Parse backup file
  let backupData = JSON.parse(fileContent);

  // Handle compressed files
  if (backupData.compressed) {
    const decompressedData = Buffer.from(backupData.data, 'base64').toString();
    backupData = JSON.parse(decompressedData);
  }

  // Validate backup file structure
  if (!backupData.metadata || !backupData.configuration) {
    throw new Error('Invalid backup file format: missing metadata or configuration');
  }

  // Validate backup file is compatible
  const metadata = backupData.metadata;
  if (!metadata.device_info) {
    throw new Error('Invalid backup file: missing device information');
  }

  // Check if compare_configs is enabled
  if (params.compare_configs) {
    // In a real implementation, we would fetch current config and compare
    // For now, just log that comparison would happen
    console.log('Configuration comparison enabled - would compare current vs backup');
  }

  // Prepare restore data
  const restoreConfig = backupData.configuration;

  return [{
    json: {
      ...params,
      backup_metadata: metadata,
      restore_config: restoreConfig,
      backup_valid: true,
      restore_ready: true,
      original_backup_size: metadata.backup_size,
      device_info: metadata.device_info
    }
  }];

} catch (error) {
  throw new Error(`Failed to process backup file: ${error.message}`);
}

// List all backup files in directory
const params = $('Parameter Processor').first().json;
const fs = require('fs');
const path = require('path');

try {
  const backupDir = params.backup_directory;

  // Check if directory exists
  if (!fs.existsSync(backupDir)) {
    return [{
      json: {
        ...params,
        backups: [],
        total_backups: 0,
        message: 'Backup directory does not exist'
      }
    }];
  }

  // Read directory contents
  const files = fs.readdirSync(backupDir);
  const backupFiles = files.filter(file => file.endsWith('.json') || file.endsWith('.json.gz'));

  // Get file details
  const backups = backupFiles.map(filename => {
    const filepath = path.join(backupDir, filename);
    const stats = fs.statSync(filepath);

    // Try to read metadata from file
    let metadata = null;
    try {
      const content = fs.readFileSync(filepath, 'utf8');
      const data = JSON.parse(content);

      if (data.compressed) {
        const decompressed = Buffer.from(data.data, 'base64').toString();
        const backupData = JSON.parse(decompressed);
        metadata = backupData.metadata;
      } else {
        metadata = data.metadata;
      }
    } catch (e) {
      // If we can't read metadata, just use file stats
    }

    return {
      filename: filename,
      size: stats.size,
      created: stats.birthtime.toISOString(),
      modified: stats.mtime.toISOString(),
      metadata: metadata
    };
  });

  // Sort by creation date (newest first)
  backups.sort((a, b) => new Date(b.created) - new Date(a.created));

  return [{
    json: {
      ...params,
      backups: backups,
      total_backups: backups.length,
      backup_directory: backupDir
    }
  }];

} catch (error) {
  throw new Error(`Failed to list backups: ${error.message}`);
}

// Cleanup old backups based on retention policy
const params = $('Parameter Processor').first().json;
const listResult = $input.first().json;
const fs = require('fs');
const path = require('path');

const retentionDays = params.retention_days || 30;
const cutoffDate = new Date();
cutoffDate.setDate(cutoffDate.getDate() - retentionDays);

let deletedFiles = [];
let errors = [];

// Filter files older than retention period
const filesToDelete = listResult.backups.filter(backup => {
  return new Date(backup.created) < cutoffDate;
});

// Delete old files
filesToDelete.forEach(backup => {
  try {
    const filepath = path.join(params.backup_directory, backup.filename);
    fs.unlinkSync(filepath);
    deletedFiles.push(backup.filename);
  } catch (error) {
    errors.push({
      filename: backup.filename,
      error: error.message
    });
  }
});

const remainingBackups = listResult.total_backups - deletedFiles.length;

return [{
  json: {
    ...params,
    backups: listResult.backups.filter(b => !deletedFiles.includes(b.filename)),
    total_backups: remainingBackups,
    retention_applied: true,
    retention_days: retentionDays,
    deleted_files: deletedFiles,
    deletion_errors: errors,
    cleanup_date: new Date().toISOString()
  }
}];

// Compare current configuration with backup file
const params = $('Parameter Processor').first().json;
const currentConfig = $input.all()[0].json;
const backupFileContent = $input.all()[1].json.data;

try {
  // Parse backup file
  let backupData = JSON.parse(backupFileContent);

  if (backupData.compressed) {
    const decompressed = Buffer.from(backupData.data, 'base64').toString();
    backupData = JSON.parse(decompressed);
  }

  const backupConfig = backupData.configuration;

  // Simple comparison - in production, use more sophisticated diff
  const currentConfigStr = JSON.stringify(currentConfig, null, 2);
  const backupConfigStr = JSON.stringify(backupConfig, null, 2);

  const identical = currentConfigStr === backupConfigStr;

  // Calculate basic differences
  const currentKeys = Object.keys(currentConfig || {});
  const backupKeys = Object.keys(backupConfig || {});

  const addedKeys = currentKeys.filter(key => !backupKeys.includes(key));
  const removedKeys = backupKeys.filter(key => !currentKeys.includes(key));
  const commonKeys = currentKeys.filter(key => backupKeys.includes(key));

  const modifiedKeys = commonKeys.filter(key => {
    return JSON.stringify(currentConfig[key]) !== JSON.stringify(backupConfig[key]);
  });

  const comparisonResult = {
    identical: identical,
    total_differences: addedKeys.length + removedKeys.length + modifiedKeys.length,
    added_configurations: addedKeys,
    removed_configurations: removedKeys,
    modified_configurations: modifiedKeys,
    backup_metadata: backupData.metadata,
    comparison_timestamp: new Date().toISOString()
  };

  return [{
    json: {
      ...params,
      comparison_result: comparisonResult,
      current_config_size: currentConfigStr.length,
      backup_config_size: backupConfigStr.length
    }
  }];

} catch (error) {
  throw new Error(`Failed to compare configurations: ${error.message}`);
}

// Generate comprehensive success notification
const operation = $('Parameter Processor').first().json.operation;
const data = $input.first().json;

let message = '';
let details = {};
let color = 'good'; // Slack color

switch (operation) {
  case 'backup':
    message = `‚úÖ **AOS-CX Configuration Backup Successful**\n\n`;
    message += `‚Ä¢ **Device**: ${data.device_info?.hostname || 'Unknown'} (${data.switch_ip})\n`;
    message += `‚Ä¢ **Backup Type**: ${data.backup_type}\n`;
    message += `‚Ä¢ **File**: ${data.filename}\n`;
    message += `‚Ä¢ **Size**: ${(data.backup_size / 1024).toFixed(2)} KB\n`;
    message += `‚Ä¢ **Compressed**: ${data.compression_enabled ? 'Yes' : 'No'}\n`;
    message += `‚Ä¢ **Checkpoint Created**: Yes\n`;
    message += `‚Ä¢ **Timestamp**: ${new Date().toISOString()}\n`;

    details = {
      operation: 'backup',
      success: true,
      device: data.device_info,
      backup_file: data.filename,
      backup_size: data.backup_size
    };
    break;

  case 'restore':
    message = `‚úÖ **AOS-CX Configuration Restore Successful**\n\n`;
    message += `‚Ä¢ **Device**: ${data.switch_ip}\n`;
    message += `‚Ä¢ **Restored From**: ${data.restore_file}\n`;
    message += `‚Ä¢ **Original Backup**: ${data.backup_metadata?.backup_timestamp}\n`;
    message += `‚Ä¢ **Device Model**: ${data.backup_metadata?.device_info?.model || 'Unknown'}\n`;
    message += `‚Ä¢ **Verification**: Completed\n`;
    message += `‚Ä¢ **Timestamp**: ${new Date().toISOString()}\n`;

    details = {
      operation: 'restore',
      success: true,
      device: data.switch_ip,
      restore_file: data.restore_file,
      backup_metadata: data.backup_metadata
    };
    break;

  case 'compare':
    const identical = data.comparison_result?.identical;
    message = `üìä **Configuration Comparison ${identical ? 'Complete - No Changes' : 'Complete - Differences Found'}**\n\n`;
    message += `‚Ä¢ **Device**: ${data.switch_ip}\n`;
    message += `‚Ä¢ **Backup File**: ${data.backup_name}\n`;
    message += `‚Ä¢ **Identical**: ${identical ? 'Yes' : 'No'}\n`;

    if (!identical) {
      const result = data.comparison_result;
      message += `‚Ä¢ **Total Differences**: ${result.total_differences}\n`;
      message += `‚Ä¢ **Added**: ${result.added_configurations?.length || 0}\n`;
      message += `‚Ä¢ **Removed**: ${result.removed_configurations?.length || 0}\n`;
      message += `‚Ä¢ **Modified**: ${result.modified_configurations?.length || 0}\n`;
      color = 'warning';
    }

    details = {
      operation: 'compare',
      success: true,
      comparison_result: data.comparison_result
    };
    break;

  case 'list_backups':
    message = `üìÅ **Backup List Retrieved**\n\n`;
    message += `‚Ä¢ **Total Backups**: ${data.total_backups}\n`;
    message += `‚Ä¢ **Directory**: ${data.backup_directory}\n`;

    if (data.retention_applied) {
      message += `‚Ä¢ **Cleanup Applied**: ${data.deleted_files?.length || 0} old files deleted\n`;
      message += `‚Ä¢ **Retention**: ${data.retention_days} days\n`;
    }

    details = {
      operation: 'list_backups',
      success: true,
      total_backups: data.total_backups,
      backups: data.backups
    };
    break;

  default:
    message = `‚úÖ **AOS-CX Operation Completed**\n\n`;
    message += `‚Ä¢ **Operation**: ${operation}\n`;
    message += `‚Ä¢ **Status**: Success\n`;
    message += `‚Ä¢ **Timestamp**: ${new Date().toISOString()}\n`;

    details = {
      operation: operation,
      success: true,
      data: data
    };
}

return [{
  json: {
    notification: {
      message: message,
      color: color,
      details: details,
      timestamp: new Date().toISOString(),
      success: true
    },
    operation_result: data
  }
}];
