// AOS-CX Policy Deployment - Input Validation
const item = items[0].json;

// Initialize validation results
const validation = {
  isValid: true,
  errors: [],
  warnings: []
};

// Required parameters
const requiredParams = ['operation', 'switch_ip'];
for (const param of requiredParams) {
  if (!item[param]) {
    validation.errors.push(`Missing required parameter: ${param}`);
    validation.isValid = false;
  }
}

// Validate operation type
const validOperations = [
  'create_acl', 'update_acl', 'delete_acl', 'list_acls', 
  'apply_to_interface', 'create_qos_policy', 'get_qos', 'update_qos'
];
if (item.operation && !validOperations.includes(item.operation)) {
  validation.errors.push(`Invalid operation: ${item.operation}. Valid operations: ${validOperations.join(', ')}`);
  validation.isValid = false;
}

// Validate policy type
if (item.policy_type && !['acl', 'qos'].includes(item.policy_type)) {
  validation.errors.push(`Invalid policy_type: ${item.policy_type}. Must be 'acl' or 'qos'`);
  validation.isValid = false;
}

// Validate ACL type
if (item.acl_type && !['ipv4', 'ipv6', 'mac'].includes(item.acl_type)) {
  validation.errors.push(`Invalid acl_type: ${item.acl_type}. Must be 'ipv4', 'ipv6', or 'mac'`);
  validation.isValid = false;
}

// Validate direction
if (item.direction && !['in', 'out'].includes(item.direction)) {
  validation.errors.push(`Invalid direction: ${item.direction}. Must be 'in' or 'out'`);
  validation.isValid = false;
}

// Validate switch IP
if (item.switch_ip) {
  const ipRegex = /^(?:(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)\.){3}(?:25[0-5]|2[0-4][0-9]|[01]?[0-9][0-9]?)$/;
  if (!ipRegex.test(item.switch_ip)) {
    validation.errors.push(`Invalid switch_ip format: ${item.switch_ip}`);
    validation.isValid = false;
  }
}

// Validate ACL name format
if (item.acl_name) {
  if (item.acl_name.length > 64) {
    validation.errors.push('ACL name cannot exceed 64 characters');
    validation.isValid = false;
  }
  if (!/^[a-zA-Z0-9_-]+$/.test(item.acl_name)) {
    validation.errors.push('ACL name can only contain letters, numbers, underscores, and hyphens');
    validation.isValid = false;
  }
}

// Validate interface name format
if (item.interface_name) {
  const interfaceRegex = /^(\d+\/)*\d+(\/\d+)?$/;
  if (!interfaceRegex.test(item.interface_name)) {
    validation.warnings.push(`Interface name format may be invalid: ${item.interface_name}`);
  }
}

// Validate ACL rules structure
if (item.rules && Array.isArray(item.rules)) {
  item.rules.forEach((rule, index) => {
    if (!rule.sequence || !rule.action) {
      validation.errors.push(`Rule ${index + 1}: Missing required fields (sequence, action)`);
      validation.isValid = false;
    }
    if (rule.action && !['permit', 'deny'].includes(rule.action)) {
      validation.errors.push(`Rule ${index + 1}: Invalid action '${rule.action}'. Must be 'permit' or 'deny'`);
      validation.isValid = false;
    }
  });
}

// Set default values
const processedItem = {
  ...item,
  policy_type: item.policy_type || 'acl',
  acl_type: item.acl_type || 'ipv4',
  direction: item.direction || 'in',
  validation: validation,
  timestamp: new Date().toISOString(),
  workflow_id: $workflow.id,
  execution_id: $execution.id
};

// Apply policy templates if requested
if (item.template) {
  const templates = {
    'security_basic': {
      description: 'Basic security ACL blocking common threats',
      rules: [
        { sequence: 10, action: 'deny', protocol: 'tcp', destination: 'any', dst_port: '22', comment: 'Block SSH from outside' },
        { sequence: 20, action: 'deny', protocol: 'tcp', destination: 'any', dst_port: '23', comment: 'Block Telnet' },
        { sequence: 30, action: 'deny', protocol: 'tcp', destination: 'any', dst_port: '135', comment: 'Block RPC' },
        { sequence: 40, action: 'permit', protocol: 'tcp', source: 'any', destination: 'any', dst_port: '80', comment: 'Allow HTTP' },
        { sequence: 50, action: 'permit', protocol: 'tcp', source: 'any', destination: 'any', dst_port: '443', comment: 'Allow HTTPS' },
        { sequence: 60, action: 'deny', protocol: 'any', source: 'any', destination: 'any', comment: 'Deny all others' }
      ]
    },
    'guest_network': {
      description: 'Guest network isolation policy',
      rules: [
        { sequence: 10, action: 'permit', protocol: 'tcp', source: 'any', destination: 'any', dst_port: '53', comment: 'Allow DNS' },
        { sequence: 20, action: 'permit', protocol: 'udp', source: 'any', destination: 'any', dst_port: '53', comment: 'Allow DNS UDP' },
        { sequence: 30, action: 'permit', protocol: 'tcp', source: 'any', destination: 'any', dst_port: '80', comment: 'Allow HTTP' },
        { sequence: 40, action: 'permit', protocol: 'tcp', source: 'any', destination: 'any', dst_port: '443', comment: 'Allow HTTPS' },
        { sequence: 50, action: 'deny', protocol: 'any', source: 'any', destination: '192.168.0.0/16', comment: 'Block private networks' },
        { sequence: 60, action: 'deny', protocol: 'any', source: 'any', destination: '10.0.0.0/8', comment: 'Block private networks' },
        { sequence: 70, action: 'permit', protocol: 'any', source: 'any', destination: 'any', comment: 'Allow internet access' }
      ]
    },
    'iot_security': {
      description: 'IoT device security policy',
      rules: [
        { sequence: 10, action: 'permit', protocol: 'tcp', source: 'any', destination: 'any', dst_port: '443', comment: 'Allow HTTPS for updates' },
        { sequence: 20, action: 'permit', protocol: 'udp', source: 'any', destination: 'any', dst_port: '123', comment: 'Allow NTP' },
        { sequence: 30, action: 'permit', protocol: 'udp', source: 'any', destination: 'any', dst_port: '53', comment: 'Allow DNS' },
        { sequence: 40, action: 'deny', protocol: 'tcp', source: 'any', destination: 'any', dst_port: '22', comment: 'Block SSH' },
        { sequence: 50, action: 'deny', protocol: 'tcp', source: 'any', destination: 'any', dst_port: '23', comment: 'Block Telnet' },
        { sequence: 60, action: 'deny', protocol: 'any', source: 'any', destination: 'any', comment: 'Default deny' }
      ]
    },
    'qos_voice_priority': {
      description: 'QoS policy prioritizing voice traffic',
      qos_rules: {
        classes: [
          { name: 'voice', dscp: 46, priority: 7, bandwidth_percent: 30 },
          { name: 'video', dscp: 34, priority: 5, bandwidth_percent: 40 },
          { name: 'data', dscp: 0, priority: 1, bandwidth_percent: 30 }
        ],
        policies: [
          { class: 'voice', action: 'set_dscp', value: 46 },
          { class: 'video', action: 'set_dscp', value: 34 },
          { class: 'data', action: 'set_dscp', value: 0 }
        ]
      }
    }
  };

  if (templates[item.template]) {
    processedItem.rules = processedItem.rules || templates[item.template].rules;
    processedItem.qos_rules = processedItem.qos_rules || templates[item.template].qos_rules;
    processedItem.template_applied = item.template;
    processedItem.template_description = templates[item.template].description;
  } else {
    validation.warnings.push(`Unknown template: ${item.template}`);
  }
}

return [{ json: processedItem }];

// Build ACL Configuration for AOS-CX
const item = items[0].json;

// Basic ACL structure
const aclConfig = {
  [item.acl_name]: {
    list_type: item.acl_type || 'ipv4',
    cfg_aces: {}
  }
};

// Convert rules to AOS-CX format
if (item.rules && Array.isArray(item.rules)) {
  item.rules.forEach(rule => {
    const aceKey = rule.sequence.toString();
    const ace = {
      action: rule.action,
      comment: rule.comment || '',
      count: true,
      log: false
    };

    // Add protocol
    if (rule.protocol && rule.protocol !== 'any') {
      ace.protocol = rule.protocol;
    }

    // Add source
    if (rule.source && rule.source !== 'any') {
      ace.src_ip = rule.source;
    }

    // Add destination
    if (rule.destination && rule.destination !== 'any') {
      ace.dst_ip = rule.destination;
    }

    // Add destination port
    if (rule.dst_port) {
      ace.dst_l4_port_max = rule.dst_port;
      ace.dst_l4_port_min = rule.dst_port;
    }

    // Add source port
    if (rule.src_port) {
      ace.src_l4_port_max = rule.src_port;
      ace.src_l4_port_min = rule.src_port;
    }

    aclConfig[item.acl_name].cfg_aces[aceKey] = ace;
  });
}

const result = {
  ...item,
  acl_config: aclConfig,
  api_url: `https://${item.switch_ip}/rest/v10.08/system/acls`,
  method: 'POST'
};

return [{ json: result }];

// Build Interface ACL Configuration
const item = items[0].json;

// Interface configuration for ACL application
const interfaceConfig = {
  aclmac_in_cfg: item.direction === 'in' && item.acl_type === 'mac' ? {
    [item.acl_name]: {
      direction: 'in',
      type: 'mac'
    }
  } : undefined,
  aclmac_out_cfg: item.direction === 'out' && item.acl_type === 'mac' ? {
    [item.acl_name]: {
      direction: 'out',
      type: 'mac'
    }
  } : undefined,
  aclv4_in_cfg: item.direction === 'in' && item.acl_type === 'ipv4' ? {
    [item.acl_name]: {
      direction: 'in',
      type: 'ipv4'
    }
  } : undefined,
  aclv4_out_cfg: item.direction === 'out' && item.acl_type === 'ipv4' ? {
    [item.acl_name]: {
      direction: 'out',
      type: 'ipv4'
    }
  } : undefined,
  aclv6_in_cfg: item.direction === 'in' && item.acl_type === 'ipv6' ? {
    [item.acl_name]: {
      direction: 'in',
      type: 'ipv6'
    }
  } : undefined,
  aclv6_out_cfg: item.direction === 'out' && item.acl_type === 'ipv6' ? {
    [item.acl_name]: {
      direction: 'out',
      type: 'ipv6'
    }
  } : undefined
};

// Remove undefined properties
Object.keys(interfaceConfig).forEach(key => {
  if (interfaceConfig[key] === undefined) {
    delete interfaceConfig[key];
  }
});

const result = {
  ...item,
  interface_config: interfaceConfig,
  api_url: `https://${item.switch_ip}/rest/v10.08/system/interfaces/${item.interface_name}`,
  method: 'PUT'
};

return [{ json: result }];

// Build QoS Configuration for AOS-CX
const item = items[0].json;

// Basic QoS structure
const qosConfig = {
  global_qos: {
    qos_enable: true,
    qos_config: {
      classification: {},
      marking: {},
      queuing: {
        queue_profile: {}
      }
    }
  }
};

// Apply QoS rules if provided
if (item.qos_rules && item.qos_rules.classes) {
  item.qos_rules.classes.forEach((qosClass, index) => {
    const className = qosClass.name || `class_${index + 1}`;

    // Classification rules
    qosConfig.global_qos.qos_config.classification[className] = {
      dscp: qosClass.dscp || 0,
      priority: qosClass.priority || 0,
      match_criteria: qosClass.match_criteria || 'dscp'
    };

    // Marking rules
    qosConfig.global_qos.qos_config.marking[className] = {
      dscp_mark: qosClass.dscp || 0,
      cos_mark: qosClass.cos || 0
    };

    // Queue configuration
    qosConfig.global_qos.qos_config.queuing.queue_profile[className] = {
      algorithm: 'strict',
      bandwidth_percent: qosClass.bandwidth_percent || 10,
      priority: qosClass.priority || 0
    };
  });
}

// Apply policy rules if provided
if (item.qos_rules && item.qos_rules.policies) {
  item.qos_rules.policies.forEach(policy => {
    if (policy.action === 'set_dscp' && qosConfig.global_qos.qos_config.marking[policy.class]) {
      qosConfig.global_qos.qos_config.marking[policy.class].dscp_mark = policy.value;
    }
  });
}

const result = {
  ...item,
  qos_config: qosConfig,
  api_url: `https://${item.switch_ip}/rest/v10.08/system/qos`,
  method: 'PUT'
};

return [{ json: result }];

// Success Response Processing
const item = items[0].json;
const httpResponse = item.response || item;

// Extract response data
let responseData;
try {
  responseData = typeof httpResponse === 'string' ? JSON.parse(httpResponse) : httpResponse;
} catch (e) {
  responseData = httpResponse;
}

const result = {
  status: 'success',
  operation: item.operation,
  switch_ip: item.switch_ip,
  timestamp: new Date().toISOString(),
  execution_id: item.execution_id,
  response_data: responseData,
  summary: generateSummary(item.operation, responseData),
  next_steps: generateNextSteps(item.operation)
};

function generateSummary(operation, data) {
  switch (operation) {
    case 'list_acls':
      const aclCount = data && typeof data === 'object' ? Object.keys(data).length : 0;
      return `Found ${aclCount} ACL(s) configured on the switch`;
    case 'create_acl':
      return `Successfully created ACL '${item.acl_name}' with ${item.rules ? item.rules.length : 0} rules`;
    case 'update_acl':
      return `Successfully updated ACL '${item.acl_name}'`;
    case 'delete_acl':
      return `Successfully deleted ACL '${item.acl_name}'`;
    case 'apply_to_interface':
      return `Successfully applied ACL '${item.acl_name}' to interface '${item.interface_name}' (${item.direction}bound)`;
    case 'create_qos_policy':
      return `Successfully created/updated QoS policy with ${item.qos_rules && item.qos_rules.classes ? item.qos_rules.classes.length : 0} classes`;
    case 'get_qos':
      return `Retrieved QoS configuration from switch`;
    default:
      return `Completed ${operation} successfully`;
  }
}

function generateNextSteps(operation) {
  switch (operation) {
    case 'create_acl':
      return ['Apply ACL to interface(s)', 'Test ACL functionality', 'Monitor ACL hit counters'];
    case 'apply_to_interface':
      return ['Verify ACL is active on interface', 'Test traffic filtering', 'Monitor interface statistics'];
    case 'create_qos_policy':
      return ['Verify QoS classes are active', 'Monitor queue statistics', 'Test traffic prioritization'];
    default:
      return ['Verify configuration is active', 'Test functionality', 'Monitor performance'];
  }
}

return [{ json: result }];

// Error Response Processing
const item = items[0].json;
const error = item.error || item;

// Categorize error type
let errorCategory = 'unknown';
let rollbackRequired = false;
let rollbackSteps = [];

if (error.httpCode) {
  switch (error.httpCode) {
    case 400:
      errorCategory = 'invalid_request';
      break;
    case 401:
      errorCategory = 'authentication_failed';
      break;
    case 403:
      errorCategory = 'authorization_failed';
      break;
    case 404:
      errorCategory = 'resource_not_found';
      break;
    case 409:
      errorCategory = 'resource_conflict';
      rollbackRequired = true;
      break;
    case 422:
      errorCategory = 'validation_failed';
      break;
    case 500:
      errorCategory = 'server_error';
      rollbackRequired = true;
      break;
    default:
      errorCategory = 'http_error';
  }
}

// Determine rollback steps
if (rollbackRequired && item.operation) {
  switch (item.operation) {
    case 'create_acl':
      rollbackSteps = [
        `Delete ACL '${item.acl_name}' if partially created`,
        'Verify no interface assignments remain'
      ];
      break;
    case 'apply_to_interface':
      rollbackSteps = [
        `Remove ACL from interface '${item.interface_name}'`,
        'Restore previous interface configuration'
      ];
      break;
    case 'create_qos_policy':
      rollbackSteps = [
        'Restore previous QoS configuration',
        'Verify no traffic disruption'
      ];
      break;
  }
}

const result = {
  status: 'error',
  operation: item.operation,
  switch_ip: item.switch_ip,
  timestamp: new Date().toISOString(),
  execution_id: item.execution_id,
  error_category: errorCategory,
  error_message: error.message || error.error || 'Unknown error occurred',
  error_details: error,
  rollback_required: rollbackRequired,
  rollback_steps: rollbackSteps,
  troubleshooting: generateTroubleshootingSteps(errorCategory, item.operation)
};

function generateTroubleshootingSteps(category, operation) {
  const steps = [];

  switch (category) {
    case 'authentication_failed':
      steps.push('Verify API credentials are correct');
      steps.push('Check if authentication token has expired');
      steps.push('Confirm API access is enabled on the switch');
      break;
    case 'invalid_request':
      steps.push('Review request parameters and format');
      steps.push('Check ACL name and rule syntax');
      steps.push('Verify interface name format');
      break;
    case 'resource_conflict':
      steps.push('Check if ACL already exists');
      steps.push('Verify interface is not already configured');
      steps.push('Review existing policy conflicts');
      break;
    case 'validation_failed':
      steps.push('Review input validation errors');
      steps.push('Check ACL rule syntax and parameters');
      steps.push('Verify DSCP and priority values are valid');
      break;
    default:
      steps.push('Check switch connectivity and status');
      steps.push('Review switch logs for additional details');
      steps.push('Verify API version compatibility');
  }

  return steps;
}

return [{ json: result }];

