// Error Rollback Logic
const errorData = items[0].json;
const operation = errorData.operation;
const inputParams = errorData.input_parameters;

// Determine if rollback is needed and possible
let rollbackNeeded = false;
let rollbackAction = null;
let rollbackMessage = 'No rollback required';

// Only certain operations need rollback
if (operation === 'create' && errorData.error_type !== 'conflict') {
  // If VLAN creation failed, no rollback needed (VLAN wasn't created)
  rollbackNeeded = false;
  rollbackMessage = 'VLAN creation failed - no rollback needed';
} else if (operation === 'update') {
  // For update failures, we could restore previous configuration
  // This would require storing the previous state first
  rollbackNeeded = false; // Simplified for now
  rollbackMessage = 'Update failed - manual verification recommended';
} else if (operation === 'delete') {
  // If delete failed, VLAN still exists - no rollback needed
  rollbackNeeded = false;
  rollbackMessage = 'Delete failed - VLAN still exists';
}

// Prepare rollback result
let result = {
  ...errorData,
  rollback_attempted: rollbackNeeded,
  rollback_action: rollbackAction,
  rollback_message: rollbackMessage,
  requires_manual_intervention: true,
  recommended_actions: []
};

// Add recommended manual actions based on error type
switch(errorData.error_type) {
  case 'authentication_error':
    result.recommended_actions = [
      'Verify switch credentials',
      'Check if API is enabled on switch',
      'Verify network connectivity to switch'
    ];
    break;
  case 'not_found':
    if (operation === 'read' || operation === 'update' || operation === 'delete') {
      result.recommended_actions = [
        `Verify VLAN ${inputParams.vlan_id} exists`,
        'Check switch connectivity',
        'Verify VLAN ID is correct'
      ];
    }
    break;
  case 'conflict':
    if (operation === 'create') {
      result.recommended_actions = [
        `VLAN ${inputParams.vlan_id} already exists`,
        'Use update operation instead',
        'Or choose a different VLAN ID'
      ];
    }
    break;
  case 'network_error':
    result.recommended_actions = [
      'Check network connectivity to switch',
      'Verify switch is powered on and accessible',
      'Check if switch API service is running'
    ];
    break;
  case 'bad_request':
    result.recommended_actions = [
      'Verify input parameters are correct',
      'Check VLAN ID is in valid range (1-4094)',
      'Verify VLAN name follows naming conventions'
    ];
    break;
  default:
    result.recommended_actions = [
      'Review error details',
      'Check switch logs',
      'Contact network administrator'
    ];
}

return [{ json: result }];

