// Central Platform Device Group Management
// Parse and validate incoming request

const operation = items[0].json.operation;
const groupData = items[0].json.group_data || {};
const options = items[0].json.options || {};

// Validate required fields
if (!operation) {
  throw new Error('Operation is required');
}

// Supported operations
const supportedOps = [
  'create_group',
  'update_group',
  'delete_group',
  'list_groups',
  'add_devices',
  'remove_devices',
  'move_devices',
  'group_status',
  'group_inventory',
  'apply_configuration',
  'sync_group',
  'group_analytics'
];

if (!supportedOps.includes(operation)) {
  throw new Error(`Unsupported operation: ${operation}. Supported: ${supportedOps.join(', ')}`);
}

// Operation-specific validation
if (['create_group', 'update_group'].includes(operation)) {
  if (!groupData.group_name) {
    throw new Error('Group name is required');
  }
  if (operation === 'create_group' && !groupData.group_type) {
    throw new Error('Group type is required for creating groups');
  }
}

if (operation === 'update_group' && !groupData.group_id) {
  throw new Error('Group ID is required for update operations');
}

if (['add_devices', 'remove_devices', 'move_devices'].includes(operation) && !groupData.device_ids) {
  throw new Error('Device IDs are required for device operations');
}

// Add timestamp and execution ID
const executionData = {
  operation,
  group_data: groupData,
  options,
  timestamp: new Date().toISOString(),
  execution_id: `device-group-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
};

console.log(`Device Group Management: ${operation} - ${executionData.execution_id}`);

return [{ json: executionData }];

// Create Device Group Operation
const data = items[0].json;
const groupData = data.group_data;

// Group creation payload
const createPayload = {
  group_name: groupData.group_name,
  group_type: groupData.group_type,
  description: groupData.description || `Device group created via automation - ${data.timestamp}`,
  parent_group_id: groupData.parent_group_id || null,
  group_properties: groupData.group_properties || {},
  metadata: {
    created_by: 'Central Platform Automation',
    created_at: data.timestamp,
    execution_id: data.execution_id,
    source: 'n8n-workflow'
  }
};

// Group type-specific configuration
switch (groupData.group_type) {
  case 'static':
    createPayload.static_config = {
      device_ids: groupData.device_ids || [],
      auto_add_rules: groupData.auto_add_rules || [],
      membership_locked: groupData.membership_locked || false
    };
    break;

  case 'dynamic':
    createPayload.dynamic_config = {
      membership_rules: groupData.membership_rules || [],
      evaluation_frequency: groupData.evaluation_frequency || 'hourly',
      auto_update: groupData.auto_update !== false,
      rule_logic: groupData.rule_logic || 'AND'
    };
    // Example membership rules
    if (!createPayload.dynamic_config.membership_rules.length) {
      createPayload.dynamic_config.membership_rules = [
        {
          attribute: 'device_type',
          operator: 'equals',
          value: 'switch'
        },
        {
          attribute: 'location',
          operator: 'contains',
          value: 'branch'
        }
      ];
    }
    break;

  case 'location_based':
    createPayload.location_config = {
      location_hierarchy: groupData.location_hierarchy || [],
      include_sublocations: groupData.include_sublocations !== false,
      location_attributes: groupData.location_attributes || {},
      geo_boundaries: groupData.geo_boundaries || null
    };
    break;

  case 'template_based':
    createPayload.template_config = {
      template_id: groupData.template_id,
      template_parameters: groupData.template_parameters || {},
      auto_apply_template: groupData.auto_apply_template !== false,
      template_version: groupData.template_version || 'latest'
    };
    break;

  case 'role_based':
    createPayload.role_config = {
      device_roles: groupData.device_roles || [],
      service_types: groupData.service_types || [],
      capability_requirements: groupData.capability_requirements || [],
      role_priority: groupData.role_priority || 'high'
    };
    break;
}

// Add group policies
if (groupData.policies) {
  createPayload.policies = {
    configuration_policies: groupData.policies.configuration || [],
    security_policies: groupData.policies.security || [],
    compliance_policies: groupData.policies.compliance || [],
    monitoring_policies: groupData.policies.monitoring || []
  };
}

// Add group permissions
if (groupData.permissions) {
  createPayload.permissions = {
    read_users: groupData.permissions.read_users || [],
    write_users: groupData.permissions.write_users || [],
    admin_users: groupData.permissions.admin_users || [],
    inherit_parent: groupData.permissions.inherit_parent !== false
  };
}

// Add automation settings
if (groupData.automation) {
  createPayload.automation = {
    auto_provision: groupData.automation.auto_provision || false,
    auto_configure: groupData.automation.auto_configure || false,
    auto_monitor: groupData.automation.auto_monitor !== false,
    auto_remediate: groupData.automation.auto_remediate || false,
    notification_settings: groupData.automation.notification_settings || {}
  };
}

// Return API request data
return [{ 
  json: {
    ...data,
    api_payload: createPayload,
    api_endpoint: '/api/v2/device-groups',
    api_method: 'POST'
  }
}];

// Add Devices to Group Operation
const data = items[0].json;
const groupData = data.group_data;

// Add devices payload
const addDevicesPayload = {
  group_id: groupData.group_id,
  device_ids: groupData.device_ids || [],
  operation_mode: groupData.operation_mode || 'add', // add, replace, merge
  validation_settings: {
    validate_compatibility: groupData.validate_compatibility !== false,
    check_duplicates: groupData.check_duplicates !== false,
    verify_device_status: groupData.verify_device_status !== false,
    reject_invalid: groupData.reject_invalid !== false
  },
  device_configuration: {
    apply_group_config: groupData.apply_group_config !== false,
    inherit_policies: groupData.inherit_policies !== false,
    preserve_device_config: groupData.preserve_device_config || false,
    config_merge_strategy: groupData.config_merge_strategy || 'group_priority'
  },
  notification_settings: {
    notify_on_success: true,
    notify_on_failure: true,
    notify_device_owners: groupData.notify_device_owners || false
  }
};

// Add device filters if provided
if (groupData.device_filters) {
  addDevicesPayload.device_filters = {
    device_types: groupData.device_filters.device_types || [],
    models: groupData.device_filters.models || [],
    firmware_versions: groupData.device_filters.firmware_versions || [],
    locations: groupData.device_filters.locations || [],
    tags: groupData.device_filters.tags || []
  };
}

// Add bulk operation settings for large device sets
if (groupData.device_ids.length > 100) {
  addDevicesPayload.bulk_settings = {
    batch_size: groupData.batch_size || 50,
    batch_delay: groupData.batch_delay || 5,
    parallel_batches: groupData.parallel_batches || 2,
    continue_on_error: groupData.continue_on_error !== false
  };
}

// Validate device count
if (!addDevicesPayload.device_ids || addDevicesPayload.device_ids.length === 0) {
  throw new Error('At least one device ID must be specified');
}

// Add estimated completion time
const estimatedTime = addDevicesPayload.device_ids.length * 2; // 2 seconds per device
addDevicesPayload.estimated_completion_time = new Date(Date.now() + (estimatedTime * 1000)).toISOString();

return [{ 
  json: {
    ...data,
    api_payload: addDevicesPayload,
    api_endpoint: `/api/v2/device-groups/${groupData.group_id}/devices`,
    api_method: 'POST',
    device_count: addDevicesPayload.device_ids.length
  }
}];

// Apply Configuration to Group Operation
const data = items[0].json;
const groupData = data.group_data;

// Apply configuration payload
const applyConfigPayload = {
  group_id: groupData.group_id,
  configuration_type: groupData.configuration_type || 'template',
  configuration_data: groupData.configuration_data || {},
  application_settings: {
    mode: groupData.application_mode || 'staged',
    schedule: groupData.schedule || 'immediate',
    rollback_enabled: groupData.rollback_enabled !== false,
    backup_before_apply: groupData.backup_before_apply !== false,
    test_mode: groupData.test_mode || false
  },
  deployment_options: {
    batch_size: groupData.batch_size || 25,
    batch_delay: groupData.batch_delay || 60,
    priority_order: groupData.priority_order || 'alphabetical',
    failure_threshold: groupData.failure_threshold || 10,
    continue_on_error: groupData.continue_on_error || false
  },
  validation_settings: {
    pre_validation: groupData.pre_validation !== false,
    syntax_check: groupData.syntax_check !== false,
    compatibility_check: groupData.compatibility_check !== false,
    simulation_mode: groupData.simulation_mode || false
  }
};

// Configuration type-specific settings
switch (groupData.configuration_type) {
  case 'template':
    applyConfigPayload.template_config = {
      template_id: groupData.template_id,
      template_version: groupData.template_version || 'latest',
      variable_overrides: groupData.variable_overrides || {},
      merge_with_existing: groupData.merge_with_existing !== false
    };
    break;

  case 'policy':
    applyConfigPayload.policy_config = {
      policy_ids: groupData.policy_ids || [],
      policy_priority: groupData.policy_priority || [],
      enforcement_mode: groupData.enforcement_mode || 'enforce',
      conflict_resolution: groupData.conflict_resolution || 'newest_wins'
    };
    break;

  case 'direct':
    applyConfigPayload.direct_config = {
      configuration_commands: groupData.configuration_commands || [],
      configuration_format: groupData.configuration_format || 'cli',
      atomic_operation: groupData.atomic_operation !== false,
      transaction_mode: groupData.transaction_mode || 'commit_on_success'
    };
    break;

  case 'service':
    applyConfigPayload.service_config = {
      service_ids: groupData.service_ids || [],
      service_parameters: groupData.service_parameters || {},
      service_dependencies: groupData.service_dependencies || [],
      startup_order: groupData.startup_order || 'parallel'
    };
    break;
}

// Add compliance requirements
if (groupData.compliance_requirements) {
  applyConfigPayload.compliance = {
    standards: groupData.compliance_requirements.standards || [],
    validation_required: groupData.compliance_requirements.validation_required !== false,
    audit_trail: groupData.compliance_requirements.audit_trail !== false,
    approval_required: groupData.compliance_requirements.approval_required || false
  };
}

// Add notification settings
applyConfigPayload.notifications = {
  channels: data.options.notification_channels || ['slack'],
  notify_on_start: true,
  notify_on_progress: groupData.notify_on_progress || false,
  notify_on_completion: true,
  include_summary: true
};

return [{ 
  json: {
    ...data,
    api_payload: applyConfigPayload,
    api_endpoint: `/api/v2/device-groups/${groupData.group_id}/apply-configuration`,
    api_method: 'POST'
  }
}];

// Group Inventory Operation
const data = items[0].json;
const groupData = data.group_data;

// Inventory query parameters
const inventoryParams = {
  include_devices: data.options.include_devices !== false,
  include_subgroups: data.options.include_subgroups !== false,
  include_statistics: data.options.include_statistics !== false,
  include_health: data.options.include_health || false,
  include_compliance: data.options.include_compliance || false,
  device_fields: data.options.device_fields || ['id', 'name', 'type', 'status', 'location'],
  sort_by: data.options.sort_by || 'name',
  sort_order: data.options.sort_order || 'asc'
};

// Add filters if provided
if (groupData.filters) {
  inventoryParams.filters = {
    device_status: groupData.filters.device_status || [],
    device_types: groupData.filters.device_types || [],
    locations: groupData.filters.locations || [],
    tags: groupData.filters.tags || [],
    last_seen: groupData.filters.last_seen || null
  };
}

// Build query string
const queryString = Object.keys(inventoryParams)
  .filter(key => inventoryParams[key] !== undefined && inventoryParams[key] !== null)
  .map(key => {
    if (Array.isArray(inventoryParams[key])) {
      return `${key}=${inventoryParams[key].join(',')}`;
    }
    return `${key}=${encodeURIComponent(inventoryParams[key])}`;
  })
  .join('&');

return [{ 
  json: {
    ...data,
    api_payload: null,
    api_endpoint: `/api/v2/device-groups/${groupData.group_id}/inventory?${queryString}`,
    api_method: 'GET'
  }
}];

// Handle other device group operations
const data = items[0].json;
const operation = data.operation;
const groupData = data.group_data;

let apiEndpoint = '';
let apiMethod = 'GET';
let apiPayload = null;

switch (operation) {
  case 'update_group':
    apiEndpoint = `/api/v2/device-groups/${groupData.group_id}`;
    apiMethod = 'PUT';
    apiPayload = {
      group_name: groupData.group_name,
      description: groupData.description,
      group_properties: groupData.group_properties,
      policies: groupData.policies,
      permissions: groupData.permissions,
      automation: groupData.automation,
      metadata: {
        updated_by: 'Central Platform Automation',
        updated_at: data.timestamp,
        execution_id: data.execution_id
      }
    };
    // Remove undefined fields
    Object.keys(apiPayload).forEach(key => {
      if (apiPayload[key] === undefined) delete apiPayload[key];
    });
    break;

  case 'delete_group':
    apiEndpoint = `/api/v2/device-groups/${groupData.group_id}`;
    apiMethod = 'DELETE';
    apiPayload = {
      force_delete: groupData.force_delete || false,
      reassign_devices_to: groupData.reassign_devices_to || null,
      delete_policies: groupData.delete_policies || false
    };
    break;

  case 'list_groups':
    const listParams = {
      limit: data.options.limit || 100,
      offset: data.options.offset || 0,
      group_type: data.options.group_type,
      parent_group_id: data.options.parent_group_id,
      include_device_count: data.options.include_device_count !== false,
      include_subgroups: data.options.include_subgroups || false,
      sort_by: data.options.sort_by || 'name'
    };
    const queryString = Object.keys(listParams)
      .filter(key => listParams[key] !== undefined)
      .map(key => `${key}=${encodeURIComponent(listParams[key])}`)
      .join('&');
    apiEndpoint = `/api/v2/device-groups?${queryString}`;
    break;

  case 'remove_devices':
    apiEndpoint = `/api/v2/device-groups/${groupData.group_id}/devices`;
    apiMethod = 'DELETE';
    apiPayload = {
      device_ids: groupData.device_ids || [],
      remove_from_subgroups: groupData.remove_from_subgroups || false,
      preserve_device_config: groupData.preserve_device_config !== false
    };
    break;

  case 'move_devices':
    apiEndpoint = `/api/v2/device-groups/move-devices`;
    apiMethod = 'POST';
    apiPayload = {
      device_ids: groupData.device_ids || [],
      source_group_id: groupData.source_group_id,
      target_group_id: groupData.target_group_id,
      move_options: {
        preserve_config: groupData.preserve_config !== false,
        apply_target_policies: groupData.apply_target_policies !== false,
        remove_source_policies: groupData.remove_source_policies || false,
        validate_move: groupData.validate_move !== false
      }
    };
    break;

  case 'group_status':
    apiEndpoint = `/api/v2/device-groups/${groupData.group_id}/status`;
    if (data.options.include_metrics) {
      apiEndpoint += '?include_metrics=true';
    }
    break;

  case 'sync_group':
    apiEndpoint = `/api/v2/device-groups/${groupData.group_id}/sync`;
    apiMethod = 'POST';
    apiPayload = {
      sync_type: groupData.sync_type || 'full',
      sync_options: {
        update_membership: groupData.update_membership !== false,
        update_configuration: groupData.update_configuration !== false,
        update_policies: groupData.update_policies !== false,
        force_sync: groupData.force_sync || false
      }
    };
    break;

  case 'group_analytics':
    const analyticsParams = {
      metrics: groupData.metrics || ['device_count', 'health_score', 'compliance_rate'],
      time_range: groupData.time_range || '24h',
      aggregation: groupData.aggregation || 'avg',
      group_by: groupData.group_by || 'hour'
    };
    const analyticsQuery = Object.keys(analyticsParams)
      .map(key => `${key}=${encodeURIComponent(analyticsParams[key])}`)
      .join('&');
    apiEndpoint = `/api/v2/device-groups/${groupData.group_id}/analytics?${analyticsQuery}`;
    break;

  default:
    throw new Error(`Unsupported operation: ${operation}`);
}

return [{ 
  json: {
    ...data,
    api_payload: apiPayload,
    api_endpoint: apiEndpoint,
    api_method: apiMethod
  }
}];

// Process API Success Response
const data = items[0].json;
const originalRequest = data.originalRequest || {};

// Extract response data
const responseData = data.response || data;
const statusCode = data.statusCode || 200;
const operation = originalRequest.operation;

// Success response processing
let processedResponse = {
  success: true,
  operation: operation,
  timestamp: new Date().toISOString(),
  execution_id: originalRequest.execution_id,
  status_code: statusCode,
  data: responseData
};

// Operation-specific response processing
switch (operation) {
  case 'create_group':
    processedResponse.group_id = responseData.group_id || responseData.id;
    processedResponse.group_name = responseData.group_name;
    processedResponse.group_type = responseData.group_type;
    processedResponse.message = `Device group '${responseData.group_name}' created successfully`;
    break;

  case 'update_group':
    processedResponse.group_id = responseData.group_id || responseData.id;
    processedResponse.group_name = responseData.group_name;
    processedResponse.message = `Device group '${responseData.group_name}' updated successfully`;
    break;

  case 'add_devices':
    processedResponse.group_id = responseData.group_id;
    processedResponse.devices_added = responseData.devices_added || responseData.added_count;
    processedResponse.devices_failed = responseData.devices_failed || 0;
    processedResponse.total_devices = responseData.total_devices;
    processedResponse.message = `Added ${processedResponse.devices_added} devices to group`;
    break;

  case 'remove_devices':
    processedResponse.group_id = responseData.group_id;
    processedResponse.devices_removed = responseData.devices_removed || responseData.removed_count;
    processedResponse.message = `Removed ${processedResponse.devices_removed} devices from group`;
    break;

  case 'move_devices':
    processedResponse.devices_moved = responseData.devices_moved || responseData.moved_count;
    processedResponse.source_group = responseData.source_group;
    processedResponse.target_group = responseData.target_group;
    processedResponse.message = `Moved ${processedResponse.devices_moved} devices between groups`;
    break;

  case 'apply_configuration':
    processedResponse.application_id = responseData.application_id;
    processedResponse.target_devices = responseData.target_devices;
    processedResponse.status = responseData.status;
    processedResponse.message = `Configuration application started for ${responseData.target_devices} devices`;
    break;

  case 'list_groups':
    processedResponse.total_count = responseData.total_count || responseData.length;
    processedResponse.groups = responseData.groups || responseData;
    processedResponse.message = `Retrieved ${processedResponse.total_count} device groups`;
    break;

  case 'delete_group':
    processedResponse.message = 'Device group deleted successfully';
    if (responseData.reassigned_devices) {
      processedResponse.reassigned_devices = responseData.reassigned_devices;
    }
    break;

  case 'group_status':
    processedResponse.group_id = responseData.group_id;
    processedResponse.status = responseData.status;
    processedResponse.device_count = responseData.device_count;
    processedResponse.health_score = responseData.health_score;
    processedResponse.message = `Group status: ${responseData.status}`;
    break;

  case 'group_inventory':
    processedResponse.group_id = responseData.group_id;
    processedResponse.total_devices = responseData.total_devices || 0;
    processedResponse.devices = responseData.devices || [];
    processedResponse.subgroups = responseData.subgroups || [];
    processedResponse.statistics = responseData.statistics || {};
    processedResponse.message = `Retrieved inventory for ${responseData.total_devices} devices`;
    break;

  case 'sync_group':
    processedResponse.group_id = responseData.group_id;
    processedResponse.sync_status = responseData.sync_status;
    processedResponse.devices_synced = responseData.devices_synced;
    processedResponse.sync_duration = responseData.sync_duration;
    processedResponse.message = `Group sync completed: ${responseData.devices_synced} devices synchronized`;
    break;

  case 'group_analytics':
    processedResponse.group_id = responseData.group_id;
    processedResponse.metrics = responseData.metrics;
    processedResponse.time_range = responseData.time_range;
    processedResponse.analytics_data = responseData.analytics_data;
    processedResponse.message = `Analytics data retrieved for group`;
    break;
}

// Add performance metrics
processedResponse.metrics = {
  response_time: data.response_time || 'N/A',
  api_calls: 1,
  success_rate: '100%'
};

console.log(`Device Group Success: ${operation} - ${processedResponse.execution_id}`);

return [{ json: processedResponse }];

// Process Error Response
const error = items[0].json.error || items[0].json;
const originalRequest = items[0].json.originalRequest || {};

// Extract error details
const errorResponse = {
  success: false,
  operation: originalRequest.operation,
  timestamp: new Date().toISOString(),
  execution_id: originalRequest.execution_id,
  error: {
    code: error.code || error.statusCode || 'UNKNOWN_ERROR',
    message: error.message || 'Unknown error occurred',
    details: error.details || error.response || {},
    stack: error.stack
  }
};

// Error categorization
const errorCode = errorResponse.error.code;
if (errorCode >= 400 && errorCode < 500) {
  errorResponse.error.category = 'CLIENT_ERROR';
  errorResponse.error.retry_recommended = false;
} else if (errorCode >= 500) {
  errorResponse.error.category = 'SERVER_ERROR';
  errorResponse.error.retry_recommended = true;
} else {
  errorResponse.error.category = 'UNKNOWN_ERROR';
  errorResponse.error.retry_recommended = false;
}

// Operation-specific error handling
switch (originalRequest.operation) {
  case 'create_group':
    if (errorCode === 409) {
      errorResponse.error.message = 'Device group with this name already exists';
    } else if (errorCode === 400) {
      errorResponse.error.message = 'Invalid group configuration';
    }
    break;

  case 'add_devices':
    if (errorCode === 404) {
      errorResponse.error.message = 'Device group not found';
    } else if (errorCode === 422) {
      errorResponse.error.message = 'One or more devices are invalid or incompatible';
    }
    break;

  case 'move_devices':
    if (errorCode === 409) {
      errorResponse.error.message = 'Device move conflict - devices may already be in target group';
    } else if (errorCode === 403) {
      errorResponse.error.message = 'Insufficient permissions to move devices between groups';
    }
    break;

  case 'apply_configuration':
    if (errorCode === 422) {
      errorResponse.error.message = 'Configuration validation failed';
    } else if (errorCode === 503) {
      errorResponse.error.message = 'Configuration service temporarily unavailable';
    }
    break;

  case 'delete_group':
    if (errorCode === 409) {
      errorResponse.error.message = 'Cannot delete group - it contains devices or subgroups';
    } else if (errorCode === 404) {
      errorResponse.error.message = 'Device group not found';
    }
    break;
}

console.error(`Device Group Error: ${originalRequest.operation} - ${errorResponse.execution_id}`, errorResponse.error);

return [{ json: errorResponse }];

