// Final Response Formatter
const data = items[0].json;

// Format final response
const finalResponse = {
  success: data.success,
  operation: data.operation,
  execution_id: data.execution_id,
  timestamp: data.timestamp,
  message: data.message
};

// Add operation-specific data
if (data.success) {
  finalResponse.data = data.data;
  finalResponse.metrics = data.metrics;

  // Add specific fields based on operation
  if (data.group_id) finalResponse.group_id = data.group_id;
  if (data.group_name) finalResponse.group_name = data.group_name;
  if (data.devices_added !== undefined) finalResponse.devices_added = data.devices_added;
  if (data.devices_removed !== undefined) finalResponse.devices_removed = data.devices_removed;
  if (data.devices_moved !== undefined) finalResponse.devices_moved = data.devices_moved;
  if (data.total_count !== undefined) finalResponse.total_count = data.total_count;
  if (data.total_devices !== undefined) finalResponse.total_devices = data.total_devices;
} else {
  finalResponse.error = data.error;
  finalResponse.retry_recommended = data.error?.retry_recommended;
}

// Add execution summary
finalResponse.execution_summary = {
  workflow: 'Central Platform Device Group Management',
  version: '1.0.0',
  executed_at: data.timestamp,
  duration: 'N/A' // Could be calculated if needed
};

return [{ json: finalResponse }];
