// Central Platform Policy and Rule Automation
// Parse and validate incoming request

const operation = items[0].json.operation;
const policyData = items[0].json.policy_data || {};
const options = items[0].json.options || {};

// Validate required fields
if (!operation) {
  throw new Error('Operation is required');
}

// Supported operations
const supportedOps = [
  'create_policy',
  'update_policy',
  'delete_policy',
  'list_policies',
  'apply_policy',
  'validate_policy',
  'policy_compliance_check',
  'create_rule',
  'update_rule',
  'delete_rule',
  'bulk_policy_apply',
  'policy_rollback'
];

if (!supportedOps.includes(operation)) {
  throw new Error(`Unsupported operation: ${operation}. Supported: ${supportedOps.join(', ')}`);
}

// Operation-specific validation
if (['create_policy', 'update_policy', 'apply_policy'].includes(operation)) {
  if (!policyData.policy_name) {
    throw new Error('Policy name is required');
  }
  if (!policyData.policy_type) {
    throw new Error('Policy type is required');
  }
}

if (operation === 'update_policy' && !policyData.policy_id) {
  throw new Error('Policy ID is required for update operations');
}

if (operation === 'apply_policy' && !policyData.target_devices) {
  throw new Error('Target devices are required for policy application');
}

// Add timestamp and execution ID
const executionData = {
  operation,
  policy_data: policyData,
  options,
  timestamp: new Date().toISOString(),
  execution_id: `policy-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
};

console.log(`Policy Automation: ${operation} - ${executionData.execution_id}`);

return [{ json: executionData }];

// Create Policy Operation
const data = items[0].json;
const policyData = data.policy_data;

// Policy creation payload
const createPayload = {
  policy_name: policyData.policy_name,
  policy_type: policyData.policy_type,
  description: policyData.description || `Policy created via automation - ${data.timestamp}`,
  version: policyData.version || '1.0.0',
  enabled: policyData.enabled !== false,
  priority: policyData.priority || 100,
  metadata: {
    created_by: 'Central Platform Automation',
    created_at: data.timestamp,
    execution_id: data.execution_id,
    source: 'n8n-workflow'
  }
};

// Policy type-specific configuration
switch (policyData.policy_type) {
  case 'network_access':
    createPayload.access_rules = policyData.access_rules || [];
    createPayload.authentication_required = policyData.authentication_required !== false;
    createPayload.vlan_assignment = policyData.vlan_assignment || {};
    createPayload.bandwidth_limits = policyData.bandwidth_limits || {};
    break;
  case 'security':
    createPayload.security_rules = policyData.security_rules || [];
    createPayload.threat_detection = policyData.threat_detection || 'enabled';
    createPayload.intrusion_prevention = policyData.intrusion_prevention || 'enabled';
    createPayload.encryption_requirements = policyData.encryption_requirements || {};
    break;
  case 'qos':
    createPayload.qos_rules = policyData.qos_rules || [];
    createPayload.traffic_classification = policyData.traffic_classification || [];
    createPayload.bandwidth_allocation = policyData.bandwidth_allocation || {};
    createPayload.priority_queues = policyData.priority_queues || [];
    break;
  case 'compliance':
    createPayload.compliance_rules = policyData.compliance_rules || [];
    createPayload.audit_requirements = policyData.audit_requirements || {};
    createPayload.regulatory_standards = policyData.regulatory_standards || [];
    createPayload.reporting_schedule = policyData.reporting_schedule || 'daily';
    break;
  case 'application':
    createPayload.application_rules = policyData.application_rules || [];
    createPayload.allowed_applications = policyData.allowed_applications || [];
    createPayload.blocked_applications = policyData.blocked_applications || [];
    createPayload.bandwidth_per_app = policyData.bandwidth_per_app || {};
    break;
}

// Add conditions
if (policyData.conditions) {
  createPayload.conditions = {
    time_based: policyData.conditions.time_based || {},
    location_based: policyData.conditions.location_based || {},
    device_based: policyData.conditions.device_based || {},
    user_based: policyData.conditions.user_based || {}
  };
}

// Add enforcement settings
createPayload.enforcement = {
  mode: policyData.enforcement_mode || 'enforce',
  logging: policyData.logging_enabled !== false,
  alert_on_violation: policyData.alert_on_violation !== false,
  auto_remediation: policyData.auto_remediation || false
};

// Return API request data
return [{ 
  json: {
    ...data,
    api_payload: createPayload,
    api_endpoint: '/api/v2/policies',
    api_method: 'POST'
  }
}];

// Apply Policy Operation
const data = items[0].json;
const policyData = data.policy_data;

// Policy application payload
const applyPayload = {
  policy_id: policyData.policy_id || policyData.policy_name,
  target_devices: policyData.target_devices,
  target_groups: policyData.target_groups || [],
  target_users: policyData.target_users || [],
  application_settings: {
    mode: policyData.application_mode || 'immediate',
    schedule: policyData.application_schedule || null,
    rollback_on_failure: policyData.rollback_on_failure !== false,
    pre_validation: policyData.pre_validation !== false,
    post_validation: policyData.post_validation !== false
  },
  deployment_options: {
    staged: policyData.staged_deployment || false,
    batch_size: policyData.batch_size || 50,
    delay_between_batches: policyData.delay_between_batches || 30,
    priority: policyData.deployment_priority || 'normal'
  },
  override_settings: policyData.override_settings || {},
  conflict_resolution: policyData.conflict_resolution || 'merge',
  notification_settings: {
    notify_on_success: true,
    notify_on_failure: true,
    notify_users: policyData.notify_users || []
  }
};

// Add application metadata
applyPayload.metadata = {
  applied_by: 'Central Platform Automation',
  applied_at: data.timestamp,
  execution_id: data.execution_id,
  reason: policyData.reason || 'Automated policy application'
};

// Validate targets
if ((!applyPayload.target_devices || applyPayload.target_devices.length === 0) &&
    (!applyPayload.target_groups || applyPayload.target_groups.length === 0) &&
    (!applyPayload.target_users || applyPayload.target_users.length === 0)) {
  throw new Error('At least one target (devices, groups, or users) must be specified');
}

return [{ 
  json: {
    ...data,
    api_payload: applyPayload,
    api_endpoint: '/api/v2/policies/apply',
    api_method: 'POST',
    target_count: (applyPayload.target_devices?.length || 0) + 
                  (applyPayload.target_groups?.length || 0) + 
                  (applyPayload.target_users?.length || 0)
  }
}];

// Validate Policy Operation
const data = items[0].json;
const policyData = data.policy_data;

// Validation payload
const validatePayload = {
  policy_id: policyData.policy_id,
  validation_level: policyData.validation_level || 'full',
  target_devices: policyData.target_devices || [],
  simulation_mode: policyData.simulation_mode !== false,
  checks: {
    syntax_check: true,
    conflict_check: true,
    dependency_check: true,
    performance_impact: true,
    compliance_check: policyData.compliance_check !== false
  },
  validation_options: {
    check_prerequisites: true,
    validate_targets: true,
    estimate_impact: true,
    generate_report: true
  }
};

return [{ 
  json: {
    ...data,
    api_payload: validatePayload,
    api_endpoint: `/api/v2/policies/${policyData.policy_id}/validate`,
    api_method: 'POST'
  }
}];

// Policy Compliance Check Operation
const data = items[0].json;
const policyData = data.policy_data;

// Compliance check payload
const compliancePayload = {
  policy_ids: policyData.policy_ids || [],
  target_devices: policyData.target_devices || [],
  compliance_standards: policyData.compliance_standards || [],
  check_options: {
    deep_scan: policyData.deep_scan !== false,
    include_inactive: policyData.include_inactive || false,
    check_exceptions: policyData.check_exceptions !== false,
    validate_remediation: policyData.validate_remediation || true
  },
  reporting_options: {
    detailed_report: true,
    executive_summary: true,
    remediation_steps: true,
    compliance_score: true
  },
  time_range: {
    start: policyData.start_time || new Date(Date.now() - 24*60*60*1000).toISOString(),
    end: policyData.end_time || new Date().toISOString()
  }
};

return [{ 
  json: {
    ...data,
    api_payload: compliancePayload,
    api_endpoint: '/api/v2/policies/compliance-check',
    api_method: 'POST'
  }
}];

// Handle other policy operations
const data = items[0].json;
const operation = data.operation;
const policyData = data.policy_data;

let apiEndpoint = '';
let apiMethod = 'GET';
let apiPayload = null;

switch (operation) {
  case 'update_policy':
    apiEndpoint = `/api/v2/policies/${policyData.policy_id}`;
    apiMethod = 'PUT';
    apiPayload = {
      policy_name: policyData.policy_name,
      description: policyData.description,
      version: policyData.version,
      enabled: policyData.enabled,
      priority: policyData.priority,
      rules: policyData.rules,
      conditions: policyData.conditions,
      metadata: {
        updated_by: 'Central Platform Automation',
        updated_at: data.timestamp,
        execution_id: data.execution_id
      }
    };
    // Remove undefined fields
    Object.keys(apiPayload).forEach(key => {
      if (apiPayload[key] === undefined) delete apiPayload[key];
    });
    break;
  case 'delete_policy':
    apiEndpoint = `/api/v2/policies/${policyData.policy_id}`;
    apiMethod = 'DELETE';
    break;
  case 'list_policies':
    const queryParams = {
      limit: data.options.limit || 50,
      offset: data.options.offset || 0,
      policy_type: data.options.policy_type,
      enabled: data.options.enabled,
      sort_by: data.options.sort_by || 'created_at',
      sort_order: data.options.sort_order || 'desc'
    };
    const queryString = Object.keys(queryParams)
      .filter(key => queryParams[key] !== undefined)
      .map(key => `${key}=${encodeURIComponent(queryParams[key])}`)
      .join('&');
    apiEndpoint = `/api/v2/policies?${queryString}`;
    break;
  case 'create_rule':
    apiEndpoint = `/api/v2/policies/${policyData.policy_id}/rules`;
    apiMethod = 'POST';
    apiPayload = {
      rule_name: policyData.rule_name,
      rule_type: policyData.rule_type,
      conditions: policyData.conditions,
      actions: policyData.actions,
      priority: policyData.priority || 100,
      enabled: policyData.enabled !== false
    };
    break;
  case 'update_rule':
    apiEndpoint = `/api/v2/policies/${policyData.policy_id}/rules/${policyData.rule_id}`;
    apiMethod = 'PUT';
    apiPayload = {
      rule_name: policyData.rule_name,
      conditions: policyData.conditions,
      actions: policyData.actions,
      priority: policyData.priority,
      enabled: policyData.enabled
    };
    break;
  case 'delete_rule':
    apiEndpoint = `/api/v2/policies/${policyData.policy_id}/rules/${policyData.rule_id}`;
    apiMethod = 'DELETE';
    break;
  case 'bulk_policy_apply':
    apiEndpoint = '/api/v2/policies/bulk-apply';
    apiMethod = 'POST';
    apiPayload = {
      policy_applications: policyData.policy_applications || [],
      global_settings: policyData.global_settings || {},
      execution_mode: policyData.execution_mode || 'parallel'
    };
    break;
  case 'policy_rollback':
    apiEndpoint = `/api/v2/policies/${policyData.policy_id}/rollback`;
    apiMethod = 'POST';
    apiPayload = {
      rollback_to: policyData.rollback_version || 'previous',
      target_devices: policyData.target_devices || [],
      reason: policyData.reason || 'Manual rollback request'
    };
    break;
  default:
    throw new Error(`Unsupported operation: ${operation}`);
}

return [{ 
  json: {
    ...data,
    api_payload: apiPayload,
    api_endpoint: apiEndpoint,
    api_method: apiMethod
  }
}];

// Process API Success Response
const data = items[0].json;
const originalRequest = data.originalRequest || {};

// Extract response data
const responseData = data.response || data;
const statusCode = data.statusCode || 200;
const operation = originalRequest.operation;

// Success response processing
let processedResponse = {
  success: true,
  operation: operation,
  timestamp: new Date().toISOString(),
  execution_id: originalRequest.execution_id,
  status_code: statusCode,
  data: responseData
};

// Operation-specific response processing
switch (operation) {
  case 'create_policy':
    processedResponse.policy_id = responseData.policy_id || responseData.id;
    processedResponse.policy_name = responseData.policy_name;
    processedResponse.policy_type = responseData.policy_type;
    processedResponse.message = `Policy '${responseData.policy_name}' created successfully`;
    break;
  case 'update_policy':
    processedResponse.policy_id = responseData.policy_id || responseData.id;
    processedResponse.version = responseData.version;
    processedResponse.message = `Policy updated to version ${responseData.version}`;
    break;
  case 'apply_policy':
    processedResponse.application_id = responseData.application_id;
    processedResponse.affected_devices = responseData.affected_devices || [];
    processedResponse.status = responseData.status;
    processedResponse.message = `Policy applied to ${responseData.affected_devices?.length || 0} targets`;
    break;
  case 'validate_policy':
    processedResponse.validation_result = responseData.validation_result;
    processedResponse.validation_score = responseData.validation_score;
    processedResponse.issues = responseData.issues || [];
    processedResponse.warnings = responseData.warnings || [];
    processedResponse.message = `Policy validation completed - Score: ${responseData.validation_score}/100`;
    break;
  case 'policy_compliance_check':
    processedResponse.compliance_score = responseData.compliance_score;
    processedResponse.compliant_devices = responseData.compliant_devices || [];
    processedResponse.non_compliant_devices = responseData.non_compliant_devices || [];
    processedResponse.violations = responseData.violations || [];
    processedResponse.message = `Compliance check completed - Score: ${responseData.compliance_score}%`;
    break;
  case 'list_policies':
    processedResponse.total_count = responseData.total_count || responseData.length;
    processedResponse.policies = responseData.policies || responseData;
    processedResponse.message = `Retrieved ${processedResponse.total_count} policies`;
    break;
  case 'delete_policy':
    processedResponse.message = 'Policy deleted successfully';
    break;
  case 'create_rule':
    processedResponse.rule_id = responseData.rule_id;
    processedResponse.rule_name = responseData.rule_name;
    processedResponse.message = `Rule '${responseData.rule_name}' created successfully`;
    break;
  case 'bulk_policy_apply':
    processedResponse.batch_id = responseData.batch_id;
    processedResponse.total_applications = responseData.total_applications;
    processedResponse.successful = responseData.successful || 0;
    processedResponse.failed = responseData.failed || 0;
    processedResponse.message = `Bulk policy application initiated - ${responseData.total_applications} policies`;
    break;
  case 'policy_rollback':
    processedResponse.rollback_id = responseData.rollback_id;
    processedResponse.rollback_status = responseData.status;
    processedResponse.affected_devices = responseData.affected_devices || [];
    processedResponse.message = `Policy rollback initiated for ${responseData.affected_devices?.length || 0} devices`;
    break;
}

// Add performance metrics
processedResponse.metrics = {
  response_time: data.response_time || 'N/A',
  api_calls: 1,
  success_rate: '100%'
};

console.log(`Policy Automation Success: ${operation} - ${processedResponse.execution_id}`);

return [{ json: processedResponse }];

// Process Error Response
const error = items[0].json.error || items[0].json;
const originalRequest = items[0].json.originalRequest || {};

// Extract error details
const errorResponse = {
  success: false,
  operation: originalRequest.operation,
  timestamp: new Date().toISOString(),
  execution_id: originalRequest.execution_id,
  error: {
    code: error.code || error.statusCode || 'UNKNOWN_ERROR',
    message: error.message || 'Unknown error occurred',
    details: error.details || error.response || {},
    stack: error.stack
  }
};

// Error categorization
const errorCode = errorResponse.error.code;
if (errorCode >= 400 && errorCode < 500) {
  errorResponse.error.category = 'CLIENT_ERROR';
  errorResponse.error.retry_recommended = false;
} else if (errorCode >= 500) {
  errorResponse.error.category = 'SERVER_ERROR';
  errorResponse.error.retry_recommended = true;
} else {
  errorResponse.error.category = 'UNKNOWN_ERROR';
  errorResponse.error.retry_recommended = false;
}

// Operation-specific error handling
switch (originalRequest.operation) {
  case 'create_policy':
    if (errorCode === 409) {
      errorResponse.error.message = 'Policy with this name already exists';
    } else if (errorCode === 400) {
      errorResponse.error.message = 'Invalid policy configuration';
    }
    break;
  case 'apply_policy':
    if (errorCode === 404) {
      errorResponse.error.message = 'Policy not found';
    } else if (errorCode === 422) {
      errorResponse.error.message = 'Policy validation failed for target devices';
    }
    break;
  case 'validate_policy':
    if (errorCode === 400) {
      errorResponse.error.message = 'Policy syntax validation failed';
    }
    break;
  case 'policy_compliance_check':
    if (errorCode === 503) {
      errorResponse.error.message = 'Compliance service temporarily unavailable';
    }
    break;
}

console.error(`Policy Automation Error: ${originalRequest.operation} - ${errorResponse.execution_id}`, errorResponse.error);

return [{ json: errorResponse }];

