{{$json.success ? '**Details**: ' + ($json.compliance_score ? 'Compliance Score: ' + $json.compliance_score + '%' : $json.affected_devices ? 'Affected Devices: ' + $json.affected_devices.length : '') : '**Error Code**: ' + $json.error?.code}}

parameters.functionCode:
// Final Response Formatter
const data = items[0].json;

// Format final response
const finalResponse = {
  success: data.success,
  operation: data.operation,
  execution_id: data.execution_id,
  timestamp: data.timestamp,
  message: data.message
};

// Add operation-specific data
if (data.success) {
  finalResponse.data = data.data;
  finalResponse.metrics = data.metrics;

  // Add specific fields based on operation
  if (data.policy_id) finalResponse.policy_id = data.policy_id;
  if (data.policy_name) finalResponse.policy_name = data.policy_name;
  if (data.application_id) finalResponse.application_id = data.application_id;
  if (data.validation_result) finalResponse.validation_result = data.validation_result;
  if (data.compliance_score) finalResponse.compliance_score = data.compliance_score;
  if (data.total_count) finalResponse.total_count = data.total_count;
} else {
  finalResponse.error = data.error;
  finalResponse.retry_recommended = data.error?.retry_recommended;
}

// Add execution summary
finalResponse.execution_summary = {
  workflow: 'Central Platform Policy and Rule Automation',
  version: '1.0.0',
  executed_at: data.timestamp,
  duration: 'N/A' // Could be calculated if needed
};

return [{ json: finalResponse }];
