// Central Platform Template Management Workflow
// Parse and validate incoming request

const operation = items[0].json.operation;
const templateData = items[0].json.template_data || {};
const options = items[0].json.options || {};

// Validate required fields
if (!operation) {
  throw new Error('Operation is required');
}

// Supported operations
const supportedOps = [
  'create_template',
  'update_template', 
  'delete_template',
  'list_templates',
  'deploy_template',
  'validate_template',
  'backup_template',
  'restore_template',
  'clone_template',
  'bulk_deploy'
];

if (!supportedOps.includes(operation)) {
  throw new Error(`Unsupported operation: ${operation}. Supported: ${supportedOps.join(', ')}`);
}

// Operation-specific validation
if (['create_template', 'update_template', 'deploy_template'].includes(operation)) {
  if (!templateData.name) {
    throw new Error('Template name is required');
  }
  if (!templateData.type) {
    throw new Error('Template type is required');
  }
}

if (operation === 'update_template' && !templateData.template_id) {
  throw new Error('Template ID is required for update operations');
}

if (operation === 'deploy_template' && !templateData.target_devices) {
  throw new Error('Target devices are required for deployment');
}

// Add timestamp and execution ID
const executionData = {
  operation,
  template_data: templateData,
  options,
  timestamp: new Date().toISOString(),
  execution_id: `template-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
};

console.log(`Template Management: ${operation} - ${executionData.execution_id}`);

return [{ json: executionData }];

// Create Template Operation
const data = items[0].json;
const templateData = data.template_data;

// Template creation payload
const createPayload = {
  name: templateData.name,
  type: templateData.type,
  description: templateData.description || `Template created via automation - ${data.timestamp}`,
  version: templateData.version || '1.0.0',
  configuration: templateData.configuration || {},
  variables: templateData.variables || {},
  metadata: {
    created_by: 'Central Platform Automation',
    created_at: data.timestamp,
    execution_id: data.execution_id,
    source: 'n8n-workflow'
  }
};

// Template type-specific configuration
switch (templateData.type) {
  case 'device_config':
    createPayload.device_types = templateData.device_types || ['switch', 'ap'];
    createPayload.config_sections = templateData.config_sections || ['network', 'security'];
    break;
  case 'network_policy':
    createPayload.policy_rules = templateData.policy_rules || [];
    createPayload.enforcement_mode = templateData.enforcement_mode || 'monitor';
    break;
  case 'security_profile':
    createPayload.security_settings = templateData.security_settings || {};
    createPayload.compliance_standards = templateData.compliance_standards || [];
    break;
  case 'service_config':
    createPayload.service_settings = templateData.service_settings || {};
    createPayload.dependencies = templateData.dependencies || [];
    break;
}

// Add validation rules
createPayload.validation_rules = {
  required_fields: templateData.required_fields || [],
  format_validation: templateData.format_validation || {},
  dependency_checks: templateData.dependency_checks || []
};

// Return API request data
return [{ 
  json: {
    ...data,
    api_payload: createPayload,
    api_endpoint: '/api/v2/templates',
    api_method: 'POST'
  }
}];

// Update Template Operation
const data = items[0].json;
const templateData = data.template_data;

// Get current template first
const getCurrentPayload = {
  template_id: templateData.template_id
};

// Update payload
const updatePayload = {
  name: templateData.name,
  description: templateData.description,
  version: templateData.version,
  configuration: templateData.configuration,
  variables: templateData.variables,
  metadata: {
    updated_by: 'Central Platform Automation',
    updated_at: data.timestamp,
    execution_id: data.execution_id,
    previous_version: templateData.current_version
  }
};

// Remove undefined fields
Object.keys(updatePayload).forEach(key => {
  if (updatePayload[key] === undefined) {
    delete updatePayload[key];
  }
});

return [{ 
  json: {
    ...data,
    api_payload: updatePayload,
    api_endpoint: `/api/v2/templates/${templateData.template_id}`,
    api_method: 'PUT',
    template_id: templateData.template_id
  }
}];

// Deploy Template Operation
const data = items[0].json;
const templateData = data.template_data;

// Deployment configuration
const deployPayload = {
  template_id: templateData.template_id || templateData.name,
  target_devices: templateData.target_devices,
  deployment_settings: {
    mode: templateData.deployment_mode || 'staged',
    batch_size: templateData.batch_size || 10,
    delay_between_batches: templateData.delay_between_batches || 30,
    rollback_on_failure: templateData.rollback_on_failure !== false,
    pre_deployment_validation: templateData.pre_deployment_validation !== false
  },
  variable_overrides: templateData.variable_overrides || {},
  deployment_schedule: templateData.deployment_schedule || 'immediate',
  notification_settings: {
    notify_on_success: true,
    notify_on_failure: true,
    notify_on_completion: true
  }
};

// Add deployment metadata
deployPayload.metadata = {
  initiated_by: 'Central Platform Automation',
  initiated_at: data.timestamp,
  execution_id: data.execution_id,
  expected_completion: new Date(Date.now() + (deployPayload.target_devices.length * 60000)).toISOString()
};

// Validation for deployment
if (!Array.isArray(deployPayload.target_devices) || deployPayload.target_devices.length === 0) {
  throw new Error('Target devices must be a non-empty array');
}

return [{ 
  json: {
    ...data,
    api_payload: deployPayload,
    api_endpoint: '/api/v2/templates/deploy',
    api_method: 'POST',
    target_count: deployPayload.target_devices.length
  }
}];

// List Templates Operation
const data = items[0].json;
const options = data.options;

// Query parameters for listing
const queryParams = {
  limit: options.limit || 50,
  offset: options.offset || 0,
  sort_by: options.sort_by || 'created_at',
  sort_order: options.sort_order || 'desc'
};

// Filters
if (options.template_type) {
  queryParams.type = options.template_type;
}
if (options.name_filter) {
  queryParams.name_contains = options.name_filter;
}
if (options.created_after) {
  queryParams.created_after = options.created_after;
}
if (options.status) {
  queryParams.status = options.status;
}

// Build query string
const queryString = Object.keys(queryParams)
  .map(key => `${key}=${encodeURIComponent(queryParams[key])}`)
  .join('&');

return [{ 
  json: {
    ...data,
    api_payload: null,
    api_endpoint: `/api/v2/templates?${queryString}`,
    api_method: 'GET',
    query_params: queryParams
  }
}];

// Handle other operations
const data = items[0].json;
const operation = data.operation;

let apiEndpoint = '';
let apiMethod = 'GET';
let apiPayload = null;

switch (operation) {
  case 'delete_template':
    apiEndpoint = `/api/v2/templates/${data.template_data.template_id}`;
    apiMethod = 'DELETE';
    break;
  case 'validate_template':
    apiEndpoint = `/api/v2/templates/${data.template_data.template_id}/validate`;
    apiMethod = 'POST';
    apiPayload = {
      validation_level: data.options.validation_level || 'full',
      target_devices: data.template_data.target_devices || []
    };
    break;
  case 'backup_template':
    apiEndpoint = `/api/v2/templates/${data.template_data.template_id}/backup`;
    apiMethod = 'POST';
    apiPayload = {
      backup_name: data.options.backup_name || `backup-${Date.now()}`,
      include_metadata: data.options.include_metadata !== false
    };
    break;
  case 'restore_template':
    apiEndpoint = `/api/v2/templates/${data.template_data.template_id}/restore`;
    apiMethod = 'POST';
    apiPayload = {
      backup_id: data.template_data.backup_id,
      restore_mode: data.options.restore_mode || 'replace'
    };
    break;
  case 'clone_template':
    apiEndpoint = `/api/v2/templates/${data.template_data.template_id}/clone`;
    apiMethod = 'POST';
    apiPayload = {
      new_name: data.template_data.new_name,
      new_description: data.template_data.new_description
    };
    break;
  case 'bulk_deploy':
    apiEndpoint = '/api/v2/templates/bulk-deploy';
    apiMethod = 'POST';
    apiPayload = {
      deployments: data.template_data.deployments || [],
      deployment_settings: data.options.deployment_settings || {}
    };
    break;
  default:
    throw new Error(`Unsupported operation: ${operation}`);
}

return [{ 
  json: {
    ...data,
    api_payload: apiPayload,
    api_endpoint: apiEndpoint,
    api_method: apiMethod
  }
}];

// Process API Response
const data = items[0].json;
const originalRequest = data.originalRequest || {};

// Extract response data
const responseData = data.response || data;
const statusCode = data.statusCode || 200;
const operation = originalRequest.operation;

// Success response processing
let processedResponse = {
  success: true,
  operation: operation,
  timestamp: new Date().toISOString(),
  execution_id: originalRequest.execution_id,
  status_code: statusCode,
  data: responseData
};

// Operation-specific response processing
switch (operation) {
  case 'create_template':
    processedResponse.template_id = responseData.template_id || responseData.id;
    processedResponse.template_name = responseData.name;
    processedResponse.message = `Template '${responseData.name}' created successfully`;
    break;
  case 'update_template':
    processedResponse.template_id = responseData.template_id || responseData.id;
    processedResponse.version = responseData.version;
    processedResponse.message = `Template updated to version ${responseData.version}`;
    break;
  case 'deploy_template':
    processedResponse.deployment_id = responseData.deployment_id;
    processedResponse.target_count = responseData.target_count;
    processedResponse.message = `Template deployment started for ${responseData.target_count} devices`;
    break;
  case 'list_templates':
    processedResponse.total_count = responseData.total_count || responseData.length;
    processedResponse.templates = responseData.templates || responseData;
    processedResponse.message = `Retrieved ${processedResponse.total_count} templates`;
    break;
  case 'delete_template':
    processedResponse.message = 'Template deleted successfully';
    break;
  case 'validate_template':
    processedResponse.validation_result = responseData.validation_result;
    processedResponse.errors = responseData.errors || [];
    processedResponse.warnings = responseData.warnings || [];
    processedResponse.message = `Template validation completed - ${responseData.errors?.length || 0} errors, ${responseData.warnings?.length || 0} warnings`;
    break;
  case 'backup_template':
    processedResponse.backup_id = responseData.backup_id;
    processedResponse.message = `Template backup created: ${responseData.backup_id}`;
    break;
  case 'restore_template':
    processedResponse.message = 'Template restored successfully';
    break;
  case 'clone_template':
    processedResponse.new_template_id = responseData.template_id;
    processedResponse.message = `Template cloned successfully: ${responseData.template_id}`;
    break;
  case 'bulk_deploy':
    processedResponse.deployment_ids = responseData.deployment_ids || [];
    processedResponse.message = `Bulk deployment started for ${responseData.deployment_ids?.length || 0} templates`;
    break;
}

// Add performance metrics
processedResponse.metrics = {
  response_time: data.response_time || 'N/A',
  api_calls: 1,
  success_rate: '100%'
};

console.log(`Template Management Success: ${operation} - ${processedResponse.execution_id}`);

return [{ json: processedResponse }];

// Process Error Response
const error = items[0].json.error || items[0].json;
const originalRequest = items[0].json.originalRequest || {};

// Extract error details
const errorResponse = {
  success: false,
  operation: originalRequest.operation,
  timestamp: new Date().toISOString(),
  execution_id: originalRequest.execution_id,
  error: {
    code: error.code || error.statusCode || 'UNKNOWN_ERROR',
    message: error.message || 'Unknown error occurred',
    details: error.details || error.response || {},
    stack: error.stack
  }
};

// Error categorization
const errorCode = errorResponse.error.code;
if (errorCode >= 400 && errorCode < 500) {
  errorResponse.error.category = 'CLIENT_ERROR';
  errorResponse.error.retry_recommended = false;
} else if (errorCode >= 500) {
  errorResponse.error.category = 'SERVER_ERROR';
  errorResponse.error.retry_recommended = true;
} else {
  errorResponse.error.category = 'UNKNOWN_ERROR';
  errorResponse.error.retry_recommended = false;
}

// Operation-specific error handling
switch (originalRequest.operation) {
  case 'create_template':
    if (errorCode === 409) {
      errorResponse.error.message = 'Template with this name already exists';
    }
    break;
  case 'update_template':
    if (errorCode === 404) {
      errorResponse.error.message = 'Template not found';
    }
    break;
  case 'deploy_template':
    if (errorCode === 422) {
      errorResponse.error.message = 'Template validation failed before deployment';
    }
    break;
}

console.error(`Template Management Error: ${originalRequest.operation} - ${errorResponse.execution_id}`, errorResponse.error);

return [{ json: errorResponse }];

