// Final Response Formatter
const data = items[0].json;

// Format final response
const finalResponse = {
  success: data.success,
  operation: data.operation,
  execution_id: data.execution_id,
  timestamp: data.timestamp,
  message: data.message
};

// Add operation-specific data
if (data.success) {
  finalResponse.data = data.data;
  finalResponse.metrics = data.metrics;

  // Add specific fields based on operation
  if (data.template_id) finalResponse.template_id = data.template_id;
  if (data.deployment_id) finalResponse.deployment_id = data.deployment_id;
  if (data.backup_id) finalResponse.backup_id = data.backup_id;
  if (data.total_count) finalResponse.total_count = data.total_count;
} else {
  finalResponse.error = data.error;
  finalResponse.retry_recommended = data.error?.retry_recommended;
}

// Add execution summary
finalResponse.execution_summary = {
  workflow: 'Central Platform Template Management',
  version: '1.0.0',
  executed_at: data.timestamp,
  duration: 'N/A' // Could be calculated if needed
};

return [{ json: finalResponse }];
