// EdgeConnect Branch Connectivity Management Workflow
// Operations: configure_branch, update_connectivity, failover_test, optimize_paths, monitor_links, reset_configuration

const operation = $input.item(0).json.operation;
const branchData = $input.item(0).json.branch_data || {};
const orchestratorUrl = $input.item(0).json.orchestrator_url;
const branchId = $input.item(0).json.branch_id;
const connectivityType = $input.item(0).json.connectivity_type || 'hybrid';

// Validate required parameters
if (!operation) {
  throw new Error('Operation is required. Valid operations: configure_branch, update_connectivity, failover_test, optimize_paths, monitor_links, reset_configuration');
}

if (!orchestratorUrl) {
  throw new Error('Orchestrator URL is required');
}

if (['configure_branch', 'update_connectivity', 'failover_test'].includes(operation) && !branchId) {
  throw new Error('Branch ID is required for this operation');
}

// Branch connectivity templates
const connectivityTemplates = {
  'small_branch': {
    name: 'Small Branch Office',
    wan_links: [
      {
        name: 'Primary MPLS',
        type: 'mpls',
        bandwidth: '10mbps',
        priority: 'high',
        cost: 100,
        failover_enabled: true
