        backup_only: true
      }
    ],
    traffic_policies: {
      critical_apps: { preferred_path: 'mpls', backup_path: 'broadband' },
      internet_traffic: { preferred_path: 'broadband', backup_path: 'mpls' },
      voice_video: { dedicated_path: 'mpls', bandwidth_guarantee: '2mbps' }
    },
    failover_settings: {
      detection_time: '5s',
      recovery_time: '10s',
      blackhole_detection: true
    }
  },
  'medium_branch': {
    name: 'Medium Branch Office',
    wan_links: [
      {
        name: 'Primary MPLS',
        type: 'mpls',
        bandwidth: '50mbps',
        priority: 'high',
        cost: 100,
        failover_enabled: true
      },
      {
        name: 'Secondary Broadband',
        type: 'broadband',
        bandwidth: '100mbps',
        priority: 'medium',
        cost: 150,
        load_balancing: true
      },
      {
        name: 'Backup LTE',
        type: 'lte',
        bandwidth: '25mbps',
        priority: 'low',
        cost: 300,
        backup_only: true
      }
    ],
    traffic_policies: {
      critical_apps: { preferred_path: 'mpls', backup_path: 'broadband' },
      internet_traffic: { load_balance: ['broadband', 'mpls'], ratio: '70:30' },
      voice_video: { dedicated_path: 'mpls', bandwidth_guarantee: '5mbps' },
      backup_traffic: { backup_path: 'lte' }
    },
    failover_settings: {
      detection_time: '3s',
      recovery_time: '5s',
      blackhole_detection: true,
      path_monitoring: true
    }
  },
  'large_branch': {
    name: 'Large Branch Office',
    wan_links: [
      {
        name: 'Primary MPLS',
        type: 'mpls',
        bandwidth: '100mbps',
        priority: 'high',
        cost: 100,
        failover_enabled: true
      },
      {
        name: 'Secondary MPLS',
        type: 'mpls',
        bandwidth: '100mbps',
        priority: 'high',
        cost: 110,
        load_balancing: true
      },
      {
        name: 'Broadband Primary',
        type: 'broadband',
        bandwidth: '200mbps',
        priority: 'medium',
        cost: 150,
        load_balancing: true
      },
      {
        name: 'Broadband Secondary',
        type: 'broadband',
        bandwidth: '200mbps',
        priority: 'medium',
        cost: 160,
        load_balancing: true
      }
    ],
    traffic_policies: {
      critical_apps: { preferred_paths: ['mpls', 'mpls'], load_balance: true },
      internet_traffic: { preferred_paths: ['broadband', 'broadband'], load_balance: true },
      voice_video: { dedicated_paths: ['mpls'], bandwidth_guarantee: '10mbps' },
      data_backup: { preferred_paths: ['broadband'], time_restriction: 'off_hours' }
    },
    failover_settings: {
