        cost: 200,
        backup_only: true
      }
    ],
    traffic_policies: {
      pos_systems: { preferred_path: 'broadband', backup_path: 'lte', priority: 'critical' },
      credit_card: { preferred_path: 'broadband', backup_path: 'lte', encryption: 'required' },
      inventory: { preferred_path: 'broadband', time_restriction: 'business_hours' },
      guest_wifi: { preferred_path: 'broadband', bandwidth_limit: '10mbps' }
    },
    failover_settings: {
