      blackhole_detection: true,
      pos_priority: true
    }
  },
  'manufacturing': {
    name: 'Manufacturing Site',
    wan_links: [
      {
        name: 'Primary Fiber',
        type: 'fiber',
        bandwidth: '1gbps',
        priority: 'high',
        cost: 100,
        failover_enabled: true
      },
      {
        name: 'Secondary MPLS',
        type: 'mpls',
        bandwidth: '100mbps',
        priority: 'medium',
        cost: 150,
        backup_path: true
      },
      {
        name: 'Emergency LTE',
        type: 'lte',
        bandwidth: '50mbps',
        priority: 'low',
        cost: 300,
        emergency_only: true
      }
    ],
    traffic_policies: {
      scada_systems: { dedicated_path: 'fiber', backup_path: 'mpls', priority: 'critical' },
      erp_systems: { preferred_path: 'fiber', backup_path: 'mpls', priority: 'high' },
      internet_access: { preferred_path: 'fiber', bandwidth_limit: '100mbps' },
      backup_systems: { emergency_path: 'lte', time_restriction: 'maintenance_window' }
    },
    failover_settings: {
      detection_time: '1s',
      recovery_time: '2s',
      blackhole_detection: true,
      industrial_grade: true
    }
  }
};

// Generate branch configuration based on connectivity type
let branchConfig = branchData;
if (operation === 'configure_branch' && connectivityType && connectivityTemplates[connectivityType]) {
  branchConfig = { ...connectivityTemplates[connectivityType], ...branchData };
}

// Prepare output data
const outputData = {
  operation,
  branch_id: branchId,
  branch_config: branchConfig,
  orchestrator_url: orchestratorUrl,
  connectivity_type: connectivityType,
  timestamp: new Date().toISOString(),
  validation_rules: {
    branch_id_required: ['configure_branch', 'update_connectivity', 'failover_test', 'monitor_links'].includes(operation),
    config_required: ['configure_branch', 'update_connectivity'].includes(operation),
    links_required: ['configure_branch', 'optimize_paths'].includes(operation)
  }
};

// Add operation-specific data
if (operation === 'failover_test') {
  outputData.test_config = {
    test_duration: branchData.test_duration || '60s',
    test_links: branchData.test_links || 'all',
    simulate_failure: branchData.simulate_failure || false,
    rollback_enabled: branchData.rollback_enabled !== false
  };
}

if (operation === 'optimize_paths') {
  outputData.optimization_config = {
    algorithm: branchData.algorithm || 'latency_based',
    metrics: branchData.metrics || ['latency', 'jitter', 'packet_loss', 'bandwidth'],
    update_frequency: branchData.update_frequency || '5m',
    auto_apply: branchData.auto_apply !== false
  };
}

if (operation === 'monitor_links') {
  outputData.monitoring_config = {
    interval: branchData.interval || '30s',
    thresholds: branchData.thresholds || {
      latency: '100ms',
      jitter: '50ms',
      packet_loss: '5%',
      bandwidth_utilization: '80%'
    },
    alert_enabled: branchData.alert_enabled !== false
  };
}

return [{ json: outputData }];

// Process EdgeConnect Branch Connectivity response and handle errors
const responseData = $input.item(0).json;
const operation = $input.item(0).json.operation || 'unknown';
const timestamp = new Date().toISOString();

// Determine success based on HTTP status code
const isSuccess = responseData.status_code ? 
  (responseData.status_code >= 200 && responseData.status_code < 300) : 
  (!responseData.error && responseData.success !== false);

// Categorize errors
let errorCategory = 'none';
let errorMessage = null;

if (!isSuccess) {
  const statusCode = responseData.status_code || 0;
  const errorText = responseData.error || responseData.message || 'Unknown error';

  if (statusCode === 400) {
    errorCategory = 'validation_error';
    errorMessage = `Invalid branch configuration: ${errorText}`;
  } else if (statusCode === 401) {
    errorCategory = 'authentication_error';
    errorMessage = `Authentication failed: ${errorText}`;
  } else if (statusCode === 403) {
    errorCategory = 'authorization_error';
    errorMessage = `Authorization failed: ${errorText}`;
  } else if (statusCode === 404) {
    errorCategory = 'branch_not_found';
    errorMessage = `Branch not found: ${errorText}`;
  } else if (statusCode === 409) {
    errorCategory = 'configuration_conflict';
    errorMessage = `Configuration conflict: ${errorText}`;
  } else if (statusCode === 422) {
    errorCategory = 'link_unavailable';
    errorMessage = `WAN link unavailable: ${errorText}`;
  } else if (statusCode === 429) {
    errorCategory = 'rate_limit_error';
    errorMessage = `Rate limit exceeded: ${errorText}`;
  } else if (statusCode >= 500) {
    errorCategory = 'orchestrator_error';
    errorMessage = `Orchestrator error: ${errorText}`;
  } else {
    errorCategory = 'unknown_error';
    errorMessage = `Unexpected error: ${errorText}`;
  }
}

// Format success response
let result = {
  operation,
  success: isSuccess,
  timestamp,
  error_category: errorCategory,
  error_message: errorMessage
};

// Add operation-specific success data
if (isSuccess) {
  switch (operation) {
    case 'configure_branch':
      result.branch_id = responseData.branch_id;
      result.connectivity_type = responseData.connectivity_type;
      result.wan_links_configured = responseData.wan_links_count || 0;
      result.policies_applied = responseData.policies_count || 0;
      result.message = `Branch '${responseData.branch_name || 'Unknown'}' configured successfully with ${result.wan_links_configured} WAN links`;
      break;

    case 'update_connectivity':
      result.branch_id = responseData.branch_id;
      result.updated_links = responseData.updated_links || [];
      result.policies_updated = responseData.policies_updated || [];
      result.message = `Connectivity updated for ${result.updated_links.length} links`;
      break;

    case 'failover_test':
      result.test_id = responseData.test_id;
      result.test_duration = responseData.actual_duration;
      result.links_tested = responseData.links_tested || [];
      result.failover_success = responseData.failover_success || false;
      result.recovery_time = responseData.recovery_time;
      result.message = `Failover test ${result.failover_success ? 'passed' : 'failed'} - Recovery time: ${result.recovery_time}`;
      break;

    case 'optimize_paths':
      result.optimization_id = responseData.optimization_id;
      result.paths_optimized = responseData.paths_optimized || [];
      result.performance_improvement = responseData.performance_improvement || '0%';
      result.message = `Path optimization completed - ${result.performance_improvement} improvement achieved`;
      break;

    case 'monitor_links':
      result.monitoring_data = responseData.links || [];
      result.healthy_links = result.monitoring_data.filter(link => link.status === 'healthy').length;
      result.total_links = result.monitoring_data.length;
      result.alerts_generated = responseData.alerts_count || 0;
      result.message = `Monitoring: ${result.healthy_links}/${result.total_links} links healthy, ${result.alerts_generated} alerts`;
      break;

    case 'reset_configuration':
      result.reset_successful = responseData.reset_successful || false;
      result.backup_created = responseData.backup_id || null;
      result.links_preserved = responseData.links_preserved || [];
      result.message = `Configuration reset ${result.reset_successful ? 'successful' : 'failed'}`;
      break;

    default:
      result.message = 'Branch connectivity operation completed successfully';
  }

  // Add raw response data for debugging
  result.raw_response = responseData;
}

// Determine notification level
result.notification_level = isSuccess ? 'success' : 
  (['authentication_error', 'authorization_error', 'orchestrator_error'].includes(errorCategory) ? 'critical' : 'warning');

// Add rollback requirement for critical failures
if (!isSuccess && ['configure_branch', 'update_connectivity'].includes(operation)) {
  result.requires_rollback = ['configuration_conflict', 'orchestrator_error'].includes(errorCategory);
}

// Add performance metrics for monitoring operations
if (operation === 'monitor_links' && isSuccess) {
  result.performance_summary = {
    average_latency: responseData.avg_latency,
    total_bandwidth: responseData.total_bandwidth,
    utilization_percentage: responseData.utilization_pct,
    packet_loss_rate: responseData.packet_loss_rate
  };
}

return [{ json: result }];

