// EdgeConnect Performance Optimization Workflow
// Operations: analyze_performance, optimize_wan, tune_qos, compress_traffic, balance_load, generate_report

const operation = $input.item(0).json.operation;
const optimizationData = $input.item(0).json.optimization_data || {};
const orchestratorUrl = $input.item(0).json.orchestrator_url;
const applianceId = $input.item(0).json.appliance_id;
const optimizationType = $input.item(0).json.optimization_type || 'balanced';

// Validate required parameters
if (!operation) {
  throw new Error('Operation is required. Valid operations: analyze_performance, optimize_wan, tune_qos, compress_traffic, balance_load, generate_report');
}

if (!orchestratorUrl) {
  throw new Error('Orchestrator URL is required');
}

if (['optimize_wan', 'tune_qos', 'compress_traffic', 'balance_load'].includes(operation) && !applianceId) {
  throw new Error('Appliance ID is required for optimization operations');
}

// Performance optimization templates
const optimizationTemplates = {
  'latency_focused': {
    name: 'Latency Optimization',
    priority: 'minimize_latency',
    wan_optimization: {
      tcp_acceleration: true,
      compression: 'adaptive',
      deduplication: true,
      caching: 'aggressive',
      packet_coalescing: false
    },
    qos_settings: {
      voice_priority: 'highest',
      video_priority: 'high',
      data_priority: 'medium',
      background_priority: 'low',
      latency_threshold: '50ms'
    },
    traffic_shaping: {
      burst_handling: 'immediate',
      queue_management: 'priority_based',
      bandwidth_allocation: 'dynamic'
    }
  },
  'bandwidth_focused': {
    name: 'Bandwidth Optimization',
    priority: 'maximize_throughput',
    wan_optimization: {
      tcp_acceleration: true,
      compression: 'maximum',
      deduplication: true,
      caching: 'maximum',
      packet_coalescing: true
    },
    qos_settings: {
      voice_priority: 'high',
      video_priority: 'medium',
      data_priority: 'high',
      background_priority: 'low',
      bandwidth_guarantee: '80%'
    },
    traffic_shaping: {
      burst_handling: 'buffer',
      queue_management: 'bandwidth_based',
      bandwidth_allocation: 'static'
    }
  },
  'balanced': {
    name: 'Balanced Performance',
    priority: 'balanced_optimization',
    wan_optimization: {
      tcp_acceleration: true,
      compression: 'adaptive',
      deduplication: true,
      caching: 'moderate',
      packet_coalescing: false
    },
    qos_settings: {
      voice_priority: 'highest',
      video_priority: 'high',
      data_priority: 'medium',
      background_priority: 'low',
      latency_threshold: '100ms',
      bandwidth_guarantee: '60%'
    },
    traffic_shaping: {
      burst_handling: 'adaptive',
      queue_management: 'hybrid',
      bandwidth_allocation: 'dynamic'
    }
  },
  'application_aware': {
    name: 'Application-Aware Optimization',
    priority: 'application_performance',
    wan_optimization: {
      tcp_acceleration: true,
      compression: 'application_specific',
      deduplication: true,
      caching: 'application_aware',
      packet_coalescing: false
    },
    qos_settings: {
      office365_priority: 'highest',
      salesforce_priority: 'high',
      web_browsing_priority: 'medium',
      file_transfer_priority: 'low',
      application_recognition: true
    },
    traffic_shaping: {
      burst_handling: 'application_based',
      queue_management: 'application_aware',
      bandwidth_allocation: 'application_driven'
    }
  },
  'cost_optimized': {
    name: 'Cost-Optimized Performance',
    priority: 'minimize_wan_cost',
    wan_optimization: {
      tcp_acceleration: true,
      compression: 'maximum',
      deduplication: true,
      caching: 'aggressive',
      packet_coalescing: true
    },
    qos_settings: {
      voice_priority: 'high',
      video_priority: 'medium',
      data_priority: 'medium',
      background_priority: 'lowest',
      cost_awareness: true
    },
    traffic_shaping: {
      burst_handling: 'cost_aware',
      queue_management: 'cost_based',
      bandwidth_allocation: 'cost_optimized',
      offload_to_cheaper_links: true
    }
  },
  'security_focused': {
    name: 'Security-First Optimization',
    priority: 'secure_performance',
    wan_optimization: {
      tcp_acceleration: true,
      compression: 'encrypted_only',
      deduplication: false,
      caching: 'secure_only',
      packet_coalescing: false
    },
    qos_settings: {
      encrypted_traffic_priority: 'highest',
      voice_priority: 'high',
      video_priority: 'medium',
      data_priority: 'medium',
      security_scanning: true
    },
    traffic_shaping: {
      burst_handling: 'security_first',
      queue_management: 'security_aware',
      bandwidth_allocation: 'secure_priority'
    }
  }
};

// Generate optimization configuration based on type
let optimizationConfig = optimizationData;
if (optimizationType && optimizationTemplates[optimizationType]) {
  optimizationConfig = { ...optimizationTemplates[optimizationType], ...optimizationData };
}

// Prepare output data
const outputData = {
  operation,
  appliance_id: applianceId,
  optimization_config: optimizationConfig,
  orchestrator_url: orchestratorUrl,
  optimization_type: optimizationType,
  timestamp: new Date().toISOString(),
  validation_rules: {
    appliance_id_required: ['optimize_wan', 'tune_qos', 'compress_traffic', 'balance_load'].includes(operation),
    config_required: ['optimize_wan', 'tune_qos', 'compress_traffic'].includes(operation),
    metrics_required: ['analyze_performance', 'generate_report'].includes(operation)
  }
};

// Add operation-specific data
if (operation === 'analyze_performance') {
  outputData.analysis_config = {
    duration: optimizationData.duration || '24h',
    metrics: optimizationData.metrics || ['latency', 'throughput', 'packet_loss', 'jitter', 'utilization'],
    granularity: optimizationData.granularity || '5m',
    include_applications: optimizationData.include_applications !== false,
    baseline_comparison: optimizationData.baseline_comparison !== false
  };
}

if (operation === 'optimize_wan') {
  outputData.wan_config = {
    tcp_acceleration: optimizationConfig.wan_optimization?.tcp_acceleration || true,
    compression_level: optimizationConfig.wan_optimization?.compression || 'adaptive',
    deduplication: optimizationConfig.wan_optimization?.deduplication || true,
    caching_policy: optimizationConfig.wan_optimization?.caching || 'moderate',
    packet_coalescing: optimizationConfig.wan_optimization?.packet_coalescing || false,
    auto_tune: optimizationData.auto_tune !== false
  };
}

if (operation === 'tune_qos') {
  outputData.qos_config = {
    voice_priority: optimizationConfig.qos_settings?.voice_priority || 'highest',
    video_priority: optimizationConfig.qos_settings?.video_priority || 'high',
    data_priority: optimizationConfig.qos_settings?.data_priority || 'medium',
    background_priority: optimizationConfig.qos_settings?.background_priority || 'low',
    latency_threshold: optimizationConfig.qos_settings?.latency_threshold || '100ms',
    bandwidth_guarantee: optimizationConfig.qos_settings?.bandwidth_guarantee || '60%'
  };
}

if (operation === 'compress_traffic') {
  outputData.compression_config = {
    algorithm: optimizationData.algorithm || 'lz4',
    level: optimizationData.level || 'medium',
    exclude_types: optimizationData.exclude_types || ['encrypted', 'compressed'],
    cpu_threshold: optimizationData.cpu_threshold || '80%',
    adaptive_compression: optimizationData.adaptive_compression !== false
  };
}

if (operation === 'balance_load') {
  outputData.load_balance_config = {
    algorithm: optimizationData.algorithm || 'weighted_round_robin',
    health_check_interval: optimizationData.health_check_interval || '30s',
    failover_threshold: optimizationData.failover_threshold || '3',
    sticky_sessions: optimizationData.sticky_sessions || false,
    weight_adjustment: optimizationData.weight_adjustment || 'automatic'
  };
}

if (operation === 'generate_report') {
  outputData.report_config = {
    time_period: optimizationData.time_period || '7d',
    report_type: optimizationData.report_type || 'comprehensive',
    include_recommendations: optimizationData.include_recommendations !== false,
    format: optimizationData.format || 'pdf',
    email_recipients: optimizationData.email_recipients || []
  };
}

return [{ json: outputData }];

// Process EdgeConnect Performance Optimization response and handle errors
const responseData = $input.item(0).json;
const operation = $input.item(0).json.operation || 'unknown';
const timestamp = new Date().toISOString();

// Determine success based on HTTP status code
const isSuccess = responseData.status_code ? 
  (responseData.status_code >= 200 && responseData.status_code < 300) : 
  (!responseData.error && responseData.success !== false);

// Categorize errors
let errorCategory = 'none';
let errorMessage = null;

if (!isSuccess) {
  const statusCode = responseData.status_code || 0;
  const errorText = responseData.error || responseData.message || 'Unknown error';

  if (statusCode === 400) {
    errorCategory = 'validation_error';
    errorMessage = `Invalid optimization configuration: ${errorText}`;
  } else if (statusCode === 401) {
    errorCategory = 'authentication_error';
    errorMessage = `Authentication failed: ${errorText}`;
  } else if (statusCode === 403) {
    errorCategory = 'authorization_error';
    errorMessage = `Authorization failed: ${errorText}`;
  } else if (statusCode === 404) {
    errorCategory = 'appliance_not_found';
    errorMessage = `Appliance not found: ${errorText}`;
  } else if (statusCode === 409) {
    errorCategory = 'optimization_conflict';
    errorMessage = `Optimization conflict: ${errorText}`;
  } else if (statusCode === 422) {
    errorCategory = 'resource_unavailable';
    errorMessage = `Resource unavailable: ${errorText}`;
  } else if (statusCode === 429) {
    errorCategory = 'rate_limit_error';
    errorMessage = `Rate limit exceeded: ${errorText}`;
  } else if (statusCode >= 500) {
    errorCategory = 'orchestrator_error';
    errorMessage = `Orchestrator error: ${errorText}`;
  } else {
    errorCategory = 'unknown_error';
    errorMessage = `Unexpected error: ${errorText}`;
  }
}

// Format success response
let result = {
  operation,
  success: isSuccess,
  timestamp,
  error_category: errorCategory,
  error_message: errorMessage
};

// Add operation-specific success data
if (isSuccess) {
  switch (operation) {
    case 'analyze_performance':
      result.analysis_id = responseData.analysis_id;
      result.baseline_metrics = responseData.baseline_metrics || {};
      result.current_metrics = responseData.current_metrics || {};
      result.performance_score = responseData.performance_score || 0;
      result.bottlenecks = responseData.bottlenecks || [];
      result.recommendations = responseData.recommendations || [];
      result.message = `Performance analysis completed - Score: ${result.performance_score}/100`;
      break;

    case 'optimize_wan':
      result.optimization_id = responseData.optimization_id;
      result.wan_improvements = responseData.wan_improvements || {};
      result.throughput_increase = responseData.throughput_increase || '0%';
      result.latency_reduction = responseData.latency_reduction || '0%';
      result.message = `WAN optimization applied - Throughput: +${result.throughput_increase}, Latency: -${result.latency_reduction}`;
      break;

    case 'tune_qos':
      result.qos_policy_id = responseData.qos_policy_id;
      result.priority_changes = responseData.priority_changes || [];
      result.bandwidth_allocation = responseData.bandwidth_allocation || {};
      result.message = `QoS tuning applied - ${result.priority_changes.length} priority changes made`;
      break;

    case 'compress_traffic':
      result.compression_id = responseData.compression_id;
      result.compression_ratio = responseData.compression_ratio || '0%';
      result.bandwidth_savings = responseData.bandwidth_savings || '0%';
      result.cpu_usage_increase = responseData.cpu_usage_increase || '0%';
      result.message = `Traffic compression enabled - ${result.compression_ratio} compression, ${result.bandwidth_savings} bandwidth saved`;
      break;

    case 'balance_load':
      result.load_balancer_id = responseData.load_balancer_id;
      result.balanced_links = responseData.balanced_links || [];
      result.load_distribution = responseData.load_distribution || {};
      result.message = `Load balancing configured - ${result.balanced_links.length} links balanced`;
      break;

    case 'generate_report':
      result.report_id = responseData.report_id;
      result.report_url = responseData.report_url;
      result.report_format = responseData.format || 'pdf';
      result.email_sent = responseData.email_sent || false;
      result.message = `Performance report generated - ${result.report_format.toUpperCase()}`;
      break;

    default:
      result.message = 'Performance optimization operation completed successfully';
  }

  // Add raw response data for debugging
  result.raw_response = responseData;
}

// Determine notification level
result.notification_level = isSuccess ? 'success' : 
  (['authentication_error', 'authorization_error', 'orchestrator_error'].includes(errorCategory) ? 'critical' : 'warning');

// Add rollback requirement for critical failures
if (!isSuccess && ['optimize_wan', 'tune_qos', 'compress_traffic', 'balance_load'].includes(operation)) {
  result.requires_rollback = ['optimization_conflict', 'orchestrator_error'].includes(errorCategory);
}

// Add performance improvement metrics
if (operation === 'analyze_performance' && isSuccess) {
  result.improvement_potential = {
    wan_optimization: responseData.wan_optimization_potential || 0,
    qos_tuning: responseData.qos_tuning_potential || 0,
    compression: responseData.compression_potential || 0,
    load_balancing: responseData.load_balancing_potential || 0
  };
}

return [{ json: result }];

