// EdgeConnect SD-WAN Policy Management Workflow
// Operations: create_policy, update_policy, delete_policy, list_policies, apply_policy, validate_policy

const operation = $input.item(0).json.operation;
const policyData = $input.item(0).json.policy_data || {};
const orchestratorUrl = $input.item(0).json.orchestrator_url;
const policyId = $input.item(0).json.policy_id;
const businessIntent = $input.item(0).json.business_intent || 'optimize_performance';

// Validate required parameters
if (!operation) {
  throw new Error('Operation is required. Valid operations: create_policy, update_policy, delete_policy, list_policies, apply_policy, validate_policy');
}

if (!orchestratorUrl) {
  throw new Error('Orchestrator URL is required');
}

if (['create_policy', 'update_policy', 'apply_policy'].includes(operation) && !policyData.name) {
  throw new Error('Policy name is required for create, update, and apply operations');
}

if (['update_policy', 'delete_policy', 'apply_policy'].includes(operation) && !policyId) {
  throw new Error('Policy ID is required for update, delete, and apply operations');
}

// Policy template configurations
const policyTemplates = {
  'qos_voice_priority': {
    name: 'QoS Voice Priority',
    type: 'qos',
    priority: 'high',
    bandwidth: '100mbps',
    latency_tolerance: '50ms',
    jitter_tolerance: '20ms',
    packet_loss_tolerance: '1%',
    rules: [
      {
        name: 'Voice Traffic',
        match: { dscp: 'ef', protocol: 'udp', port_range: '5060-5080' },
        action: { priority: 'high', bandwidth_guarantee: '10mbps' }
      },
      {
        name: 'Video Traffic',
        match: { dscp: 'af41', protocol: 'udp', port_range: '1024-65535' },
        action: { priority: 'medium', bandwidth_guarantee: '20mbps' }
      }
    ]
  },
  'security_standard': {
    name: 'Standard Security Policy',
    type: 'security',
    firewall_enabled: true,
    ips_enabled: true,
    antivirus_enabled: true,
    web_filtering: true,
    rules: [
      {
        name: 'Block Malicious Traffic',
        match: { threat_level: 'high' },
        action: { type: 'block', log: true, alert: true }
      },
      {
        name: 'Allow Business Traffic',
        match: { application_category: 'business' },
        action: { type: 'allow', log: true, qos_class: 'business' }
      }
    ]
  },
  'wan_optimization': {
    name: 'WAN Optimization Policy',
    type: 'optimization',
    compression_enabled: true,
    deduplication_enabled: true,
    tcp_optimization: true,
    caching_enabled: true,
    rules: [
      {
        name: 'Optimize HTTP Traffic',
        match: { protocol: 'http', port: '80' },
        action: { compress: true, cache: true, tcp_optimize: true }
      },
      {
        name: 'Optimize HTTPS Traffic',
        match: { protocol: 'https', port: '443' },
        action: { compress: false, cache: false, tcp_optimize: true }
      }
    ]
  },
  'branch_connectivity': {
    name: 'Branch Connectivity Policy',
    type: 'connectivity',
    primary_wan: 'mpls',
    secondary_wan: 'broadband',
    failover_enabled: true,
    load_balancing: true,
    rules: [
      {
        name: 'Critical Applications',
        match: { application_category: 'critical' },
        action: { preferred_path: 'mpls', failover: 'broadband' }
      },
      {
        name: 'Internet Traffic',
        match: { destination_type: 'internet' },
        action: { preferred_path: 'broadband', failover: 'mpls' }
      }
    ]
  }
};

// Generate policy configuration based on business intent
let policyConfig = policyData;
if (operation === 'create_policy' && businessIntent && policyTemplates[businessIntent]) {
  policyConfig = { ...policyTemplates[businessIntent], ...policyData };
}

// Prepare output data
const outputData = {
  operation,
  policy_id: policyId,
  policy_config: policyConfig,
  orchestrator_url: orchestratorUrl,
  business_intent: businessIntent,
  timestamp: new Date().toISOString(),
  validation_rules: {
    name_required: ['create_policy', 'update_policy', 'apply_policy'].includes(operation),
    id_required: ['update_policy', 'delete_policy', 'apply_policy'].includes(operation),
    config_required: ['create_policy', 'update_policy'].includes(operation)
  }
};

// Add operation-specific data
if (operation === 'list_policies') {
  outputData.list_filters = {
    type: policyData.type || 'all',
    status: policyData.status || 'all',
    limit: policyData.limit || 100
  };
}

if (operation === 'validate_policy') {
  outputData.validation_checks = {
    syntax_check: true,
    conflict_check: true,
    performance_impact: true,
    security_compliance: true
  };
}

return [{ json: outputData }];

// Process EdgeConnect API response and handle errors
const responseData = $input.item(0).json;
const operation = $input.item(0).json.operation || 'unknown';
const timestamp = new Date().toISOString();

// Determine success based on HTTP status code
const isSuccess = responseData.status_code ? 
  (responseData.status_code >= 200 && responseData.status_code < 300) : 
  (!responseData.error && responseData.success !== false);

// Categorize errors
let errorCategory = 'none';
let errorMessage = null;

if (!isSuccess) {
  const statusCode = responseData.status_code || 0;
  const errorText = responseData.error || responseData.message || 'Unknown error';

  if (statusCode === 400) {
    errorCategory = 'validation_error';
    errorMessage = `Invalid request: ${errorText}`;
  } else if (statusCode === 401) {
    errorCategory = 'authentication_error';
    errorMessage = `Authentication failed: ${errorText}`;
  } else if (statusCode === 403) {
    errorCategory = 'authorization_error';
    errorMessage = `Authorization failed: ${errorText}`;
  } else if (statusCode === 404) {
    errorCategory = 'resource_not_found';
    errorMessage = `Resource not found: ${errorText}`;
  } else if (statusCode === 409) {
    errorCategory = 'conflict_error';
    errorMessage = `Conflict: ${errorText}`;
  } else if (statusCode === 429) {
    errorCategory = 'rate_limit_error';
    errorMessage = `Rate limit exceeded: ${errorText}`;
  } else if (statusCode >= 500) {
    errorCategory = 'server_error';
    errorMessage = `Server error: ${errorText}`;
  } else {
    errorCategory = 'unknown_error';
    errorMessage = `Unexpected error: ${errorText}`;
  }
}

// Format success response
let result = {
  operation,
  success: isSuccess,
  timestamp,
  error_category: errorCategory,
  error_message: errorMessage
};

// Add operation-specific success data
if (isSuccess) {
  switch (operation) {
    case 'create_policy':
      result.policy_id = responseData.id || responseData.policy_id;
      result.policy_name = responseData.name;
      result.policy_type = responseData.type;
      result.message = `Policy '${responseData.name}' created successfully`;
      break;

    case 'update_policy':
      result.policy_id = responseData.id || responseData.policy_id;
      result.policy_name = responseData.name;
      result.message = `Policy '${responseData.name}' updated successfully`;
      break;

    case 'delete_policy':
      result.message = `Policy deleted successfully`;
      break;

    case 'list_policies':
      result.policies = responseData.policies || responseData.data || [];
      result.total_count = responseData.total || result.policies.length;
      result.message = `Retrieved ${result.total_count} policies`;
      break;

    case 'apply_policy':
      result.deployment_id = responseData.deployment_id;
      result.target_appliances = responseData.target_appliances || [];
      result.message = `Policy applied to ${result.target_appliances.length} appliances`;
      break;

    case 'validate_policy':
      result.validation_result = responseData.validation_result || {};
      result.is_valid = responseData.is_valid || false;
      result.warnings = responseData.warnings || [];
      result.message = `Policy validation ${result.is_valid ? 'passed' : 'failed'}`;
      break;

    default:
      result.message = 'Operation completed successfully';
  }

  // Add raw response data for debugging
  result.raw_response = responseData;
}

// Determine notification level
result.notification_level = isSuccess ? 'success' : 
  (['authentication_error', 'authorization_error', 'server_error'].includes(errorCategory) ? 'critical' : 'warning');

// Add rollback requirement for critical failures
if (!isSuccess && ['create_policy', 'update_policy', 'apply_policy'].includes(operation)) {
  result.requires_rollback = ['conflict_error', 'server_error'].includes(errorCategory);
}

return [{ json: result }];

