// EdgeConnect Security Policy Management Workflow
// Operations: create_security_policy, update_security_policy, delete_security_policy, deploy_security_policy, audit_security, threat_response

const operation = $input.item(0).json.operation;
const securityData = $input.item(0).json.security_data || {};
const orchestratorUrl = $input.item(0).json.orchestrator_url;
const policyId = $input.item(0).json.policy_id;
const securityProfile = $input.item(0).json.security_profile || 'standard';

// Validate required parameters
if (!operation) {
  throw new Error('Operation is required. Valid operations: create_security_policy, update_security_policy, delete_security_policy, deploy_security_policy, audit_security, threat_response');
}

if (!orchestratorUrl) {
  throw new Error('Orchestrator URL is required');
}

if (['update_security_policy', 'delete_security_policy', 'deploy_security_policy'].includes(operation) && !policyId) {
  throw new Error('Policy ID is required for this operation');
}

// Security policy templates
const securityTemplates = {
  'basic': {
    name: 'Basic Security Policy',
    security_level: 'basic',
    firewall: {
      enabled: true,
      default_action: 'allow',
      stateful_inspection: true,
      application_control: false
    },
    intrusion_prevention: {
      enabled: false,
      sensitivity: 'low',
      block_threats: false,
      alert_only: true
    },
    antivirus: {
      enabled: false,
      real_time_scanning: false,
      quarantine: false
    },
    web_filtering: {
      enabled: false,
      categories: [],
      block_malware: false
    },
    ssl_inspection: {
      enabled: false,
      decrypt_traffic: false,
      certificate_validation: false
    }
  },
  'standard': {
    name: 'Standard Security Policy',
    security_level: 'standard',
    firewall: {
      enabled: true,
      default_action: 'deny',
      stateful_inspection: true,
      application_control: true,
      geo_blocking: false
    },
    intrusion_prevention: {
      enabled: true,
      sensitivity: 'medium',
      block_threats: true,
      alert_enabled: true,
      signature_updates: 'daily'
    },
    antivirus: {
      enabled: true,
      real_time_scanning: true,
      quarantine: true,
      cloud_lookup: true
    },
    web_filtering: {
      enabled: true,
      categories: ['malware', 'phishing', 'gambling', 'adult'],
      block_malware: true,
      safe_search: true
    },
    ssl_inspection: {
      enabled: false,
      decrypt_traffic: false,
      certificate_validation: true
    }
  },
  'high_security': {
    name: 'High Security Policy',
    security_level: 'high',
    firewall: {
      enabled: true,
      default_action: 'deny',
      stateful_inspection: true,
      application_control: true,
      geo_blocking: true,
      advanced_threats: true
    },
    intrusion_prevention: {
      enabled: true,
      sensitivity: 'high',
      block_threats: true,
      alert_enabled: true,
      signature_updates: 'real_time',
      behavioral_analysis: true
    },
    antivirus: {
      enabled: true,
      real_time_scanning: true,
      quarantine: true,
      cloud_lookup: true,
      advanced_heuristics: true
    },
    web_filtering: {
      enabled: true,
      categories: ['malware', 'phishing', 'gambling', 'adult', 'social_media', 'entertainment'],
      block_malware: true,
      safe_search: true,
      ssl_scanning: true
    },
    ssl_inspection: {
      enabled: true,
      decrypt_traffic: true,
      certificate_validation: true,
      deep_packet_inspection: true
    },
    data_loss_prevention: {
      enabled: true,
      scan_uploads: true,
      scan_downloads: true,
      block_sensitive_data: true
    }
  },
  'compliance_focused': {
    name: 'Compliance-Focused Security Policy',
    security_level: 'compliance',
    firewall: {
      enabled: true,
      default_action: 'deny',
      stateful_inspection: true,
      application_control: true,
      geo_blocking: true,
      compliance_logging: true
    },
    intrusion_prevention: {
      enabled: true,
      sensitivity: 'high',
      block_threats: true,
      alert_enabled: true,
      signature_updates: 'real_time',
      compliance_reporting: true
    },
    antivirus: {
      enabled: true,
      real_time_scanning: true,
      quarantine: true,
      cloud_lookup: true,
      compliance_logging: true
    },
    web_filtering: {
      enabled: true,
      categories: ['malware', 'phishing', 'gambling', 'adult', 'social_media'],
      block_malware: true,
      safe_search: true,
      compliance_reporting: true
    },
    ssl_inspection: {
      enabled: true,
      decrypt_traffic: true,
      certificate_validation: true,
      compliance_logging: true
    },
    data_loss_prevention: {
      enabled: true,
      scan_uploads: true,
      scan_downloads: true,
      block_sensitive_data: true,
      compliance_templates: ['pci_dss', 'hipaa', 'gdpr']
    },
    audit_logging: {
      enabled: true,
      log_all_traffic: true,
      retention_period: '7_years',
      tamper_protection: true
    }
  },
  'zero_trust': {
    name: 'Zero Trust Security Policy',
    security_level: 'zero_trust',
    firewall: {
      enabled: true,
      default_action: 'deny',
      stateful_inspection: true,
      application_control: true,
      geo_blocking: true,
      micro_segmentation: true
    },
    intrusion_prevention: {
      enabled: true,
      sensitivity: 'maximum',
      block_threats: true,
      alert_enabled: true,
      signature_updates: 'real_time',
      behavioral_analysis: true,
      machine_learning: true
    },
    antivirus: {
      enabled: true,
      real_time_scanning: true,
      quarantine: true,
      cloud_lookup: true,
      advanced_heuristics: true,
      sandboxing: true
    },
    web_filtering: {
      enabled: true,
      categories: ['malware', 'phishing', 'gambling', 'adult', 'social_media', 'entertainment', 'shopping'],
      block_malware: true,
      safe_search: true,
      ssl_scanning: true,
      reputation_filtering: true
    },
    ssl_inspection: {
      enabled: true,
      decrypt_traffic: true,
      certificate_validation: true,
      deep_packet_inspection: true,
      certificate_pinning: true
    },
    data_loss_prevention: {
      enabled: true,
      scan_uploads: true,
      scan_downloads: true,
      block_sensitive_data: true,
      content_inspection: true,
      machine_learning: true
    },
    identity_access: {
      enabled: true,
      multi_factor_auth: true,
      continuous_verification: true,
      behavioral_analytics: true,
      privileged_access_management: true
    },
    network_segmentation: {
      enabled: true,
      micro_segmentation: true,
      application_segmentation: true,
      user_segmentation: true
    }
  }
};

// Generate security configuration based on profile
let securityConfig = securityData;
if (operation === 'create_security_policy' && securityProfile && securityTemplates[securityProfile]) {
  securityConfig = { ...securityTemplates[securityProfile], ...securityData };
}

// Prepare output data
const outputData = {
  operation,
  policy_id: policyId,
  security_config: securityConfig,
  orchestrator_url: orchestratorUrl,
  security_profile: securityProfile,
  timestamp: new Date().toISOString(),
  validation_rules: {
    policy_id_required: ['update_security_policy', 'delete_security_policy', 'deploy_security_policy'].includes(operation),
    config_required: ['create_security_policy', 'update_security_policy'].includes(operation),
    profile_required: ['create_security_policy'].includes(operation)
  }
};

// Add operation-specific data
if (operation === 'create_security_policy') {
  outputData.policy_config = {
    name: securityConfig.name || 'Custom Security Policy',
    description: securityConfig.description || 'Auto-generated security policy',
    security_level: securityConfig.security_level || 'standard',
    auto_deploy: securityData.auto_deploy !== false,
    policy_version: '1.0'
  };
}

if (operation === 'deploy_security_policy') {
  outputData.deployment_config = {
    target_appliances: securityData.target_appliances || [],
    deployment_mode: securityData.deployment_mode || 'immediate',
    rollback_enabled: securityData.rollback_enabled !== false,
    validation_enabled: securityData.validation_enabled !== false,
    maintenance_window: securityData.maintenance_window || false
  };
}

if (operation === 'audit_security') {
  outputData.audit_config = {
    audit_scope: securityData.audit_scope || 'all',
    compliance_frameworks: securityData.compliance_frameworks || ['iso27001', 'nist'],
    include_vulnerabilities: securityData.include_vulnerabilities !== false,
    generate_report: securityData.generate_report !== false,
    remediation_plan: securityData.remediation_plan !== false
  };
}

if (operation === 'threat_response') {
  outputData.threat_config = {
    threat_id: securityData.threat_id,
    threat_type: securityData.threat_type || 'unknown',
    response_action: securityData.response_action || 'block',
    isolate_source: securityData.isolate_source || false,
    alert_security_team: securityData.alert_security_team !== false,
    forensic_analysis: securityData.forensic_analysis || false
  };
}

return [{ json: outputData }];

// Process EdgeConnect Security Policy response and handle errors
const responseData = $input.item(0).json;
const operation = $input.item(0).json.operation || 'unknown';
const timestamp = new Date().toISOString();

// Determine success based on HTTP status code
const isSuccess = responseData.status_code ? 
  (responseData.status_code >= 200 && responseData.status_code < 300) : 
  (!responseData.error && responseData.success !== false);

// Categorize errors
let errorCategory = 'none';
let errorMessage = null;

if (!isSuccess) {
  const statusCode = responseData.status_code || 0;
  const errorText = responseData.error || responseData.message || 'Unknown error';

  if (statusCode === 400) {
    errorCategory = 'validation_error';
    errorMessage = `Invalid security policy configuration: ${errorText}`;
  } else if (statusCode === 401) {
    errorCategory = 'authentication_error';
    errorMessage = `Authentication failed: ${errorText}`;
  } else if (statusCode === 403) {
    errorCategory = 'authorization_error';
    errorMessage = `Authorization failed: ${errorText}`;
  } else if (statusCode === 404) {
    errorCategory = 'policy_not_found';
    errorMessage = `Security policy not found: ${errorText}`;
  } else if (statusCode === 409) {
    errorCategory = 'policy_conflict';
    errorMessage = `Policy conflict: ${errorText}`;
  } else if (statusCode === 422) {
    errorCategory = 'security_validation_failed';
    errorMessage = `Security validation failed: ${errorText}`;
  } else if (statusCode === 429) {
    errorCategory = 'rate_limit_error';
    errorMessage = `Rate limit exceeded: ${errorText}`;
  } else if (statusCode >= 500) {
    errorCategory = 'orchestrator_error';
    errorMessage = `Orchestrator error: ${errorText}`;
  } else {
    errorCategory = 'unknown_error';
    errorMessage = `Unexpected error: ${errorText}`;
  }
}

// Format success response
let result = {
  operation,
  success: isSuccess,
  timestamp,
  error_category: errorCategory,
  error_message: errorMessage
};

// Add operation-specific success data
if (isSuccess) {
  switch (operation) {
    case 'create_security_policy':
      result.policy_id = responseData.policy_id;
      result.policy_name = responseData.name;
      result.security_level = responseData.security_level;
      result.components_enabled = responseData.components_enabled || [];
      result.message = `Security policy '${responseData.name}' created successfully (Level: ${result.security_level})`;
      break;

    case 'update_security_policy':
      result.policy_id = responseData.policy_id;
      result.policy_name = responseData.name;
      result.changes_applied = responseData.changes_applied || [];
      result.message = `Security policy '${responseData.name}' updated successfully (${result.changes_applied.length} changes)`;
      break;

    case 'delete_security_policy':
      result.policy_id = responseData.policy_id;
      result.backup_created = responseData.backup_created || false;
      result.message = `Security policy deleted successfully${result.backup_created ? ' (backup created)' : ''}`;
      break;

    case 'deploy_security_policy':
      result.deployment_id = responseData.deployment_id;
      result.target_appliances = responseData.target_appliances || [];
      result.deployment_status = responseData.deployment_status;
      result.rollback_available = responseData.rollback_available || false;
      result.message = `Security policy deployed to ${result.target_appliances.length} appliances (Status: ${result.deployment_status})`;
      break;

    case 'audit_security':
      result.audit_id = responseData.audit_id;
      result.compliance_score = responseData.compliance_score || 0;
      result.vulnerabilities_found = responseData.vulnerabilities_count || 0;
      result.recommendations = responseData.recommendations || [];
      result.compliance_status = responseData.compliance_status;
      result.message = `Security audit completed - Score: ${result.compliance_score}/100, ${result.vulnerabilities_found} vulnerabilities found`;
      break;

    case 'threat_response':
      result.response_id = responseData.response_id;
      result.threat_id = responseData.threat_id;
      result.action_taken = responseData.action_taken;
      result.threat_contained = responseData.threat_contained || false;
      result.forensic_data = responseData.forensic_data || null;
      result.message = `Threat response executed - Action: ${result.action_taken}, Contained: ${result.threat_contained}`;
      break;

    default:
      result.message = 'Security policy operation completed successfully';
  }

  // Add raw response data for debugging
  result.raw_response = responseData;
}

// Determine notification level
result.notification_level = isSuccess ? 'success' : 
  (['authentication_error', 'authorization_error', 'orchestrator_error', 'security_validation_failed'].includes(errorCategory) ? 'critical' : 'warning');

// Add rollback requirement for critical failures
if (!isSuccess && ['create_security_policy', 'update_security_policy', 'deploy_security_policy'].includes(operation)) {
  result.requires_rollback = ['policy_conflict', 'orchestrator_error', 'security_validation_failed'].includes(errorCategory);
}

// Add security metrics for audit operations
if (operation === 'audit_security' && isSuccess) {
  result.security_metrics = {
    firewall_compliance: responseData.firewall_compliance || 0,
    ips_compliance: responseData.ips_compliance || 0,
    antivirus_compliance: responseData.antivirus_compliance || 0,
    web_filtering_compliance: responseData.web_filtering_compliance || 0,
    ssl_inspection_compliance: responseData.ssl_inspection_compliance || 0,
    overall_security_posture: responseData.overall_security_posture || 'unknown'
  };
}

return [{ json: result }];

