// Alert Aggregation Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['source', 'alert_type'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid sources and severity levels
const validSources = ['aruba_central', 'aos_cx', 'edgeconnect', 'uxi', 'monitoring', 'security'];
const validSeverities = ['critical', 'high', 'medium', 'low', 'info'];

if (!validSources.includes(input.source)) {
  throw new Error(`Invalid source: ${input.source}. Valid sources: ${validSources.join(', ')}`);
}

// Set defaults and prepare alert
const processedAlert = {
  ...input,
  alert_id: input.alert_id || `alert-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  timestamp: input.timestamp || new Date().toISOString(),
  severity: input.severity || 'medium',
  status: input.status || 'new',

  // Standardize fields
  device_id: input.device_id || input.device_serial || input.hostname || 'unknown',
  message: input.message || input.description || 'Alert message not provided',
  source_system: input.source,

  // Processing metadata
  processing_timestamp: new Date().toISOString(),
  correlation_id: input.correlation_id || null,
  parent_alert_id: input.parent_alert_id || null
};

return [{ json: processedAlert }];

// Alert Categorization Engine
const alert = items[0].json;

const categoryRules = {
  device_health: {
    keywords: ['cpu', 'memory', 'temperature', 'disk', 'health', 'utilization', 'performance'],
    patterns: [/cpu.*usage/i, /memory.*usage/i, /temperature.*high/i, /disk.*full/i]
  },
  connectivity: {
    keywords: ['link', 'connection', 'interface', 'port', 'down', 'up', 'unreachable', 'timeout'],
    patterns: [/link.*down/i, /interface.*down/i, /connection.*lost/i, /unreachable/i]
  },
  security: {
    keywords: ['security', 'intrusion', 'threat', 'unauthorized', 'breach', 'attack', 'malware'],
    patterns: [/security.*violation/i, /unauthorized.*access/i, /intrusion.*detected/i]
  },
  configuration: {
    keywords: ['config', 'configuration', 'settings', 'policy', 'template', 'deployment'],
    patterns: [/config.*change/i, /policy.*violation/i, /deployment.*failed/i]
  },
  power: {
    keywords: ['power', 'poe', 'battery', 'ups', 'voltage', 'current'],
    patterns: [/power.*failure/i, /poe.*overload/i, /battery.*low/i]
  },
  wireless: {
    keywords: ['wireless', 'wifi', 'radio', 'ssid', 'client', 'association'],
    patterns: [/radio.*failure/i, /client.*disconnected/i, /ssid.*down/i]
  }
};

// Determine alert category
let category = 'general';
let confidence = 0;
const message = alert.message.toLowerCase();
const alertType = alert.alert_type.toLowerCase();

for (const [cat, rules] of Object.entries(categoryRules)) {
  let score = 0;

  // Check keywords
  for (const keyword of rules.keywords) {
    if (message.includes(keyword) || alertType.includes(keyword)) {
      score += 1;
    }
  }

  // Check patterns
  for (const pattern of rules.patterns) {
    if (pattern.test(message) || pattern.test(alertType)) {
      score += 2;
    }
  }

  if (score > confidence) {
    confidence = score;
    category = cat;
  }
}

// Priority mapping based on source and severity
const priorityMapping = {
  aruba_central: { critical: 1, high: 2, medium: 3, low: 4, info: 5 },
  aos_cx: { critical: 1, high: 2, medium: 3, low: 4, info: 5 },
  edgeconnect: { critical: 1, high: 2, medium: 3, low: 4, info: 5 },
  uxi: { critical: 2, high: 3, medium: 4, low: 5, info: 5 },
  monitoring: { critical: 1, high: 2, medium: 3, low: 4, info: 5 },
  security: { critical: 1, high: 1, medium: 2, low: 3, info: 4 }
};

const priority = priorityMapping[alert.source_system]?.[alert.severity] || 3;

// Enhanced alert with categorization
const categorizedAlert = {
  ...alert,
  category: category,
  confidence_score: confidence,
  priority: priority,
  tags: [
    alert.source_system,
    category,
    alert.severity,
    `priority-${priority}`
  ],

  // Correlation hints for grouping
  correlation_keys: {
    device: alert.device_id,
    category: category,
    severity: alert.severity,
    time_window: Math.floor(Date.now() / (5 * 60 * 1000)) // 5-minute windows
  }
};

return [{ json: categorizedAlert }];

// Alert Correlation Engine
const alert = items[0].json;

// Simulated correlation store (in production, use database)
const recentAlerts = global.alertCorrelationStore || [];

// Clean old alerts (older than 30 minutes)
const thirtyMinutesAgo = Date.now() - 30 * 60 * 1000;
global.alertCorrelationStore = recentAlerts.filter(a => 
  new Date(a.timestamp).getTime() > thirtyMinutesAgo
);

// Look for correlated alerts
const correlatedAlerts = global.alertCorrelationStore.filter(existing => {
  // Same device correlation
  if (existing.device_id === alert.device_id && 
      existing.category === alert.category &&
      existing.alert_id !== alert.alert_id) {
    return true;
  }

  // Time window correlation (same category, similar time)
  if (existing.category === alert.category &&
      existing.correlation_keys.time_window === alert.correlation_keys.time_window &&
      existing.severity === alert.severity &&
      existing.alert_id !== alert.alert_id) {
    return true;
  }

  return false;
});

// Determine if this is a new incident or part of existing one
let incident_id = null;
let correlation_type = 'none';
const correlation_count = correlatedAlerts.length;

if (correlatedAlerts.length > 0) {
  // Find existing incident or create new one
  incident_id = correlatedAlerts[0].incident_id || 
                `incident-${Date.now()}-${Math.random().toString(36).substr(2, 6)}`;

  if (correlatedAlerts.some(a => a.device_id === alert.device_id)) {
    correlation_type = 'device_correlation';
  } else {
    correlation_type = 'pattern_correlation';
  }

  // Update existing alerts with incident ID
  correlatedAlerts.forEach(existing => {
    existing.incident_id = incident_id;
  });
} else if (alert.priority <= 2 || alert.category === 'security') {
  // High priority or security alerts always create incidents
  incident_id = `incident-${Date.now()}-${Math.random().toString(36).substr(2, 6)}`;
  correlation_type = 'new_incident';
}

// Enhanced alert with correlation data
const correlatedAlert = {
  ...alert,
  incident_id: incident_id,
  correlation_type: correlation_type,
  correlation_count: correlation_count,
  correlated_alert_ids: correlatedAlerts.map(a => a.alert_id),

  // Escalation logic
  requires_escalation: alert.priority <= 2 || correlation_count >= 3,
  auto_resolution: alert.category === 'device_health' && correlation_count === 0,

  // Processing metadata
  correlation_processed_at: new Date().toISOString()
};

// Add to correlation store
global.alertCorrelationStore = global.alertCorrelationStore || [];
global.alertCorrelationStore.push(correlatedAlert);

return [{ json: correlatedAlert }];

