// AOS-CX Switch Configuration Management Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['operation'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations
const validOperations = [
  'health_check', 'system_info', 'interface_status', 'vlan_info',
  'create_vlan', 'update_vlan', 'delete_vlan',
  'configure_interface', 'update_interface',
  'backup_config', 'get_running_config'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Extract configuration parameters
const config = {
  switchIP: input.switch_ip || '192.168.1.100',
  username: input.username || 'admin',
  password: input.password || 'admin',
  operation: input.operation,
  timestamp: new Date().toISOString(),

  // VLAN configuration if provided
  vlanConfig: input.vlan_config || {
    vlanId: input.vlan_id || 100,
    name: input.vlan_name || 'default_vlan',
    description: input.description || 'Default VLAN configuration'
  },

  // Interface configuration if provided
  interfaceConfig: input.interface_config || {
    interfaceId: input.interface_id || '1/1/1',
    adminState: input.admin_state || 'up',
    vlanMode: input.vlan_mode || 'access',
    accessVlan: input.access_vlan || 100
  }
};

// AOS-CX REST API base URL (v10.08)
const baseUrl = `https://${config.switchIP}/rest/v10.08`;

const validatedInput = {
  config: config,
  baseUrl: baseUrl,
  operation: config.operation,
  timestamp: config.timestamp,
  requestId: `aos-cx-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed'
};

return [{ json: validatedInput }];

// Prepare AOS-CX API calls based on operation
const requestData = items[0].json;
const authResponse = items[1].json;

const config = requestData.config;
const baseUrl = requestData.baseUrl;
const operation = requestData.operation;
const requestId = requestData.requestId;

// Extract session cookie from authentication
const authHeaders = authResponse.$response?.headers || {};
const sessionCookie = authHeaders['set-cookie']?.[0] || '';
const cookieValue = sessionCookie.split(';')[0] || '';

// Check authentication success
if (authResponse.statusCode !== 200 && authResponse.statusCode !== 201) {
  throw new Error(`Authentication failed: ${authResponse.statusCode} - ${authResponse.message || 'Unknown error'}`);
}

let method = 'GET';
let url = `${baseUrl}/system`;
let body = null;

switch (operation) {
  case 'health_check':
  case 'system_info':
    method = 'GET';
    url = `${baseUrl}/system`;
    break;

  case 'interface_status':
    method = 'GET';
    url = `${baseUrl}/system/interfaces`;
    break;

  case 'vlan_info':
    method = 'GET';
    url = `${baseUrl}/system/vlans`;
    break;

  case 'create_vlan':
  case 'update_vlan':
    method = 'PUT';
    url = `${baseUrl}/system/vlans/${config.vlanConfig.vlanId}`;
    body = {
      'name': config.vlanConfig.name,
      'description': config.vlanConfig.description,
      'admin': 'up',
      'vlan_id': config.vlanConfig.vlanId
    };
    break;

  case 'delete_vlan':
    method = 'DELETE';
    url = `${baseUrl}/system/vlans/${config.vlanConfig.vlanId}`;
    break;

  case 'configure_interface':
  case 'update_interface':
    method = 'PUT';
    url = `${baseUrl}/system/interfaces/${encodeURIComponent(config.interfaceConfig.interfaceId)}`;
    body = {
      'admin': config.interfaceConfig.adminState,
      'vlan_mode': config.interfaceConfig.vlanMode,
      'vlan_tag': config.interfaceConfig.accessVlan
    };
    break;

  case 'backup_config':
  case 'get_running_config':
    method = 'GET';
    url = `${baseUrl}/fullconfigs/running-config`;
    break;

  default:
    method = 'GET';
    url = `${baseUrl}/system`;
}

return [{ json: {
  method: method,
  url: url,
  body: body,
  headers: {
    'Cookie': cookieValue,
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  },
  operation: operation,
  requestId: requestId,
  config: config
}}];

// Process AOS-CX API response
const response = items[0].json;
const statusCode = response.statusCode || 0;
const isSuccess = statusCode >= 200 && statusCode < 300;

// Get original request data
const preparedData = $('Prepare AOS-CX API Calls').first().json;
const operation = preparedData.operation;
const requestId = preparedData.requestId;

let result = {
  requestId: requestId,
  operation: operation,
  timestamp: new Date().toISOString(),
  status: isSuccess ? 'success' : 'error',
  statusCode: statusCode
};

if (isSuccess) {
  const data = response.result || response.body || response;
  result.data = data;

  switch (operation) {
    case 'health_check':
    case 'system_info':
      result.message = 'System information retrieved successfully';
      result.systemInfo = {
        hostname: data.hostname || 'Unknown',
        platform: data.platform_name || 'Unknown',
        software_version: data.software_version || 'Unknown',
        uptime: data.uptime || 0,
        cpu_utilization: data.cpu_utilization || 0,
        memory_utilization: data.memory_utilization || 0,
        temperature: data.temperature || 0
      };

      // Health check warnings
      result.warnings = [];
      if (data.cpu_utilization > 80) {
        result.warnings.push(`High CPU utilization: ${data.cpu_utilization}%`);
      }
      if (data.memory_utilization > 85) {
        result.warnings.push(`High memory utilization: ${data.memory_utilization}%`);
      }
      if (data.temperature > 65) {
        result.warnings.push(`High temperature: ${data.temperature}¬∞C`);
      }
      break;

    case 'interface_status':
      result.message = 'Interface status retrieved successfully';
      result.interfaceCount = Object.keys(data || {}).length;
      break;

    case 'vlan_info':
      result.message = 'VLAN information retrieved successfully';
      result.vlanCount = Object.keys(data || {}).length;
      break;

    case 'create_vlan':
    case 'update_vlan':
      result.message = `VLAN ${preparedData.config.vlanConfig.vlanId} configured successfully`;
      result.vlanId = preparedData.config.vlanConfig.vlanId;
      break;

    case 'delete_vlan':
      result.message = `VLAN ${preparedData.config.vlanConfig.vlanId} deleted successfully`;
      result.vlanId = preparedData.config.vlanConfig.vlanId;
      break;

    case 'configure_interface':
    case 'update_interface':
      result.message = `Interface ${preparedData.config.interfaceConfig.interfaceId} configured successfully`;
      result.interfaceId = preparedData.config.interfaceConfig.interfaceId;
      break;

    case 'backup_config':
    case 'get_running_config':
      result.message = 'Configuration backup completed successfully';
      result.configSize = JSON.stringify(data).length;
      break;

    default:
      result.message = `${operation} completed successfully`;
  }
} else {
  result.message = `AOS-CX API call failed: ${response.error?.message || response.message || 'Unknown error'}`;
  result.error = {
    statusCode: statusCode,
    message: response.error?.message || response.message || 'Unknown error',
    details: response
  };
}

return [{ json: result }];

