// Aruba Central AP Provisioning Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['operation'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations
const validOperations = [
  'provision_ap', 'deprovision_ap', 'update_ap_config', 'move_ap_group',
  'assign_ap_template', 'get_ap_status', 'reboot_ap', 'factory_reset_ap',
  'bulk_provision', 'get_provisioning_status', 'update_ap_location'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Operation-specific validation
if (['provision_ap', 'deprovision_ap', 'update_ap_config', 'reboot_ap', 'factory_reset_ap'].includes(input.operation)) {
  if (!input.device_serial) {
    throw new Error(`${input.operation} requires device_serial`);
  }
}

if (input.operation === 'provision_ap') {
  if (!input.group_name) {
    throw new Error('provision_ap requires group_name');
  }
}

if (input.operation === 'bulk_provision') {
  if (!input.devices || !Array.isArray(input.devices)) {
    throw new Error('bulk_provision requires devices array');
  }
}

// Set defaults and prepare request
const validatedInput = {
  ...input,
  timestamp: new Date().toISOString(),
  request_id: `ap-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed',

  // Set Central API defaults
  central_api_url: input.central_api_url || 'https://apigw-prod2.central.arubanetworks.com',
  group_name: input.group_name || 'default-ap-group',
  template_name: input.template_name || 'standard-ap-template',
  site_name: input.site_name || 'default-site',

  // AP configuration defaults
  ap_config: {
    ...input.ap_config,
    country_code: input.country_code || input.ap_config?.country_code || 'US',
    rf_band: input.rf_band || input.ap_config?.rf_band || 'dual',
    power_mode: input.power_mode || input.ap_config?.power_mode || 'auto',
    led_override: input.led_override || input.ap_config?.led_override || false,
    external_antenna: input.external_antenna || input.ap_config?.external_antenna || false
  },

  // Location information
  location_info: {
    ...input.location_info,
    address: input.address || input.location_info?.address || '',
    latitude: input.latitude || input.location_info?.latitude || null,
    longitude: input.longitude || input.location_info?.longitude || null,
    floor: input.floor || input.location_info?.floor || '',
    building: input.building || input.location_info?.building || ''
  }
};

return [{ json: validatedInput }];

// Provision Single AP
const data = items[0].json;

const provisioningRequest = {
  method: 'POST',
  url: `${data.central_api_url}/api/v2/configuration/devices`,
  body: {
    device_serial: data.device_serial,
    device_type: 'ap',
    group_name: data.group_name,
    template_name: data.template_name,
    site_name: data.site_name,
    hostname: data.hostname || `AP-${data.device_serial.slice(-6)}`,
    location: {
      address: data.location_info.address,
      latitude: data.location_info.latitude,
      longitude: data.location_info.longitude,
      floor: data.location_info.floor,
      building: data.location_info.building
    },
    ap_config: {
      country_code: data.ap_config.country_code,
      rf_band: data.ap_config.rf_band,
      power_mode: data.ap_config.power_mode,
      led_override: data.ap_config.led_override,
      external_antenna: data.ap_config.external_antenna,
      radio_settings: {
        radio_2_4ghz: {
          enabled: data.radio_2_4ghz_enabled !== false,
          channel: data.radio_2_4ghz_channel || 'auto',
          power: data.radio_2_4ghz_power || 'auto'
        },
        radio_5ghz: {
          enabled: data.radio_5ghz_enabled !== false,
          channel: data.radio_5ghz_channel || 'auto',
          power: data.radio_5ghz_power || 'auto'
        }
      }
    },
    tags: data.tags || ['automated', 'n8n-provisioned'],
    notes: data.notes || `Provisioned via n8n automation on ${data.timestamp}`
  },
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-aruba-api-token'}`,
    'Accept': 'application/json'
  },
  operation: data.operation,
  request_id: data.request_id,
  device_serial: data.device_serial
};

return [{ json: provisioningRequest }];

// Update AP Configuration Operations
const data = items[0].json;
const operation = data.operation;

let method = 'PUT';
let url = `${data.central_api_url}/api/v2/configuration/devices/${data.device_serial}`;
let body = null;

switch (operation) {
  case 'update_ap_config':
    method = 'PUT';
    body = {
      ap_config: data.ap_config,
      location: data.location_info,
      template_name: data.template_name,
      hostname: data.hostname,
      notes: `Updated via n8n automation on ${data.timestamp}`
    };
    break;

  case 'move_ap_group':
    method = 'PUT';
    url = `${data.central_api_url}/api/v2/configuration/devices/${data.device_serial}/group`;
    body = {
      group_name: data.new_group_name || data.group_name,
      preserve_config: data.preserve_config !== false
    };
    break;

  case 'assign_ap_template':
    method = 'PUT';
    url = `${data.central_api_url}/api/v2/configuration/devices/${data.device_serial}/template`;
    body = {
      template_name: data.new_template_name || data.template_name,
      variables: data.template_variables || {}
    };
    break;
}

const updateRequest = {
  method: method,
  url: url,
  body: body,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-aruba-api-token'}`,
    'Accept': 'application/json'
  },
  operation: operation,
  request_id: data.request_id,
  device_serial: data.device_serial
};

return [{ json: updateRequest }];

// AP Management Operations (Deprovision, Reboot, Factory Reset)
const data = items[0].json;
const operation = data.operation;

let method = 'POST';
let url = `${data.central_api_url}/api/v2/devices/${data.device_serial}`;
let body = null;

switch (operation) {
  case 'deprovision_ap':
    method = 'DELETE';
    url = `${data.central_api_url}/api/v2/configuration/devices/${data.device_serial}`;
    body = {
      reason: data.deprovision_reason || 'Deprovisioned via automation',
      preserve_license: data.preserve_license !== false
    };
    break;

  case 'reboot_ap':
    method = 'POST';
    url = `${data.central_api_url}/api/v2/devices/${data.device_serial}/action`;
    body = {
      action: 'reboot',
      reason: data.reboot_reason || 'Reboot via automation',
      scheduled_time: data.scheduled_time || null
    };
    break;

  case 'factory_reset_ap':
    method = 'POST';
    url = `${data.central_api_url}/api/v2/devices/${data.device_serial}/action`;
    body = {
      action: 'factory_reset',
      reason: data.reset_reason || 'Factory reset via automation',
      preserve_network_config: data.preserve_network_config || false
    };
    break;
}

const managementRequest = {
  method: method,
  url: url,
  body: body,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-aruba-api-token'}`,
    'Accept': 'application/json'
  },
  operation: operation,
  request_id: data.request_id,
  device_serial: data.device_serial
};

return [{ json: managementRequest }];

// AP Query Operations (Status, Provisioning Status)
const data = items[0].json;
const operation = data.operation;

let method = 'GET';
let url = `${data.central_api_url}/api/v2/devices`;
let queryParams = {};

switch (operation) {
  case 'get_ap_status':
    method = 'GET';
    url = `${data.central_api_url}/api/v2/devices/${data.device_serial}`;
    queryParams = {
      include_details: true,
      include_stats: data.include_stats !== false
    };
    break;

  case 'get_provisioning_status':
    method = 'GET';
    url = `${data.central_api_url}/api/v2/devices/provisioning/status`;
    queryParams = {
      device_serial: data.device_serial || undefined,
      group_name: data.group_name || undefined,
      status_filter: data.status_filter || undefined,
      limit: data.limit || 100
    };
    break;
}

const queryRequest = {
  method: method,
  url: url,
  queryParams: queryParams,
  body: null,
  headers: {
    'Authorization': `Bearer ${data.api_token || 'your-aruba-api-token'}`,
    'Accept': 'application/json'
  },
  operation: operation,
  request_id: data.request_id
};

return [{ json: queryRequest }];

// Bulk AP Operations
const data = items[0].json;
const operation = data.operation;

if (operation === 'bulk_provision') {
  const bulkRequest = {
    method: 'POST',
    url: `${data.central_api_url}/api/v2/configuration/devices/bulk`,
    body: {
      devices: data.devices.map(device => ({
        device_serial: device.device_serial,
        device_type: 'ap',
        group_name: device.group_name || data.group_name,
        template_name: device.template_name || data.template_name,
        site_name: device.site_name || data.site_name,
        hostname: device.hostname || `AP-${device.device_serial.slice(-6)}`,
        location: device.location || data.location_info,
        ap_config: { ...data.ap_config, ...device.ap_config },
        tags: device.tags || data.tags || ['bulk-provisioned', 'n8n-automated']
      })),
      provisioning_options: {
        batch_size: data.batch_size || 10,
        delay_between_batches: data.batch_delay || 5000,
        rollback_on_failure: data.rollback_on_failure !== false,
        notification_settings: {
          on_success: data.notify_on_success !== false,
          on_failure: data.notify_on_failure !== false
        }
      }
    },
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${data.api_token || 'your-aruba-api-token'}`,
      'Accept': 'application/json'
    },
    operation: operation,
    request_id: data.request_id,
    device_count: data.devices.length
  };

  return [{ json: bulkRequest }];
} else if (operation === 'update_ap_location') {
  const locationRequest = {
    method: 'PUT',
    url: `${data.central_api_url}/api/v2/devices/${data.device_serial}/location`,
    body: {
      location: data.location_info,
      update_time: data.timestamp
    },
    headers: {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${data.api_token || 'your-aruba-api-token'}`,
      'Accept': 'application/json'
    },
    operation: operation,
    request_id: data.request_id,
    device_serial: data.device_serial
  };

  return [{ json: locationRequest }];
}

return [{ json: {
  method: 'GET',
  url: data.central_api_url,
  operation: operation,
  request_id: data.request_id
}}];

// Process Aruba Central AP API response
const response = items[0].json;
const statusCode = response.statusCode || 0;
const isSuccess = statusCode >= 200 && statusCode < 300;

// Get original request data
const preparedData = $('Route AP Operation').first().json;
const originalRequest = $('Validate AP Provisioning Request').first().json;

let result = {
  request_id: originalRequest.request_id,
  operation: originalRequest.operation,
  timestamp: new Date().toISOString(),
  status: isSuccess ? 'success' : 'error',
  statusCode: statusCode
};

if (isSuccess) {
  const data = response.result || response.body || response;
  result.data = data;

  switch (originalRequest.operation) {
    case 'provision_ap':
      result.message = `AP '${originalRequest.device_serial}' provisioned successfully`;
      result.device_serial = originalRequest.device_serial;
      result.group_name = originalRequest.group_name;
      result.template_name = originalRequest.template_name;
      result.hostname = data.hostname || originalRequest.hostname;
      break;

    case 'deprovision_ap':
      result.message = `AP '${originalRequest.device_serial}' deprovisioned successfully`;
      result.device_serial = originalRequest.device_serial;
      break;

    case 'update_ap_config':
      result.message = `AP '${originalRequest.device_serial}' configuration updated successfully`;
      result.device_serial = originalRequest.device_serial;
      result.config_changes = originalRequest.ap_config;
      break;

    case 'move_ap_group':
      result.message = `AP '${originalRequest.device_serial}' moved to group '${originalRequest.new_group_name || originalRequest.group_name}' successfully`;
      result.device_serial = originalRequest.device_serial;
      result.new_group = originalRequest.new_group_name || originalRequest.group_name;
      break;

    case 'assign_ap_template':
      result.message = `AP '${originalRequest.device_serial}' assigned template '${originalRequest.new_template_name || originalRequest.template_name}' successfully`;
      result.device_serial = originalRequest.device_serial;
      result.template_name = originalRequest.new_template_name || originalRequest.template_name;
      break;

    case 'get_ap_status':
      result.message = `AP status retrieved successfully`;
      result.device_serial = originalRequest.device_serial;
      result.ap_status = {
        status: data.status || 'unknown',
        model: data.model,
        firmware_version: data.firmware_version,
        ip_address: data.ip_address,
        group_name: data.group_name,
        site_name: data.site_name,
        last_modified: data.last_modified,
        uptime: data.uptime
      };
      break;

    case 'reboot_ap':
      result.message = `AP '${originalRequest.device_serial}' reboot initiated successfully`;
      result.device_serial = originalRequest.device_serial;
      result.action_id = data.action_id;
      break;

    case 'factory_reset_ap':
      result.message = `AP '${originalRequest.device_serial}' factory reset initiated successfully`;
      result.device_serial = originalRequest.device_serial;
      result.action_id = data.action_id;
      break;

    case 'bulk_provision':
      result.message = `Bulk provisioning initiated for ${originalRequest.devices ? originalRequest.devices.length : 'unknown'} APs`;
      result.device_count = originalRequest.devices ? originalRequest.devices.length : 0;
      result.batch_id = data.batch_id;
      result.provisioning_status = data.status;
      break;

    case 'get_provisioning_status':
      result.message = 'Provisioning status retrieved successfully';
      result.provisioning_data = data;
      result.device_count = Array.isArray(data.devices) ? data.devices.length : 0;
      break;

    case 'update_ap_location':
      result.message = `AP '${originalRequest.device_serial}' location updated successfully`;
      result.device_serial = originalRequest.device_serial;
      result.location = originalRequest.location_info;
      break;

    default:
      result.message = `AP ${originalRequest.operation} completed successfully`;
  }
} else {
  result.message = `AP API call failed: ${response.error?.message || response.message || 'Unknown error'}`;
  result.error = {
    statusCode: statusCode,
    message: response.error?.message || response.message || 'Unknown error',
    details: response
  };
}

return [{ json: result }];

