// Aruba Central Device Health Monitoring Initialization
const timestamp = new Date().toISOString();

// Configuration for Aruba Central
const config = {
  clientId: $vars.ARUBA_CLIENT_ID || 'your-client-id',
  clientSecret: $vars.ARUBA_CLIENT_SECRET || 'your-client-secret',
  baseUrl: $vars.ARUBA_BASE_URL || 'https://apigw-prod2.central.arubanetworks.com',
  customerId: $vars.ARUBA_CUSTOMER_ID || 'your-customer-id',
  region: $vars.ARUBA_REGION || 'us-central',
  timestamp: timestamp,

  // Health check thresholds
  thresholds: {
    cpu: 80,
    memory: 85,
    temperature: 60,
    uptime: 86400, // 24 hours in seconds
    signalStrength: -70 // dBm for APs
  },

  // Device filters
  filters: {
    deviceTypes: ['ap', 'switch', 'gateway'],
    status: ['up', 'down'],
    locations: ['all'] // or specific site names
  }
};

// OAuth token endpoint
const authUrl = `${config.baseUrl}/oauth2/token`;

return [{
  json: {
    config: config,
    authUrl: authUrl,
    operation: 'initialize_monitoring',
    timestamp: timestamp,
    requestId: `health-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`
  }
}];

// Prepare device health monitoring API calls
const authResponse = items[1].json;
const configData = items[0].json;

// Check authentication success
if (authResponse.statusCode !== 200 && authResponse.statusCode !== 201) {
  throw new Error(`Authentication failed: ${authResponse.statusCode} - ${authResponse.message || 'Unknown error'}`);
}

const accessToken = authResponse.access_token;
const config = configData.config;
const baseUrl = config.baseUrl;
const requestId = configData.requestId;

// Build comprehensive API request for device health
const healthRequest = {
  url: `${baseUrl}/api/v2/monitoring/devices`,
  method: 'GET',
  headers: {
    'Authorization': `Bearer ${accessToken}`,
    'Accept': 'application/json',
    'Content-Type': 'application/json'
  },
  queryParams: {
    limit: 100,
    offset: 0,
    calculate_total: true,
    show_resource_details: true
  },
  requestType: 'device_health',
  timestamp: config.timestamp,
  requestId: requestId,
  config: config
};

return [{ json: healthRequest }];

// Analyze Aruba Central device health
const response = items[0].json;
const statusCode = response.statusCode || 0;
const isSuccess = statusCode >= 200 && statusCode < 300;

// Get original request data
const preparedData = $('Prepare Health API Requests').first().json;
const config = preparedData.config;
const requestId = preparedData.requestId;
const timestamp = new Date().toISOString();

// Initialize health report
const healthReport = {
  requestId: requestId,
  timestamp: timestamp,
  status: isSuccess ? 'success' : 'error',
  statusCode: statusCode,
  summary: {
    totalDevices: 0,
    healthyDevices: 0,
    unhealthyDevices: 0,
    offlineDevices: 0,
    criticalAlerts: 0,
    warnings: 0,
    overallHealth: 100,
    overallStatus: 'healthy'
  },
  devices: [],
  alerts: [],
  thresholds: config.thresholds
};

if (isSuccess) {
  const data = response.result || response.body || response;

  if (data.devices && Array.isArray(data.devices)) {
    healthReport.summary.totalDevices = data.devices.length;

    data.devices.forEach(device => {
      const deviceHealth = {
        serial: device.serial,
        macAddr: device.macaddr,
        hostname: device.hostname || device.name || device.serial,
        deviceType: device.device_type,
        model: device.model,
        status: device.status,
        uptime: device.uptime || 0,
        lastSeen: device.last_modified,
        location: device.site || 'Unknown',
        firmware: device.firmware_version,
        ip_address: device.ip_address,
        healthScore: 100,
        alerts: []
      };

      // Check device status
      if (device.status === 'up') {
        healthReport.summary.healthyDevices++;
      } else {
        healthReport.summary.offlineDevices++;
        healthReport.summary.unhealthyDevices++;

        // Create offline alert
        const offlineAlert = {
          type: 'error',
          category: 'device_offline',
          device: deviceHealth,
          message: `Device ${deviceHealth.hostname} is offline`,
          severity: 'critical',
          timestamp: timestamp
        };

        healthReport.alerts.push(offlineAlert);
        deviceHealth.alerts.push(offlineAlert);
        healthReport.summary.criticalAlerts++;
        deviceHealth.healthScore = 0;
      }

      // Check performance metrics if available
      if (device.cpu_utilization !== undefined) {
        deviceHealth.performance = {
          cpu: device.cpu_utilization || 0,
          memory: device.memory_utilization || 0,
          temperature: device.temperature || 0
        };

        // CPU threshold check
        if (device.cpu_utilization > config.thresholds.cpu) {
          const cpuAlert = {
            type: 'warning',
            category: 'high_cpu',
            device: deviceHealth,
            message: `High CPU usage: ${device.cpu_utilization}%`,
            threshold: `${config.thresholds.cpu}%`,
            actual: `${device.cpu_utilization}%`,
            timestamp: timestamp
          };

          healthReport.alerts.push(cpuAlert);
          deviceHealth.alerts.push(cpuAlert);
          healthReport.summary.warnings++;
          deviceHealth.healthScore -= 20;
        }

        // Memory threshold check
        if (device.memory_utilization > config.thresholds.memory) {
          const memoryAlert = {
            type: 'warning',
            category: 'high_memory',
            device: deviceHealth,
            message: `High memory usage: ${device.memory_utilization}%`,
            threshold: `${config.thresholds.memory}%`,
            actual: `${device.memory_utilization}%`,
            timestamp: timestamp
          };

          healthReport.alerts.push(memoryAlert);
          deviceHealth.alerts.push(memoryAlert);
          healthReport.summary.warnings++;
          deviceHealth.healthScore -= 20;
        }

        // Temperature threshold check
        if (device.temperature > config.thresholds.temperature) {
          const tempAlert = {
            type: 'error',
            category: 'high_temperature',
            device: deviceHealth,
            message: `High temperature: ${device.temperature}¬∞C`,
            threshold: `${config.thresholds.temperature}¬∞C`,
            actual: `${device.temperature}¬∞C`,
            timestamp: timestamp
          };

          healthReport.alerts.push(tempAlert);
          deviceHealth.alerts.push(tempAlert);
          healthReport.summary.criticalAlerts++;
          deviceHealth.healthScore -= 30;
        }
      }

      // Ensure health score doesn't go below 0
      deviceHealth.healthScore = Math.max(0, deviceHealth.healthScore);

      healthReport.devices.push(deviceHealth);
    });

    // Calculate overall health
    const totalHealthScore = healthReport.devices.reduce((sum, device) => sum + device.healthScore, 0);
    healthReport.summary.overallHealth = healthReport.summary.totalDevices > 0
      ? Math.round(totalHealthScore / healthReport.summary.totalDevices)
      : 100;

    // Determine overall status
    if (healthReport.summary.criticalAlerts > 0) {
      healthReport.summary.overallStatus = 'critical';
    } else if (healthReport.summary.warnings > 0) {
      healthReport.summary.overallStatus = 'warning';
    } else {
      healthReport.summary.overallStatus = 'healthy';
    }

    healthReport.message = `Health check completed successfully. ${healthReport.summary.totalDevices} devices monitored.`;
  } else {
    healthReport.message = 'No device data received from Aruba Central API';
    healthReport.summary.overallStatus = 'unknown';
  }
} else {
  healthReport.message = `API call failed: ${response.error?.message || response.message || 'Unknown error'}`;
  healthReport.error = {
    statusCode: statusCode,
    message: response.error?.message || response.message || 'Unknown error',
    details: response
  };
  healthReport.summary.overallStatus = 'error';
}

return [{ json: healthReport }];

