// Enhanced Input Validation and Security
const input = items[0].json;

// Input validation
const validDeviceTypes = ['all', 'switches', 'aps', 'gateways'];
const deviceFilter = input.device_filter || 'all';

if (!validDeviceTypes.includes(deviceFilter)) {
  throw new Error(`Invalid device_filter: ${deviceFilter}. Must be one of: ${validDeviceTypes.join(', ')}`);
}

// Validate device serial if provided
if (input.device_serial) {
  const serialPattern = /^[A-Za-z0-9_-]{6,50}$/;
  if (!serialPattern.test(input.device_serial)) {
    throw new Error('Invalid device_serial format. Must be 6-50 alphanumeric characters, hyphens, or underscores.');
  }
}

// Validate thresholds
const validateThreshold = (value, name, min, max) => {
  if (value !== undefined && (typeof value !== 'number' || value < min || value > max)) {
    throw new Error(`Invalid ${name}: must be number between ${min} and ${max}`);
  }
};

validateThreshold(input.cpu_critical, 'cpu_critical', 50, 100);
validateThreshold(input.cpu_warning, 'cpu_warning', 30, 95);
validateThreshold(input.memory_critical, 'memory_critical', 50, 100);
validateThreshold(input.memory_warning, 'memory_warning', 30, 95);

const healthRequest = {
  timestamp: new Date().toISOString(),
  request_id: `health-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  request_type: input.device_serial ? 'manual' : 'scheduled',

  // Secure API configuration - use credentials
  device_filter: deviceFilter,
  device_serial: input.device_serial || null,

  // Health check thresholds with defaults
  thresholds: {
    cpu_critical: input.cpu_critical || 90,
    cpu_warning: input.cpu_warning || 80,
    memory_critical: input.memory_critical || 95,
    memory_warning: input.memory_warning || 85,
    temperature_critical: input.temperature_critical || 65,
    temperature_warning: input.temperature_warning || 55,
    uptime_minimum: input.uptime_minimum || 300
  },

  // Feature flags
  include_interfaces: input.include_interfaces !== false,
  include_wireless: input.include_wireless !== false,
  include_power: input.include_power !== false
};

return [{ json: healthRequest }];

// Enhanced Device Health Analysis with Security
const response = items[0].json;
const preparedRequest = $('Validate Input').first().json;
const thresholds = preparedRequest.thresholds;

// Handle API errors
if (response.error || response.status >= 400) {
  throw new Error(`API Error: ${response.error || response.message || 'Unknown error'}`);
}

// Parse response data with fallbacks
const devices = response.result?.devices || response.devices || (Array.isArray(response) ? response : []);

if (devices.length === 0) {
  return [{ 
    json: {
      request_id: preparedRequest.request_id,
      timestamp: new Date().toISOString(),
      total_devices: 0,
      healthy_devices: 0,
      warning_devices: 0,
      critical_devices: 0,
      total_alerts: 0,
      devices: [],
      status: 'no_devices_found'
    }
  }];
}

const healthResults = [];

for (const device of devices) {
  // Sanitize device data
  const deviceSerial = String(device.serial || device.device_serial || 'unknown').substring(0, 50);
  const deviceName = String(device.name || device.hostname || deviceSerial).substring(0, 100);

  const healthStatus = {
    device_serial: deviceSerial,
    device_name: deviceName,
    device_type: device.type || device.device_type || 'unknown',
    timestamp: new Date().toISOString(),
    overall_status: 'healthy',
    alerts: [],
    metrics: {
      cpu_usage: Math.min(Math.max(parseFloat(device.cpu_usage) || 0, 0), 100),
      memory_usage: Math.min(Math.max(parseFloat(device.memory_usage) || 0, 0), 100),
      temperature: Math.min(Math.max(parseFloat(device.temperature) || 25, -50), 150),
      uptime: Math.max(parseInt(device.uptime) || 0, 0),
      disk_usage: Math.min(Math.max(parseFloat(device.disk_usage) || 0, 0), 100)
    },
    connectivity: {
      last_seen: device.last_seen || new Date().toISOString(),
      ip_address: device.ip_address || 'unknown',
      status: device.status || 'unknown'
    }
  };

  // CPU Analysis
  if (healthStatus.metrics.cpu_usage >= thresholds.cpu_critical) {
    healthStatus.alerts.push({
      type: 'cpu_critical',
      severity: 'critical',
      message: `CPU usage critical: ${healthStatus.metrics.cpu_usage.toFixed(1)}%`,
      threshold: thresholds.cpu_critical,
      timestamp: new Date().toISOString()
    });
    healthStatus.overall_status = 'critical';
  } else if (healthStatus.metrics.cpu_usage >= thresholds.cpu_warning) {
    healthStatus.alerts.push({
      type: 'cpu_warning',
      severity: 'warning',
      message: `CPU usage warning: ${healthStatus.metrics.cpu_usage.toFixed(1)}%`,
      threshold: thresholds.cpu_warning,
      timestamp: new Date().toISOString()
    });
    if (healthStatus.overall_status === 'healthy') healthStatus.overall_status = 'warning';
  }

  // Memory Analysis
  if (healthStatus.metrics.memory_usage >= thresholds.memory_critical) {
    healthStatus.alerts.push({
      type: 'memory_critical',
      severity: 'critical',
      message: `Memory usage critical: ${healthStatus.metrics.memory_usage.toFixed(1)}%`,
      threshold: thresholds.memory_critical,
      timestamp: new Date().toISOString()
    });
    healthStatus.overall_status = 'critical';
  } else if (healthStatus.metrics.memory_usage >= thresholds.memory_warning) {
    healthStatus.alerts.push({
      type: 'memory_warning',
      severity: 'warning',
      message: `Memory usage warning: ${healthStatus.metrics.memory_usage.toFixed(1)}%`,
      threshold: thresholds.memory_warning,
      timestamp: new Date().toISOString()
    });
    if (healthStatus.overall_status === 'healthy') healthStatus.overall_status = 'warning';
  }

  // Temperature Analysis
  if (healthStatus.metrics.temperature >= thresholds.temperature_critical) {
    healthStatus.alerts.push({
      type: 'temperature_critical',
      severity: 'critical',
      message: `Temperature critical: ${healthStatus.metrics.temperature.toFixed(1)}¬∞C`,
      threshold: thresholds.temperature_critical,
      timestamp: new Date().toISOString()
    });
    healthStatus.overall_status = 'critical';
  } else if (healthStatus.metrics.temperature >= thresholds.temperature_warning) {
    healthStatus.alerts.push({
      type: 'temperature_warning',
      severity: 'warning',
      message: `Temperature warning: ${healthStatus.metrics.temperature.toFixed(1)}¬∞C`,
      threshold: thresholds.temperature_warning,
      timestamp: new Date().toISOString()
    });
    if (healthStatus.overall_status === 'healthy') healthStatus.overall_status = 'warning';
  }

  // Connectivity Analysis
  if (healthStatus.connectivity.status !== 'online' && healthStatus.connectivity.status !== 'up') {
    healthStatus.alerts.push({
      type: 'connectivity',
      severity: 'critical',
      message: 'Device offline or unreachable',
      last_seen: healthStatus.connectivity.last_seen,
      timestamp: new Date().toISOString()
    });
    healthStatus.overall_status = 'critical';
  }

  healthResults.push(healthStatus);
}

// Generate comprehensive summary
const summary = {
  request_id: preparedRequest.request_id,
  timestamp: new Date().toISOString(),
  total_devices: healthResults.length,
  healthy_devices: healthResults.filter(d => d.overall_status === 'healthy').length,
  warning_devices: healthResults.filter(d => d.overall_status === 'warning').length,
  critical_devices: healthResults.filter(d => d.overall_status === 'critical').length,
  total_alerts: healthResults.reduce((sum, d) => sum + d.alerts.length, 0),
  devices: healthResults,
  status: 'completed'
};

return [{ json: summary }];

