// Enhanced Error Handling
const error = $input.first().error;
const context = $input.first().json || {};

// Sanitize error message
const sanitizedError = {
  error_id: `err-${Date.now()}-${Math.random().toString(36).substr(2, 6)}`,
  timestamp: new Date().toISOString(),
  error_type: error.name || 'UnknownError',
  message: 'An error occurred during workflow execution',
  request_id: context.request_id || 'unknown',
  step: $runIndex || 'unknown',
  status: 'error'
};

// Log detailed error (secure logging)
console.error('Workflow Error:', {
  error_id: sanitizedError.error_id,
  type: error.name,
  message: error.message,
  stack: error.stack?.substring(0, 500),
  context: {
    request_id: context.request_id,
    node: $workflow.name,
    step: $runIndex
  }
});

return [{ json: sanitizedError }];

