// HPE Aruba Central Access Points Monitoring Input Validation
const input = items[0].json;

// Valid operations
const validOperations = [
  'list_aps', 'get_ap_details', 'get_ap_clients', 'get_ap_performance',
  'get_ap_events', 'get_site_aps', 'health_check_all'
];

const operation = input.operation || 'list_aps';

if (!validOperations.includes(operation)) {
  throw new Error(`Invalid operation: ${operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Set defaults and prepare request
const validatedInput = {
  operation: operation,

  // Filtering parameters
  site_id: input.site_id || null,
  site_name: input.site_name || null,
  ap_serial: input.ap_serial || input.serial || null,
  ap_name: input.ap_name || null,
  swarm_id: input.swarm_id || null,

  // Query parameters
  limit: Math.min(parseInt(input.limit) || 100, 1000),
  offset: parseInt(input.offset) || 0,

  // Monitoring thresholds
  thresholds: {
    cpu_usage: parseInt(input.cpu_threshold) || 80,
    memory_usage: parseInt(input.memory_threshold) || 85,
    client_count_max: parseInt(input.client_threshold) || 100,
    uptime_min: parseInt(input.uptime_threshold) || 86400, // 24 hours
    signal_strength_min: parseInt(input.signal_threshold) || -70 // dBm
  },

  // Time range for performance data
  time_range: input.time_range || '1h', // 1h, 6h, 24h, 7d

  timestamp: new Date().toISOString(),
  requestId: `hpe-ap-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed'
};

return [{ json: validatedInput }];

// Prepare HPE Aruba Central Access Points monitoring API calls
const authResponse = items[1].json;
const requestData = items[0].json;

// Check authentication success
if (authResponse.status !== 'success' || !authResponse.access_token) {
  throw new Error(`Authentication failed: ${authResponse.message || 'No access token received'}`);
}

const accessToken = authResponse.access_token;
const baseUrl = authResponse.base_url || 'https://apigw-prod2.central.arubanetworks.com';
const operation = requestData.operation;
const requestId = requestData.requestId;

let apiRequests = [];

// Build API requests based on operation
switch (operation) {
  case 'list_aps':
    const listUrl = `${baseUrl}/monitoring/v1/aps`;
    const queryParams = [];

    if (requestData.site_id) queryParams.push(`site=${requestData.site_id}`);
    if (requestData.swarm_id) queryParams.push(`swarm_id=${requestData.swarm_id}`);
    queryParams.push(`limit=${requestData.limit}`);
    queryParams.push(`offset=${requestData.offset}`);

    apiRequests.push({
      url: queryParams.length > 0 ? `${listUrl}?${queryParams.join('&')}` : listUrl,
      method: 'GET',
      operation: 'list_aps',
      description: 'Get list of access points'
    });
    break;

  case 'get_ap_details':
    if (!requestData.ap_serial) {
      throw new Error('ap_serial is required for get_ap_details operation');
    }
    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/aps/${requestData.ap_serial}`,
      method: 'GET',
      operation: 'get_ap_details',
      description: `Get details for AP ${requestData.ap_serial}`
    });
    break;

  case 'get_ap_clients':
    if (!requestData.ap_serial) {
      throw new Error('ap_serial is required for get_ap_clients operation');
    }
    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/aps/${requestData.ap_serial}/clients`,
      method: 'GET',
      operation: 'get_ap_clients',
      description: `Get clients for AP ${requestData.ap_serial}`
    });
    break;

  case 'get_ap_performance':
    if (!requestData.ap_serial) {
      throw new Error('ap_serial is required for get_ap_performance operation');
    }
    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/aps/${requestData.ap_serial}/performance?duration=${requestData.time_range}`,
      method: 'GET',
      operation: 'get_ap_performance',
      description: `Get performance data for AP ${requestData.ap_serial}`
    });
    break;

  case 'health_check_all':
    // Multiple API calls for comprehensive health check
    apiRequests = [
      {
        url: `${baseUrl}/monitoring/v1/aps?limit=${requestData.limit}`,
        method: 'GET',
        operation: 'list_all_aps',
        description: 'Get all access points for health check'
      },
      {
        url: `${baseUrl}/monitoring/v1/sites`,
        method: 'GET',
        operation: 'list_sites',
        description: 'Get sites information'
      }
    ];
    break;

  default:
    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/aps`,
      method: 'GET',
      operation: 'default_list',
      description: 'Default AP listing'
    });
}

// Prepare common headers
const headers = {
  'Authorization': `Bearer ${accessToken}`,
  'Accept': 'application/json',
  'Content-Type': 'application/json'
};

// Return prepared requests
const result = {
  requests: apiRequests,
  headers: headers,
  baseUrl: baseUrl,
  operation: operation,
  requestId: requestId,
  thresholds: requestData.thresholds,
  timestamp: requestData.timestamp,
  config: requestData
};

return [{ json: result }];

// Process HPE Aruba Central Access Points monitoring data
const apiResults = items;
const configData = $('Prepare Monitoring API Requests').first().json;
const thresholds = configData.thresholds;
const operation = configData.operation;
const requestId = configData.requestId;

let processedData = {
  requestId: requestId,
  operation: operation,
  timestamp: new Date().toISOString(),
  status: 'success',
  summary: {
    total_aps: 0,
    online_aps: 0,
    offline_aps: 0,
    warning_aps: 0,
    critical_aps: 0,
    total_clients: 0
  },
  access_points: [],
  sites: [],
  alerts: [],
  performance_data: []
};

try {
  // Process each API result
  for (const result of apiResults) {
    const statusCode = result.statusCode || 0;
    const isSuccess = statusCode >= 200 && statusCode < 300;

    if (!isSuccess) {
      processedData.alerts.push({
        type: 'error',
        category: 'api_error',
        message: `API call failed with status ${statusCode}`,
        details: result,
        timestamp: new Date().toISOString()
      });
      continue;
    }

    const data = result.result || result.body || result;

    // Process APs data
    if (data.aps && Array.isArray(data.aps)) {
      processedData.summary.total_aps += data.aps.length;

      for (const ap of data.aps) {
        const apData = {
          serial: ap.serial,
          name: ap.name || ap.hostname,
          model: ap.model,
          site: ap.site,
          status: ap.status,
          ip_address: ap.ip_address,
          mac_address: ap.macaddr,
          uptime: ap.uptime || 0,
          client_count: ap.client_count || 0,
          cpu_utilization: ap.cpu_utilization || 0,
          memory_utilization: ap.memory_utilization || 0,
          temperature: ap.temperature || 0,
          firmware_version: ap.firmware_version,
          last_modified: ap.last_modified,
          health_score: 100,
          alerts: []
        };

        // Health analysis
        if (ap.status === 'Up') {
          processedData.summary.online_aps++;
        } else {
          processedData.summary.offline_aps++;

          const offlineAlert = {
            type: 'critical',
            category: 'ap_offline',
            ap_serial: ap.serial,
            ap_name: apData.name,
            site: ap.site,
            message: `Access Point ${apData.name} is offline`,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(offlineAlert);
          apData.alerts.push(offlineAlert);
          processedData.summary.critical_aps++;
          apData.health_score = 0;
        }

        // Performance threshold checks
        if (ap.cpu_utilization > thresholds.cpu_usage) {
          const cpuAlert = {
            type: 'warning',
            category: 'high_cpu',
            ap_serial: ap.serial,
            ap_name: apData.name,
            site: ap.site,
            message: `High CPU usage: ${ap.cpu_utilization}%`,
            threshold: thresholds.cpu_usage,
            actual: ap.cpu_utilization,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(cpuAlert);
          apData.alerts.push(cpuAlert);
          processedData.summary.warning_aps++;
          apData.health_score -= 20;
        }

        if (ap.memory_utilization > thresholds.memory_usage) {
          const memoryAlert = {
            type: 'warning',
            category: 'high_memory',
            ap_serial: ap.serial,
            ap_name: apData.name,
            site: ap.site,
            message: `High memory usage: ${ap.memory_utilization}%`,
            threshold: thresholds.memory_usage,
            actual: ap.memory_utilization,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(memoryAlert);
          apData.alerts.push(memoryAlert);
          processedData.summary.warning_aps++;
          apData.health_score -= 20;
        }

        if (ap.client_count > thresholds.client_count_max) {
          const clientAlert = {
            type: 'warning',
            category: 'high_client_count',
            ap_serial: ap.serial,
            ap_name: apData.name,
            site: ap.site,
            message: `High client count: ${ap.client_count} clients`,
            threshold: thresholds.client_count_max,
            actual: ap.client_count,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(clientAlert);
          apData.alerts.push(clientAlert);
          processedData.summary.warning_aps++;
          apData.health_score -= 15;
        }

        // Ensure health score doesn't go below 0
        apData.health_score = Math.max(0, apData.health_score);

        // Count total clients
        processedData.summary.total_clients += ap.client_count || 0;

        processedData.access_points.push(apData);
      }
    }

    // Process sites data
    if (data.sites && Array.isArray(data.sites)) {
      processedData.sites = data.sites.map(site => ({
        site_id: site.site_id,
        site_name: site.site_name,
        address: site.address,
        country: site.country,
        latitude: site.latitude,
        longitude: site.longitude,
        associated_device_count: site.associated_device_count || 0
      }));
    }

    // Process performance data
    if (data.performance || data.metrics) {
      processedData.performance_data.push({
        type: 'performance_metrics',
        data: data.performance || data.metrics,
        collected_at: new Date().toISOString()
      });
    }
  }

  // Calculate overall health
  const totalHealthScore = processedData.access_points.reduce((sum, ap) => sum + ap.health_score, 0);
  processedData.summary.overall_health = processedData.summary.total_aps > 0
    ? Math.round(totalHealthScore / processedData.summary.total_aps)
    : 100;

  // Set overall status
  if (processedData.summary.critical_aps > 0) {
    processedData.summary.overall_status = 'critical';
  } else if (processedData.summary.warning_aps > 0) {
    processedData.summary.overall_status = 'warning';
  } else {
    processedData.summary.overall_status = 'healthy';
  }

  processedData.message = `Monitoring completed. ${processedData.summary.total_aps} APs processed, ${processedData.alerts.length} alerts generated.`;

} catch (error) {
  processedData.status = 'error';
  processedData.message = `Error processing monitoring data: ${error.message}`;
  processedData.error = {
    message: error.message,
    stack: error.stack,
    timestamp: new Date().toISOString()
  };
}

return [{ json: processedData }];

