// HPE Aruba Central Application Visibility Monitoring Input Validation
const input = items[0].json;

// Valid operations
const validOperations = [
  'get_application_usage', 'get_top_applications', 'get_application_performance', 
  'get_bandwidth_by_application', 'get_user_application_usage', 'analyze_application_trends',
  'get_application_categories', 'comprehensive_app_analysis'
];

const operation = input.operation || 'get_top_applications';

if (!validOperations.includes(operation)) {
  throw new Error(`Invalid operation: ${operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Set defaults and prepare request
const validatedInput = {
  operation: operation,

  // Filtering parameters
  site_id: input.site_id || null,
  site_name: input.site_name || null,
  group_name: input.group_name || null,

  // Application filtering
  application_name: input.application_name || null,
  application_category: input.application_category || null,

  // User/device filtering
  user_id: input.user_id || null,
  username: input.username || null,
  device_mac: input.device_mac || null,
  device_type: input.device_type || null,

  // Time range for analysis
  time_range: input.time_range || '24h', // 1h, 6h, 24h, 7d, 30d
  start_time: input.start_time || null,
  end_time: input.end_time || null,

  // Query parameters
  limit: Math.min(parseInt(input.limit) || 50, 500),
  offset: parseInt(input.offset) || 0,

  // Analysis thresholds
  thresholds: {
    high_usage_threshold: parseInt(input.usage_threshold) || 1000, // MB
    response_time_max: parseInt(input.response_time_threshold) || 500, // milliseconds
    bandwidth_high_threshold: parseInt(input.bandwidth_threshold) || 50, // Mbps
    session_duration_max: parseInt(input.session_duration_threshold) || 3600, // seconds
    concurrent_users_max: parseInt(input.concurrent_users_threshold) || 100
  },

  // Reporting options
  include_user_details: input.include_user_details !== false,
  include_bandwidth_analysis: input.include_bandwidth_analysis !== false,
  include_performance_metrics: input.include_performance_metrics !== false,
  include_trends: input.include_trends !== false,

  // Sorting and grouping
  sort_by: input.sort_by || 'usage', // usage, bandwidth, users, performance
  group_by: input.group_by || 'application', // application, category, site, user

  timestamp: new Date().toISOString(),
  requestId: `hpe-app-vis-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed'
};

return [{ json: validatedInput }];

// Prepare HPE Aruba Central Application Visibility monitoring API calls
const authResponse = items[1].json;
const requestData = items[0].json;

// Check authentication success
if (authResponse.status !== 'success' || !authResponse.access_token) {
  throw new Error(`Authentication failed: ${authResponse.message || 'No access token received'}`);
}

const accessToken = authResponse.access_token;
const baseUrl = authResponse.base_url || 'https://apigw-prod2.central.arubanetworks.com';
const operation = requestData.operation;
const requestId = requestData.requestId;

let apiRequests = [];

// Build API requests based on operation
switch (operation) {
  case 'get_application_usage':
    const usageUrl = `${baseUrl}/monitoring/v1/application_visibility/usage`;
    const usageParams = [];

    if (requestData.site_id) usageParams.push(`site=${requestData.site_id}`);
    if (requestData.application_name) usageParams.push(`application=${requestData.application_name}`);
    if (requestData.application_category) usageParams.push(`category=${requestData.application_category}`);
    if (requestData.time_range) usageParams.push(`duration=${requestData.time_range}`);
    usageParams.push(`limit=${requestData.limit}`);
    usageParams.push(`offset=${requestData.offset}`);

    apiRequests.push({
      url: usageParams.length > 0 ? `${usageUrl}?${usageParams.join('&')}` : usageUrl,
      method: 'GET',
      operation: 'get_application_usage',
      description: 'Get application usage statistics'
    });
    break;

  case 'get_top_applications':
    const topAppsUrl = `${baseUrl}/monitoring/v1/application_visibility/top_applications`;
    const topAppsParams = [];

    if (requestData.site_id) topAppsParams.push(`site=${requestData.site_id}`);
    if (requestData.time_range) topAppsParams.push(`duration=${requestData.time_range}`);
    topAppsParams.push(`limit=${requestData.limit}`);
    topAppsParams.push(`sort_by=${requestData.sort_by}`);

    apiRequests.push({
      url: topAppsParams.length > 0 ? `${topAppsUrl}?${topAppsParams.join('&')}` : topAppsUrl,
      method: 'GET',
      operation: 'get_top_applications',
      description: 'Get top applications by usage'
    });
    break;

  case 'get_application_performance':
    if (!requestData.application_name) {
      throw new Error('application_name is required for get_application_performance operation');
    }

    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/application_visibility/applications/${requestData.application_name}/performance?duration=${requestData.time_range}`,
      method: 'GET',
      operation: 'get_application_performance',
      description: `Get performance metrics for ${requestData.application_name}`
    });
    break;

  case 'get_bandwidth_by_application':
    const bandwidthUrl = `${baseUrl}/monitoring/v1/application_visibility/bandwidth`;
    const bandwidthParams = [];

    if (requestData.site_id) bandwidthParams.push(`site=${requestData.site_id}`);
    if (requestData.time_range) bandwidthParams.push(`duration=${requestData.time_range}`);
    bandwidthParams.push(`group_by=${requestData.group_by}`);
    bandwidthParams.push(`limit=${requestData.limit}`);

    apiRequests.push({
      url: bandwidthParams.length > 0 ? `${bandwidthUrl}?${bandwidthParams.join('&')}` : bandwidthUrl,
      method: 'GET',
      operation: 'get_bandwidth_by_application',
      description: 'Get bandwidth usage by application'
    });
    break;

  case 'get_user_application_usage':
    if (!requestData.user_id && !requestData.username && !requestData.device_mac) {
      throw new Error('user_id, username, or device_mac is required for get_user_application_usage operation');
    }

    const userUsageUrl = `${baseUrl}/monitoring/v1/application_visibility/users`;
    const userUsageParams = [];

    if (requestData.user_id) userUsageParams.push(`user_id=${requestData.user_id}`);
    if (requestData.username) userUsageParams.push(`username=${requestData.username}`);
    if (requestData.device_mac) userUsageParams.push(`device_mac=${requestData.device_mac}`);
    if (requestData.time_range) userUsageParams.push(`duration=${requestData.time_range}`);
    userUsageParams.push(`limit=${requestData.limit}`);

    apiRequests.push({
      url: userUsageParams.length > 0 ? `${userUsageUrl}?${userUsageParams.join('&')}` : userUsageUrl,
      method: 'GET',
      operation: 'get_user_application_usage',
      description: 'Get application usage by user'
    });
    break;

  case 'analyze_application_trends':
    const trendsUrl = `${baseUrl}/monitoring/v1/application_visibility/trends`;
    const trendsParams = [];

    if (requestData.site_id) trendsParams.push(`site=${requestData.site_id}`);
    if (requestData.application_category) trendsParams.push(`category=${requestData.application_category}`);
    if (requestData.time_range) trendsParams.push(`duration=${requestData.time_range}`);
    if (requestData.start_time) trendsParams.push(`start_time=${requestData.start_time}`);
    if (requestData.end_time) trendsParams.push(`end_time=${requestData.end_time}`);

    apiRequests.push({
      url: trendsParams.length > 0 ? `${trendsUrl}?${trendsParams.join('&')}` : trendsUrl,
      method: 'GET',
      operation: 'analyze_application_trends',
      description: 'Analyze application usage trends'
    });
    break;

  case 'get_application_categories':
    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/application_visibility/categories`,
      method: 'GET',
      operation: 'get_application_categories',
      description: 'Get application categories and their usage'
    });
    break;

  case 'comprehensive_app_analysis':
    // Multiple API calls for comprehensive application analysis
    apiRequests = [
      {
        url: `${baseUrl}/monitoring/v1/application_visibility/top_applications?duration=${requestData.time_range}&limit=${requestData.limit}`,
        method: 'GET',
        operation: 'top_applications',
        description: 'Get top applications'
      },
      {
        url: `${baseUrl}/monitoring/v1/application_visibility/bandwidth?duration=${requestData.time_range}&group_by=application`,
        method: 'GET',
        operation: 'bandwidth_by_app',
        description: 'Get bandwidth usage by application'
      },
      {
        url: `${baseUrl}/monitoring/v1/application_visibility/categories`,
        method: 'GET',
        operation: 'app_categories',
        description: 'Get application categories'
      },
      {
        url: `${baseUrl}/monitoring/v1/application_visibility/trends?duration=${requestData.time_range}`,
        method: 'GET',
        operation: 'app_trends',
        description: 'Get application trends'
      }
    ];

    if (requestData.site_id) {
      apiRequests.push({
        url: `${baseUrl}/monitoring/v1/application_visibility/usage?site=${requestData.site_id}&duration=${requestData.time_range}`,
        method: 'GET',
        operation: 'site_app_usage',
        description: `Get application usage for site ${requestData.site_id}`
      });
    }
    break;

  default:
    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/application_visibility/top_applications`,
      method: 'GET',
      operation: 'default_top_apps',
      description: 'Default top applications'
    });
}

// Prepare common headers
const headers = {
  'Authorization': `Bearer ${accessToken}`,
  'Accept': 'application/json',
  'Content-Type': 'application/json'
};

// Return prepared requests
const result = {
  requests: apiRequests,
  headers: headers,
  baseUrl: baseUrl,
  operation: operation,
  requestId: requestId,
  thresholds: requestData.thresholds,
  timestamp: requestData.timestamp,
  config: requestData
};

return [{ json: result }];

// Process HPE Aruba Central Application Visibility monitoring data
const apiResults = items;
const configData = $('Prepare Application Visibility API Requests').first().json;
const thresholds = configData.thresholds;
const operation = configData.operation;
const requestId = configData.requestId;

let processedData = {
  requestId: requestId,
  operation: operation,
  timestamp: new Date().toISOString(),
  status: 'success',
  summary: {
    total_applications: 0,
    top_applications: [],
    total_bandwidth_usage: 0,
    total_users: 0,
    high_usage_applications: 0,
    performance_issues: 0,
    categories_analyzed: 0
  },
  applications: [],
  bandwidth_analysis: [],
  user_analysis: [],
  performance_metrics: [],
  categories: [],
  trends: [],
  alerts: []
};

try {
  // Process each API result
  for (const result of apiResults) {
    const statusCode = result.statusCode || 0;
    const isSuccess = statusCode >= 200 && statusCode < 300;

    if (!isSuccess) {
      processedData.alerts.push({
        type: 'error',
        category: 'api_error',
        message: `Application visibility API call failed with status ${statusCode}`,
        details: result,
        timestamp: new Date().toISOString()
      });
      continue;
    }

    const data = result.result || result.body || result;

    // Process applications data
    if (data.applications && Array.isArray(data.applications)) {
      processedData.summary.total_applications += data.applications.length;

      for (const app of data.applications) {
        const appData = {
          application_name: app.name || app.application_name,
          category: app.category || 'unknown',

          // Usage statistics
          data_usage: app.data_usage || 0,
          session_count: app.session_count || 0,
          user_count: app.user_count || 0,
          bandwidth_usage: app.bandwidth_usage || 0,

          // Performance metrics
          response_time: app.response_time || 0,
          session_duration: app.session_duration || 0,
          concurrent_users: app.concurrent_users || 0,

          // Quality metrics
          success_rate: app.success_rate || 100,
          error_rate: app.error_rate || 0,

          // Risk assessment
          risk_score: 0,
          alerts: []
        };

        // Check for high usage
        if (appData.data_usage > thresholds.high_usage_threshold) {
          const usageAlert = {
            type: 'warning',
            category: 'high_data_usage',
            application: appData.application_name,
            message: `High data usage detected: ${appData.data_usage}MB`,
            threshold: thresholds.high_usage_threshold,
            actual: appData.data_usage,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(usageAlert);
          appData.alerts.push(usageAlert);
          processedData.summary.high_usage_applications++;
          appData.risk_score += 30;
        }

        // Check response time
        if (appData.response_time > thresholds.response_time_max) {
          const responseAlert = {
            type: 'warning',
            category: 'high_response_time',
            application: appData.application_name,
            message: `High response time: ${appData.response_time}ms`,
            threshold: thresholds.response_time_max,
            actual: appData.response_time,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(responseAlert);
          appData.alerts.push(responseAlert);
          processedData.summary.performance_issues++;
          appData.risk_score += 25;
        }

        // Check bandwidth usage
        if (appData.bandwidth_usage > thresholds.bandwidth_high_threshold) {
          const bandwidthAlert = {
            type: 'warning',
            category: 'high_bandwidth_usage',
            application: appData.application_name,
            message: `High bandwidth usage: ${appData.bandwidth_usage}Mbps`,
            threshold: thresholds.bandwidth_high_threshold,
            actual: appData.bandwidth_usage,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(bandwidthAlert);
          appData.alerts.push(bandwidthAlert);
          appData.risk_score += 20;
        }

        // Check concurrent users
        if (appData.concurrent_users > thresholds.concurrent_users_max) {
          const usersAlert = {
            type: 'info',
            category: 'high_concurrent_users',
            application: appData.application_name,
            message: `High concurrent users: ${appData.concurrent_users}`,
            threshold: thresholds.concurrent_users_max,
            actual: appData.concurrent_users,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(usersAlert);
          appData.alerts.push(usersAlert);
          appData.risk_score += 15;
        }

        // Check error rate
        if (appData.error_rate > 5) {
          const errorAlert = {
            type: 'critical',
            category: 'high_error_rate',
            application: appData.application_name,
            message: `High error rate detected: ${appData.error_rate}%`,
            actual: appData.error_rate,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(errorAlert);
          appData.alerts.push(errorAlert);
          appData.risk_score += 40;
        }

        // Calculate risk level
        if (appData.risk_score >= 60) {
          appData.risk_level = 'high';
        } else if (appData.risk_score >= 30) {
          appData.risk_level = 'medium';
        } else {
          appData.risk_level = 'low';
        }

        processedData.applications.push(appData);
        processedData.summary.total_users += appData.user_count;
        processedData.summary.total_bandwidth_usage += appData.bandwidth_usage;
      }

      // Sort applications by usage and get top 10
      const sortedApps = processedData.applications
        .sort((a, b) => b.data_usage - a.data_usage)
        .slice(0, 10);

      processedData.summary.top_applications = sortedApps.map(app => ({
        name: app.application_name,
        category: app.category,
        usage: app.data_usage,
        users: app.user_count,
        risk_level: app.risk_level
      }));
    }

    // Process bandwidth data
    if (data.bandwidth && Array.isArray(data.bandwidth)) {
      processedData.bandwidth_analysis = data.bandwidth.map(bw => ({
        application: bw.application,
        category: bw.category,
        bandwidth_used: bw.bandwidth_used,
        percentage_of_total: bw.percentage_of_total,
        peak_usage: bw.peak_usage,
        average_usage: bw.average_usage
      }));
    }

    // Process user analysis data
    if (data.users && Array.isArray(data.users)) {
      processedData.user_analysis = data.users.map(user => ({
        user_id: user.user_id,
        username: user.username,
        device_mac: user.device_mac,
        applications_used: user.applications_used || [],
        total_usage: user.total_usage || 0,
        session_duration: user.session_duration || 0,
        last_activity: user.last_activity
      }));
    }

    // Process categories data
    if (data.categories && Array.isArray(data.categories)) {
      processedData.summary.categories_analyzed = data.categories.length;
      processedData.categories = data.categories.map(cat => ({
        category_name: cat.name,
        application_count: cat.application_count || 0,
        total_usage: cat.total_usage || 0,
        bandwidth_usage: cat.bandwidth_usage || 0,
        user_count: cat.user_count || 0,
        risk_level: cat.total_usage > (thresholds.high_usage_threshold * 5) ? 'high' : 'low'
      }));
    }

    // Process trends data
    if (data.trends || data.historical) {
      processedData.trends.push({
        type: 'application_trends',
        data: data.trends || data.historical,
        collected_at: new Date().toISOString()
      });
    }

    // Process performance metrics
    if (data.performance || data.metrics) {
      processedData.performance_metrics.push({
        type: 'performance_analysis',
        data: data.performance || data.metrics,
        collected_at: new Date().toISOString()
      });
    }
  }

  // Calculate summary statistics
  if (processedData.applications.length > 0) {
    const avgResponseTime = processedData.applications
      .reduce((sum, app) => sum + app.response_time, 0) / processedData.applications.length;

    processedData.summary.average_response_time = Math.round(avgResponseTime);
    processedData.summary.applications_at_risk = processedData.applications
      .filter(app => app.risk_level === 'high' || app.risk_level === 'medium').length;
  }

  // Set overall status based on alerts and performance
  const criticalAlerts = processedData.alerts.filter(a => a.type === 'critical').length;
  const warningAlerts = processedData.alerts.filter(a => a.type === 'warning').length;

  if (criticalAlerts > 0 || processedData.summary.performance_issues > 5) {
    processedData.summary.overall_status = 'critical';
  } else if (warningAlerts > 0 || processedData.summary.high_usage_applications > 3) {
    processedData.summary.overall_status = 'warning';
  } else {
    processedData.summary.overall_status = 'healthy';
  }

  processedData.message = `Application visibility analysis completed. ${processedData.summary.total_applications} applications analyzed, ${processedData.alerts.length} alerts generated.`;

} catch (error) {
  processedData.status = 'error';
  processedData.message = `Error processing application visibility data: ${error.message}`;
  processedData.error = {
    message: error.message,
    stack: error.stack,
    timestamp: new Date().toISOString()
  };
}

return [{ json: processedData }];

