// HPE Aruba Central Authentication Input Validation
const input = items[0].json;

// Check if we need to refresh token or get new one
const operation = input.operation || 'get_token';
const validOperations = ['get_token', 'refresh_token', 'validate_token'];

if (!validOperations.includes(operation)) {
  throw new Error(`Invalid operation: ${operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Set defaults and prepare request
const validatedInput = {
  operation: operation,
  client_id: input.client_id || $vars.HPE_ARUBA_CLIENT_ID || 'your-client-id',
  client_secret: input.client_secret || $vars.HPE_ARUBA_CLIENT_SECRET || 'your-client-secret',
  base_url: input.base_url || $vars.HPE_ARUBA_BASE_URL || 'https://apigw-prod2.central.arubanetworks.com',
  customer_id: input.customer_id || $vars.HPE_ARUBA_CUSTOMER_ID || 'your-customer-id',

  // Token validation
  existing_token: input.access_token || null,
  token_expires_at: input.expires_at || null,

  timestamp: new Date().toISOString(),
  requestId: `hpe-auth-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed'
};

return [{ json: validatedInput }];

// Check if existing token is still valid
const input = items[0].json;
const currentTime = Math.floor(Date.now() / 1000);

let needNewToken = true;
let tokenStatus = 'expired';

// Check if we have a valid existing token
if (input.existing_token && input.token_expires_at) {
  const expiresAt = parseInt(input.token_expires_at);
  const timeBuffer = 300; // 5 minutes buffer

  if (expiresAt > (currentTime + timeBuffer)) {
    needNewToken = false;
    tokenStatus = 'valid';
  } else if (expiresAt > currentTime) {
    tokenStatus = 'expiring_soon';
  }
}

const result = {
  ...input,
  need_new_token: needNewToken,
  token_status: tokenStatus,
  current_timestamp: currentTime,
  check_performed_at: new Date().toISOString()
};

return [{ json: result }];

// Return existing valid token
const input = items[0].json;

const result = {
  requestId: input.requestId,
  operation: 'existing_token_valid',
  access_token: input.existing_token,
  token_type: 'Bearer',
  expires_at: input.token_expires_at,
  customer_id: input.customer_id,
  base_url: input.base_url,
  status: 'success',
  message: 'Using existing valid token',
  token_source: 'cached',
  timestamp: new Date().toISOString()
};

return [{ json: result }];

// Process OAuth token response
const response = items[0].json;
const originalRequest = $('Validate Authentication Request').first().json;
const statusCode = response.statusCode || 0;
const isSuccess = statusCode >= 200 && statusCode < 300;

let result = {
  requestId: originalRequest.requestId,
  operation: originalRequest.operation,
  customer_id: originalRequest.customer_id,
  base_url: originalRequest.base_url,
  timestamp: new Date().toISOString(),
  status: isSuccess ? 'success' : 'error',
  statusCode: statusCode
};

if (isSuccess && response.access_token) {
  const currentTime = Math.floor(Date.now() / 1000);
  const expiresIn = response.expires_in || 7200; // Default 2 hours
  const expiresAt = currentTime + expiresIn;

  result = {
    ...result,
    access_token: response.access_token,
    token_type: response.token_type || 'Bearer',
    expires_in: expiresIn,
    expires_at: expiresAt,
    issued_at: currentTime,
    message: 'Authentication successful',
    token_source: 'new',

    // Additional metadata
    scope: response.scope || 'full_access',
    auth_headers: {
      'Authorization': `Bearer ${response.access_token}`,
      'Content-Type': 'application/json',
      'Accept': 'application/json'
    }
  };
} else {
  result = {
    ...result,
    message: `Authentication failed: ${response.error_description || response.error || response.message || 'Unknown error'}`,
    error: {
      error: response.error || 'authentication_failed',
      error_description: response.error_description || 'Failed to obtain access token',
      statusCode: statusCode,
      details: response
    }
  };
}

return [{ json: result }];

// Final token validation and response preparation
const authResult = items[0].json;

// Ensure we have a valid token result
if (!authResult.access_token) {
  throw new Error('No access token available after authentication process');
}

// Prepare final response
const finalResult = {
  ...authResult,

  // Add convenience fields for other workflows
  auth_header: `Bearer ${authResult.access_token}`,
  is_valid: true,
  ready_for_api_calls: true,

  // API endpoints for reference
  api_endpoints: {
    base_url: authResult.base_url,
    monitoring: `${authResult.base_url}/monitoring/v1`,
    reporting: `${authResult.base_url}/reports/v1`,
    configuration: `${authResult.base_url}/configuration/v1`
  },

  // Usage instructions
  usage_example: {
    headers: {
      'Authorization': `Bearer ${authResult.access_token}`,
      'Content-Type': 'application/json',
      'Accept': 'application/json'
    },
    sample_api_call: `${authResult.base_url}/monitoring/v1/aps`
  },

  validation_completed_at: new Date().toISOString()
};

return [{ json: finalResult }];

