// HPE Aruba Central Device Location Monitoring Input Validation
const input = items[0].json;

// Valid operations
const validOperations = [
  'get_device_locations', 'track_device_movement', 'get_location_analytics',
  'get_floor_plan_data', 'get_site_locations', 'get_zone_analytics'
];

const operation = input.operation || 'get_device_locations';

if (!validOperations.includes(operation)) {
  throw new Error(`Invalid operation: ${operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Set defaults and prepare request
const validatedInput = {
  operation: operation,

  // Location filtering parameters
  site_id: input.site_id || null,
  site_name: input.site_name || null,
  building: input.building || null,
  floor: input.floor || null,
  zone: input.zone || null,

  // Device filtering
  device_type: input.device_type || null, // 'ap', 'gateway', 'switch', 'client'
  device_serial: input.device_serial || null,
  mac_address: input.mac_address || null,

  // Time range for movement tracking
  time_range: input.time_range || '1h', // 1h, 6h, 24h, 7d
  start_time: input.start_time || null,
  end_time: input.end_time || null,

  // Query parameters
  limit: Math.min(parseInt(input.limit) || 100, 1000),
  offset: parseInt(input.offset) || 0,

  // Location accuracy thresholds
  thresholds: {
    location_accuracy_min: parseFloat(input.accuracy_threshold) || 1.0, // meters
    movement_threshold: parseFloat(input.movement_threshold) || 5.0, // meters
    inactive_threshold: parseInt(input.inactive_threshold) || 3600 // seconds
  },

  // Coordinate system
  coordinate_system: input.coordinate_system || 'cartesian', // cartesian, gps
  include_history: input.include_history !== false,

  timestamp: new Date().toISOString(),
  requestId: `hpe-location-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed'
};

return [{ json: validatedInput }];

// Prepare HPE Aruba Central Device Location monitoring API calls
const authResponse = items[1].json;
const requestData = items[0].json;

// Check authentication success
if (authResponse.status !== 'success' || !authResponse.access_token) {
  throw new Error(`Authentication failed: ${authResponse.message || 'No access token received'}`);
}

const accessToken = authResponse.access_token;
const baseUrl = authResponse.base_url || 'https://apigw-prod2.central.arubanetworks.com';
const operation = requestData.operation;
const requestId = requestData.requestId;

let apiRequests = [];

// Build API requests based on operation
switch (operation) {
  case 'get_device_locations':
    const locationUrl = `${baseUrl}/location/v1/devices`;
    const queryParams = [];

    if (requestData.site_id) queryParams.push(`site=${requestData.site_id}`);
    if (requestData.device_type) queryParams.push(`device_type=${requestData.device_type}`);
    if (requestData.building) queryParams.push(`building=${requestData.building}`);
    if (requestData.floor) queryParams.push(`floor=${requestData.floor}`);
    queryParams.push(`limit=${requestData.limit}`);
    queryParams.push(`offset=${requestData.offset}`);

    apiRequests.push({
      url: queryParams.length > 0 ? `${locationUrl}?${queryParams.join('&')}` : locationUrl,
      method: 'GET',
      operation: 'get_device_locations',
      description: 'Get device location data'
    });
    break;

  case 'track_device_movement':
    if (!requestData.device_serial && !requestData.mac_address) {
      throw new Error('device_serial or mac_address is required for track_device_movement operation');
    }

    const trackingUrl = requestData.device_serial 
      ? `${baseUrl}/location/v1/devices/${requestData.device_serial}/history`
      : `${baseUrl}/location/v1/devices/mac/${requestData.mac_address}/history`;

    const trackingParams = [];
    if (requestData.start_time) trackingParams.push(`start_time=${requestData.start_time}`);
    if (requestData.end_time) trackingParams.push(`end_time=${requestData.end_time}`);
    if (requestData.time_range) trackingParams.push(`duration=${requestData.time_range}`);

    apiRequests.push({
      url: trackingParams.length > 0 ? `${trackingUrl}?${trackingParams.join('&')}` : trackingUrl,
      method: 'GET',
      operation: 'track_device_movement',
      description: `Track movement for device ${requestData.device_serial || requestData.mac_address}`
    });
    break;

  case 'get_floor_plan_data':
    if (!requestData.site_id) {
      throw new Error('site_id is required for get_floor_plan_data operation');
    }

    apiRequests.push({
      url: `${baseUrl}/location/v1/sites/${requestData.site_id}/floorplans`,
      method: 'GET',
      operation: 'get_floor_plan_data',
      description: `Get floor plan data for site ${requestData.site_id}`
    });
    break;

  case 'get_location_analytics':
    const analyticsUrl = `${baseUrl}/location/v1/analytics`;
    const analyticsParams = [];

    if (requestData.site_id) analyticsParams.push(`site=${requestData.site_id}`);
    if (requestData.zone) analyticsParams.push(`zone=${requestData.zone}`);
    if (requestData.time_range) analyticsParams.push(`duration=${requestData.time_range}`);

    apiRequests.push({
      url: analyticsParams.length > 0 ? `${analyticsUrl}?${analyticsParams.join('&')}` : analyticsUrl,
      method: 'GET',
      operation: 'get_location_analytics',
      description: 'Get location analytics data'
    });
    break;

  case 'get_site_locations':
    // Get comprehensive site location information
    apiRequests = [
      {
        url: `${baseUrl}/central/v2/sites`,
        method: 'GET',
        operation: 'list_sites',
        description: 'Get all sites information'
      },
      {
        url: `${baseUrl}/location/v1/devices?limit=${requestData.limit}`,
        method: 'GET',
        operation: 'all_device_locations',
        description: 'Get all device locations'
      }
    ];

    if (requestData.site_id) {
      apiRequests.push({
        url: `${baseUrl}/location/v1/sites/${requestData.site_id}/zones`,
        method: 'GET',
        operation: 'site_zones',
        description: `Get zones for site ${requestData.site_id}`
      });
    }
    break;

  case 'get_zone_analytics':
    if (!requestData.site_id || !requestData.zone) {
      throw new Error('site_id and zone are required for get_zone_analytics operation');
    }

    apiRequests.push({
      url: `${baseUrl}/location/v1/sites/${requestData.site_id}/zones/${requestData.zone}/analytics?duration=${requestData.time_range}`,
      method: 'GET',
      operation: 'get_zone_analytics',
      description: `Get analytics for zone ${requestData.zone} in site ${requestData.site_id}`
    });
    break;

  default:
    apiRequests.push({
      url: `${baseUrl}/location/v1/devices`,
      method: 'GET',
      operation: 'default_locations',
      description: 'Default device locations'
    });
}

// Prepare common headers
const headers = {
  'Authorization': `Bearer ${accessToken}`,
  'Accept': 'application/json',
  'Content-Type': 'application/json'
};

// Return prepared requests
const result = {
  requests: apiRequests,
  headers: headers,
  baseUrl: baseUrl,
  operation: operation,
  requestId: requestId,
  thresholds: requestData.thresholds,
  timestamp: requestData.timestamp,
  config: requestData
};

return [{ json: result }];

// Process HPE Aruba Central Device Location monitoring data
const apiResults = items;
const configData = $('Prepare Location API Requests').first().json;
const thresholds = configData.thresholds;
const operation = configData.operation;
const requestId = configData.requestId;

let processedData = {
  requestId: requestId,
  operation: operation,
  timestamp: new Date().toISOString(),
  status: 'success',
  summary: {
    total_devices: 0,
    located_devices: 0,
    unlocated_devices: 0,
    sites_count: 0,
    zones_count: 0,
    accuracy_issues: 0
  },
  device_locations: [],
  sites: [],
  zones: [],
  movement_data: [],
  analytics: [],
  alerts: []
};

try {
  // Process each API result
  for (const result of apiResults) {
    const statusCode = result.statusCode || 0;
    const isSuccess = statusCode >= 200 && statusCode < 300;

    if (!isSuccess) {
      processedData.alerts.push({
        type: 'error',
        category: 'api_error',
        message: `Location API call failed with status ${statusCode}`,
        details: result,
        timestamp: new Date().toISOString()
      });
      continue;
    }

    const data = result.result || result.body || result;

    // Process device location data
    if (data.devices && Array.isArray(data.devices)) {
      processedData.summary.total_devices += data.devices.length;

      for (const device of data.devices) {
        const locationData = {
          device_id: device.serial || device.mac_address,
          device_name: device.name || device.hostname,
          device_type: device.device_type,
          mac_address: device.mac_address,

          // Location information
          site: device.site,
          building: device.building,
          floor: device.floor,
          zone: device.zone,

          // Coordinates
          coordinates: {
            x: device.x || null,
            y: device.y || null,
            latitude: device.latitude || null,
            longitude: device.longitude || null
          },

          // Location metadata
          accuracy: device.accuracy || null,
          confidence: device.confidence || null,
          last_located: device.last_located || device.last_seen,
          location_method: device.location_method || 'triangulation',

          // Status
          is_located: !!(device.x && device.y) || !!(device.latitude && device.longitude),
          location_age: device.location_age || null,

          alerts: []
        };

        // Check location status
        if (locationData.is_located) {
          processedData.summary.located_devices++;

          // Check location accuracy
          if (locationData.accuracy && locationData.accuracy > thresholds.location_accuracy_min) {
            const accuracyAlert = {
              type: 'warning',
              category: 'low_accuracy',
              device_id: locationData.device_id,
              device_name: locationData.device_name,
              message: `Low location accuracy: ${locationData.accuracy}m`,
              threshold: thresholds.location_accuracy_min,
              actual: locationData.accuracy,
              timestamp: new Date().toISOString()
            };

            processedData.alerts.push(accuracyAlert);
            locationData.alerts.push(accuracyAlert);
            processedData.summary.accuracy_issues++;
          }
        } else {
          processedData.summary.unlocated_devices++;

          const unlocatedAlert = {
            type: 'warning',
            category: 'device_unlocated',
            device_id: locationData.device_id,
            device_name: locationData.device_name,
            message: `Device location not available`,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(unlocatedAlert);
          locationData.alerts.push(unlocatedAlert);
        }

        // Check for stale location data
        if (locationData.last_located) {
          const lastLocatedTime = new Date(locationData.last_located).getTime();
          const currentTime = Date.now();
          const ageSeconds = (currentTime - lastLocatedTime) / 1000;

          if (ageSeconds > thresholds.inactive_threshold) {
            const staleAlert = {
              type: 'warning',
              category: 'stale_location',
              device_id: locationData.device_id,
              device_name: locationData.device_name,
              message: `Location data is stale (${Math.round(ageSeconds / 60)} minutes old)`,
              threshold: `${thresholds.inactive_threshold / 60} minutes`,
              actual: `${Math.round(ageSeconds / 60)} minutes`,
              timestamp: new Date().toISOString()
            };

            processedData.alerts.push(staleAlert);
            locationData.alerts.push(staleAlert);
          }
        }

        processedData.device_locations.push(locationData);
      }
    }

    // Process sites data
    if (data.sites && Array.isArray(data.sites)) {
      processedData.summary.sites_count = data.sites.length;
      processedData.sites = data.sites.map(site => ({
        site_id: site.site_id,
        site_name: site.site_name,
        address: site.address,
        country: site.country,
        latitude: site.latitude,
        longitude: site.longitude,
        timezone: site.timezone,
        associated_device_count: site.associated_device_count || 0
      }));
    }

    // Process zones data
    if (data.zones && Array.isArray(data.zones)) {
      processedData.summary.zones_count += data.zones.length;
      processedData.zones = processedData.zones.concat(data.zones.map(zone => ({
        zone_id: zone.zone_id,
        zone_name: zone.zone_name,
        site_id: zone.site_id,
        floor: zone.floor,
        zone_type: zone.zone_type,
        boundaries: zone.boundaries || [],
        device_count: zone.device_count || 0
      })));
    }

    // Process movement/history data
    if (data.history && Array.isArray(data.history)) {
      processedData.movement_data = data.history.map(point => ({
        timestamp: point.timestamp,
        coordinates: {
          x: point.x,
          y: point.y,
          latitude: point.latitude,
          longitude: point.longitude
        },
        accuracy: point.accuracy,
        building: point.building,
        floor: point.floor,
        zone: point.zone
      }));

      // Analyze movement patterns
      if (data.history.length > 1) {
        let totalDistance = 0;
        for (let i = 1; i < data.history.length; i++) {
          const prev = data.history[i - 1];
          const curr = data.history[i];

          if (prev.x && prev.y && curr.x && curr.y) {
            const distance = Math.sqrt(
              Math.pow(curr.x - prev.x, 2) + Math.pow(curr.y - prev.y, 2)
            );
            totalDistance += distance;

            // Check for significant movement
            if (distance > thresholds.movement_threshold) {
              processedData.alerts.push({
                type: 'info',
                category: 'significant_movement',
                message: `Device moved ${distance.toFixed(2)}m between ${prev.timestamp} and ${curr.timestamp}`,
                distance: distance,
                timestamp: new Date().toISOString()
              });
            }
          }
        }

        processedData.movement_data.summary = {
          total_distance: totalDistance,
          total_points: data.history.length,
          time_span: {
            start: data.history[0].timestamp,
            end: data.history[data.history.length - 1].timestamp
          }
        };
      }
    }

    // Process analytics data
    if (data.analytics || data.occupancy || data.dwell_time) {
      processedData.analytics.push({
        type: 'location_analytics',
        data: data.analytics || { occupancy: data.occupancy, dwell_time: data.dwell_time },
        collected_at: new Date().toISOString()
      });
    }
  }

  // Calculate location coverage percentage
  processedData.summary.location_coverage = processedData.summary.total_devices > 0
    ? Math.round((processedData.summary.located_devices / processedData.summary.total_devices) * 100)
    : 0;

  // Set overall status based on coverage and alerts
  if (processedData.summary.location_coverage < 70 || processedData.summary.accuracy_issues > (processedData.summary.total_devices * 0.3)) {
    processedData.summary.overall_status = 'critical';
  } else if (processedData.summary.location_coverage < 90 || processedData.summary.accuracy_issues > 0) {
    processedData.summary.overall_status = 'warning';
  } else {
    processedData.summary.overall_status = 'healthy';
  }

  processedData.message = `Location monitoring completed. ${processedData.summary.total_devices} devices processed, ${processedData.summary.location_coverage}% location coverage.`;

} catch (error) {
  processedData.status = 'error';
  processedData.message = `Error processing location data: ${error.message}`;
  processedData.error = {
    message: error.message,
    stack: error.stack,
    timestamp: new Date().toISOString()
  };
}

return [{ json: processedData }];

