// HPE Aruba Central Reporting Management Input Validation
const input = items[0].json;

// Valid operations
const validOperations = [
  'health_check', 'list_reports', 'create_report', 'update_report', 
  'delete_report', 'get_report_details', 'list_report_runs', 
  'delete_report_run', 'get_report_download_link', 'execute_report'
];

const operation = input.operation || 'health_check';

if (!validOperations.includes(operation)) {
  throw new Error(`Invalid operation: ${operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Set defaults and prepare request
const validatedInput = {
  operation: operation,

  // Report management parameters
  report_id: input.report_id || null,
  report_name: input.report_name || null,
  report_type: input.report_type || 'standard', // standard, custom, scheduled

  // Report run parameters
  run_id: input.run_id || null,
  execution_id: input.execution_id || null,

  // Report creation/update parameters
  report_config: input.report_config || {
    name: input.report_name || `Auto Report ${Date.now()}`,
    description: input.description || 'Automated report generated via n8n',
    category: input.category || 'network_monitoring',
    format: input.format || 'pdf', // pdf, csv, xlsx
    schedule: input.schedule || null,
    recipients: input.recipients || [],
    parameters: input.parameters || {}
  },

  // Query parameters
  limit: Math.min(parseInt(input.limit) || 50, 1000),
  offset: parseInt(input.offset) || 0,

  // Filtering
  status: input.status || null, // pending, running, completed, failed
  created_after: input.created_after || null,
  created_before: input.created_before || null,

  // Download parameters
  download_format: input.download_format || 'pdf',
  include_metadata: input.include_metadata !== false,

  timestamp: new Date().toISOString(),
  requestId: `hpe-report-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed'
};

// Operation-specific validation
if (['update_report', 'delete_report', 'get_report_details'].includes(operation)) {
  if (!validatedInput.report_id) {
    throw new Error(`${operation} operation requires report_id parameter`);
  }
}

if (['delete_report_run', 'get_report_download_link'].includes(operation)) {
  if (!validatedInput.run_id && !validatedInput.execution_id) {
    throw new Error(`${operation} operation requires run_id or execution_id parameter`);
  }
}

if (operation === 'create_report') {
  if (!validatedInput.report_config.name) {
    throw new Error('create_report operation requires report name');
  }
}

return [{ json: validatedInput }];

// Prepare HPE Aruba Central Reporting API calls
const authResponse = items[1].json;
const requestData = items[0].json;

// Check authentication success
if (authResponse.status !== 'success' || !authResponse.access_token) {
  throw new Error(`Authentication failed: ${authResponse.message || 'No access token received'}`);
}

const accessToken = authResponse.access_token;
const baseUrl = authResponse.base_url || 'https://apigw-prod2.central.arubanetworks.com';
const operation = requestData.operation;
const requestId = requestData.requestId;

let apiRequest = null;

// Build API request based on operation
switch (operation) {
  case 'health_check':
    apiRequest = {
      url: `${baseUrl}/reports/v1/health`,
      method: 'GET',
      operation: 'health_check',
      description: 'Check reporting service health'
    };
    break;

  case 'list_reports':
    const listUrl = `${baseUrl}/reports/v1/reports`;
    const queryParams = [];

    if (requestData.status) queryParams.push(`status=${requestData.status}`);
    if (requestData.created_after) queryParams.push(`created_after=${requestData.created_after}`);
    if (requestData.created_before) queryParams.push(`created_before=${requestData.created_before}`);
    queryParams.push(`limit=${requestData.limit}`);
    queryParams.push(`offset=${requestData.offset}`);

    apiRequest = {
      url: queryParams.length > 0 ? `${listUrl}?${queryParams.join('&')}` : listUrl,
      method: 'GET',
      operation: 'list_reports',
      description: 'Get list of reports'
    };
    break;

  case 'get_report_details':
    apiRequest = {
      url: `${baseUrl}/reports/v1/reports/${requestData.report_id}`,
      method: 'GET',
      operation: 'get_report_details',
      description: `Get details for report ${requestData.report_id}`
    };
    break;

  case 'create_report':
    apiRequest = {
      url: `${baseUrl}/reports/v1/reports`,
      method: 'POST',
      operation: 'create_report',
      description: 'Create new report',
      body: {
        name: requestData.report_config.name,
        description: requestData.report_config.description,
        category: requestData.report_config.category,
        type: requestData.report_type,
        format: requestData.report_config.format,
        schedule: requestData.report_config.schedule,
        recipients: requestData.report_config.recipients,
        parameters: requestData.report_config.parameters,
        created_by: 'n8n-automation',
        tags: ['automated', 'n8n']
      }
    };
    break;

  case 'update_report':
    apiRequest = {
      url: `${baseUrl}/reports/v1/reports/${requestData.report_id}`,
      method: 'PUT',
      operation: 'update_report',
      description: `Update report ${requestData.report_id}`,
      body: {
        name: requestData.report_config.name,
        description: requestData.report_config.description,
        category: requestData.report_config.category,
        format: requestData.report_config.format,
        schedule: requestData.report_config.schedule,
        recipients: requestData.report_config.recipients,
        parameters: requestData.report_config.parameters,
        updated_by: 'n8n-automation',
        updated_at: new Date().toISOString()
      }
    };
    break;

  case 'delete_report':
    apiRequest = {
      url: `${baseUrl}/reports/v1/reports/${requestData.report_id}`,
      method: 'DELETE',
      operation: 'delete_report',
      description: `Delete report ${requestData.report_id}`
    };
    break;

  case 'execute_report':
    apiRequest = {
      url: `${baseUrl}/reports/v1/reports/${requestData.report_id}/execute`,
      method: 'POST',
      operation: 'execute_report',
      description: `Execute report ${requestData.report_id}`,
      body: {
        format: requestData.download_format,
        parameters: requestData.report_config.parameters || {},
        triggered_by: 'n8n-automation',
        execution_time: new Date().toISOString()
      }
    };
    break;

  case 'list_report_runs':
    const runsUrl = requestData.report_id 
      ? `${baseUrl}/reports/v1/reports/${requestData.report_id}/runs`
      : `${baseUrl}/reports/v1/runs`;

    const runsParams = [];
    if (requestData.status) runsParams.push(`status=${requestData.status}`);
    runsParams.push(`limit=${requestData.limit}`);
    runsParams.push(`offset=${requestData.offset}`);

    apiRequest = {
      url: runsParams.length > 0 ? `${runsUrl}?${runsParams.join('&')}` : runsUrl,
      method: 'GET',
      operation: 'list_report_runs',
      description: requestData.report_id ? `Get runs for report ${requestData.report_id}` : 'Get all report runs'
    };
    break;

  case 'delete_report_run':
    const runId = requestData.run_id || requestData.execution_id;
    apiRequest = {
      url: `${baseUrl}/reports/v1/runs/${runId}`,
      method: 'DELETE',
      operation: 'delete_report_run',
      description: `Delete report run ${runId}`
    };
    break;

  case 'get_report_download_link':
    const downloadRunId = requestData.run_id || requestData.execution_id;
    const downloadParams = [];
    if (requestData.download_format) downloadParams.push(`format=${requestData.download_format}`);
    if (requestData.include_metadata) downloadParams.push(`include_metadata=true`);

    const downloadUrl = `${baseUrl}/reports/v1/runs/${downloadRunId}/download`;

    apiRequest = {
      url: downloadParams.length > 0 ? `${downloadUrl}?${downloadParams.join('&')}` : downloadUrl,
      method: 'GET',
      operation: 'get_report_download_link',
      description: `Get download link for report run ${downloadRunId}`
    };
    break;

  default:
    apiRequest = {
      url: `${baseUrl}/reports/v1/health`,
      method: 'GET',
      operation: 'default_health',
      description: 'Default health check'
    };
}

// Prepare common headers
const headers = {
  'Authorization': `Bearer ${accessToken}`,
  'Accept': 'application/json',
  'Content-Type': 'application/json'
};

// Return prepared request
const result = {
  request: apiRequest,
  headers: headers,
  baseUrl: baseUrl,
  operation: operation,
  requestId: requestId,
  timestamp: requestData.timestamp,
  config: requestData
};

return [{ json: result }];

// Process HPE Aruba Central Reporting API response
const response = items[0].json;
const configData = $('Prepare Reporting API Requests').first().json;
const statusCode = response.statusCode || 0;
const isSuccess = statusCode >= 200 && statusCode < 300;

const operation = configData.operation;
const requestId = configData.requestId;

let processedData = {
  requestId: requestId,
  operation: operation,
  timestamp: new Date().toISOString(),
  status: isSuccess ? 'success' : 'error',
  statusCode: statusCode,
  summary: {
    operation_completed: isSuccess,
    api_response_time: response.response_time || null,
    data_processed: false
  },
  data: null,
  alerts: []
};

if (isSuccess) {
  const data = response.result || response.body || response;
  processedData.data = data;
  processedData.summary.data_processed = true;

  // Operation-specific processing
  switch (operation) {
    case 'health_check':
      processedData.summary.service_status = data.status || 'unknown';
      processedData.summary.service_version = data.version || 'unknown';
      processedData.summary.uptime = data.uptime || null;
      processedData.summary.last_check = data.last_health_check || new Date().toISOString();

      if (data.status !== 'healthy' && data.status !== 'ok') {
        processedData.alerts.push({
          type: 'warning',
          category: 'service_health',
          message: `Reporting service status: ${data.status}`,
          timestamp: new Date().toISOString()
        });
      }

      processedData.message = `Reporting service health check completed. Status: ${data.status || 'unknown'}`;
      break;

    case 'list_reports':
      const reports = data.reports || data.items || [];
      processedData.summary.total_reports = reports.length;
      processedData.summary.report_types = [...new Set(reports.map(r => r.type || 'unknown'))];
      processedData.summary.report_statuses = [...new Set(reports.map(r => r.status || 'unknown'))];

      // Analyze report health
      const failedReports = reports.filter(r => r.status === 'failed' || r.status === 'error');
      if (failedReports.length > 0) {
        processedData.alerts.push({
          type: 'warning',
          category: 'failed_reports',
          message: `${failedReports.length} reports have failed`,
          failed_reports: failedReports.map(r => ({ id: r.id, name: r.name, status: r.status })),
          timestamp: new Date().toISOString()
        });
      }

      processedData.message = `Retrieved ${reports.length} reports. ${failedReports.length} failed reports found.`;
      break;

    case 'get_report_details':
      processedData.summary.report_id = data.id || configData.config.report_id;
      processedData.summary.report_name = data.name || 'Unknown';
      processedData.summary.report_status = data.status || 'unknown';
      processedData.summary.last_execution = data.last_execution || null;
      processedData.summary.next_execution = data.next_execution || null;

      processedData.message = `Report details retrieved for ${data.name || configData.config.report_id}`;
      break;

    case 'create_report':
      processedData.summary.created_report_id = data.id || data.report_id;
      processedData.summary.created_report_name = data.name || configData.config.report_config.name;
      processedData.summary.creation_status = data.status || 'created';

      processedData.message = `Report '${data.name || configData.config.report_config.name}' created successfully with ID: ${data.id || data.report_id}`;
      break;

    case 'update_report':
      processedData.summary.updated_report_id = configData.config.report_id;
      processedData.summary.update_status = data.status || 'updated';

      processedData.message = `Report ${configData.config.report_id} updated successfully`;
      break;

    case 'delete_report':
      processedData.summary.deleted_report_id = configData.config.report_id;
      processedData.summary.deletion_status = 'deleted';

      processedData.message = `Report ${configData.config.report_id} deleted successfully`;
      break;

    case 'execute_report':
      processedData.summary.execution_id = data.execution_id || data.run_id;
      processedData.summary.execution_status = data.status || 'started';
      processedData.summary.estimated_completion = data.estimated_completion || null;

      processedData.message = `Report execution started. Execution ID: ${data.execution_id || data.run_id}`;
      break;

    case 'list_report_runs':
      const runs = data.runs || data.executions || data.items || [];
      processedData.summary.total_runs = runs.length;
      processedData.summary.run_statuses = [...new Set(runs.map(r => r.status || 'unknown'))];

      const recentRuns = runs.filter(r => {
        const runTime = new Date(r.created_at || r.started_at);
        const oneDayAgo = new Date(Date.now() - 24 * 60 * 60 * 1000);
        return runTime > oneDayAgo;
      });

      processedData.summary.recent_runs_24h = recentRuns.length;

      const failedRuns = runs.filter(r => r.status === 'failed' || r.status === 'error');
      if (failedRuns.length > 0) {
        processedData.alerts.push({
          type: 'warning',
          category: 'failed_runs',
          message: `${failedRuns.length} report runs have failed`,
          failed_runs: failedRuns.slice(0, 5).map(r => ({ id: r.id, status: r.status, error: r.error })),
          timestamp: new Date().toISOString()
        });
      }

      processedData.message = `Retrieved ${runs.length} report runs. ${failedRuns.length} failed runs found.`;
      break;

    case 'delete_report_run':
      processedData.summary.deleted_run_id = configData.config.run_id || configData.config.execution_id;
      processedData.summary.deletion_status = 'deleted';

      processedData.message = `Report run ${configData.config.run_id || configData.config.execution_id} deleted successfully`;
      break;

    case 'get_report_download_link':
      processedData.summary.download_url = data.download_url || data.url;
      processedData.summary.download_expires = data.expires_at || data.expiry;
      processedData.summary.file_size = data.file_size || null;
      processedData.summary.file_format = data.format || configData.config.download_format;

      if (!data.download_url && !data.url) {
        processedData.alerts.push({
          type: 'warning',
          category: 'download_unavailable',
          message: 'Download URL not available in response',
          timestamp: new Date().toISOString()
        });
      }

      processedData.message = data.download_url || data.url 
        ? `Download link retrieved successfully`
        : `Download link request processed but URL not available`;
      break;

    default:
      processedData.message = `${operation} completed successfully`;
  }

} else {
  processedData.message = `Reporting API call failed: ${response.error?.message || response.message || 'Unknown error'}`;
  processedData.error = {
    statusCode: statusCode,
    message: response.error?.message || response.message || 'Unknown error',
    details: response
  };

  processedData.alerts.push({
    type: 'error',
    category: 'api_error',
    message: `${operation} operation failed with status ${statusCode}`,
    error_details: response.error || response.message,
    timestamp: new Date().toISOString()
  });
}

// Set overall status based on operation success and alerts
if (processedData.status === 'success' && processedData.alerts.filter(a => a.type === 'error').length === 0) {
  if (processedData.alerts.filter(a => a.type === 'warning').length > 0) {
    processedData.summary.overall_status = 'warning';
  } else {
    processedData.summary.overall_status = 'healthy';
  }
} else {
  processedData.summary.overall_status = 'error';
}

return [{ json: processedData }];

