// HPE Aruba Central Site Health Monitoring Input Validation
const input = items[0].json;

// Valid operations
const validOperations = [
  'get_site_health', 'get_all_sites_health', 'get_site_details', 'get_site_connectivity',
  'get_site_performance', 'analyze_site_trends', 'comprehensive_health_check'
];

const operation = input.operation || 'get_all_sites_health';

if (!validOperations.includes(operation)) {
  throw new Error(`Invalid operation: ${operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Set defaults and prepare request
const validatedInput = {
  operation: operation,

  // Site filtering parameters
  site_id: input.site_id || null,
  site_name: input.site_name || null,
  group_name: input.group_name || null,

  // Geographic filtering
  country: input.country || null,
  region: input.region || null,
  city: input.city || null,

  // Health filtering
  health_status: input.health_status || null, // 'good', 'fair', 'poor'
  min_health_score: parseInt(input.min_health_score) || 0,
  max_health_score: parseInt(input.max_health_score) || 100,

  // Time range for trend analysis
  time_range: input.time_range || '24h', // 1h, 6h, 24h, 7d, 30d
  start_time: input.start_time || null,
  end_time: input.end_time || null,

  // Query parameters
  limit: Math.min(parseInt(input.limit) || 100, 1000),
  offset: parseInt(input.offset) || 0,

  // Health thresholds
  thresholds: {
    health_score_min: parseInt(input.health_threshold) || 70,
    uptime_min: parseFloat(input.uptime_threshold) || 98.0, // percentage
    device_down_max: parseInt(input.device_down_threshold) || 5, // max devices down
    bandwidth_utilization_max: parseInt(input.bandwidth_threshold) || 80, // percentage
    latency_max: parseInt(input.latency_threshold) || 100, // milliseconds
    packet_loss_max: parseFloat(input.packet_loss_threshold) || 1.0 // percentage
  },

  // Analysis options
  include_trends: input.include_trends !== false,
  include_devices: input.include_devices !== false,
  include_performance: input.include_performance !== false,
  include_connectivity: input.include_connectivity !== false,

  timestamp: new Date().toISOString(),
  requestId: `hpe-site-health-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed'
};

return [{ json: validatedInput }];

// Prepare HPE Aruba Central Site Health monitoring API calls
const authResponse = items[1].json;
const requestData = items[0].json;

// Check authentication success
if (authResponse.status !== 'success' || !authResponse.access_token) {
  throw new Error(`Authentication failed: ${authResponse.message || 'No access token received'}`);
}

const accessToken = authResponse.access_token;
const baseUrl = authResponse.base_url || 'https://apigw-prod2.central.arubanetworks.com';
const operation = requestData.operation;
const requestId = requestData.requestId;

let apiRequests = [];

// Build API requests based on operation
switch (operation) {
  case 'get_site_health':
    if (!requestData.site_id) {
      throw new Error('site_id is required for get_site_health operation');
    }

    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/sites/${requestData.site_id}/health`,
      method: 'GET',
      operation: 'get_site_health',
      description: `Get health for site ${requestData.site_id}`
    });
    break;

  case 'get_all_sites_health':
    const healthUrl = `${baseUrl}/monitoring/v1/sites/health`;
    const healthParams = [];

    if (requestData.health_status) healthParams.push(`status=${requestData.health_status}`);
    if (requestData.country) healthParams.push(`country=${requestData.country}`);
    if (requestData.group_name) healthParams.push(`group=${requestData.group_name}`);
    healthParams.push(`limit=${requestData.limit}`);
    healthParams.push(`offset=${requestData.offset}`);

    apiRequests.push({
      url: healthParams.length > 0 ? `${healthUrl}?${healthParams.join('&')}` : healthUrl,
      method: 'GET',
      operation: 'get_all_sites_health',
      description: 'Get health status for all sites'
    });
    break;

  case 'get_site_details':
    if (!requestData.site_id) {
      throw new Error('site_id is required for get_site_details operation');
    }

    apiRequests = [
      {
        url: `${baseUrl}/central/v2/sites/${requestData.site_id}`,
        method: 'GET',
        operation: 'site_info',
        description: `Get site ${requestData.site_id} information`
      },
      {
        url: `${baseUrl}/monitoring/v1/sites/${requestData.site_id}/devices`,
        method: 'GET',
        operation: 'site_devices',
        description: `Get devices for site ${requestData.site_id}`
      },
      {
        url: `${baseUrl}/monitoring/v1/sites/${requestData.site_id}/connectivity`,
        method: 'GET',
        operation: 'site_connectivity',
        description: `Get connectivity for site ${requestData.site_id}`
      }
    ];
    break;

  case 'get_site_connectivity':
    if (!requestData.site_id) {
      throw new Error('site_id is required for get_site_connectivity operation');
    }

    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/sites/${requestData.site_id}/connectivity?duration=${requestData.time_range}`,
      method: 'GET',
      operation: 'get_site_connectivity',
      description: `Get connectivity status for site ${requestData.site_id}`
    });
    break;

  case 'get_site_performance':
    if (!requestData.site_id) {
      throw new Error('site_id is required for get_site_performance operation');
    }

    apiRequests = [
      {
        url: `${baseUrl}/monitoring/v1/sites/${requestData.site_id}/stats?duration=${requestData.time_range}`,
        method: 'GET',
        operation: 'site_stats',
        description: `Get performance stats for site ${requestData.site_id}`
      },
      {
        url: `${baseUrl}/monitoring/v1/sites/${requestData.site_id}/bandwidth?duration=${requestData.time_range}`,
        method: 'GET',
        operation: 'site_bandwidth',
        description: `Get bandwidth usage for site ${requestData.site_id}`
      }
    ];
    break;

  case 'analyze_site_trends':
    const trendsUrl = requestData.site_id 
      ? `${baseUrl}/monitoring/v1/sites/${requestData.site_id}/trends`
      : `${baseUrl}/monitoring/v1/sites/trends`;

    const trendsParams = [];
    if (requestData.time_range) trendsParams.push(`duration=${requestData.time_range}`);
    if (requestData.start_time) trendsParams.push(`start_time=${requestData.start_time}`);
    if (requestData.end_time) trendsParams.push(`end_time=${requestData.end_time}`);

    apiRequests.push({
      url: trendsParams.length > 0 ? `${trendsUrl}?${trendsParams.join('&')}` : trendsUrl,
      method: 'GET',
      operation: 'analyze_site_trends',
      description: requestData.site_id ? `Analyze trends for site ${requestData.site_id}` : 'Analyze trends for all sites'
    });
    break;

  case 'comprehensive_health_check':
    // Multiple API calls for comprehensive health assessment
    apiRequests = [
      {
        url: `${baseUrl}/monitoring/v1/sites/health?limit=${requestData.limit}`,
        method: 'GET',
        operation: 'all_sites_health',
        description: 'Get health for all sites'
      },
      {
        url: `${baseUrl}/central/v2/sites`,
        method: 'GET',
        operation: 'sites_list',
        description: 'Get sites information'
      },
      {
        url: `${baseUrl}/monitoring/v1/sites/connectivity`,
        method: 'GET',
        operation: 'all_sites_connectivity',
        description: 'Get connectivity for all sites'
      },
      {
        url: `${baseUrl}/monitoring/v1/sites/uptime?duration=${requestData.time_range}`,
        method: 'GET',
        operation: 'sites_uptime',
        description: 'Get uptime statistics for all sites'
      }
    ];
    break;

  default:
    apiRequests.push({
      url: `${baseUrl}/monitoring/v1/sites/health`,
      method: 'GET',
      operation: 'default_health',
      description: 'Default sites health check'
    });
}

// Prepare common headers
const headers = {
  'Authorization': `Bearer ${accessToken}`,
  'Accept': 'application/json',
  'Content-Type': 'application/json'
};

// Return prepared requests
const result = {
  requests: apiRequests,
  headers: headers,
  baseUrl: baseUrl,
  operation: operation,
  requestId: requestId,
  thresholds: requestData.thresholds,
  timestamp: requestData.timestamp,
  config: requestData
};

return [{ json: result }];

// Process HPE Aruba Central Site Health monitoring data
const apiResults = items;
const configData = $('Prepare Site Health API Requests').first().json;
const thresholds = configData.thresholds;
const operation = configData.operation;
const requestId = configData.requestId;

let processedData = {
  requestId: requestId,
  operation: operation,
  timestamp: new Date().toISOString(),
  status: 'success',
  summary: {
    total_sites: 0,
    healthy_sites: 0,
    warning_sites: 0,
    critical_sites: 0,
    average_health_score: 0,
    total_devices: 0,
    devices_down: 0,
    connectivity_issues: 0
  },
  sites: [],
  health_scores: [],
  connectivity_status: [],
  performance_metrics: [],
  trends: [],
  alerts: []
};

try {
  // Process each API result
  for (const result of apiResults) {
    const statusCode = result.statusCode || 0;
    const isSuccess = statusCode >= 200 && statusCode < 300;

    if (!isSuccess) {
      processedData.alerts.push({
        type: 'error',
        category: 'api_error',
        message: `Site health API call failed with status ${statusCode}`,
        details: result,
        timestamp: new Date().toISOString()
      });
      continue;
    }

    const data = result.result || result.body || result;

    // Process sites health data
    if (data.sites && Array.isArray(data.sites)) {
      processedData.summary.total_sites += data.sites.length;

      for (const site of data.sites) {
        const siteData = {
          site_id: site.site_id,
          site_name: site.site_name || site.name,
          health_score: site.health_score || 0,
          health_status: site.health_status || 'unknown',

          // Location information
          address: site.address || null,
          country: site.country || null,
          region: site.region || null,
          city: site.city || null,
          latitude: site.latitude || null,
          longitude: site.longitude || null,

          // Status information
          uptime: site.uptime || 0,
          connectivity_status: site.connectivity_status || 'unknown',
          last_seen: site.last_seen || null,

          // Device counts
          total_devices: site.total_devices || 0,
          devices_up: site.devices_up || 0,
          devices_down: site.devices_down || 0,

          // Performance metrics
          bandwidth_utilization: site.bandwidth_utilization || 0,
          latency: site.latency || 0,
          packet_loss: site.packet_loss || 0,

          // Health classification
          health_category: 'unknown',
          alerts: []
        };

        // Classify health status
        if (siteData.health_score >= thresholds.health_score_min) {
          siteData.health_category = 'healthy';
          processedData.summary.healthy_sites++;
        } else if (siteData.health_score >= 50) {
          siteData.health_category = 'warning';
          processedData.summary.warning_sites++;

          const healthAlert = {
            type: 'warning',
            category: 'low_health_score',
            site_id: siteData.site_id,
            site_name: siteData.site_name,
            message: `Site health score below threshold: ${siteData.health_score}%`,
            threshold: thresholds.health_score_min,
            actual: siteData.health_score,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(healthAlert);
          siteData.alerts.push(healthAlert);
        } else {
          siteData.health_category = 'critical';
          processedData.summary.critical_sites++;

          const criticalAlert = {
            type: 'critical',
            category: 'critical_health_score',
            site_id: siteData.site_id,
            site_name: siteData.site_name,
            message: `Site in critical health state: ${siteData.health_score}%`,
            threshold: thresholds.health_score_min,
            actual: siteData.health_score,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(criticalAlert);
          siteData.alerts.push(criticalAlert);
        }

        // Check uptime threshold
        if (siteData.uptime < thresholds.uptime_min) {
          const uptimeAlert = {
            type: 'warning',
            category: 'low_uptime',
            site_id: siteData.site_id,
            site_name: siteData.site_name,
            message: `Low site uptime: ${siteData.uptime}%`,
            threshold: thresholds.uptime_min,
            actual: siteData.uptime,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(uptimeAlert);
          siteData.alerts.push(uptimeAlert);
        }

        // Check devices down threshold
        if (siteData.devices_down > thresholds.device_down_max) {
          const deviceAlert = {
            type: 'critical',
            category: 'devices_down',
            site_id: siteData.site_id,
            site_name: siteData.site_name,
            message: `${siteData.devices_down} devices are down at site`,
            threshold: thresholds.device_down_max,
            actual: siteData.devices_down,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(deviceAlert);
          siteData.alerts.push(deviceAlert);
        }

        // Check bandwidth utilization
        if (siteData.bandwidth_utilization > thresholds.bandwidth_utilization_max) {
          const bandwidthAlert = {
            type: 'warning',
            category: 'high_bandwidth_usage',
            site_id: siteData.site_id,
            site_name: siteData.site_name,
            message: `High bandwidth utilization: ${siteData.bandwidth_utilization}%`,
            threshold: thresholds.bandwidth_utilization_max,
            actual: siteData.bandwidth_utilization,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(bandwidthAlert);
          siteData.alerts.push(bandwidthAlert);
        }

        // Check latency and packet loss
        if (siteData.latency > thresholds.latency_max) {
          const latencyAlert = {
            type: 'warning',
            category: 'high_latency',
            site_id: siteData.site_id,
            site_name: siteData.site_name,
            message: `High latency detected: ${siteData.latency}ms`,
            threshold: thresholds.latency_max,
            actual: siteData.latency,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(latencyAlert);
          siteData.alerts.push(latencyAlert);
        }

        if (siteData.packet_loss > thresholds.packet_loss_max) {
          const packetLossAlert = {
            type: 'critical',
            category: 'packet_loss',
            site_id: siteData.site_id,
            site_name: siteData.site_name,
            message: `Packet loss detected: ${siteData.packet_loss}%`,
            threshold: thresholds.packet_loss_max,
            actual: siteData.packet_loss,
            timestamp: new Date().toISOString()
          };

          processedData.alerts.push(packetLossAlert);
          siteData.alerts.push(packetLossAlert);
        }

        // Count devices
        processedData.summary.total_devices += siteData.total_devices;
        processedData.summary.devices_down += siteData.devices_down;

        processedData.sites.push(siteData);
        processedData.health_scores.push({
          site_id: siteData.site_id,
          site_name: siteData.site_name,
          score: siteData.health_score,
          category: siteData.health_category
        });
      }
    }

    // Process connectivity data
    if (data.connectivity || data.connection_status) {
      const connectivity = data.connectivity || data.connection_status;
      processedData.connectivity_status.push({
        type: 'connectivity_report',
        data: connectivity,
        collected_at: new Date().toISOString()
      });

      if (connectivity.issues && connectivity.issues.length > 0) {
        processedData.summary.connectivity_issues += connectivity.issues.length;
      }
    }

    // Process performance metrics
    if (data.stats || data.performance || data.bandwidth) {
      processedData.performance_metrics.push({
        type: 'performance_data',
        data: data.stats || data.performance || data.bandwidth,
        collected_at: new Date().toISOString()
      });
    }

    // Process trends data
    if (data.trends || data.historical) {
      processedData.trends.push({
        type: 'trend_analysis',
        data: data.trends || data.historical,
        collected_at: new Date().toISOString()
      });
    }

    // Process uptime data
    if (data.uptime && Array.isArray(data.uptime)) {
      for (const uptimeRecord of data.uptime) {
        if (uptimeRecord.uptime_percentage < thresholds.uptime_min) {
          processedData.alerts.push({
            type: 'warning',
            category: 'low_uptime',
            site_id: uptimeRecord.site_id,
            message: `Site ${uptimeRecord.site_name || uptimeRecord.site_id} has low uptime: ${uptimeRecord.uptime_percentage}%`,
            threshold: thresholds.uptime_min,
            actual: uptimeRecord.uptime_percentage,
            timestamp: new Date().toISOString()
          });
        }
      }
    }
  }

  // Calculate average health score
  if (processedData.sites.length > 0) {
    const totalHealthScore = processedData.sites.reduce((sum, site) => sum + site.health_score, 0);
    processedData.summary.average_health_score = Math.round(totalHealthScore / processedData.sites.length);
  }

  // Set overall status based on critical sites and alerts
  if (processedData.summary.critical_sites > 0 || processedData.alerts.filter(a => a.type === 'critical').length > 0) {
    processedData.summary.overall_status = 'critical';
  } else if (processedData.summary.warning_sites > 0 || processedData.alerts.filter(a => a.type === 'warning').length > 0) {
    processedData.summary.overall_status = 'warning';
  } else {
    processedData.summary.overall_status = 'healthy';
  }

  processedData.message = `Site health monitoring completed. ${processedData.summary.total_sites} sites analyzed, average health score: ${processedData.summary.average_health_score}%.`;

} catch (error) {
  processedData.status = 'error';
  processedData.message = `Error processing site health data: ${error.message}`;
  processedData.error = {
    message: error.message,
    stack: error.stack,
    timestamp: new Date().toISOString()
  };
}

return [{ json: processedData }];

