// Network Performance Monitoring Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['operation'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations
const validOperations = [
  'run_latency_test', 'run_throughput_test', 'run_packet_loss_test', 'run_comprehensive_test',
  'get_performance_metrics', 'set_performance_thresholds', 'get_historical_data',
  'generate_performance_report', 'check_sla_compliance'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Set defaults and prepare request
const validatedInput = {
  ...input,
  timestamp: new Date().toISOString(),
  request_id: `perf-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed',

  // Set performance monitoring defaults
  test_duration: input.test_duration || 60,
  test_interval: input.test_interval || 5,
  target_hosts: input.target_hosts || ['8.8.8.8', '1.1.1.1'],
  metrics_to_collect: input.metrics_to_collect || ['latency', 'throughput', 'packet_loss', 'jitter'],

  // Performance thresholds
  thresholds: {
    latency: {
      excellent: input.latency_excellent || 20,
      good: input.latency_good || 50,
      acceptable: input.latency_acceptable || 100,
      poor: input.latency_poor || 200,
      critical: input.latency_critical || 500
    },
    throughput: {
      excellent: input.throughput_excellent || 90,
      good: input.throughput_good || 70,
      acceptable: input.throughput_acceptable || 50,
      poor: input.throughput_poor || 30,
      critical: input.throughput_critical || 10
    },
    packet_loss: {
      excellent: input.packet_loss_excellent || 0.01,
      good: input.packet_loss_good || 0.1,
      acceptable: input.packet_loss_acceptable || 0.5,
      poor: input.packet_loss_poor || 1.0,
      critical: input.packet_loss_critical || 5.0
    },
    jitter: {
      excellent: input.jitter_excellent || 5,
      good: input.jitter_good || 15,
      acceptable: input.jitter_acceptable || 30,
      poor: input.jitter_poor || 50,
      critical: input.jitter_critical || 100
    }
  }
};

return [{ json: validatedInput }];

// Execute Network Performance Tests
const data = items[0].json;
const operation = data.operation;

// Simulate performance test execution
const results = {
  test_id: `test-${Date.now()}`,
  operation: operation,
  start_time: new Date().toISOString(),
  test_duration: data.test_duration,
  targets: data.target_hosts,
  metrics: {}
};

// Generate realistic performance metrics
for (const target of data.target_hosts) {
  const metrics = {};

  if (data.metrics_to_collect.includes('latency')) {
    metrics.latency = {
      avg: Math.random() * 100 + 10, // 10-110ms
      min: Math.random() * 20 + 5,   // 5-25ms
      max: Math.random() * 200 + 50, // 50-250ms
      unit: 'ms'
    };
  }

  if (data.metrics_to_collect.includes('throughput')) {
    metrics.throughput = {
      download: Math.random() * 800 + 200, // 200-1000 Mbps
      upload: Math.random() * 400 + 100,   // 100-500 Mbps
      unit: 'Mbps'
    };
  }

  if (data.metrics_to_collect.includes('packet_loss')) {
    metrics.packet_loss = {
      percentage: Math.random() * 2, // 0-2%
      packets_sent: 1000,
      packets_received: Math.floor(1000 - (Math.random() * 20)),
      unit: '%'
    };
  }

  if (data.metrics_to_collect.includes('jitter')) {
    metrics.jitter = {
      avg: Math.random() * 20 + 1, // 1-21ms
      max: Math.random() * 50 + 10, // 10-60ms
      unit: 'ms'
    };
  }

  results.metrics[target] = metrics;
}

// Evaluate performance against thresholds
results.evaluation = {};
for (const target of data.target_hosts) {
  const targetMetrics = results.metrics[target];
  const evaluation = {};

  if (targetMetrics.latency) {
    const latency = targetMetrics.latency.avg;
    if (latency <= data.thresholds.latency.excellent) evaluation.latency = 'excellent';
    else if (latency <= data.thresholds.latency.good) evaluation.latency = 'good';
    else if (latency <= data.thresholds.latency.acceptable) evaluation.latency = 'acceptable';
    else if (latency <= data.thresholds.latency.poor) evaluation.latency = 'poor';
    else evaluation.latency = 'critical';
  }

  if (targetMetrics.packet_loss) {
    const packetLoss = targetMetrics.packet_loss.percentage;
    if (packetLoss <= data.thresholds.packet_loss.excellent) evaluation.packet_loss = 'excellent';
    else if (packetLoss <= data.thresholds.packet_loss.good) evaluation.packet_loss = 'good';
    else if (packetLoss <= data.thresholds.packet_loss.acceptable) evaluation.packet_loss = 'acceptable';
    else if (packetLoss <= data.thresholds.packet_loss.poor) evaluation.packet_loss = 'poor';
    else evaluation.packet_loss = 'critical';
  }

  results.evaluation[target] = evaluation;
}

results.end_time = new Date().toISOString();
results.status = 'completed';
results.overall_health = Object.values(results.evaluation).every(eval => 
  Object.values(eval).every(rating => ['excellent', 'good'].includes(rating))
) ? 'healthy' : 'degraded';

return [{ json: {
  ...data,
  test_results: results,
  message: `Performance test ${operation} completed successfully`,
  health_status: results.overall_health
}}];

// Get Current Performance Metrics
const data = items[0].json;

// Simulate current metrics retrieval
const currentMetrics = {
  timestamp: new Date().toISOString(),
  network_interfaces: {
    'eth0': {
      status: 'up',
      speed: '1000Mbps',
      utilization: Math.random() * 80 + 10, // 10-90%
      errors: Math.floor(Math.random() * 5),
      drops: Math.floor(Math.random() * 3)
    },
    'wlan0': {
      status: 'up',
      speed: '866Mbps',
      utilization: Math.random() * 60 + 20, // 20-80%
      signal_strength: Math.random() * 30 - 70, // -70 to -40 dBm
      errors: Math.floor(Math.random() * 10)
    }
  },
  bandwidth_usage: {
    download: Math.random() * 500 + 100, // 100-600 Mbps
    upload: Math.random() * 200 + 50,    // 50-250 Mbps
    total: Math.random() * 700 + 150     // 150-850 Mbps
  },
  connection_stats: {
    active_connections: Math.floor(Math.random() * 1000 + 100),
    tcp_connections: Math.floor(Math.random() * 800 + 80),
    udp_connections: Math.floor(Math.random() * 200 + 20)
  },
  quality_metrics: {
    availability: 99.9 - (Math.random() * 1), // 98.9-99.9%
    reliability: 99.5 - (Math.random() * 0.5), // 99.0-99.5%
    response_time: Math.random() * 50 + 10     // 10-60ms
  }
};

return [{ json: {
  ...data,
  current_metrics: currentMetrics,
  message: 'Current performance metrics retrieved successfully',
  status: 'success'
}}];

// Update Performance Thresholds
const data = items[0].json;

// Update thresholds configuration
const updatedThresholds = {
  ...data.thresholds,
  last_updated: new Date().toISOString(),
  updated_by: 'n8n-automation'
};

// Validate threshold values
for (const metric in updatedThresholds) {
  if (typeof updatedThresholds[metric] === 'object' && updatedThresholds[metric].excellent !== undefined) {
    const thresholds = updatedThresholds[metric];
    if (thresholds.excellent > thresholds.good || 
        thresholds.good > thresholds.acceptable ||
        thresholds.acceptable > thresholds.poor ||
        thresholds.poor > thresholds.critical) {
      throw new Error(`Invalid threshold progression for ${metric}`);
    }
  }
}

return [{ json: {
  ...data,
  updated_thresholds: updatedThresholds,
  message: 'Performance thresholds updated successfully',
  status: 'success'
}}];

// Generate Performance Reports
const data = items[0].json;
const operation = data.operation;

let report = {
  report_id: `rpt-${Date.now()}`,
  generated_at: new Date().toISOString(),
  report_type: operation,
  period: data.report_period || 'last_24_hours'
};

if (operation === 'generate_performance_report') {
  report.sections = {
    executive_summary: {
      overall_health: 'Good',
      key_findings: [
        'Network latency within acceptable limits',
        'Bandwidth utilization at 65% average',
        'No critical performance issues detected'
      ],
      recommendations: [
        'Monitor bandwidth growth trends',
        'Consider capacity planning for Q4',
        'Implement proactive alerting for latency spikes'
      ]
    },
    performance_metrics: {
      average_latency: '45ms',
      peak_bandwidth: '850 Mbps',
      availability: '99.95%',
      packet_loss: '0.02%'
    },
    trend_analysis: {
      latency_trend: 'stable',
      bandwidth_trend: 'increasing',
      availability_trend: 'improving'
    },
    incidents: [
      {
        timestamp: new Date(Date.now() - 86400000).toISOString(),
        type: 'latency_spike',
        duration: '15 minutes',
        impact: 'low',
        resolution: 'auto-resolved'
      }
    ]
  };
} else if (operation === 'check_sla_compliance') {
  report.sla_metrics = {
    availability_sla: {
      target: '99.9%',
      actual: '99.95%',
      status: 'compliant'
    },
    latency_sla: {
      target: '<100ms',
      actual: '45ms avg',
      status: 'compliant'
    },
    throughput_sla: {
      target: '>500 Mbps',
      actual: '750 Mbps avg',
      status: 'compliant'
    }
  };
  report.compliance_score = 100;
  report.violations = [];
}

return [{ json: {
  ...data,
  performance_report: report,
  message: `${operation} completed successfully`,
  status: 'success'
}}];

// Process Network Performance Results
const data = items[0].json;

let result = {
  request_id: data.request_id,
  operation: data.operation,
  timestamp: new Date().toISOString(),
  status: 'success'
};

// Operation-specific result processing
switch (data.operation) {
  case 'run_latency_test':
  case 'run_throughput_test':
  case 'run_packet_loss_test':
  case 'run_comprehensive_test':
    result.message = `Performance test ${data.operation} completed successfully`;
    result.test_results = data.test_results;
    result.health_status = data.health_status;
    result.targets_tested = data.target_hosts.length;
    break;

  case 'get_performance_metrics':
  case 'get_historical_data':
    result.message = 'Performance metrics retrieved successfully';
    result.metrics = data.current_metrics;
    break;

  case 'set_performance_thresholds':
    result.message = 'Performance thresholds updated successfully';
    result.thresholds = data.updated_thresholds;
    break;

  case 'generate_performance_report':
  case 'check_sla_compliance':
    result.message = `${data.operation} completed successfully`;
    result.report = data.performance_report;
    if (data.performance_report.compliance_score) {
      result.compliance_score = data.performance_report.compliance_score;
    }
    break;

  default:
    result.message = `${data.operation} completed successfully`;
}

// Add performance summary if test results available
if (data.test_results && data.test_results.evaluation) {
  const evaluations = Object.values(data.test_results.evaluation);
  const ratings = evaluations.flatMap(eval => Object.values(eval));
  const criticalCount = ratings.filter(r => r === 'critical').length;
  const poorCount = ratings.filter(r => r === 'poor').length;

  result.performance_summary = {
    total_metrics: ratings.length,
    critical_issues: criticalCount,
    poor_performance: poorCount,
    overall_status: criticalCount > 0 ? 'critical' : poorCount > 0 ? 'degraded' : 'healthy'
  };
}

return [{ json: result }];

