// Enhanced Security Event Validation
const eventData = items[0].json;

// Validate required fields
const requiredFields = ['event_id', 'threat_type', 'severity', 'device_id', 'timestamp'];
for (const field of requiredFields) {
  if (!eventData[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Validate field formats and sanitize
const validateEventId = (id) => {
  const pattern = /^[A-Za-z0-9_-]{6,50}$/;
  if (!pattern.test(id)) {
    throw new Error('Invalid event_id format. Must be 6-50 alphanumeric characters, hyphens, or underscores.');
  }
  return String(id).substring(0, 50);
};

const validateSeverity = (severity) => {
  const validSeverities = ['low', 'medium', 'high', 'critical'];
  const cleanSeverity = String(severity).toLowerCase().trim();
  if (!validSeverities.includes(cleanSeverity)) {
    throw new Error(`Invalid severity: ${severity}. Must be one of: ${validSeverities.join(', ')}`);
  }
  return cleanSeverity;
};

const validateDeviceId = (deviceId) => {
  const pattern = /^[A-Za-z0-9._-]{3,50}$/;
  if (!pattern.test(deviceId)) {
    throw new Error('Invalid device_id format. Must be 3-50 alphanumeric characters, dots, hyphens, or underscores.');
  }
  return String(deviceId).substring(0, 50);
};

const validateThreatType = (threatType) => {
  const validTypes = ['malware', 'intrusion', 'dos', 'vulnerability', 'policy_violation', 'anomaly', 'other'];
  const cleanType = String(threatType).toLowerCase().replace(/[^a-z_]/g, '');
  if (!validTypes.includes(cleanType)) {
    return 'other'; // Default to 'other' if invalid
  }
  return cleanType;
};

// Validate and sanitize input
const validatedEvent = {
  event_id: validateEventId(eventData.event_id),
  threat_type: validateThreatType(eventData.threat_type),
  severity: validateSeverity(eventData.severity),
  device_id: validateDeviceId(eventData.device_id),
  timestamp: new Date(eventData.timestamp).toISOString(),

  // Optional fields with validation
  source_ip: eventData.source_ip ? String(eventData.source_ip).substring(0, 45) : null,
  destination_ip: eventData.destination_ip ? String(eventData.destination_ip).substring(0, 45) : null,
  description: eventData.description ? String(eventData.description).substring(0, 500) : '',
  confidence_score: Math.min(Math.max(parseFloat(eventData.confidence_score) || 0.8, 0), 1),

  // System metadata
  received_at: new Date().toISOString(),
  processing_id: `sec-${Date.now()}-${Math.random().toString(36).substr(2, 8)}`
};

// Calculate threat score
const severityScores = {
  'low': 25,
  'medium': 50,
  'high': 75,
  'critical': 95
};

const baseThreatScore = severityScores[validatedEvent.severity];
const threatScore = Math.round(baseThreatScore * validatedEvent.confidence_score);

// Enrich with response parameters
const enrichedEvent = {
  ...validatedEvent,
  threat_score: threatScore,
  response_required: threatScore >= 70,
  isolation_required: threatScore >= 90,
  notification_level: threatScore >= 80 ? 'critical' : threatScore >= 60 ? 'high' : 'medium',
  auto_quarantine: threatScore >= 85,
  escalation_required: threatScore >= 95
};

return [{ json: enrichedEvent }];

// Critical Threat Response Handler
const event = items[0].json;

// Log critical event
console.log('CRITICAL SECURITY EVENT:', {
  event_id: event.event_id,
  device_id: event.device_id,
  threat_score: event.threat_score,
  threat_type: event.threat_type,
  timestamp: event.timestamp
});

// Prepare isolation request
const isolationRequest = {
  action: 'isolate_device',
  device_id: event.device_id,
  reason: `Critical security threat detected: ${event.threat_type}`,
  threat_score: event.threat_score,
  event_id: event.event_id,
  isolation_level: 'full',
  rollback_timer: 3600, // 1 hour auto-rollback
  timestamp: new Date().toISOString()
};

// Prepare notification
const response = {
  ...event,
  response_action: 'device_isolation',
  isolation_request: isolationRequest,
  status: 'processing_critical_response'
};

return [{ json: response }];

// High Threat Response Handler
const event = items[0].json;

// Log high threat event
console.log('HIGH SECURITY EVENT:', {
  event_id: event.event_id,
  device_id: event.device_id,
  threat_score: event.threat_score,
  threat_type: event.threat_type
});

// Prepare monitoring enhancement
const monitoringRequest = {
  action: 'enhance_monitoring',
  device_id: event.device_id,
  reason: `High security threat detected: ${event.threat_type}`,
  threat_score: event.threat_score,
  event_id: event.event_id,
  monitoring_level: 'enhanced',
  duration: 1800, // 30 minutes enhanced monitoring
  timestamp: new Date().toISOString()
};

const response = {
  ...event,
  response_action: 'enhanced_monitoring',
  monitoring_request: monitoringRequest,
  status: 'processing_high_response'
};

return [{ json: response }];

// Medium Threat Response Handler
const event = items[0].json;

// Log medium threat event
console.log('MEDIUM SECURITY EVENT:', {
  event_id: event.event_id,
  device_id: event.device_id,
  threat_score: event.threat_score,
  threat_type: event.threat_type
});

// Prepare logging and analysis
const analysisRequest = {
  action: 'log_and_analyze',
  device_id: event.device_id,
  reason: `Medium security threat detected: ${event.threat_type}`,
  threat_score: event.threat_score,
  event_id: event.event_id,
  analysis_priority: 'normal',
  timestamp: new Date().toISOString()
};

const response = {
  ...event,
  response_action: 'log_and_analyze',
  analysis_request: analysisRequest,
  status: 'processing_medium_response'
};

return [{ json: response }];

