// ServiceNow Service Request Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['operation', 'request_type'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations
const validOperations = [
  'submit_request', 'get_request_status', 'update_request', 'cancel_request',
  'approve_request', 'reject_request', 'fulfill_request', 'get_catalog_items',
  'get_approval_status', 'add_request_comments'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}`);
}

// Valid request types
const validRequestTypes = ['network_access', 'hardware_provision', 'software_install', 'access_rights', 'network_config'];
if (!validRequestTypes.includes(input.request_type)) {
  throw new Error(`Invalid request type: ${input.request_type}`);
}

// Operation-specific validation
if (input.operation === 'submit_request') {
  if (!input.catalog_item && !input.short_description) {
    throw new Error('submit_request requires catalog_item or short_description');
  }
  if (!input.requested_for) {
    throw new Error('submit_request requires requested_for field');
  }
}

if (input.operation === 'approve_request' || input.operation === 'reject_request') {
  if (!input.request_id && !input.approval_id) {
    throw new Error('Approval operations require request_id or approval_id');
  }
}

// Set defaults and prepare request
const validatedInput = {
  ...input,
  timestamp: new Date().toISOString(),
  request_id: input.request_id || `snow-req-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed',

  // Set ServiceNow defaults
  priority: input.priority || '3',
  urgency: input.urgency || '3',
  requested_for: input.requested_for || 'network.admin@company.com',
  opened_by: input.opened_by || 'aruba.automation@company.com',
  assignment_group: input.assignment_group || 'Network Operations',
  approval_required: input.approval_required !== false, // Default to true
  state: input.state || 'open'
};

return [{ json: validatedInput }];

// Set ServiceNow configuration from input
const input = items[0].json;

const config = {
  ...input,
  snow_base_url: input.instance_url || 'https://company.service-now.com',
  snow_username: input.username || 'aruba_automation',
  snow_password: input.password || 'default-password',
  catalog_id: input.catalog_id || 'network_services_catalog'
};

return [{ json: config }];

