parameters.jsCode:
// Process ServiceNow service request operation results
const input = items[0].json;
const originalRequest = $('Validate Service Request').first().json;

let result = {
  request_id: originalRequest.request_id,
  operation: originalRequest.operation,
  request_type: originalRequest.request_type,
  timestamp: new Date().toISOString(),
  status: 'success',
  message: 'ServiceNow service request operation completed successfully'
};

try {
  if (input.error) {
    result.status = 'error';
    result.message = input.error.message || input.error;
    result.error_details = input.error;
  } else {
    result.data = input;

    // Operation-specific processing
    switch (originalRequest.operation) {
      case 'submit_request':
        result.message = `Service request submitted successfully: ${input.result?.number || 'Unknown'}`;
        result.request_number = input.result?.number;
        result.request_sys_id = input.result?.sys_id;
        result.request_state = input.result?.state;
        result.requested_for = originalRequest.requested_for;
        break;

      case 'get_request_status':
        result.message = `Request status retrieved: ${input.result?.state || 'Unknown'}`;
        result.request_sys_id = originalRequest.request_id;
        result.current_state = input.result?.state;
        result.approval_status = input.result?.approval;
        result.stage = input.result?.stage;
        break;

      case 'get_catalog_items':
        result.message = `Found ${input.result?.length || 0} catalog items`;
        result.catalog_count = input.result?.length || 0;
        result.catalog_items = input.result?.map(item => ({
          sys_id: item.sys_id,
          name: item.name,
          short_description: item.short_description,
          category: item.category,
          price: item.price
        })) || [];
        break;

      case 'approve_request':
      case 'reject_request':
        const action = originalRequest.operation === 'approve_request' ? 'approved' : 'rejected';
        result.message = `Request ${action} successfully`;
        result.approval_id = originalRequest.approval_id;
        result.approval_action = action;
        result.approver = originalRequest.approver;
        break;

      case 'fulfill_request':
        result.message = `Request ${originalRequest.request_item_id} fulfilled successfully`;
        result.request_item_id = originalRequest.request_item_id;
        result.deployment_status = originalRequest.deployment_status;
        result.device_serial = originalRequest.device_serial;
        result.device_ip = originalRequest.device_ip;
        break;

      default:
        result.message = `${originalRequest.operation} completed successfully`;
    }

    // Add request details if available
    if (originalRequest.device_type || originalRequest.location) {
      result.request_details = {
        device_type: originalRequest.device_type,
        location: originalRequest.location,
        vlan_id: originalRequest.vlan_id,
        ssid_name: originalRequest.ssid_name,
        justification: originalRequest.justification
      };
    }
  }
} catch (error) {
  result.status = 'error';
  result.message = 'Error processing ServiceNow service request operation result';
  result.error_details = error.message;
}

return [{ json: result }];

