// SIEM Integration Management Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['operation', 'siem_type'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations
const validOperations = [
  'configure_siem_connection', 'test_siem_connectivity', 'send_logs',
  'setup_log_forwarding', 'create_correlation_rule', 'update_correlation_rule',
  'delete_correlation_rule', 'list_correlation_rules', 'send_event',
  'query_siem_data', 'sync_threat_intelligence', 'export_logs',
  'configure_alerts', 'test_alert_integration'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Valid SIEM types
const validSiemTypes = ['splunk', 'qradar', 'arcsight', 'sentinel', 'elastic', 'logrhythm', 'secureworks', 'generic_syslog'];
if (!validSiemTypes.includes(input.siem_type)) {
  throw new Error(`Invalid SIEM type: ${input.siem_type}. Valid types: ${validSiemTypes.join(', ')}`);
}

// Operation-specific validation
if (['configure_siem_connection', 'setup_log_forwarding'].includes(input.operation)) {
  if (!input.siem_config) {
    throw new Error('SIEM configuration is required for connection operations');
  }
  if (!input.siem_config.host) {
    throw new Error('SIEM host is required in configuration');
  }
}

if (['send_logs', 'send_event'].includes(input.operation)) {
  if (!input.log_data && !input.event_data) {
    throw new Error('Log data or event data is required for send operations');
  }
}

if (['create_correlation_rule', 'update_correlation_rule'].includes(input.operation)) {
  if (!input.rule_name) {
    throw new Error('Rule name is required for correlation rule operations');
  }
  if (!input.rule_definition) {
    throw new Error('Rule definition is required for correlation rule operations');
  }
}

// Set defaults and prepare request
const validatedInput = {
  ...input,
  timestamp: new Date().toISOString(),
  request_id: `siem-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed',

  // Set SIEM integration defaults
  siem_api_url: input.siem_api_url || 'https://central.arubanetworks.com/siem-integration/v1',

  // Default SIEM configuration
  siem_config: {
    ...input.siem_config,
    port: input.siem_config?.port || getSiemDefaultPort(input.siem_type),
    protocol: input.siem_config?.protocol || 'tcp',
    auth_type: input.siem_config?.auth_type || 'api_key',
    encryption: {
      enabled: input.siem_config?.encryption?.enabled !== false,
      tls_version: input.siem_config?.encryption?.tls_version || '1.2'
    }
  },

  // Default log configuration
  log_config: {
    ...input.log_config,
    sources: input.log_config?.sources || ['security', 'network', 'system'],
    level: input.log_config?.level || 'info',
    format: input.log_config?.format || getSiemLogFormat(input.siem_type),
    batch_size: input.log_config?.batch_size || 100,
    flush_interval: input.log_config?.flush_interval || 60
  },

  // Default alert configuration
  alert_config: {
    ...input.alert_config,
    enabled: input.alert_config?.enabled !== false,
    thresholds: {
      low: input.alert_config?.thresholds?.low || 10,
      medium: input.alert_config?.thresholds?.medium || 5,
      high: input.alert_config?.thresholds?.high || 2,
      critical: input.alert_config?.thresholds?.critical || 1
    },
    channels: input.alert_config?.channels || ['siem', 'slack'],
    escalation: input.alert_config?.escalation || {}
  }
};

function getSiemDefaultPort(siemType) {
  const portMap = {
    'splunk': 8088,
    'qradar': 514,
    'arcsight': 514,
    'sentinel': 443,
    'elastic': 9200,
    'logrhythm': 514,
    'secureworks': 514,
    'generic_syslog': 514
  };
  return portMap[siemType] || 514;
}

function getSiemLogFormat(siemType) {
  const formatMap = {
    'splunk': 'json',
    'qradar': 'leef',
    'arcsight': 'cef',
    'sentinel': 'json',
    'elastic': 'json',
    'logrhythm': 'syslog',
    'secureworks': 'cef',
    'generic_syslog': 'syslog'
  };
  return formatMap[siemType] || 'cef';
}

return [{ json: validatedInput }];

// Prepare SIEM Connection Management API call
const data = items[0].json;
const operation = data.operation;

let method = 'GET';
let url = `${data.siem_api_url}/node_list/group/default/config/connection/${data.siem_type}`;
let body = null;

if (operation === 'configure_siem_connection') {
  method = 'PUT';
  body = {
    siem_type: data.siem_type,
    connection_config: {
      host: data.siem_config.host,
      port: data.siem_config.port,
      protocol: data.siem_config.protocol,
      authentication: {
        type: data.siem_config.auth_type,
        credentials: data.siem_config.credentials || {},
        api_key: data.siem_config.api_key,
        username: data.siem_config.username,
        password: data.siem_config.password
      },
      encryption: data.siem_config.encryption,
      connection_pool: {
        max_connections: data.siem_config.max_connections || 10,
        timeout: data.siem_config.timeout || 30000,
        retry_attempts: data.siem_config.retry_attempts || 3
      }
    },
    validation_config: {
      test_on_create: true,
      health_check_interval: data.siem_config.health_check_interval || 300
    }
  };
} else if (operation === 'test_siem_connectivity') {
  method = 'POST';
  url += '/test';
  body = {
    test_type: data.test_type || 'full',
    timeout: data.test_timeout || 30000,
    verify_authentication: data.verify_auth !== false,
    check_log_delivery: data.check_log_delivery !== false
  };
}

return [{ json: {
  method: method,
  url: url,
  body: body,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-siem-api-token'}`,
    'Accept': 'application/json'
  },
  operation: operation,
  request_id: data.request_id,
  siem_type: data.siem_type
}}];

// Prepare Log Management API call
const data = items[0].json;
const operation = data.operation;

let method = 'GET';
let url = `${data.siem_api_url}/node_list/group/default/config/logs/${data.siem_type}`;
let body = null;

if (operation === 'send_logs') {
  method = 'POST';
  url += '/send';
  body = {
    log_data: data.log_data || [],
    format: data.log_config.format,
    timestamp: data.timestamp,
    source: data.log_source || 'aruba_central',
    tags: data.log_tags || ['automation', 'aruba'],
    metadata: {
      device_count: data.device_count,
      log_level: data.log_config.level,
      batch_id: `batch-${Date.now()}`
    }
  };
} else if (operation === 'setup_log_forwarding') {
  method = 'PUT';
  url += '/forwarding';
  body = {
    forwarding_config: {
      enabled: data.log_forwarding_enabled !== false,
      log_sources: data.log_config.sources,
      log_level: data.log_config.level,
      format: data.log_config.format,
      batch_settings: {
        batch_size: data.log_config.batch_size,
        flush_interval: data.log_config.flush_interval,
        compression: data.log_config.compression || 'gzip'
      },
      filtering_rules: data.log_filtering_rules || [],
      transformation_rules: data.log_transformation_rules || []
    },
    destination_config: {
      siem_type: data.siem_type,
      connection_id: data.connection_id,
      delivery_mode: data.delivery_mode || 'realtime',
      retry_policy: {
        max_retries: 3,
        backoff_strategy: 'exponential',
        retry_delay: 5000
      }
    }
  };
} else if (operation === 'export_logs') {
  method = 'POST';
  url += '/export';
  body = {
    export_config: {
      start_time: data.export_start_time || new Date(Date.now() - 86400000).toISOString(),
      end_time: data.export_end_time || new Date().toISOString(),
      log_sources: data.export_sources || data.log_config.sources,
      format: data.export_format || data.log_config.format,
      compression: data.export_compression || 'gzip',
      max_file_size: data.max_file_size || '100MB'
    }
  };
}

return [{ json: {
  method: method,
  url: url,
  body: body,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-siem-api-token'}`,
    'Accept': 'application/json'
  },
  operation: operation,
  request_id: data.request_id,
  siem_type: data.siem_type
}}];

// Prepare Correlation Rule Management API call
const data = items[0].json;
const operation = data.operation;

let method = 'GET';
let url = `${data.siem_api_url}/node_list/group/default/config/rules/${data.siem_type}`;
let body = null;

if (operation === 'create_correlation_rule') {
  method = 'POST';
  body = {
    rule_name: data.rule_name,
    rule_type: data.rule_type || 'correlation',
    rule_definition: {
      conditions: data.rule_definition.conditions || [],
      actions: data.rule_definition.actions || [],
      severity: data.rule_definition.severity || 'medium',
      time_window: data.rule_definition.time_window || 300,
      threshold: data.rule_definition.threshold || 1,
      aggregation_method: data.rule_definition.aggregation_method || 'count'
    },
    enabled: data.enabled !== false,
    description: data.description || 'Automated correlation rule',
    metadata: {
      created_by: 'n8n-automation',
      source: 'aruba_network_automation',
      version: '1.0',
      tags: data.rule_tags || ['automation', 'security']
    }
  };
} else if (operation === 'update_correlation_rule') {
  method = 'PUT';
  url += `/${data.rule_id || data.rule_name}`;
  body = {
    rule_definition: data.rule_definition,
    enabled: data.enabled,
    description: data.description,
    metadata: {
      modified_by: 'n8n-automation',
      modified_at: data.timestamp,
      version: data.rule_version || '1.1'
    }
  };
} else if (operation === 'delete_correlation_rule') {
  method = 'DELETE';
  url += `/${data.rule_id || data.rule_name}`;
} else if (operation === 'list_correlation_rules') {
  method = 'GET';
  if (data.rule_filter) {
    url += `?filter=${encodeURIComponent(JSON.stringify(data.rule_filter))}`;
  }
}

return [{ json: {
  method: method,
  url: url,
  body: body,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-siem-api-token'}`,
    'Accept': 'application/json'
  },
  operation: operation,
  request_id: data.request_id,
  rule_name: data.rule_name,
  siem_type: data.siem_type
}}];

// Prepare Event Management API call
const data = items[0].json;
const operation = data.operation;

let method = 'POST';
let url = `${data.siem_api_url}/node_list/group/default/config/events/${data.siem_type}`;
let body = null;

if (operation === 'send_event') {
  method = 'POST';
  url += '/send';
  body = {
    event_type: data.event_data?.type || 'security_incident',
    event_data: {
      id: data.event_data?.id || `event-${Date.now()}`,
      source: data.event_data?.source || 'aruba_automation',
      severity: data.event_data?.severity || 'medium',
      category: data.event_data?.category || 'network_security',
      timestamp: data.timestamp,
      message: data.event_data?.message,
      details: {
        ...data.event_data?.details,
        device_id: data.device_id,
        user_id: data.user_id,
        location: data.location,
        action_taken: data.action_taken
      }
    },
    correlation_data: {
      ...data.correlation_data,
      correlation_id: data.correlation_id || `corr-${Date.now()}`,
      related_events: data.related_events || [],
      confidence_score: data.confidence_score || 0.8
    },
    enrichment_data: {
      threat_intelligence: data.threat_intelligence || {},
      geolocation: data.geolocation || {},
      asset_information: data.asset_information || {}
    }
  };
} else if (operation === 'query_siem_data') {
  method = 'POST';
  url += '/query';
  body = {
    query_config: {
      query_type: data.query_type || 'search',
      search_criteria: {
        time_range: {
          start: data.query_start_time || new Date(Date.now() - 3600000).toISOString(),
          end: data.query_end_time || new Date().toISOString()
        },
        filters: data.query_filters || {},
        keywords: data.query_keywords || [],
        fields: data.query_fields || ['timestamp', 'severity', 'message']
      },
      aggregation: data.query_aggregation || {},
      limit: data.query_limit || 1000,
      offset: data.query_offset || 0
    }
  };
}

return [{ json: {
  method: method,
  url: url,
  body: body,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-siem-api-token'}`,
    'Accept': 'application/json'
  },
  operation: operation,
  request_id: data.request_id,
  siem_type: data.siem_type
}}];

// Prepare Alert and Threat Intelligence Management API call
const data = items[0].json;
const operation = data.operation;

let method = 'GET';
let url = `${data.siem_api_url}/node_list/group/default/config/alerts/${data.siem_type}`;
let body = null;

if (operation === 'configure_alerts') {
  method = 'PUT';
  body = {
    alert_config: {
      enabled: data.alert_config.enabled,
      severity_thresholds: data.alert_config.thresholds,
      notification_settings: {
        channels: data.alert_config.channels,
        templates: {
          critical: data.alert_templates?.critical || 'Critical SIEM alert: {{event.message}}',
          high: data.alert_templates?.high || 'High priority SIEM alert: {{event.message}}',
          medium: data.alert_templates?.medium || 'Medium priority SIEM alert: {{event.message}}',
          low: data.alert_templates?.low || 'Low priority SIEM alert: {{event.message}}'
        },
        escalation_rules: {
          ...data.alert_config.escalation,
          auto_escalate: data.alert_config.auto_escalate !== false,
          escalation_delay: data.alert_config.escalation_delay || 300
        }
      },
      suppression_rules: data.alert_suppression_rules || [],
      correlation_settings: {
        enable_correlation: data.enable_alert_correlation !== false,
        correlation_window: data.alert_correlation_window || 300,
        max_correlations: data.max_alert_correlations || 10
      }
    }
  };
} else if (operation === 'test_alert_integration') {
  method = 'POST';
  url += '/test';
  body = {
    test_alert: {
      severity: data.test_severity || 'medium',
      message: data.test_message || 'Test alert from Aruba automation',
      source: 'aruba_automation_test',
      timestamp: data.timestamp,
      test_id: `test-${Date.now()}`
    },
    notification_test: {
      test_all_channels: data.test_all_channels !== false,
      specific_channels: data.test_channels || data.alert_config.channels
    }
  };
} else if (operation === 'sync_threat_intelligence') {
  method = 'POST';
  url = `${data.siem_api_url}/node_list/group/default/config/threat-intelligence/${data.siem_type}`;
  body = {
    sync_config: {
      intelligence_sources: data.threat_sources || ['aruba_threat_feed', 'global_threat_db'],
      sync_frequency: data.sync_frequency || 'hourly',
      data_types: data.threat_data_types || ['ip_addresses', 'domains', 'file_hashes', 'urls'],
      filtering_rules: data.threat_filtering_rules || [],
      auto_apply: data.auto_apply_intelligence !== false
    },
    enrichment_config: {
      enable_enrichment: data.enable_threat_enrichment !== false,
      enrichment_fields: data.enrichment_fields || ['reputation', 'category', 'confidence'],
      cache_duration: data.threat_cache_duration || 3600
    }
  };
}

return [{ json: {
  method: method,
  url: url,
  body: body,
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-siem-api-token'}`,
    'Accept': 'application/json'
  },
  operation: operation,
  request_id: data.request_id,
  siem_type: data.siem_type
}}];

// Process SIEM integration operation results
const response = items[0].json;
const statusCode = response.statusCode || 0;
const isSuccess = statusCode >= 200 && statusCode < 300;

// Get original request data
const preparedData = $('Route SIEM Operation').first().json;
const originalRequest = $('Validate SIEM Request').first().json;

let result = {
  request_id: originalRequest.request_id,
  operation: originalRequest.operation,
  siem_type: originalRequest.siem_type,
  timestamp: new Date().toISOString(),
  status: isSuccess ? 'success' : 'error',
  statusCode: statusCode
};

if (isSuccess) {
  const data = response.result || response.body || response;
  result.data = data;

  switch (originalRequest.operation) {
    case 'configure_siem_connection':
      result.message = `SIEM connection configured for ${originalRequest.siem_type}`;
      result.connection_id = data.connection_id || data.id;
      result.connection_status = data.status || 'configured';
      result.host = originalRequest.siem_config.host;
      result.port = originalRequest.siem_config.port;
      break;

    case 'test_siem_connectivity':
      result.message = `SIEM connectivity test completed for ${originalRequest.siem_type}`;
      result.connectivity_status = data.connectivity_status || data.status;
      result.response_time = data.response_time;
      result.test_results = data.test_results || {};
      break;

    case 'send_logs':
      result.message = `Logs sent to ${originalRequest.siem_type} SIEM`;
      result.logs_sent = data.logs_sent || originalRequest.log_data?.length || 0;
      result.batch_id = data.batch_id;
      result.delivery_status = data.delivery_status;
      break;

    case 'setup_log_forwarding':
      result.message = `Log forwarding configured for ${originalRequest.siem_type}`;
      result.forwarding_id = data.forwarding_id || data.id;
      result.forwarding_status = data.status;
      result.log_sources = originalRequest.log_config.sources;
      break;

    case 'create_correlation_rule':
      result.message = `Correlation rule '${originalRequest.rule_name}' created for ${originalRequest.siem_type}`;
      result.rule_id = data.rule_id || data.id;
      result.rule_name = originalRequest.rule_name;
      result.rule_status = data.status || 'active';
      break;

    case 'update_correlation_rule':
      result.message = `Correlation rule '${originalRequest.rule_name}' updated for ${originalRequest.siem_type}`;
      result.rule_id = data.rule_id || originalRequest.rule_id;
      result.rule_name = originalRequest.rule_name;
      break;

    case 'delete_correlation_rule':
      result.message = `Correlation rule '${originalRequest.rule_name}' deleted from ${originalRequest.siem_type}`;
      result.rule_name = originalRequest.rule_name;
      break;

    case 'list_correlation_rules':
      result.message = `Retrieved ${Array.isArray(data.rules) ? data.rules.length : 'unknown'} correlation rules from ${originalRequest.siem_type}`;
      result.rule_count = Array.isArray(data.rules) ? data.rules.length : 0;
      result.rules = data.rules || data;
      break;

    case 'send_event':
      result.message = `Security event sent to ${originalRequest.siem_type} SIEM`;
      result.event_id = data.event_id || data.id;
      result.event_type = originalRequest.event_data?.type;
      result.correlation_id = data.correlation_id;
      break;

    case 'query_siem_data':
      result.message = `SIEM data query completed for ${originalRequest.siem_type}`;
      result.query_results = data.results || data;
      result.result_count = Array.isArray(data.results) ? data.results.length : 0;
      result.query_duration = data.query_duration;
      break;

    case 'configure_alerts':
      result.message = `Alert configuration updated for ${originalRequest.siem_type}`;
      result.alert_config_id = data.config_id || data.id;
      result.alert_channels = originalRequest.alert_config.channels;
      break;

    case 'test_alert_integration':
      result.message = `Alert integration test completed for ${originalRequest.siem_type}`;
      result.test_alert_id = data.test_alert_id || data.id;
      result.test_status = data.test_status;
      result.notification_results = data.notification_results || {};
      break;

    case 'sync_threat_intelligence':
      result.message = `Threat intelligence sync completed for ${originalRequest.siem_type}`;
      result.sync_id = data.sync_id || data.id;
      result.indicators_synced = data.indicators_synced || 0;
      result.sync_status = data.sync_status;
      break;

    case 'export_logs':
      result.message = `Log export completed for ${originalRequest.siem_type}`;
      result.export_id = data.export_id || data.id;
      result.export_file = data.export_file;
      result.records_exported = data.records_exported || 0;
      break;

    default:
      result.message = `${originalRequest.operation} completed successfully for ${originalRequest.siem_type}`;
  }
} else {
  result.message = `SIEM API call failed: ${response.error?.message || response.message || 'Unknown error'}`;
  result.error = {
    statusCode: statusCode,
    message: response.error?.message || response.message || 'Unknown error',
    details: response
  };
}

return [{ json: result }];

