// UXI Test Configuration Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['operation'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations
const validOperations = [
  'create_test', 'update_test', 'delete_test', 'list_tests', 
  'run_test', 'schedule_test', 'get_test_results', 'cancel_test'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}. Valid operations: ${validOperations.join(', ')}`);
}

// Operation-specific validation
if (['update_test', 'delete_test', 'run_test', 'cancel_test', 'get_test_results'].includes(input.operation)) {
  if (!input.test_id) {
    throw new Error(`${input.operation} requires test_id`);
  }
}

if (['create_test', 'run_test', 'schedule_test'].includes(input.operation)) {
  if (!input.sensor_id) {
    throw new Error(`${input.operation} requires sensor_id`);
  }
}

if (input.operation === 'create_test') {
  if (!input.test_type) {
    throw new Error('create_test requires test_type');
  }
}

// Set defaults and prepare request
const validatedInput = {
  ...input,
  timestamp: new Date().toISOString(),
  request_id: `uxi-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed',

  // Set UXI API defaults
  uxi_api_url: input.uxi_api_url || 'https://api.uxi.arubanetworks.com',
  test_type: input.test_type || 'network_performance',
  frequency: input.frequency || 300,
  timeout: input.timeout || 60,
  enabled: input.enabled !== false,

  // Test configuration defaults
  test_config: {
    ...input.test_config,
    name: input.test_name || input.test_config?.name || 'Automated Network Test',
    description: input.description || input.test_config?.description || 'Automated test via n8n workflow',
    test_type: input.test_type || 'network_performance',
    frequency: input.frequency || 300,
    timeout: input.timeout || 60,
    enabled: input.enabled !== false
  }
};

return [{ json: validatedInput }];

// Prepare Create Test API call
const data = items[0].json;

// Build comprehensive test configuration
const testConfig = {
  method: 'POST',
  url: `${data.uxi_api_url}/api/v1/sensors/${data.sensor_id}/tests`,
  body: {
    name: data.test_config.name,
    description: data.test_config.description,
    test_type: data.test_type,
    frequency: data.frequency,
    timeout: data.timeout,
    enabled: data.enabled,
    configuration: {
      targets: data.test_targets || ['auto'],
      thresholds: {
        download_speed: data.min_download_speed || 25,
        upload_speed: data.min_upload_speed || 5,
        latency: data.max_latency || 50,
        jitter: data.max_jitter || 20,
        packet_loss: data.max_packet_loss || 1
      },
      advanced: {
        parallel_connections: data.parallel_connections || 4,
        test_duration: data.test_duration || 10,
        retry_attempts: data.retry_attempts || 3
      }
    },
    tags: data.tags || ['automated', 'n8n']
  },
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-uxi-api-token'}`,
    'Accept': 'application/json'
  },
  operation: data.operation,
  request_id: data.request_id,
  sensor_id: data.sensor_id
};

return [{ json: testConfig }];

// Prepare Update Test API call
const data = items[0].json;

const updateConfig = {
  method: 'PUT',
  url: `${data.uxi_api_url}/api/v1/tests/${data.test_id}`,
  body: {
    name: data.test_config.name,
    description: data.test_config.description,
    frequency: data.frequency,
    timeout: data.timeout,
    enabled: data.enabled,
    configuration: {
      thresholds: {
        download_speed: data.min_download_speed || 25,
        upload_speed: data.min_upload_speed || 5,
        latency: data.max_latency || 50,
        jitter: data.max_jitter || 20
      }
    }
  },
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-uxi-api-token'}`,
    'Accept': 'application/json'
  },
  operation: data.operation,
  request_id: data.request_id,
  test_id: data.test_id
};

return [{ json: updateConfig }];

// Prepare Delete Test API call
const data = items[0].json;

const deleteConfig = {
  method: 'DELETE',
  url: `${data.uxi_api_url}/api/v1/tests/${data.test_id}`,
  body: null,
  headers: {
    'Authorization': `Bearer ${data.api_token || 'your-uxi-api-token'}`,
    'Accept': 'application/json'
  },
  operation: data.operation,
  request_id: data.request_id,
  test_id: data.test_id
};

return [{ json: deleteConfig }];

// Prepare List Tests API call
const data = items[0].json;

const listConfig = {
  method: 'GET',
  url: `${data.uxi_api_url}/api/v1/tests`,
  queryParams: {
    sensor_id: data.sensor_id || undefined,
    test_type: data.test_type_filter || undefined,
    status: data.status_filter || undefined,
    limit: data.limit || 100,
    offset: data.offset || 0
  },
  body: null,
  headers: {
    'Authorization': `Bearer ${data.api_token || 'your-uxi-api-token'}`,
    'Accept': 'application/json'
  },
  operation: data.operation,
  request_id: data.request_id
};

return [{ json: listConfig }];

// Prepare Run Test API call
const data = items[0].json;

const runConfig = {
  method: 'POST',
  url: `${data.uxi_api_url}/api/v1/tests/${data.test_id}/execute`,
  body: {
    immediate: data.immediate || true,
    timeout: data.execution_timeout || 60,
    retry_on_failure: data.retry_on_failure || true,
    max_retries: data.max_retries || 3,
    execution_options: {
      priority: data.priority || 'normal',
      notification_enabled: data.notification_enabled || true
    }
  },
  headers: {
    'Content-Type': 'application/json',
    'Authorization': `Bearer ${data.api_token || 'your-uxi-api-token'}`,
    'Accept': 'application/json'
  },
  operation: data.operation,
  request_id: data.request_id,
  test_id: data.test_id
};

return [{ json: runConfig }];

// Prepare Schedule/Results/Cancel API calls
const data = items[0].json;
const operation = data.operation;

let method = 'GET';
let url = `${data.uxi_api_url}/api/v1/tests`;
let body = null;

switch (operation) {
  case 'schedule_test':
    method = 'POST';
    url = `${data.uxi_api_url}/api/v1/tests/${data.test_id}/schedule`;
    body = {
      schedule_type: data.schedule_type || 'interval',
      interval: data.schedule_interval || 300,
      start_time: data.start_time || new Date().toISOString(),
      end_time: data.end_time || null,
      timezone: data.timezone || 'UTC',
      enabled: data.schedule_enabled !== false
    };
    break;

  case 'get_test_results':
    method = 'GET';
    url = `${data.uxi_api_url}/api/v1/tests/${data.test_id}/results`;
    if (data.execution_id) {
      url += `/${data.execution_id}`;
    }
    break;

  case 'cancel_test':
    method = 'POST';
    url = `${data.uxi_api_url}/api/v1/tests/${data.test_id}/cancel`;
    body = {
      reason: data.cancel_reason || 'Manual cancellation via automation'
    };
    break;
}

const config = {
  method: method,
  url: url,
  body: body,
  headers: {
    'Authorization': `Bearer ${data.api_token || 'your-uxi-api-token'}`,
    'Accept': 'application/json'
  },
  operation: operation,
  request_id: data.request_id,
  test_id: data.test_id
};

if (body) {
  config.headers['Content-Type'] = 'application/json';
}

return [{ json: config }];

// Process UXI Test API response
const response = items[0].json;
const statusCode = response.statusCode || 0;
const isSuccess = statusCode >= 200 && statusCode < 300;

// Get original request data
const preparedData = $('Route UXI Operation').first().json;
const originalRequest = $('Validate UXI Request').first().json;

let result = {
  request_id: originalRequest.request_id,
  operation: originalRequest.operation,
  timestamp: new Date().toISOString(),
  status: isSuccess ? 'success' : 'error',
  statusCode: statusCode
};

if (isSuccess) {
  const data = response.result || response.body || response;
  result.data = data;

  switch (originalRequest.operation) {
    case 'create_test':
      result.message = `UXI test '${data.name || 'Unknown'}' created successfully`;
      result.test_id = data.id || data.test_id;
      result.test_name = data.name;
      result.sensor_id = originalRequest.sensor_id;
      result.test_type = originalRequest.test_type;
      break;

    case 'update_test':
      result.message = `UXI test '${originalRequest.test_id}' updated successfully`;
      result.test_id = originalRequest.test_id;
      break;

    case 'delete_test':
      result.message = `UXI test '${originalRequest.test_id}' deleted successfully`;
      result.test_id = originalRequest.test_id;
      break;

    case 'list_tests':
      result.message = `Retrieved ${Array.isArray(data.tests) ? data.tests.length : 'unknown'} UXI tests`;
      result.test_count = Array.isArray(data.tests) ? data.tests.length : 0;
      result.tests = data.tests || data;
      break;

    case 'run_test':
      result.message = `UXI test '${originalRequest.test_id}' execution started`;
      result.test_id = originalRequest.test_id;
      result.execution_id = data.execution_id;
      result.execution_status = data.status || 'running';
      break;

    case 'schedule_test':
      result.message = `UXI test '${originalRequest.test_id}' scheduled successfully`;
      result.test_id = originalRequest.test_id;
      result.schedule_id = data.schedule_id;
      result.next_execution = data.next_execution;
      break;

    case 'get_test_results':
      result.message = `UXI test results retrieved successfully`;
      result.test_id = originalRequest.test_id;
      result.results = data.results || data;
      result.result_count = Array.isArray(data.results) ? data.results.length : 1;
      break;

    case 'cancel_test':
      result.message = `UXI test '${originalRequest.test_id}' cancelled successfully`;
      result.test_id = originalRequest.test_id;
      result.cancellation_reason = data.reason;
      break;

    default:
      result.message = `UXI ${originalRequest.operation} completed successfully`;
  }
} else {
  result.message = `UXI API call failed: ${response.error?.message || response.message || 'Unknown error'}`;
  result.error = {
    statusCode: statusCode,
    message: response.error?.message || response.message || 'Unknown error',
    details: response
  };
}

return [{ json: result }];

