// Alert Aggregation and Correlation Engine
// Processes incoming alerts from multiple sources and performs intelligent correlation

const incomingAlert = $input.item(0).json;

// Alert processing configuration
const alertConfig = {
  // Alert sources and their characteristics
  sources: {
    'aruba_central': {
      name: 'Aruba Central',
      priority_mapping: {
        'critical': 1,
        'high': 2,
        'medium': 3,
        'low': 4,
        'info': 5
      },
      typical_volume: 'medium',
      reliability: 'high'
    },
    'aos_cx': {
      name: 'AOS-CX Switches',
      priority_mapping: {
        'emergency': 1,
        'alert': 2,
        'critical': 2,
        'error': 3,
        'warning': 4,
        'notice': 5,
        'info': 5
      },
      typical_volume: 'high',
      reliability: 'high'
    },
    'edgeconnect': {
      name: 'EdgeConnect SD-WAN',
      priority_mapping: {
        'critical': 1,
        'major': 2,
        'minor': 3,
        'warning': 4,
        'info': 5
      },
      typical_volume: 'medium',
      reliability: 'high'
    },
    'uxi': {
      name: 'UXI Sensors',
      priority_mapping: {
        'critical': 1,
        'high': 2,
        'medium': 3,
        'low': 4
      },
      typical_volume: 'low',
      reliability: 'medium'
    },
    'monitoring_system': {
      name: 'Network Monitoring',
      priority_mapping: {
        'critical': 1,
        'warning': 3,
        'info': 5
      },
      typical_volume: 'high',
      reliability: 'high'
    },
    'external_webhook': {
      name: 'External System',
      priority_mapping: {
        'critical': 1,
        'high': 2,
        'medium': 3,
        'low': 4,
        'info': 5
      },
      typical_volume: 'variable',
      reliability: 'medium'
    }
  },

  // Alert categories and their correlation rules
  categories: {
    'device_health': {
      keywords: ['cpu', 'memory', 'temperature', 'disk', 'health', 'utilization'],
      correlation_window: 300, // 5 minutes
      suppression_threshold: 5, // Suppress if more than 5 similar alerts
      escalation_time: 900      // 15 minutes
    },
    'connectivity': {
      keywords: ['link', 'connection', 'interface', 'port', 'down', 'up', 'connectivity'],
      correlation_window: 180, // 3 minutes
      suppression_threshold: 3,
      escalation_time: 300     // 5 minutes
    },
    'performance': {
      keywords: ['latency', 'throughput', 'bandwidth', 'packet_loss', 'jitter', 'slow'],
      correlation_window: 600, // 10 minutes
      suppression_threshold: 10,
      escalation_time: 1800    // 30 minutes
    },
    'security': {
      keywords: ['security', 'intrusion', 'threat', 'malware', 'unauthorized', 'attack'],
      correlation_window: 60,  // 1 minute
      suppression_threshold: 1, // No suppression for security alerts
      escalation_time: 120     // 2 minutes
    },
    'configuration': {
      keywords: ['config', 'configuration', 'policy', 'rule', 'template'],
      correlation_window: 900, // 15 minutes
      suppression_threshold: 5,
      escalation_time: 1800    // 30 minutes
    },
    'authentication': {
      keywords: ['auth', 'authentication', 'login', 'credential', 'certificate'],
      correlation_window: 300, // 5 minutes
      suppression_threshold: 3,
      escalation_time: 600     // 10 minutes
    }
  },

  // Alert correlation patterns
  correlation_patterns: {
    'cascade_failure': {
      description: 'Multiple devices failing in sequence',
      pattern: 'device_health alerts from multiple devices within 5 minutes',
      severity_multiplier: 2.0
    },
    'network_partition': {
      description: 'Network connectivity issues affecting multiple segments',
      pattern: 'connectivity alerts from multiple network segments',
      severity_multiplier: 1.5
    },
    'performance_degradation': {
      description: 'Widespread performance issues',
      pattern: 'performance alerts from multiple monitoring points',
      severity_multiplier: 1.3
    },
    'security_incident': {
      description: 'Coordinated security event',
      pattern: 'security alerts from multiple sources',
      severity_multiplier: 3.0
    }
  },

  // Notification routing rules
  notification_rules: {
    'critical_immediate': {
      conditions: ['priority <= 1', 'category = security'],
      channels: ['slack_critical', 'email_critical', 'sms_oncall'],
      delay: 0
    },
    'high_priority': {
      conditions: ['priority <= 2', 'category != security'],
      channels: ['slack_alerts', 'email_alerts'],
      delay: 60 // 1 minute
    },
    'medium_priority': {
      conditions: ['priority = 3'],
      channels: ['slack_alerts'],
      delay: 300 // 5 minutes
    },
    'low_priority': {
      conditions: ['priority >= 4'],
      channels: ['email_digest'],
      delay: 1800 // 30 minutes
    }
  }
};

// Process incoming alert
const processedAlert = {
  alert_id: incomingAlert.alert_id || `alert_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
  source: incomingAlert.source || 'unknown',
  original_severity: incomingAlert.severity || incomingAlert.priority || 'unknown',
  message: incomingAlert.message || incomingAlert.description || 'No message provided',
  device_id: incomingAlert.device_id || incomingAlert.device_name || 'unknown',
  timestamp: incomingAlert.timestamp || new Date().toISOString(),
  raw_data: incomingAlert
};

// Normalize priority based on source
const sourceConfig = alertConfig.sources[processedAlert.source] || alertConfig.sources['external_webhook'];
const normalizedPriority = sourceConfig.priority_mapping[processedAlert.original_severity.toLowerCase()] || 5;

// Categorize alert
let alertCategory = 'unknown';
let categoryScore = 0;

Object.keys(alertConfig.categories).forEach(category => {
  const keywords = alertConfig.categories[category].keywords;
  const messageText = processedAlert.message.toLowerCase();

  const matchCount = keywords.filter(keyword => messageText.includes(keyword)).length;
  const score = matchCount / keywords.length;

  if (score > categoryScore) {
    categoryScore = score;
    alertCategory = category;
  }
});

// Determine alert context
const alertContext = {
  location: incomingAlert.location || incomingAlert.site || 'unknown',
  device_type: incomingAlert.device_type || 'unknown',
  network_segment: incomingAlert.network_segment || 'unknown',
  service_affected: incomingAlert.service_affected || 'unknown',
  impact_level: incomingAlert.impact_level || 'unknown'
};

// Calculate alert fingerprint for deduplication
const alertFingerprint = require('crypto')
  .createHash('md5')
  .update(`${processedAlert.source}_${alertCategory}_${processedAlert.device_id}_${processedAlert.message.substring(0, 50)}`)
  .digest('hex');

// Determine notification routing
const notificationRouting = [];
Object.keys(alertConfig.notification_rules).forEach(ruleName => {
  const rule = alertConfig.notification_rules[ruleName];
  let shouldNotify = true;

  rule.conditions.forEach(condition => {
    if (condition.includes('priority <= 1') && normalizedPriority > 1) shouldNotify = false;
    if (condition.includes('priority <= 2') && normalizedPriority > 2) shouldNotify = false;
    if (condition.includes('priority = 3') && normalizedPriority !== 3) shouldNotify = false;
    if (condition.includes('priority >= 4') && normalizedPriority < 4) shouldNotify = false;
    if (condition.includes('category = security') && alertCategory !== 'security') shouldNotify = false;
    if (condition.includes('category != security') && alertCategory === 'security') shouldNotify = false;
  });

  if (shouldNotify) {
    notificationRouting.push({
      rule: ruleName,
      channels: rule.channels,
      delay: rule.delay
    });
  }
});

// Generate correlation data for later processing
const correlationData = {
  fingerprint: alertFingerprint,
  category: alertCategory,
  category_score: categoryScore,
  correlation_window: alertConfig.categories[alertCategory]?.correlation_window || 300,
  suppression_threshold: alertConfig.categories[alertCategory]?.suppression_threshold || 5,
  escalation_time: alertConfig.categories[alertCategory]?.escalation_time || 900
};

// Generate alert summary
const alertSummary = {
  alert_id: processedAlert.alert_id,
  source: processedAlert.source,
  source_name: sourceConfig.name,
  category: alertCategory,
  normalized_priority: normalizedPriority,
  priority_label: ['critical', 'high', 'medium', 'low', 'info'][normalizedPriority - 1] || 'unknown',
  device_id: processedAlert.device_id,
  message: processedAlert.message,
  timestamp: processedAlert.timestamp,
  context: alertContext,
  notification_routing: notificationRouting,
  correlation_data: correlationData,
  processing_time: new Date().toISOString()
};

// Add alert enrichment data
const enrichmentData = {
  alert_age: 0, // Will be calculated when processing
  similar_alerts_count: 0, // Will be populated from correlation
  affected_users_estimate: 0, // Will be calculated based on device/location
  business_impact: 'unknown', // Will be determined based on device criticality
  recommended_actions: [], // Will be populated based on category and severity
  escalation_required: false, // Will be determined by correlation engine
  auto_remediation_available: false // Will be checked against remediation catalog
};

// Generate recommended actions based on category and severity
const recommendedActions = [];

if (alertCategory === 'device_health' && normalizedPriority <= 2) {
  recommendedActions.push({
    action: 'check_device_status',
    description: 'Verify device connectivity and basic health',
    priority: 'immediate',
    estimated_time: '2-5 minutes'
  });

  if (processedAlert.message.toLowerCase().includes('cpu')) {
    recommendedActions.push({
      action: 'analyze_cpu_usage',
      description: 'Analyze CPU utilization patterns and identify high-usage processes',
      priority: 'high',
      estimated_time: '5-10 minutes'
    });
  }

  if (processedAlert.message.toLowerCase().includes('memory')) {
    recommendedActions.push({
      action: 'check_memory_usage',
      description: 'Check memory utilization and identify memory leaks',
      priority: 'high',
      estimated_time: '3-7 minutes'
    });
  }
}

if (alertCategory === 'connectivity' && normalizedPriority <= 2) {
  recommendedActions.push({
    action: 'verify_physical_connectivity',
    description: 'Check physical cable connections and port status',
    priority: 'immediate',
    estimated_time: '1-3 minutes'
  });

  recommendedActions.push({
    action: 'check_spanning_tree',
    description: 'Verify spanning tree protocol status and topology',
    priority: 'high',
    estimated_time: '5-10 minutes'
  });
}

if (alertCategory === 'security') {
  recommendedActions.push({
    action: 'isolate_threat',
    description: 'Isolate affected device/segment to prevent threat spread',
    priority: 'critical',
    estimated_time: '1-2 minutes'
  });

  recommendedActions.push({
    action: 'analyze_security_logs',
    description: 'Analyze security logs for attack patterns and indicators',
    priority: 'critical',
    estimated_time: '10-15 minutes'
  });
}

if (alertCategory === 'performance' && normalizedPriority <= 3) {
  recommendedActions.push({
    action: 'analyze_traffic_patterns',
    description: 'Analyze network traffic patterns and identify bottlenecks',
    priority: 'medium',
    estimated_time: '5-10 minutes'
  });

  recommendedActions.push({
    action: 'check_qos_policies',
    description: 'Verify QoS policies and traffic prioritization',
    priority: 'medium',
    estimated_time: '3-7 minutes'
  });
}

enrichmentData.recommended_actions = recommendedActions;

// Final processed alert object
const finalAlert = {
  ...alertSummary,
  enrichment: enrichmentData,
  requires_correlation: true,
  requires_notification: notificationRouting.length > 0,
  processing_metadata: {
    processed_at: new Date().toISOString(),
    processing_version: '1.0.0',
    correlation_required: true,
    notification_required: notificationRouting.length > 0
  }
};

return [{ json: finalAlert }];

// Advanced Alert Correlation and Analysis
const currentAlert = $input.first().json;
const similarAlerts = $input.all()[1]?.json?.alerts || [];
const correlationResults = $input.all()[2]?.json || {};

// Analyze similar alerts for patterns
const alertAnalysis = {
  similar_alerts_count: similarAlerts.length,
  duplicate_alerts: similarAlerts.filter(a => a.fingerprint === currentAlert.correlation_data.fingerprint).length,
  related_alerts: similarAlerts.filter(a => a.fingerprint !== currentAlert.correlation_data.fingerprint).length,
  time_pattern: 'none',
  severity_escalation: false,
  geographic_spread: 'none',
  device_type_pattern: 'none'
};

// Analyze timing patterns
if (similarAlerts.length > 0) {
  const alertTimes = similarAlerts.map(a => new Date(a.timestamp).getTime());
  const currentTime = new Date(currentAlert.timestamp).getTime();
  const timeDifferences = alertTimes.map(t => Math.abs(currentTime - t));

  // Check for burst pattern (multiple alerts within 5 minutes)
  const burstAlerts = timeDifferences.filter(diff => diff < 300000).length;
  if (burstAlerts > 3) {
    alertAnalysis.time_pattern = 'burst';
  }

  // Check for steady pattern (alerts spread over time)
  const avgTimeDiff = timeDifferences.reduce((sum, diff) => sum + diff, 0) / timeDifferences.length;
  if (avgTimeDiff > 600000 && burstAlerts === 0) {
    alertAnalysis.time_pattern = 'steady';
  }
}

// Analyze severity escalation
if (similarAlerts.length > 0) {
  const severities = similarAlerts.map(a => a.normalized_priority);
  const avgSeverity = severities.reduce((sum, sev) => sum + sev, 0) / severities.length;

  if (currentAlert.normalized_priority < avgSeverity) {
    alertAnalysis.severity_escalation = true;
  }
}

// Analyze geographic spread
const locations = similarAlerts.map(a => a.context?.location).filter(loc => loc && loc !== 'unknown');
const uniqueLocations = [...new Set(locations)];
if (uniqueLocations.length > 1) {
  if (uniqueLocations.length > 5) {
    alertAnalysis.geographic_spread = 'widespread';
  } else if (uniqueLocations.length > 2) {
    alertAnalysis.geographic_spread = 'multi_site';
  } else {
    alertAnalysis.geographic_spread = 'local';
  }
}

// Analyze device type patterns
const deviceTypes = similarAlerts.map(a => a.context?.device_type).filter(type => type && type !== 'unknown');
const uniqueDeviceTypes = [...new Set(deviceTypes)];
if (uniqueDeviceTypes.length > 1) {
  alertAnalysis.device_type_pattern = 'multi_device_type';
} else if (uniqueDeviceTypes.length === 1) {
  alertAnalysis.device_type_pattern = 'single_device_type';
}

// Determine correlation patterns
const correlationPatterns = [];

// Check for cascade failure pattern
if (alertAnalysis.time_pattern === 'burst' && 
    alertAnalysis.geographic_spread === 'widespread' && 
    currentAlert.category === 'device_health') {
  correlationPatterns.push({
    pattern: 'cascade_failure',
    confidence: 0.85,
    description: 'Multiple devices failing in rapid succession across different locations',
    severity_multiplier: 2.0
  });
}

// Check for network partition pattern
if (alertAnalysis.time_pattern === 'burst' && 
    currentAlert.category === 'connectivity' && 
    alertAnalysis.geographic_spread === 'multi_site') {
  correlationPatterns.push({
    pattern: 'network_partition',
    confidence: 0.75,
    description: 'Network connectivity issues affecting multiple sites',
    severity_multiplier: 1.5
  });
}

// Check for performance degradation pattern
if (alertAnalysis.time_pattern === 'steady' && 
    currentAlert.category === 'performance' && 
    alertAnalysis.similar_alerts_count > 10) {
  correlationPatterns.push({
    pattern: 'performance_degradation',
    confidence: 0.70,
    description: 'Gradual performance degradation across multiple network points',
    severity_multiplier: 1.3
  });
}

// Check for security incident pattern
if (currentAlert.category === 'security' && 
    alertAnalysis.similar_alerts_count > 0) {
  correlationPatterns.push({
    pattern: 'security_incident',
    confidence: 0.90,
    description: 'Coordinated security event affecting multiple systems',
    severity_multiplier: 3.0
  });
}

// Calculate adjusted priority based on correlation
let adjustedPriority = currentAlert.normalized_priority;
let adjustmentReason = 'no_adjustment';

if (correlationPatterns.length > 0) {
  const maxSeverityMultiplier = Math.max(...correlationPatterns.map(p => p.severity_multiplier));
  const originalPriority = adjustedPriority;
  adjustedPriority = Math.max(1, Math.round(adjustedPriority / maxSeverityMultiplier));

  if (adjustedPriority < originalPriority) {
    adjustmentReason = 'escalated_due_to_correlation';
  }
}

// Check for alert suppression
let suppressAlert = false;
let suppressionReason = 'none';

if (alertAnalysis.duplicate_alerts > currentAlert.correlation_data.suppression_threshold) {
  suppressAlert = true;
  suppressionReason = 'duplicate_threshold_exceeded';
}

// Determine business impact
let businessImpact = 'low';
let impactReason = 'standard_assessment';

if (correlationPatterns.some(p => p.pattern === 'cascade_failure')) {
  businessImpact = 'critical';
  impactReason = 'cascade_failure_detected';
} else if (correlationPatterns.some(p => p.pattern === 'security_incident')) {
  businessImpact = 'critical';
  impactReason = 'security_incident_detected';
} else if (correlationPatterns.some(p => p.pattern === 'network_partition')) {
  businessImpact = 'high';
  impactReason = 'network_partition_detected';
} else if (adjustedPriority <= 2) {
  businessImpact = 'medium';
  impactReason = 'high_priority_alert';
}

// Estimate affected users
let affectedUsersEstimate = 0;
if (currentAlert.context.location !== 'unknown') {
  // Simplified estimation based on location and device type
  const locationMultiplier = {
    'headquarters': 500,
    'main_office': 300,
    'branch_office': 100,
    'remote_site': 50,
    'data_center': 1000
  };

  const deviceMultiplier = {
    'core_switch': 0.8,
    'distribution_switch': 0.6,
    'access_switch': 0.3,
    'wireless_controller': 0.7,
    'access_point': 0.2,
    'gateway': 0.9
  };

  const basePeople = locationMultiplier[currentAlert.context.location] || 100;
  const deviceImpact = deviceMultiplier[currentAlert.context.device_type] || 0.1;

  affectedUsersEstimate = Math.round(basePeople * deviceImpact);

  // Apply correlation multiplier
  if (correlationPatterns.length > 0) {
    const correlationMultiplier = Math.max(...correlationPatterns.map(p => p.severity_multiplier));
    affectedUsersEstimate = Math.round(affectedUsersEstimate * correlationMultiplier);
  }
}

// Generate enhanced recommended actions
const enhancedActions = [...currentAlert.enrichment.recommended_actions];

// Add correlation-based actions
if (correlationPatterns.some(p => p.pattern === 'cascade_failure')) {
  enhancedActions.unshift({
    action: 'activate_incident_response',
    description: 'Activate incident response team for cascade failure',
    priority: 'critical',
    estimated_time: '5-10 minutes',
    automation_available: true
  });
}

if (correlationPatterns.some(p => p.pattern === 'security_incident')) {
  enhancedActions.unshift({
    action: 'security_incident_response',
    description: 'Initiate security incident response procedures',
    priority: 'critical',
    estimated_time: '2-5 minutes',
    automation_available: true
  });
}

if (alertAnalysis.similar_alerts_count > 10) {
  enhancedActions.push({
    action: 'analyze_root_cause',
    description: 'Perform root cause analysis for recurring issue',
    priority: 'high',
    estimated_time: '15-30 minutes',
    automation_available: false
  });
}

// Create final correlated alert
const correlatedAlert = {
  ...currentAlert,
  correlation_analysis: alertAnalysis,
  correlation_patterns: correlationPatterns,
  adjusted_priority: adjustedPriority,
  priority_adjustment_reason: adjustmentReason,
  business_impact: businessImpact,
  business_impact_reason: impactReason,
  affected_users_estimate: affectedUsersEstimate,
  suppress_alert: suppressAlert,
  suppression_reason: suppressionReason,
  enrichment: {
    ...currentAlert.enrichment,
    similar_alerts_count: alertAnalysis.similar_alerts_count,
    business_impact: businessImpact,
    recommended_actions: enhancedActions,
    escalation_required: adjustedPriority < currentAlert.normalized_priority,
    auto_remediation_available: enhancedActions.some(a => a.automation_available)
  },
  correlation_metadata: {
    correlation_completed_at: new Date().toISOString(),
    correlation_confidence: correlationPatterns.length > 0 ? Math.max(...correlationPatterns.map(p => p.confidence)) : 0,
    patterns_detected: correlationPatterns.length,
    similar_alerts_analyzed: similarAlerts.length
  }
};

return [{ json: correlatedAlert }];

