// Device Health Monitoring Configuration
// Monitors CPU, Memory, Temperature, Link Status, and Performance Metrics

const monitoringConfig = {
  // Device types to monitor
  device_types: ['switch', 'access_point', 'gateway', 'controller', 'sensor'],

  // Health check intervals
  intervals: {
    critical_devices: '1m',
    standard_devices: '5m',
    low_priority_devices: '15m'
  },

  // Health thresholds
  thresholds: {
    cpu: {
      warning: 70,
      critical: 85,
      unit: 'percentage'
    },
    memory: {
      warning: 75,
      critical: 90,
      unit: 'percentage'
    },
    temperature: {
      warning: 60,
      critical: 75,
      unit: 'celsius'
    },
    disk_usage: {
      warning: 80,
      critical: 95,
      unit: 'percentage'
    },
    uptime: {
      minimum: 300,
      unit: 'seconds'
    },
    link_utilization: {
      warning: 80,
      critical: 95,
      unit: 'percentage'
    },
    packet_loss: {
      warning: 1,
      critical: 5,
      unit: 'percentage'
    },
    latency: {
      warning: 100,
      critical: 500,
      unit: 'milliseconds'
    }
  },

  // Device priorities
  device_priorities: {
    'core_switch': 'critical',
    'distribution_switch': 'high',
    'access_switch': 'medium',
    'wireless_controller': 'critical',
    'access_point': 'medium',
    'gateway': 'critical',
    'sensor': 'low'
  },

  // Notification settings
  notifications: {
    immediate_alerts: ['critical'],
    batch_alerts: ['warning'],
    suppression_time: 300, // 5 minutes
    escalation_time: 1800  // 30 minutes
  },

  timestamp: new Date().toISOString()
};

// API endpoints for different device types
const apiEndpoints = {
  aruba_central: {
    base_url: 'https://apigw-uswest4.central.arubanetworks.com',
    devices: '/api/v2/devices',
    monitoring: '/api/v2/monitoring',
    alerts: '/api/v2/alerts'
  },
  aos_cx: {
    base_url: 'https://switch-ip:443',
    system: '/rest/v10.08/system',
    interfaces: '/rest/v10.08/system/interfaces',
    monitoring: '/rest/v10.08/monitoring'
  },
  edgeconnect: {
    base_url: 'https://orchestrator.example.com',
    appliances: '/api/v1/appliances',
    health: '/api/v1/health',
    metrics: '/api/v1/metrics'
  },
  uxi: {
    base_url: 'https://api.uxi.aruba.com',
    sensors: '/api/v1/sensors',
    tests: '/api/v1/tests',
    metrics: '/api/v1/metrics'
  }
};

// Generate monitoring tasks
const monitoringTasks = [
  {
    task_id: 'central_devices',
    type: 'aruba_central',
    description: 'Monitor Aruba Central managed devices',
    endpoint: apiEndpoints.aruba_central.devices,
    priority: 'high',
    interval: monitoringConfig.intervals.critical_devices
  },
  {
    task_id: 'switches_health',
    type: 'aos_cx',
    description: 'Monitor AOS-CX switch health',
    endpoint: apiEndpoints.aos_cx.system,
    priority: 'critical',
    interval: monitoringConfig.intervals.critical_devices
  },
  {
    task_id: 'wan_appliances',
    type: 'edgeconnect',
    description: 'Monitor EdgeConnect appliances',
    endpoint: apiEndpoints.edgeconnect.appliances,
    priority: 'high',
    interval: monitoringConfig.intervals.standard_devices
  },
  {
    task_id: 'uxi_sensors',
    type: 'uxi',
    description: 'Monitor UXI sensors',
    endpoint: apiEndpoints.uxi.sensors,
    priority: 'medium',
    interval: monitoringConfig.intervals.low_priority_devices
  }
];

return [{
  json: {
    monitoring_config: monitoringConfig,
    api_endpoints: apiEndpoints,
    monitoring_tasks: monitoringTasks,
    execution_time: new Date().toISOString()
  }
}];

// Process and analyze device health data
const devices = $input.first()?.json?.devices || [];
const stats = $input.all()[1]?.json?.stats || [];
const alerts = $input.all()[2]?.json?.alerts || [];

const thresholds = {
  cpu: { warning: 70, critical: 85 },
  memory: { warning: 75, critical: 90 },
  temperature: { warning: 60, critical: 75 },
  uptime: { minimum: 300 }
};

// Device health analysis
const deviceHealth = devices.map(device => {
  const deviceStats = stats.find(s => s.device_id === device.serial) || {};
  const deviceAlerts = alerts.filter(a => a.device_id === device.serial);

  // Calculate health score (0-100)
  let healthScore = 100;
  const issues = [];

  // CPU check
  if (deviceStats.cpu_usage > thresholds.cpu.critical) {
    healthScore -= 30;
    issues.push({ type: 'cpu', severity: 'critical', value: deviceStats.cpu_usage });
  } else if (deviceStats.cpu_usage > thresholds.cpu.warning) {
    healthScore -= 15;
    issues.push({ type: 'cpu', severity: 'warning', value: deviceStats.cpu_usage });
  }

  // Memory check
  if (deviceStats.memory_usage > thresholds.memory.critical) {
    healthScore -= 25;
    issues.push({ type: 'memory', severity: 'critical', value: deviceStats.memory_usage });
  } else if (deviceStats.memory_usage > thresholds.memory.warning) {
    healthScore -= 10;
    issues.push({ type: 'memory', severity: 'warning', value: deviceStats.memory_usage });
  }

  // Temperature check
  if (deviceStats.temperature > thresholds.temperature.critical) {
    healthScore -= 20;
    issues.push({ type: 'temperature', severity: 'critical', value: deviceStats.temperature });
  } else if (deviceStats.temperature > thresholds.temperature.warning) {
    healthScore -= 10;
    issues.push({ type: 'temperature', severity: 'warning', value: deviceStats.temperature });
  }

  // Uptime check
  if (deviceStats.uptime < thresholds.uptime.minimum) {
    healthScore -= 15;
    issues.push({ type: 'uptime', severity: 'warning', value: deviceStats.uptime });
  }

  // Active alerts impact
  deviceAlerts.forEach(alert => {
    if (alert.severity === 'critical') {
      healthScore -= 20;
    } else if (alert.severity === 'high') {
      healthScore -= 10;
    }
  });

  // Ensure health score doesn't go below 0
  healthScore = Math.max(0, healthScore);

  // Determine overall health status
  let healthStatus = 'healthy';
  if (healthScore < 30) {
    healthStatus = 'critical';
  } else if (healthScore < 60) {
    healthStatus = 'warning';
  } else if (healthScore < 90) {
    healthStatus = 'degraded';
  }

  return {
    device_id: device.serial,
    device_name: device.name,
    device_type: device.type,
    model: device.model,
    site: device.site,
    health_score: healthScore,
    health_status: healthStatus,
    issues: issues,
    stats: {
      cpu_usage: deviceStats.cpu_usage || 0,
      memory_usage: deviceStats.memory_usage || 0,
      temperature: deviceStats.temperature || 0,
      uptime: deviceStats.uptime || 0
    },
    active_alerts: deviceAlerts.length,
    last_seen: deviceStats.last_seen || device.last_modified,
    online: device.status === 'Up'
  };
});

// Generate summary statistics
const summary = {
  total_devices: deviceHealth.length,
  healthy_devices: deviceHealth.filter(d => d.health_status === 'healthy').length,
  degraded_devices: deviceHealth.filter(d => d.health_status === 'degraded').length,
  warning_devices: deviceHealth.filter(d => d.health_status === 'warning').length,
  critical_devices: deviceHealth.filter(d => d.health_status === 'critical').length,
  offline_devices: deviceHealth.filter(d => !d.online).length,
  average_health_score: deviceHealth.reduce((sum, d) => sum + d.health_score, 0) / deviceHealth.length || 0,
  total_issues: deviceHealth.reduce((sum, d) => sum + d.issues.length, 0),
  total_alerts: deviceHealth.reduce((sum, d) => sum + d.active_alerts, 0)
};

// Identify devices requiring immediate attention
const criticalDevices = deviceHealth.filter(d => d.health_status === 'critical');
const warningDevices = deviceHealth.filter(d => d.health_status === 'warning');
const offlineDevices = deviceHealth.filter(d => !d.online);

// Generate recommendations
const recommendations = [];

if (criticalDevices.length > 0) {
  recommendations.push({
    priority: 'high',
    action: 'immediate_attention',
    description: `${criticalDevices.length} devices require immediate attention`,
    devices: criticalDevices.map(d => d.device_name)
  });
}

if (warningDevices.length > 0) {
  recommendations.push({
    priority: 'medium',
    action: 'scheduled_maintenance',
    description: `${warningDevices.length} devices should be scheduled for maintenance`,
    devices: warningDevices.map(d => d.device_name)
  });
}

if (offlineDevices.length > 0) {
  recommendations.push({
    priority: 'high',
    action: 'connectivity_check',
    description: `${offlineDevices.length} devices are offline and need connectivity verification`,
    devices: offlineDevices.map(d => d.device_name)
  });
}

// Check for trending issues
const issueTypes = {};
deviceHealth.forEach(device => {
  device.issues.forEach(issue => {
    if (!issueTypes[issue.type]) {
      issueTypes[issue.type] = { count: 0, severity: [] };
    }
    issueTypes[issue.type].count++;
    issueTypes[issue.type].severity.push(issue.severity);
  });
});

Object.keys(issueTypes).forEach(issueType => {
  if (issueTypes[issueType].count > 5) {
    recommendations.push({
      priority: 'medium',
      action: 'investigate_trending',
      description: `${issueType} issues detected on ${issueTypes[issueType].count} devices - investigate trending problem`,
      issue_type: issueType
    });
  }
});

return [{
  json: {
    timestamp: new Date().toISOString(),
    summary: summary,
    device_health: deviceHealth,
    critical_devices: criticalDevices,
    warning_devices: warningDevices,
    offline_devices: offlineDevices,
    recommendations: recommendations,
    issue_trends: issueTypes,
    monitoring_interval: '5m',
    next_check: new Date(Date.now() + 300000).toISOString()
  }
}];

