// Generate automated remediation actions for critical issues
const criticalDevices = $json.critical_devices || [];
const warningDevices = $json.warning_devices || [];
const offlineDevices = $json.offline_devices || [];

const remediationActions = [];

// Process critical devices
criticalDevices.forEach(device => {
  device.issues.forEach(issue => {
    let action = null;

    switch (issue.type) {
      case 'cpu':
        if (issue.severity === 'critical') {
          action = {
            device_id: device.device_id,
            device_name: device.device_name,
            action_type: 'restart_high_cpu_processes',
            priority: 'high',
            description: `Restart high CPU processes on ${device.device_name}`,
            api_call: {
              endpoint: '/api/v2/devices/' + device.device_id + '/commands',
              method: 'POST',
              payload: {
                command: 'restart_processes',
                parameters: { cpu_threshold: 80 }
              }
            }
          };
        }
        break;

      case 'memory':
        if (issue.severity === 'critical') {
          action = {
            device_id: device.device_id,
            device_name: device.device_name,
            action_type: 'clear_memory_cache',
            priority: 'high',
            description: `Clear memory cache on ${device.device_name}`,
            api_call: {
              endpoint: '/api/v2/devices/' + device.device_id + '/commands',
              method: 'POST',
              payload: {
                command: 'clear_cache',
                parameters: { cache_type: 'memory' }
              }
            }
          };
        }
        break;

      case 'temperature':
        if (issue.severity === 'critical') {
          action = {
            device_id: device.device_id,
            device_name: device.device_name,
            action_type: 'increase_fan_speed',
            priority: 'high',
            description: `Increase fan speed on ${device.device_name}`,
            api_call: {
              endpoint: '/api/v2/devices/' + device.device_id + '/commands',
              method: 'POST',
              payload: {
                command: 'set_fan_speed',
                parameters: { speed: 'maximum' }
              }
            }
          };
        }
        break;
    }

    if (action) {
      remediationActions.push(action);
    }
  });
});

// Process offline devices
offlineDevices.forEach(device => {
  remediationActions.push({
    device_id: device.device_id,
    device_name: device.device_name,
    action_type: 'connectivity_check',
    priority: 'high',
    description: `Perform connectivity check for ${device.device_name}`,
    api_call: {
      endpoint: '/api/v2/devices/' + device.device_id + '/ping',
      method: 'POST',
      payload: {
        count: 5,
        timeout: 10
      }
    }
  });
});

// Process warning devices for preventive actions
warningDevices.forEach(device => {
  const highCpuIssues = device.issues.filter(i => i.type === 'cpu' && i.severity === 'warning');
  const highMemoryIssues = device.issues.filter(i => i.type === 'memory' && i.severity === 'warning');

  if (highCpuIssues.length > 0) {
    remediationActions.push({
      device_id: device.device_id,
      device_name: device.device_name,
      action_type: 'schedule_maintenance',
      priority: 'medium',
      description: `Schedule maintenance for ${device.device_name} - High CPU usage`,
      api_call: {
        endpoint: '/api/v2/maintenance/schedule',
        method: 'POST',
        payload: {
          device_id: device.device_id,
          maintenance_type: 'cpu_optimization',
          schedule: 'next_maintenance_window'
        }
      }
    });
  }

  if (highMemoryIssues.length > 0) {
    remediationActions.push({
      device_id: device.device_id,
      device_name: device.device_name,
      action_type: 'memory_optimization',
      priority: 'medium',
      description: `Optimize memory usage on ${device.device_name}`,
      api_call: {
        endpoint: '/api/v2/devices/' + device.device_id + '/optimize',
        method: 'POST',
        payload: {
          optimization_type: 'memory',
          aggressive: false
        }
      }
    });
  }
});

// Generate summary
const summary = {
  total_actions: remediationActions.length,
  high_priority_actions: remediationActions.filter(a => a.priority === 'high').length,
  medium_priority_actions: remediationActions.filter(a => a.priority === 'medium').length,
  action_types: {
    restart_processes: remediationActions.filter(a => a.action_type === 'restart_high_cpu_processes').length,
    clear_cache: remediationActions.filter(a => a.action_type === 'clear_memory_cache').length,
    increase_fan_speed: remediationActions.filter(a => a.action_type === 'increase_fan_speed').length,
    connectivity_check: remediationActions.filter(a => a.action_type === 'connectivity_check').length,
    schedule_maintenance: remediationActions.filter(a => a.action_type === 'schedule_maintenance').length,
    memory_optimization: remediationActions.filter(a => a.action_type === 'memory_optimization').length
  }
};

return [{
  json: {
    timestamp: new Date().toISOString(),
    remediation_actions: remediationActions,
    summary: summary,
    auto_execute: false, // Set to true for automatic execution
    requires_approval: true
  }
}];
