// Network Service Monitoring Input Validation
const input = items[0].json;

// Required fields
const requiredFields = ['operation', 'service_type'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations
const validOperations = [
  'start_monitoring', 'stop_monitoring', 'get_metrics', 'get_health',
  'set_thresholds', 'get_alerts', 'acknowledge_alert', 'get_performance',
  'get_capacity', 'get_availability', 'create_dashboard', 'export_metrics'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}`);
}

// Valid service types
const validServiceTypes = ['gateway', 'tunnel', 'api_service', 'network_interface', 'application'];
if (!validServiceTypes.includes(input.service_type)) {
  throw new Error(`Invalid service type: ${input.service_type}`);
}

// Prepare validated request
const validatedInput = {
  ...input,
  timestamp: new Date().toISOString(),
  request_id: `mon-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed'
};

return [{ json: validatedInput }];

// Process monitoring operation results
const input = items[0].json;
const originalRequest = $('Validate Monitoring Request').first().json;

let result = {
  request_id: originalRequest.request_id,
  operation: originalRequest.operation,
  service_type: originalRequest.service_type,
  timestamp: new Date().toISOString(),
  status: 'success',
  message: 'Monitoring operation completed successfully'
};

try {
  if (input.error) {
    result.status = 'error';
    result.message = input.error;
    result.error_details = input;
  } else {
    result.data = input;

    // Operation-specific processing
    switch (originalRequest.operation) {
      case 'get_metrics':
        result.message = `Metrics collected for ${originalRequest.service_type}`;
        result.metrics_count = input.metrics ? input.metrics.length : 0;
        result.time_range = `${originalRequest.start_time || 'last hour'} to ${originalRequest.end_time || 'now'}`;

        // Check for performance anomalies
        if (input.metrics) {
          const alerts = [];
          input.metrics.forEach(metric => {
            if (metric.cpu_usage > 85) alerts.push(`High CPU: ${metric.cpu_usage}%`);
            if (metric.memory_usage > 90) alerts.push(`High Memory: ${metric.memory_usage}%`);
            if (metric.latency > 100) alerts.push(`High Latency: ${metric.latency}ms`);
          });
          if (alerts.length > 0) {
            result.alerts = alerts;
            result.status = 'warning';
          }
        }
        break;

      case 'get_health':
        result.message = `Health check completed for ${originalRequest.service_type}`;
        result.health_status = input.status || 'unknown';
        result.health_score = input.health_score || 0;

        if (input.status === 'unhealthy') {
          result.status = 'critical';
          result.health_issues = input.issues || [];
        }
        break;

      case 'get_performance':
        result.message = `Performance analysis completed for ${originalRequest.service_type}`;
        result.performance_score = input.performance_score || 0;
        result.trends = input.trends || {};

        if (input.performance_score < 70) {
          result.status = 'warning';
          result.message += ' - Performance below acceptable threshold';
        }
        break;

      case 'set_thresholds':
        result.message = `Monitoring thresholds updated for ${originalRequest.service_type}`;
        result.thresholds_set = input.thresholds_count || 0;
        break;

      case 'get_alerts':
        result.message = `Retrieved ${input.alerts ? input.alerts.length : 0} alerts`;
        result.alert_count = input.alerts ? input.alerts.length : 0;
        result.critical_alerts = input.alerts ? input.alerts.filter(a => a.severity === 'critical').length : 0;
        break;

      case 'acknowledge_alert':
        result.message = `Alert ${originalRequest.alert_id} acknowledged successfully`;
        result.acknowledged_by = originalRequest.acknowledged_by;
        break;

      default:
        result.message = `${originalRequest.operation} completed successfully`;
    }

    // Add capacity information if available
    if (input.capacity_info) {
      result.capacity_utilization = input.capacity_info.utilization_percentage;
      if (input.capacity_info.utilization_percentage > 80) {
        result.capacity_alerts = [`Capacity utilization at ${input.capacity_info.utilization_percentage}%`];
      }
    }
  }
} catch (error) {
  result.status = 'error';
  result.message = 'Error processing monitoring operation result';
  result.error_details = error.message;
}

return [{ json: result }];

