// Create monitoring dashboard data
const result = items[0].json.result;
const originalRequest = $('Validate Monitoring Request').first().json;

// Dashboard configuration
const dashboard = {
  dashboard_id: `dash-${Date.now()}`,
  title: `${result.service_type} Monitoring Dashboard`,
  created: new Date().toISOString(),
  service_type: result.service_type,
  service_id: originalRequest.service_id,

  // Key metrics summary
  summary: {
    overall_status: result.status,
    health_score: result.health_score || null,
    performance_score: result.performance_score || null,
    alert_count: result.alert_count || 0,
    critical_alerts: result.critical_alerts || 0,
    capacity_utilization: result.capacity_utilization || null
  },

  // Dashboard widgets
  widgets: [
    {
      type: 'status_indicator',
      title: 'Service Status',
      value: result.status,
      color: result.status === 'success' ? 'green' : 
             result.status === 'warning' ? 'yellow' : 'red'
    },
    {
      type: 'metric_chart',
      title: 'Performance Trends',
      data: result.trends || {},
      time_range: result.time_range || 'Last Hour'
    },
    {
      type: 'alert_list',
      title: 'Active Alerts',
      alerts: result.alerts || [],
      count: result.alert_count || 0
    }
  ],

  // Refresh settings
  refresh_interval: originalRequest.monitoring_interval || 60,
  auto_refresh: true,

  // Export options
  export_formats: ['json', 'csv', 'pdf'],

  // Sharing settings
  share_url: `https://monitoring.company.com/dashboard/${result.service_type}/${originalRequest.service_id || 'all'}`,
  embed_code: `<iframe src="https://monitoring.company.com/embed/${result.service_type}" width="800" height="600"></iframe>`
};

// Add capacity widget if data available
if (result.capacity_utilization) {
  dashboard.widgets.push({
    type: 'capacity_gauge',
    title: 'Capacity Utilization',
    value: result.capacity_utilization,
    threshold: 80,
    max_value: 100
  });
}

// Add health score widget if available
if (result.health_score) {
  dashboard.widgets.push({
    type: 'health_score',
    title: 'Health Score',
    value: result.health_score,
    threshold: 70,
    max_value: 100
  });
}

console.log('Generated Monitoring Dashboard:', JSON.stringify(dashboard, null, 2));

return [{ json: dashboard }];
