// ServiceNow Asset Management Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['operation'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations for asset management
const validOperations = [
  'sync_aruba_devices', 'create_ci', 'update_ci', 'discover_devices',
  'map_relationships', 'update_inventory', 'decommission_asset',
  'bulk_sync', 'get_asset_details', 'validate_config', 'audit_assets',
  'update_warranty'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}`);
}

// Operation-specific validation
if (['create_ci', 'update_ci'].includes(input.operation)) {
  if (!input.device_serial && !input.asset_tag) {
    throw new Error('CI operations require device_serial or asset_tag');
  }
}

if (input.operation === 'map_relationships') {
  if (!input.parent_ci && !input.child_ci) {
    throw new Error('Relationship mapping requires parent_ci or child_ci');
  }
}

// Set defaults and prepare request
const validatedInput = {
  ...input,
  timestamp: new Date().toISOString(),
  request_id: `asset-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed',
  source_system: 'aruba_central'
};

return [{ json: validatedInput }];

// Discover and classify Aruba devices for ServiceNow
const input = items[0].json;
const discoveredDevices = [];

// Mock device discovery logic (replace with actual Aruba API calls)
const deviceTypes = {
  'access_point': {
    category: 'Network Gear',
    subcategory: 'Wireless Access Point',
    class: 'cmdb_ci_wireless_access_point'
  },
  'switch': {
    category: 'Network Gear',
    subcategory: 'Network Switch',
    class: 'cmdb_ci_network_gear'
  },
  'gateway': {
    category: 'Network Gear',
    subcategory: 'Network Gateway',
    class: 'cmdb_ci_network_gear'
  },
  'controller': {
    category: 'Network Gear',
    subcategory: 'Wireless Controller',
    class: 'cmdb_ci_wireless_lan_controller'
  }
};

// Process discovered devices
if (input.devices && Array.isArray(input.devices)) {
  input.devices.forEach(device => {
    const deviceInfo = deviceTypes[device.device_type] || deviceTypes['switch'];

    discoveredDevices.push({
      name: device.name || device.hostname,
      serial_number: device.serial,
      model_id: device.model,
      manufacturer: 'HPE Aruba',
      ip_address: device.ip_address,
      mac_address: device.mac_address,
      location: device.site || device.location,
      category: deviceInfo.category,
      subcategory: deviceInfo.subcategory,
      sys_class_name: deviceInfo.class,
      operational_status: device.status === 'Up' ? '1' : '2',
      install_status: '1',
      u_aruba_device_id: device.device_id,
      u_aruba_group: device.group_name,
      u_aruba_firmware: device.firmware_version,
      u_device_type: device.device_type,
      u_last_discovery: new Date().toISOString(),
      discovery_source: 'aruba_central'
    });
  });
}

// Return discovery results
const result = {
  operation: 'discover_devices',
  timestamp: new Date().toISOString(),
  discovered_count: discoveredDevices.length,
  devices: discoveredDevices,
  discovery_summary: {
    access_points: discoveredDevices.filter(d => d.u_device_type === 'access_point').length,
    switches: discoveredDevices.filter(d => d.u_device_type === 'switch').length,
    gateways: discoveredDevices.filter(d => d.u_device_type === 'gateway').length,
    controllers: discoveredDevices.filter(d => d.u_device_type === 'controller').length
  }
};

return [{ json: result }];

