// Add compliance status
auditResults.compliance_status = auditResults.compliance_score >= 95 ? 'excellent' :
                                auditResults.compliance_score >= 90 ? 'good' :
                                auditResults.compliance_score >= 80 ? 'fair' : 'poor';

return [{ json: auditResults }];

// Process asset management operation results
const input = items[0].json;
const originalRequest = $('Validate Asset Request').first().json;

let result = {
  request_id: originalRequest.request_id,
  operation: originalRequest.operation,
  timestamp: new Date().toISOString(),
  status: 'success',
  message: 'Asset management operation completed successfully',
  source_system: 'aruba_central',
  target_system: 'servicenow'
};

try {
  if (input.error || input.result?.error) {
    result.status = 'error';
    result.message = input.error || input.result.error;
    result.error_details = input;
  } else {
    result.data = input;

    // Operation-specific processing
    switch (originalRequest.operation) {
      case 'sync_aruba_devices':
        result.message = `Device synchronization completed successfully`;
        result.devices_processed = input.devices ? input.devices.length : 0;
        result.sync_summary = {
          total_devices: input.devices?.length || 0,
          successful_syncs: input.successful_syncs || 0,
          failed_syncs: input.failed_syncs || 0
        };
        break;

      case 'create_ci':
        result.message = `Configuration Item created successfully`;
        result.ci_sys_id = input.result?.sys_id || input.sys_id;
        result.ci_number = input.result?.number || input.number;
        break;

      case 'update_ci':
        result.message = `Configuration Item updated successfully`;
        result.ci_sys_id = originalRequest.sys_id;
        result.updated_fields = Object.keys(input.result || input);
        break;

      case 'discover_devices':
        result.message = `Device discovery completed successfully`;
        result.discovered_count = input.discovered_count || 0;
        result.discovery_summary = input.discovery_summary || {};
        break;

      case 'map_relationships':
        result.message = `Relationship mapping completed successfully`;
        result.relationship_sys_id = input.result?.sys_id || input.sys_id;
        result.parent_ci = originalRequest.parent_ci;
        result.child_ci = originalRequest.child_ci;
        break;

      case 'update_inventory':
        result.message = `Inventory update completed successfully`;
        result.asset_sys_id = input.result?.sys_id || originalRequest.asset_sys_id;
        result.inventory_changes = input.changes || [];
        break;

      case 'audit_assets':
        result.message = `Asset audit completed successfully`;
        result.compliance_score = input.compliance_score || 0;
        result.compliance_status = input.compliance_status || 'unknown';
        result.findings_count = input.findings ? input.findings.length : 0;
        result.recommendations_count = input.recommendations ? input.recommendations.length : 0;

        // Set status based on compliance score
        if (input.compliance_score < 80) {
          result.status = 'warning';
          result.message += ` - Compliance score below threshold (${input.compliance_score}%)`;
        }
        break;

      case 'bulk_sync':
        result.message = `Bulk synchronization completed successfully`;
        result.bulk_summary = {
          total_operations: input.total_operations || 0,
          successful_operations: input.successful_operations || 0,
          failed_operations: input.failed_operations || 0
        };
        break;

      default:
        result.message = `${originalRequest.operation} completed successfully`;
    }

    // Add ServiceNow response details if available
    if (input.result) {
      result.servicenow_response = {
        sys_id: input.result.sys_id,
        number: input.result.number,
        state: input.result.state,
        updated: input.result.sys_updated_on
      };
    }

    // Add synchronization metrics
    result.sync_metrics = {
      operation_duration: Date.now() - new Date(originalRequest.timestamp).getTime(),
      api_calls_made: input.api_calls_made || 1,
      data_volume: JSON.stringify(input).length
    };
  }
} catch (error) {
  result.status = 'error';
  result.message = 'Error processing asset management operation result';
  result.error_details = error.message;
}

return [{ json: result }];

