// ServiceNow Incident Management Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['operation', 'incident_type'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations
const validOperations = [
  'create_incident', 'update_incident', 'close_incident', 'assign_incident',
  'add_work_notes', 'add_attachment', 'get_incident', 'query_incidents',
  'escalate_incident', 'reopen_incident'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}`);
}

// Valid incident types
const validIncidentTypes = ['network', 'security', 'hardware', 'software', 'performance'];
if (!validIncidentTypes.includes(input.incident_type)) {
  throw new Error(`Invalid incident type: ${input.incident_type}`);
}

// Operation-specific validation
if (input.operation === 'create_incident') {
  if (!input.short_description) {
    throw new Error('create_incident requires short_description');
  }
  if (!input.device_name && !input.description) {
    throw new Error('create_incident requires either device_name or description');
  }
}

// Set defaults and prepare request
const validatedInput = {
  ...input,
  timestamp: new Date().toISOString(),
  request_id: `snow-inc-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed',

  // Set ServiceNow defaults
  priority: input.priority || '3',
  impact: input.impact || '2',
  urgency: input.urgency || '2',
  category: input.category || 'Network',
  subcategory: input.subcategory || (input.incident_type === 'network' ? 'Wireless' : 'General'),
  assignment_group: input.assignment_group || 'Network Operations',
  caller_id: input.caller_id || 'aruba.automation@company.com'
};

return [{ json: validatedInput }];

