parameters.functionCode:
// Process ServiceNow incident operation results
const input = items[0].json;
const originalRequest = $('Validate Incident Request').first().json;

let result = {
  request_id: originalRequest.request_id,
  operation: originalRequest.operation,
  incident_type: originalRequest.incident_type,
  timestamp: new Date().toISOString(),
  status: 'success',
  message: 'ServiceNow incident operation completed successfully'
};

try {
  if (input.error) {
    result.status = 'error';
    result.message = input.error.message || input.error;
    result.error_details = input.error;
  } else {
    result.data = input;

    // Operation-specific processing
    switch (originalRequest.operation) {
      case 'create_incident':
        result.message = `Incident created successfully: ${input.result?.number || 'Unknown'}`;
        result.incident_number = input.result?.number;
        result.incident_sys_id = input.result?.sys_id;
        result.incident_state = input.result?.state;
        break;

      case 'update_incident':
        result.message = `Incident ${originalRequest.incident_id} updated successfully`;
        result.incident_sys_id = originalRequest.incident_id;
        result.updated_fields = Object.keys(input.result || {});
        break;

      case 'query_incidents':
        result.message = `Found ${input.result?.length || 0} incidents`;
        result.incident_count = input.result?.length || 0;
        result.incidents = input.result?.map(inc => ({
          number: inc.number,
          sys_id: inc.sys_id,
          short_description: inc.short_description,
          state: inc.state,
          priority: inc.priority
        })) || [];
        break;

      case 'add_work_notes':
        result.message = `Work notes added to incident ${originalRequest.incident_id}`;
        result.incident_sys_id = originalRequest.incident_id;
        result.journal_entry = input.result?.sys_id;
        break;

      case 'assign_incident':
        result.message = `Incident ${originalRequest.incident_id} assigned to ${originalRequest.assignment_group}`;
        result.incident_sys_id = originalRequest.incident_id;
        result.assignment_group = originalRequest.assignment_group;
        result.assigned_to = originalRequest.assigned_to;
        break;

      case 'close_incident':
        result.message = `Incident ${originalRequest.incident_id} closed successfully`;
        result.incident_sys_id = originalRequest.incident_id;
        result.resolution_code = originalRequest.resolution_code;
        break;

      default:
        result.message = `${originalRequest.operation} completed successfully`;
    }

    // Add device information if available
    if (originalRequest.device_name) {
      result.device_info = {
        name: originalRequest.device_name,
        type: originalRequest.device_type,
        ip: originalRequest.device_ip,
        location: originalRequest.location
      };
    }
  }
} catch (error) {
  result.status = 'error';
  result.message = 'Error processing ServiceNow incident operation result';
  result.error_details = error.message;
}

return [{ json: result }];

// Generate intelligent incident alerts
const result = items[0].json;
const originalRequest = $('Validate Incident Request').first().json;

let notifications = [];

// Determine alert level and channels
let alertLevel = 'info';
let channels = ['#servicenow-integration'];

if (result.status === 'error') {
  alertLevel = 'error';
  channels.push('#network-alerts');
} else if (originalRequest.priority === '1' || originalRequest.priority === '2') {
  alertLevel = 'warning';
  channels.push('#network-operations');
}

// Create notifications for each channel
channels.forEach(channel => {
  let icon = 'ðŸŽ«';
  if (alertLevel === 'error') icon = 'âŒ';
  else if (alertLevel === 'warning') icon = 'âš ï¸';
  else if (result.status === 'success') icon = 'âœ…';

  let message = `${icon} **ServiceNow Incident Operation**\\n`;
  message += `**Operation**: ${result.operation}\\n`;
  message += `**Status**: ${result.status.toUpperCase()}\\n`;
  message += `**Message**: ${result.message}\\n`;
  message += `**Time**: ${result.timestamp}\\n`;
  message += `**Request ID**: ${result.request_id}\\n`;

  // Add operation-specific details
  if (result.incident_number) {
    message += `**Incident Number**: ${result.incident_number}\\n`;
  }

  if (result.incident_sys_id) {
    message += `**Incident ID**: ${result.incident_sys_id}\\n`;
  }

  if (result.incident_count !== undefined) {
    message += `**Incidents Found**: ${result.incident_count}\\n`;
  }

  if (result.assignment_group) {
    message += `**Assigned To**: ${result.assignment_group}\\n`;
  }

  // Add device information
  if (result.device_info) {
    message += `\\n**Device Information**:\\n`;
    message += `â€¢ **Name**: ${result.device_info.name}\\n`;
    if (result.device_info.type) {
      message += `â€¢ **Type**: ${result.device_info.type}\\n`;
    }
    if (result.device_info.ip) {
      message += `â€¢ **IP**: ${result.device_info.ip}\\n`;
    }
    if (result.device_info.location) {
      message += `â€¢ **Location**: ${result.device_info.location}\\n`;
    }
  }

  // Add incident list for queries
  if (result.incidents && result.incidents.length > 0) {
    message += `\\n**Recent Incidents**:\\n`;
    result.incidents.slice(0, 5).forEach(incident => {
      message += `â€¢ ${incident.number}: ${incident.short_description}\\n`;
    });
    if (result.incidents.length > 5) {
      message += `â€¢ ... and ${result.incidents.length - 5} more\\n`;
    }
  }

  // Add error details if applicable
  if (result.status === 'error' && result.error_details) {
    message += `\\n**Error Details**: ${JSON.stringify(result.error_details)}\\n`;
  }

  notifications.push({
    json: {
      channel: channel,
      message: message,
      alert_level: alertLevel,
      result: result
    }
  });
});

return notifications;
