// ServiceNow Service Request Input Validation
const input = items[0].json;

// Required fields validation
const requiredFields = ['operation', 'request_type'];
for (const field of requiredFields) {
  if (!input[field]) {
    throw new Error(`Missing required field: ${field}`);
  }
}

// Valid operations
const validOperations = [
  'submit_request', 'get_request_status', 'update_request', 'cancel_request',
  'approve_request', 'reject_request', 'fulfill_request', 'get_catalog_items',
  'get_approval_status', 'add_request_comments'
];

if (!validOperations.includes(input.operation)) {
  throw new Error(`Invalid operation: ${input.operation}`);
}

// Valid request types
const validRequestTypes = ['network_access', 'hardware_provision', 'software_install', 'access_rights', 'network_config'];
if (!validRequestTypes.includes(input.request_type)) {
  throw new Error(`Invalid request type: ${input.request_type}`);
}

// Operation-specific validation
if (input.operation === 'submit_request') {
  if (!input.catalog_item && !input.short_description) {
    throw new Error('submit_request requires catalog_item or short_description');
  }
  if (!input.requested_for) {
    throw new Error('submit_request requires requested_for field');
  }
}

if (input.operation === 'approve_request' || input.operation === 'reject_request') {
  if (!input.request_id && !input.approval_id) {
    throw new Error('Approval operations require request_id or approval_id');
  }
}

// Set defaults and prepare request
const validatedInput = {
  ...input,
  timestamp: new Date().toISOString(),
  request_id: input.request_id || `snow-req-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`,
  validation_status: 'passed',

  // Set ServiceNow defaults
  priority: input.priority || '3',
  urgency: input.urgency || '3',
  requested_for: input.requested_for || 'network.admin@company.com',
  opened_by: input.opened_by || 'aruba.automation@company.com',
  assignment_group: input.assignment_group || 'Network Operations',
  approval_required: input.approval_required !== false, // Default to true
  state: input.state || 'open'
};

return [{ json: validatedInput }];

// Process ServiceNow service request operation results
const input = items[0].json;
const originalRequest = $('Validate Service Request').first().json;

let result = {
  request_id: originalRequest.request_id,
  operation: originalRequest.operation,
  request_type: originalRequest.request_type,
  timestamp: new Date().toISOString(),
  status: 'success',
  message: 'ServiceNow service request operation completed successfully'
};

try {
  if (input.error) {
    result.status = 'error';
    result.message = input.error.message || input.error;
    result.error_details = input.error;
  } else {
    result.data = input;

    // Operation-specific processing
    switch (originalRequest.operation) {
      case 'submit_request':
        result.message = `Service request submitted successfully: ${input.result?.number || 'Unknown'}`;
        result.request_number = input.result?.number;
        result.request_sys_id = input.result?.sys_id;
        result.request_state = input.result?.state;
        result.requested_for = originalRequest.requested_for;
        break;

      case 'get_request_status':
        result.message = `Request status retrieved: ${input.result?.state || 'Unknown'}`;
        result.request_sys_id = originalRequest.request_id;
        result.current_state = input.result?.state;
        result.approval_status = input.result?.approval;
        result.stage = input.result?.stage;
        break;

      case 'get_catalog_items':
        result.message = `Found ${input.result?.length || 0} catalog items`;
        result.catalog_count = input.result?.length || 0;
        result.catalog_items = input.result?.map(item => ({
          sys_id: item.sys_id,
          name: item.name,
          short_description: item.short_description,
          category: item.category,
          price: item.price
        })) || [];
        break;

      case 'approve_request':
      case 'reject_request':
        const action = originalRequest.operation === 'approve_request' ? 'approved' : 'rejected';
        result.message = `Request ${action} successfully`;
        result.approval_id = originalRequest.approval_id;
        result.approval_action = action;
        result.approver = originalRequest.approver;
        break;

      case 'fulfill_request':
        result.message = `Request ${originalRequest.request_item_id} fulfilled successfully`;
        result.request_item_id = originalRequest.request_item_id;
        result.deployment_status = originalRequest.deployment_status;
        result.device_serial = originalRequest.device_serial;
        result.device_ip = originalRequest.device_ip;
        break;

      default:
        result.message = `${originalRequest.operation} completed successfully`;
    }

    // Add request details if available
    if (originalRequest.device_type || originalRequest.location) {
      result.request_details = {
        device_type: originalRequest.device_type,
        location: originalRequest.location,
        vlan_id: originalRequest.vlan_id,
        ssid_name: originalRequest.ssid_name,
        justification: originalRequest.justification
      };
    }
  }
} catch (error) {
  result.status = 'error';
  result.message = 'Error processing ServiceNow service request operation result';
  result.error_details = error.message;
}

return [{ json: result }];

// Generate intelligent service request alerts
const result = items[0].json;
const originalRequest = $('Validate Service Request').first().json;

let notifications = [];

// Determine alert level and channels
let alertLevel = 'info';
let channels = ['#servicenow-requests'];

if (result.status === 'error') {
  alertLevel = 'error';
  channels.push('#network-alerts');
} else if (originalRequest.priority === '1' || originalRequest.priority === '2') {
  alertLevel = 'warning';
  channels.push('#network-operations');
}

// Create notifications for each channel
channels.forEach(channel => {
  let icon = 'ðŸ“‹';
  if (alertLevel === 'error') icon = 'âŒ';
  else if (alertLevel === 'warning') icon = 'âš ï¸';
  else if (result.status === 'success') icon = 'âœ…';

  let message = `${icon} **ServiceNow Service Request Operation**\\n`;
  message += `**Operation**: ${result.operation}\\n`;
  message += `**Status**: ${result.status.toUpperCase()}\\n`;
  message += `**Message**: ${result.message}\\n`;
  message += `**Time**: ${result.timestamp}\\n`;
  message += `**Request ID**: ${result.request_id}\\n`;

  // Add operation-specific details
  if (result.request_number) {
    message += `**Request Number**: ${result.request_number}\\n`;
  }

  if (result.request_sys_id) {
    message += `**Request System ID**: ${result.request_sys_id}\\n`;
  }

  if (result.requested_for) {
    message += `**Requested For**: ${result.requested_for}\\n`;
  }

  if (result.current_state) {
    message += `**Current State**: ${result.current_state}\\n`;
  }

  if (result.approval_action) {
    message += `**Approval Action**: ${result.approval_action.toUpperCase()}\\n`;
    message += `**Approver**: ${result.approver || 'System'}\\n`;
  }

  if (result.catalog_count !== undefined) {
    message += `**Catalog Items Found**: ${result.catalog_count}\\n`;
  }

  // Add request details
  if (result.request_details) {
    message += `\\n**Request Details**:\\n`;
    if (result.request_details.device_type) {
      message += `â€¢ **Device Type**: ${result.request_details.device_type}\\n`;
    }
    if (result.request_details.location) {
      message += `â€¢ **Location**: ${result.request_details.location}\\n`;
    }
    if (result.request_details.vlan_id) {
      message += `â€¢ **VLAN**: ${result.request_details.vlan_id}\\n`;
    }
    if (result.request_details.ssid_name) {
      message += `â€¢ **SSID**: ${result.request_details.ssid_name}\\n`;
    }
    if (result.request_details.justification) {
      message += `â€¢ **Justification**: ${result.request_details.justification}\\n`;
    }
  }

  // Add fulfillment details
  if (result.deployment_status) {
    message += `\\n**Fulfillment Details**:\\n`;
    message += `â€¢ **Deployment Status**: ${result.deployment_status}\\n`;
    if (result.device_serial) {
      message += `â€¢ **Device Serial**: ${result.device_serial}\\n`;
    }
    if (result.device_ip) {
      message += `â€¢ **Device IP**: ${result.device_ip}\\n`;
    }
  }

  // Add catalog items for catalog operations
  if (result.catalog_items && result.catalog_items.length > 0) {
    message += `\\n**Available Catalog Items**:\\n`;
    result.catalog_items.slice(0, 5).forEach(item => {
      message += `â€¢ ${item.name}: ${item.short_description}\\n`;
    });
    if (result.catalog_items.length > 5) {
      message += `â€¢ ... and ${result.catalog_items.length - 5} more items\\n`;
    }
  }

  // Add error details if applicable
  if (result.status === 'error' && result.error_details) {
    message += `\\n**Error Details**: ${JSON.stringify(result.error_details)}\\n`;
  }

  notifications.push({
    json: {
      channel: channel,
      message: message,
      alert_level: alertLevel,
      result: result
    }
  });
});

return notifications;
