// UXI Analytics and Reporting Workflow
// Operations: generate_report, get_analytics, create_dashboard, schedule_report, export_data, analyze_trends

const operation = $input.item(0).json.operation;
const reportData = $input.item(0).json.report_data || {};
const uxiApiUrl = $input.item(0).json.uxi_api_url || 'https://api.uxi.aruba.com';
const reportType = $input.item(0).json.report_type || 'network_performance';
const timeRange = $input.item(0).json.time_range || 'last_24_hours';
const sensorIds = $input.item(0).json.sensor_ids || [];
const dashboardId = $input.item(0).json.dashboard_id;

// Validate required parameters
if (!operation) {
  throw new Error('Operation is required. Valid operations: generate_report, get_analytics, create_dashboard, schedule_report, export_data, analyze_trends');
}

if (['generate_report', 'get_analytics', 'export_data', 'analyze_trends'].includes(operation) && sensorIds.length === 0) {
  throw new Error('At least one sensor ID is required for report generation and analytics operations');
}

if (['schedule_report'].includes(operation) && !reportType) {
  throw new Error('Report type is required for scheduling operations');
}

// Time range configurations
const timeRangeConfigs = {
  'last_hour': {
    start: new Date(Date.now() - 60 * 60 * 1000).toISOString(),
    end: new Date().toISOString(),
    granularity: 'minute',
    data_points: 60
  },
  'last_24_hours': {
    start: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString(),
    end: new Date().toISOString(),
    granularity: 'hour',
    data_points: 24
  },
  'last_7_days': {
    start: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000).toISOString(),
    end: new Date().toISOString(),
    granularity: 'day',
    data_points: 7
  },
  'last_30_days': {
    start: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString(),
    end: new Date().toISOString(),
    granularity: 'day',
    data_points: 30
  },
  'last_90_days': {
    start: new Date(Date.now() - 90 * 24 * 60 * 60 * 1000).toISOString(),
    end: new Date().toISOString(),
    granularity: 'week',
    data_points: 13
  },
  'custom': {
    start: reportData.start_time || new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString(),
    end: reportData.end_time || new Date().toISOString(),
    granularity: reportData.granularity || 'hour',
    data_points: reportData.data_points || 24
  }
};

// Report type configurations
const reportTypeConfigs = {
  'network_performance': {
    name: 'Network Performance Report',
    description: 'Comprehensive network performance analysis',
    metrics: [
      'wifi_signal_strength',
      'download_speed',
      'upload_speed',
      'latency',
      'jitter',
      'packet_loss',
      'connection_success_rate',
      'roaming_performance'
    ],
    visualizations: [
      'time_series_chart',
      'histogram',
      'heatmap',
      'summary_table'
    ],
    thresholds: {
      wifi_signal_strength: -70,
      download_speed: 25,
      upload_speed: 5,
      latency: 50,
      jitter: 20,
      packet_loss: 2
    }
  },
  'user_experience': {
    name: 'User Experience Report',
    description: 'End-user experience analysis and insights',
    metrics: [
      'overall_score',
      'application_performance',
      'connection_quality',
      'authentication_success',
      'user_satisfaction',
      'issue_frequency',
      'resolution_time'
    ],
    visualizations: [
      'score_trends',
      'satisfaction_gauge',
      'issue_breakdown',
      'comparative_analysis'
    ],
    thresholds: {
      overall_score: 8.0,
      application_performance: 7.5,
      connection_quality: 8.5,
      authentication_success: 95,
      user_satisfaction: 85
    }
  },
  'security_compliance': {
    name: 'Security Compliance Report',
    description: 'Security posture and compliance analysis',
    metrics: [
      'security_score',
      'compliance_percentage',
      'vulnerability_count',
      'security_incidents',
      'policy_violations',
      'certificate_status',
      'encryption_coverage'
    ],
    visualizations: [
      'compliance_dashboard',
      'security_trends',
      'vulnerability_matrix',
      'incident_timeline'
    ],
    thresholds: {
      security_score: 90,
      compliance_percentage: 95,
      vulnerability_count: 5,
      security_incidents: 2,
      policy_violations: 1
    }
  },
  'capacity_planning': {
    name: 'Capacity Planning Report',
    description: 'Network capacity and growth analysis',
    metrics: [
      'bandwidth_utilization',
      'user_density',
      'device_growth',
      'peak_usage_patterns',
      'capacity_forecasting',
      'resource_allocation',
      'scalability_metrics'
    ],
    visualizations: [
      'utilization_trends',
      'growth_projections',
      'capacity_heatmap',
      'resource_allocation_chart'
    ],
    thresholds: {
      bandwidth_utilization: 80,
      user_density: 100,
      peak_usage_threshold: 90,
      growth_rate: 15
    }
  },
  'application_analytics': {
    name: 'Application Analytics Report',
    description: 'Application performance and usage analysis',
    metrics: [
      'application_response_time',
      'transaction_success_rate',
      'error_rate',
      'throughput',
      'user_adoption',
      'feature_usage',
      'performance_trends'
    ],
    visualizations: [
      'response_time_trends',
      'success_rate_chart',
      'error_analysis',
      'usage_patterns'
    ],
    thresholds: {
      response_time: 2.0,
      success_rate: 95,
      error_rate: 5,
      throughput: 1000
    }
  },
  'device_analytics': {
    name: 'Device Analytics Report',
    description: 'Device performance and health analysis',
    metrics: [
      'device_health_score',
      'battery_performance',
      'connectivity_stability',
      'firmware_compliance',
      'hardware_utilization',
      'maintenance_needs',
      'lifecycle_status'
    ],
    visualizations: [
      'health_dashboard',
      'battery_trends',
      'connectivity_matrix',
      'maintenance_schedule'
    ],
    thresholds: {
      health_score: 85,
      battery_performance: 80,
      connectivity_stability: 95,
      firmware_compliance: 90
    }
  }
};

// Dashboard configurations
const dashboardConfigs = {
  'executive_summary': {
    name: 'Executive Summary Dashboard',
    widgets: [
      'network_health_overview',
      'user_experience_summary',
      'security_status',
      'capacity_utilization',
      'key_performance_indicators'
    ],
    refresh_interval: 300, // 5 minutes
    access_level: 'executive'
  },
  'network_operations': {
    name: 'Network Operations Dashboard',
    widgets: [
      'real_time_performance',
      'active_alerts',
      'device_status',
      'bandwidth_utilization',
      'security_monitoring'
    ],
    refresh_interval: 60, // 1 minute
    access_level: 'operator'
  },
  'user_experience': {
    name: 'User Experience Dashboard',
    widgets: [
      'experience_scores',
      'application_performance',
      'satisfaction_metrics',
      'issue_tracking',
      'resolution_trends'
    ],
    refresh_interval: 180, // 3 minutes
    access_level: 'analyst'
  }
};

// Generate configuration based on operation
let config = reportData;
const timeConfig = timeRangeConfigs[timeRange] || timeRangeConfigs['last_24_hours'];

if (operation === 'generate_report' && reportTypeConfigs[reportType]) {
  const reportConfig = reportTypeConfigs[reportType];
  config = {
    ...reportData,
    report_type: reportType,
    name: reportData.name || reportConfig.name,
    description: reportData.description || reportConfig.description,
    metrics: reportData.metrics || reportConfig.metrics,
    visualizations: reportData.visualizations || reportConfig.visualizations,
    thresholds: reportData.thresholds || reportConfig.thresholds,
    time_range: timeConfig,
    sensor_ids: sensorIds,
    format: reportData.format || 'pdf',
    include_raw_data: reportData.include_raw_data || false
  };
}

if (operation === 'get_analytics') {
  config = {
    ...reportData,
    metrics: reportData.metrics || ['all'],
    aggregation: reportData.aggregation || 'average',
    grouping: reportData.grouping || 'sensor',
    time_range: timeConfig,
    sensor_ids: sensorIds,
    filters: reportData.filters || {}
  };
}

if (operation === 'create_dashboard') {
  const dashboardType = reportData.dashboard_type || 'network_operations';
  const dashboardConfig = dashboardConfigs[dashboardType];
  config = {
    ...reportData,
    dashboard_type: dashboardType,
    name: reportData.name || dashboardConfig.name,
    widgets: reportData.widgets || dashboardConfig.widgets,
    refresh_interval: reportData.refresh_interval || dashboardConfig.refresh_interval,
    access_level: reportData.access_level || dashboardConfig.access_level,
    sensor_ids: sensorIds
  };
}

if (operation === 'schedule_report') {
  config = {
    ...reportData,
    report_type: reportType,
    schedule: {
      frequency: reportData.frequency || 'daily',
      time: reportData.time || '08:00',
      timezone: reportData.timezone || 'UTC',
      enabled: reportData.enabled !== undefined ? reportData.enabled : true
    },
    recipients: reportData.recipients || [],
    delivery_method: reportData.delivery_method || 'email',
    format: reportData.format || 'pdf'
  };
}

if (operation === 'export_data') {
  config = {
    ...reportData,
    export_format: reportData.export_format || 'csv',
    data_types: reportData.data_types || ['metrics', 'events'],
    time_range: timeConfig,
    sensor_ids: sensorIds,
    compression: reportData.compression || false,
    include_metadata: reportData.include_metadata || true
  };
}

if (operation === 'analyze_trends') {
  config = {
    ...reportData,
    analysis_type: reportData.analysis_type || 'time_series',
    metrics: reportData.metrics || ['network_performance'],
    trend_period: reportData.trend_period || 'daily',
    prediction_horizon: reportData.prediction_horizon || 7,
    confidence_level: reportData.confidence_level || 95,
    time_range: timeConfig,
    sensor_ids: sensorIds
  };
}

// Prepare output data
const outputData = {
  operation,
  config: config,
  report_type: reportType,
  time_range: timeRange,
  sensor_ids: sensorIds,
  dashboard_id: dashboardId,
  uxi_api_url: uxiApiUrl,
  timestamp: new Date().toISOString(),
  validation_rules: {
    sensor_ids_required: ['generate_report', 'get_analytics', 'export_data', 'analyze_trends'].includes(operation),
    report_type_required: ['generate_report', 'schedule_report'].includes(operation),
    dashboard_id_required: ['update_dashboard', 'delete_dashboard'].includes(operation)
  }
};

return [{ json: outputData }];

// Process UXI Analytics API response and prepare success response
const operation = $input.item(0).json.operation;
const apiResponse = $input.item(0).json;

let successMessage = '';
let responseData = {};

switch (operation) {
  case 'generate_report':
    successMessage = `Report '${apiResponse.name}' generated successfully`;
    responseData = {
      report_id: apiResponse.id,
      name: apiResponse.name,
      report_type: apiResponse.report_type,
      format: apiResponse.format,
      download_url: apiResponse.download_url,
      size: apiResponse.size,
      generated_at: apiResponse.generated_at,
      status: 'completed'
    };
    break;

  case 'get_analytics':
    successMessage = `Analytics data retrieved successfully`;
    responseData = {
      metrics: apiResponse.metrics,
      aggregations: apiResponse.aggregations,
      time_range: apiResponse.time_range,
      sensor_count: apiResponse.sensor_count,
      data_points: apiResponse.data_points,
      insights: apiResponse.insights,
      status: 'completed'
    };
    break;

  case 'create_dashboard':
    successMessage = `Dashboard '${apiResponse.name}' created successfully`;
    responseData = {
      dashboard_id: apiResponse.id,
      name: apiResponse.name,
      dashboard_type: apiResponse.dashboard_type,
      widgets: apiResponse.widgets,
      access_url: apiResponse.access_url,
      refresh_interval: apiResponse.refresh_interval,
      status: 'created'
    };
    break;

  case 'schedule_report':
    successMessage = `Report schedule created successfully`;
    responseData = {
      schedule_id: apiResponse.id,
      report_type: apiResponse.report_type,
      schedule: apiResponse.schedule,
      recipients: apiResponse.recipients,
      next_execution: apiResponse.next_execution,
      status: 'scheduled'
    };
    break;

  case 'export_data':
    successMessage = `Data export completed successfully`;
    responseData = {
      export_id: apiResponse.id,
      export_format: apiResponse.export_format,
      file_size: apiResponse.file_size,
      download_url: apiResponse.download_url,
      record_count: apiResponse.record_count,
      exported_at: apiResponse.exported_at,
      status: 'completed'
    };
    break;

  case 'analyze_trends':
    successMessage = `Trend analysis completed successfully`;
    responseData = {
      analysis_id: apiResponse.id,
      analysis_type: apiResponse.analysis_type,
      trends: apiResponse.trends,
      predictions: apiResponse.predictions,
      confidence_level: apiResponse.confidence_level,
      insights: apiResponse.insights,
      status: 'completed'
    };
    break;

  default:
    successMessage = 'Analytics operation completed successfully';
    responseData = apiResponse;
}

return [{
  json: {
    success: true,
    operation: operation,
    message: successMessage,
    data: responseData,
    timestamp: new Date().toISOString()
  }
}];

// Error handling for UXI analytics operations
const operation = $input.item(0).json.operation || 'unknown';
const error = $input.item(0).json.error || {};
const sensorIds = $input.item(0).json.sensor_ids || [];
const reportType = $input.item(0).json.report_type;

// Categorize error types
let errorCategory = 'unknown';
let errorMessage = error.message || 'Unknown error occurred';
let shouldRetry = false;
let rollbackRequired = false;

if (error.code) {
  switch (error.code) {
    case 'AUTHENTICATION_ERROR':
    case 401:
      errorCategory = 'authentication';
      errorMessage = 'UXI API authentication failed. Please check credentials.';
      shouldRetry = false;
      break;

    case 'AUTHORIZATION_ERROR':
    case 403:
      errorCategory = 'authorization';
      errorMessage = 'Insufficient permissions for UXI analytics operations.';
      shouldRetry = false;
      break;

    case 'INSUFFICIENT_DATA':
      errorCategory = 'data';
      errorMessage = 'Insufficient data available for the requested time range or sensors.';
      shouldRetry = false;
      break;

    case 'VALIDATION_ERROR':
    case 400:
      errorCategory = 'validation';
      errorMessage = 'Invalid analytics configuration parameters.';
      shouldRetry = false;
      break;

    case 'PROCESSING_ERROR':
      errorCategory = 'processing';
      errorMessage = 'Error processing analytics data. Please try again.';
      shouldRetry = true;
      break;

    case 'TIMEOUT_ERROR':
      errorCategory = 'timeout';
      errorMessage = 'Analytics operation timed out. Try reducing the time range or sensor count.';
      shouldRetry = false;
      break;

    case 'EXPORT_ERROR':
      errorCategory = 'export';
      errorMessage = 'Error exporting data. Check format and size limitations.';
      shouldRetry = true;
      break;

    case 'NETWORK_ERROR':
      errorCategory = 'network';
      errorMessage = 'Network connectivity issue with UXI API.';
      shouldRetry = true;
      break;

    case 'RATE_LIMIT_EXCEEDED':
    case 429:
      errorCategory = 'rate_limit';
      errorMessage = 'UXI API rate limit exceeded. Please retry later.';
      shouldRetry = true;
      break;

    case 'QUOTA_EXCEEDED':
      errorCategory = 'quota';
      errorMessage = 'Analytics quota exceeded. Check usage limits.';
      shouldRetry = false;
      break;

    case 'INTERNAL_ERROR':
    case 500:
      errorCategory = 'server';
      errorMessage = 'UXI API internal server error.';
      shouldRetry = true;
      break;

    default:
      errorCategory = 'unknown';
      shouldRetry = false;
  }
}

// Determine if rollback is required
if (['create_dashboard', 'schedule_report'].includes(operation)) {
  rollbackRequired = true;
}

// Generate error response
const errorResponse = {
  success: false,
  operation: operation,
  error: {
    category: errorCategory,
    message: errorMessage,
    code: error.code,
    details: error.details || {}
  },
  sensor_ids: sensorIds,
  report_type: reportType,
  should_retry: shouldRetry,
  rollback_required: rollbackRequired,
  timestamp: new Date().toISOString(),
  troubleshooting: {
    authentication: 'Verify UXI API credentials and token validity',
    authorization: 'Check UXI API permissions and analytics access',
    data: 'Verify sensor data availability for the requested time range',
    validation: 'Check analytics configuration parameters against API schema',
    processing: 'Retry operation or contact support if issue persists',
    timeout: 'Reduce time range or sensor count to avoid timeout',
    export: 'Check export format and size limitations',
    network: 'Verify network connectivity to UXI API endpoint',
    rate_limit: 'Implement exponential backoff and retry logic',
    quota: 'Check analytics usage quota and upgrade if needed',
    server: 'Contact UXI support if issue persists'
  }
};

return [{ json: errorResponse }];

