// UXI Sensor Management Workflow
// Operations: register_sensor, update_sensor, delete_sensor, list_sensors, configure_sensor, get_sensor_status

const operation = $input.item(0).json.operation;
const sensorData = $input.item(0).json.sensor_data || {};
const uxiApiUrl = $input.item(0).json.uxi_api_url || 'https://api.uxi.aruba.com';
const sensorId = $input.item(0).json.sensor_id;
const locationData = $input.item(0).json.location_data || {};
const environmentType = $input.item(0).json.environment_type || 'office';

// Validate required parameters
if (!operation) {
  throw new Error('Operation is required. Valid operations: register_sensor, update_sensor, delete_sensor, list_sensors, configure_sensor, get_sensor_status');
}

if (['update_sensor', 'delete_sensor', 'configure_sensor', 'get_sensor_status'].includes(operation) && !sensorId) {
  throw new Error('Sensor ID is required for update, delete, configure, and status operations');
}

if (['register_sensor', 'update_sensor'].includes(operation) && !sensorData.name) {
  throw new Error('Sensor name is required for register and update operations');
}

// Environment-specific sensor configurations
const environmentConfigs = {
  'office': {
    name: 'Office Environment',
    test_frequency: 300, // 5 minutes
    network_tests: ['wifi_connectivity', 'internet_speed', 'dns_resolution', 'dhcp_performance'],
    application_tests: ['web_browsing', 'email_performance', 'file_sharing', 'video_conferencing'],
    thresholds: {
      wifi_signal: -70,
      download_speed: 25,
      upload_speed: 5,
      latency: 50,
      jitter: 20,
      packet_loss: 2
    },
    location_settings: {
      mobility_enabled: true,
      location_accuracy: 'high',
      floor_plan_enabled: true
    }
  },
  'retail': {
    name: 'Retail Environment',
    test_frequency: 180, // 3 minutes
    network_tests: ['wifi_connectivity', 'internet_speed', 'pos_connectivity', 'guest_wifi'],
    application_tests: ['pos_performance', 'inventory_systems', 'customer_wifi', 'digital_signage'],
    thresholds: {
      wifi_signal: -65,
      download_speed: 50,
      upload_speed: 10,
      latency: 30,
      jitter: 15,
      packet_loss: 1
    },
    location_settings: {
      mobility_enabled: true,
      location_accuracy: 'high',
      customer_analytics: true,
      heat_map_enabled: true
    }
  },
  'healthcare': {
    name: 'Healthcare Environment',
    test_frequency: 120, // 2 minutes
    network_tests: ['wifi_connectivity', 'internet_speed', 'medical_device_connectivity', 'priority_traffic'],
    application_tests: ['ehr_performance', 'medical_imaging', 'patient_monitoring', 'communication_systems'],
    thresholds: {
      wifi_signal: -60,
      download_speed: 100,
      upload_speed: 20,
      latency: 20,
      jitter: 10,
      packet_loss: 0.5
    },
    location_settings: {
      mobility_enabled: true,
      location_accuracy: 'very_high',
      asset_tracking: true,
      patient_tracking: true,
      emergency_mode: true
    }
  },
  'education': {
    name: 'Education Environment',
    test_frequency: 240, // 4 minutes
    network_tests: ['wifi_connectivity', 'internet_speed', 'student_network', 'admin_network'],
    application_tests: ['learning_management', 'video_streaming', 'collaboration_tools', 'device_management'],
    thresholds: {
      wifi_signal: -67,
      download_speed: 30,
      upload_speed: 10,
      latency: 40,
      jitter: 25,
      packet_loss: 2
    },
    location_settings: {
      mobility_enabled: true,
      location_accuracy: 'high',
      classroom_analytics: true,
      device_tracking: true
    }
  },
  'manufacturing': {
    name: 'Manufacturing Environment',
    test_frequency: 60, // 1 minute
    network_tests: ['wifi_connectivity', 'internet_speed', 'iot_connectivity', 'scada_network'],
    application_tests: ['manufacturing_systems', 'iot_sensors', 'quality_control', 'inventory_tracking'],
    thresholds: {
      wifi_signal: -65,
      download_speed: 20,
      upload_speed: 5,
      latency: 25,
      jitter: 15,
      packet_loss: 1
    },
    location_settings: {
      mobility_enabled: true,
      location_accuracy: 'high',
      asset_tracking: true,
      environmental_monitoring: true,
      safety_compliance: true
    }
  },
  'hospitality': {
    name: 'Hospitality Environment',
    test_frequency: 300, // 5 minutes
    network_tests: ['wifi_connectivity', 'internet_speed', 'guest_wifi', 'property_management'],
    application_tests: ['guest_services', 'streaming_services', 'property_systems', 'mobile_apps'],
    thresholds: {
      wifi_signal: -70,
      download_speed: 50,
      upload_speed: 15,
      latency: 35,
      jitter: 20,
      packet_loss: 1.5
    },
    location_settings: {
      mobility_enabled: true,
      location_accuracy: 'medium',
      guest_analytics: true,
      service_optimization: true
    }
  }
};

// Sensor type configurations
const sensorTypes = {
  'hardware': {
    name: 'Hardware Sensor',
    capabilities: ['wifi_testing', 'ethernet_testing', 'bluetooth_testing', 'cellular_testing'],
    deployment: 'physical',
    power_options: ['poe', 'battery', 'usb'],
    mounting_options: ['wall', 'ceiling', 'desktop', 'mobile']
  },
  'software': {
    name: 'Software Sensor',
    capabilities: ['network_testing', 'application_testing', 'user_simulation'],
    deployment: 'virtual',
    supported_platforms: ['windows', 'macos', 'linux', 'mobile'],
    installation_methods: ['agent', 'browser', 'mobile_app']
  },
  'cloud': {
    name: 'Cloud Sensor',
    capabilities: ['external_testing', 'cdn_testing', 'global_reach'],
    deployment: 'cloud',
    regions: ['us-east', 'us-west', 'eu-central', 'ap-southeast'],
    test_origins: ['multiple_locations', 'provider_networks']
  }
};

// Generate sensor configuration based on environment
let sensorConfig = sensorData;
if (operation === 'register_sensor' && environmentType && environmentConfigs[environmentType]) {
  const envConfig = environmentConfigs[environmentType];
  sensorConfig = {
    ...sensorData,
    environment_type: environmentType,
    test_frequency: envConfig.test_frequency,
    network_tests: envConfig.network_tests,
    application_tests: envConfig.application_tests,
    thresholds: envConfig.thresholds,
    location_settings: envConfig.location_settings
  };
}

// Add location data if provided
if (locationData && Object.keys(locationData).length > 0) {
  sensorConfig.location = {
    name: locationData.name || 'Unknown Location',
    address: locationData.address || '',
    coordinates: {
      latitude: locationData.latitude || 0,
      longitude: locationData.longitude || 0
    },
    floor: locationData.floor || 1,
    building: locationData.building || '',
    room: locationData.room || '',
    zone: locationData.zone || ''
  };
}

// Prepare output data
const outputData = {
  operation,
  sensor_id: sensorId,
  sensor_config: sensorConfig,
  uxi_api_url: uxiApiUrl,
  environment_type: environmentType,
  timestamp: new Date().toISOString(),
  validation_rules: {
    name_required: ['register_sensor', 'update_sensor'].includes(operation),
    id_required: ['update_sensor', 'delete_sensor', 'configure_sensor', 'get_sensor_status'].includes(operation),
    location_recommended: ['register_sensor', 'update_sensor'].includes(operation)
  }
};

// Add operation-specific data
if (operation === 'list_sensors') {
  outputData.list_filters = {
    environment_type: sensorData.environment_type || 'all',
    status: sensorData.status || 'all',
    location: sensorData.location || 'all',
    limit: sensorData.limit || 100
  };
}

if (operation === 'get_sensor_status') {
  outputData.status_checks = {
    connectivity: true,
    battery_level: true,
    test_results: true,
    location_accuracy: true,
    firmware_version: true
  };
}

if (operation === 'configure_sensor') {
  outputData.configuration_options = {
    test_frequency: sensorConfig.test_frequency,
    network_tests: sensorConfig.network_tests,
    application_tests: sensorConfig.application_tests,
    thresholds: sensorConfig.thresholds,
    location_settings: sensorConfig.location_settings
  };
}

return [{ json: outputData }];

// Process UXI API response and prepare success response
const operation = $input.item(0).json.operation;
const apiResponse = $input.item(0).json;

let successMessage = '';
let responseData = {};

switch (operation) {
  case 'register_sensor':
    successMessage = `Sensor '${apiResponse.name}' registered successfully`;
    responseData = {
      sensor_id: apiResponse.id,
      name: apiResponse.name,
      environment_type: apiResponse.environment_type,
      location: apiResponse.location,
      status: 'registered'
    };
    break;

  case 'update_sensor':
    successMessage = `Sensor '${apiResponse.name}' updated successfully`;
    responseData = {
      sensor_id: apiResponse.id,
      name: apiResponse.name,
      changes_applied: apiResponse.changes_applied,
      status: 'updated'
    };
    break;

  case 'delete_sensor':
    successMessage = `Sensor deleted successfully`;
    responseData = {
      sensor_id: apiResponse.id,
      status: 'deleted',
      cleanup_completed: true
    };
    break;

  case 'list_sensors':
    successMessage = `Retrieved ${apiResponse.sensors.length} sensors`;
    responseData = {
      total_sensors: apiResponse.sensors.length,
      sensors: apiResponse.sensors,
      filters_applied: apiResponse.filters
    };
    break;

  case 'configure_sensor':
    successMessage = `Sensor configuration updated successfully`;
    responseData = {
      sensor_id: apiResponse.id,
      configuration_applied: apiResponse.configuration,
      status: 'configured'
    };
    break;

  case 'get_sensor_status':
    successMessage = `Sensor status retrieved successfully`;
    responseData = {
      sensor_id: apiResponse.id,
      connectivity: apiResponse.connectivity,
      battery_level: apiResponse.battery_level,
      test_results: apiResponse.test_results,
      location_accuracy: apiResponse.location_accuracy,
      firmware_version: apiResponse.firmware_version,
      last_updated: apiResponse.last_updated
    };
    break;

  default:
    successMessage = 'Operation completed successfully';
    responseData = apiResponse;
}

return [{
  json: {
    success: true,
    operation: operation,
    message: successMessage,
    data: responseData,
    timestamp: new Date().toISOString()
  }
}];

// Error handling for UXI sensor operations
const operation = $input.item(0).json.operation || 'unknown';
const error = $input.item(0).json.error || {};
const sensorId = $input.item(0).json.sensor_id;

// Categorize error types
let errorCategory = 'unknown';
let errorMessage = error.message || 'Unknown error occurred';
let shouldRetry = false;
let rollbackRequired = false;

if (error.code) {
  switch (error.code) {
    case 'AUTHENTICATION_ERROR':
    case 401:
      errorCategory = 'authentication';
      errorMessage = 'UXI API authentication failed. Please check credentials.';
      shouldRetry = false;
      break;

    case 'AUTHORIZATION_ERROR':
    case 403:
      errorCategory = 'authorization';
      errorMessage = 'Insufficient permissions for UXI API operation.';
      shouldRetry = false;
      break;

    case 'SENSOR_NOT_FOUND':
    case 404:
      errorCategory = 'not_found';
      errorMessage = `Sensor with ID '${sensorId}' not found.`;
      shouldRetry = false;
      break;

    case 'VALIDATION_ERROR':
    case 400:
      errorCategory = 'validation';
      errorMessage = 'Invalid sensor configuration parameters.';
      shouldRetry = false;
      break;

    case 'NETWORK_ERROR':
    case 'TIMEOUT':
      errorCategory = 'network';
      errorMessage = 'Network connectivity issue with UXI API.';
      shouldRetry = true;
      break;

    case 'RATE_LIMIT_EXCEEDED':
    case 429:
      errorCategory = 'rate_limit';
      errorMessage = 'UXI API rate limit exceeded. Please retry later.';
      shouldRetry = true;
      break;

    case 'SENSOR_CONFLICT':
    case 409:
      errorCategory = 'conflict';
      errorMessage = 'Sensor name or configuration conflicts with existing sensor.';
      shouldRetry = false;
      break;

    case 'INTERNAL_ERROR':
    case 500:
      errorCategory = 'server';
      errorMessage = 'UXI API internal server error.';
      shouldRetry = true;
      break;

    default:
      errorCategory = 'unknown';
      shouldRetry = false;
  }
}

// Determine if rollback is required
if (['register_sensor', 'update_sensor', 'configure_sensor'].includes(operation)) {
  rollbackRequired = true;
}

// Generate error response
const errorResponse = {
  success: false,
  operation: operation,
  error: {
    category: errorCategory,
    message: errorMessage,
    code: error.code,
    details: error.details || {}
  },
  sensor_id: sensorId,
  should_retry: shouldRetry,
  rollback_required: rollbackRequired,
  timestamp: new Date().toISOString(),
  troubleshooting: {
    authentication: 'Verify UXI API credentials and token validity',
    authorization: 'Check UXI API permissions and user roles',
    not_found: 'Verify sensor ID exists in UXI dashboard',
    validation: 'Check sensor configuration parameters against API schema',
    network: 'Verify network connectivity to UXI API endpoint',
    rate_limit: 'Implement exponential backoff and retry logic',
    conflict: 'Use unique sensor names and check for existing configurations',
    server: 'Contact UXI support if issue persists'
  }
};

return [{ json: errorResponse }];

